INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(1, '&lt;p&gt;اَلا یا اَیُّهَا السّاقی اَدِرْ کَأسَاً و ناوِلْها&lt;/p&gt;&lt;p&gt;که عشق آسان نمود اوّل ولی افتاد مشکل‌ها&lt;/p&gt;&lt;p&gt;به بویِ نافه‌ای کآخر صبا زان طُرّه بگشاید&lt;/p&gt;&lt;p&gt;ز تابِ جَعدِ مشکینش چه خون افتاد در دل‌ها&lt;/p&gt;&lt;p&gt;مرا در منزلِ جانان چه امنِ عیش چون هر دَم&lt;/p&gt;&lt;p&gt;جَرَس فریاد می‌دارد که بَربندید مَحمِل‌ها&lt;/p&gt;&lt;p&gt;به مِی سجّاده رنگین کن گَرت پیرِ مُغان گوید&lt;/p&gt;&lt;p&gt;که سالِک بی‌خبر نَبوَد ز راه و رسمِ منزل‌ها&lt;/p&gt;&lt;p&gt;شبِ تاریک و بیمِ موج و گردابی چنین هایل&lt;/p&gt;&lt;p&gt;کجا دانند حالِ ما سبکبارانِ ساحل‌ها&lt;/p&gt;&lt;p&gt;همه کارم ز خودکامی به بدنامی کشید آخِر&lt;/p&gt;&lt;p&gt;نهان کِی مانَد آن رازی کزو سازند محفل‌ها&lt;/p&gt;&lt;p&gt;حضوری گر همی‌خواهی از او غایب مشو حافظ&lt;/p&gt;&lt;p&gt;مَتٰی ما تَلْقَ مَنْ تَهْویٰ دَعِ الدُّنْیا و اَهْمِلْها&lt;/p&gt;', 'غزل شماره ۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(2, '&lt;p&gt;صلاحِ کار کجا و منِ خراب کجا&lt;/p&gt;&lt;p&gt;ببین تفاوتِ ره کز کجاست تا به کجا&lt;/p&gt;&lt;p&gt;دلم ز صومعه بگرفت و خِرقِهٔ سالوس&lt;/p&gt;&lt;p&gt;کجاست دیرِ مُغان و شرابِ ناب کجا&lt;/p&gt;&lt;p&gt;چه نسبت است به‌رندی صَلاح و تقوا را&lt;/p&gt;&lt;p&gt;سماعِ وعظ کجا نغمهٔ رَباب کجا&lt;/p&gt;&lt;p&gt;ز رویِ دوست دلِ دشمنان چه دریابد&lt;/p&gt;&lt;p&gt;چراغِ مرده کجا شمعِ آفتاب کجا&lt;/p&gt;&lt;p&gt;چو کُحلِ بینشِ ما خاکِ آستانِ شماست&lt;/p&gt;&lt;p&gt;کجا رویم بفرما ازین جناب کجا&lt;/p&gt;&lt;p&gt;مَبین به سیبِ زَنَخدان که چاه در راه است&lt;/p&gt;&lt;p&gt;کجا همی‌ رَوی ای دل بدین شتاب کجا&lt;/p&gt;&lt;p&gt;بشد که یاد خوشش باد روزگارِ وصال&lt;/p&gt;&lt;p&gt;خود آن کرشمه کجا رفت و آن عِتاب کجا&lt;/p&gt;&lt;p&gt;قرار و خواب ز حافظ طمع مدار ای دوست&lt;/p&gt;&lt;p&gt;قرار چیست صبوری کدام و خواب کجا&lt;/p&gt;', 'غزل شماره ۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(3, '&lt;p&gt;اگر آن تُرک شیرازی به دست آرد دل ما را&lt;/p&gt;&lt;p&gt;به خال هِندویَش بخشم سمرقند و بخارا را&lt;/p&gt;&lt;p&gt;بده ساقی مِیِ باقی که در جنّت نخواهی یافت&lt;/p&gt;&lt;p&gt;کنار آب رُکن‌آباد و گُلگَشت مُصَلّا را&lt;/p&gt;&lt;p&gt;فَغان کـ‌این لولیانِ شوخِ شیرین‌کارِ شهرآشوب&lt;/p&gt;&lt;p&gt;چنان بردند صبر از دل، که تُرکان خوان یغما را&lt;/p&gt;&lt;p&gt;ز عشقِ ناتمامِ ما جمالِ یار، مُستَغنی است&lt;/p&gt;&lt;p&gt;به آب و رنگ و خال و خط، چه حاجت روی زیبا را؟&lt;/p&gt;&lt;p&gt;من از آن حُسن روزافزون که یوسف داشت دانستم&lt;/p&gt;&lt;p&gt;که عشق از پردهٔ عصمت برون آرد زلیخا را&lt;/p&gt;&lt;p&gt;اگر دشنام فرمایی و گر نفرین، دعا گویم&lt;/p&gt;&lt;p&gt;جوابِ تلخ می‌زیبد، لبِ لعلِ شکرخا را&lt;/p&gt;&lt;p&gt;نصیحت گوش کن جانا، که از جان دوست‌تر دارند&lt;/p&gt;&lt;p&gt;جوانان سعادتمند پند پیر دانا را&lt;/p&gt;&lt;p&gt;حدیث از مطرب و مِی گو و راز دَهر کمتر جو&lt;/p&gt;&lt;p&gt;که کس نگشود و نگشاید به حکمت این معما را&lt;/p&gt;&lt;p&gt;غزل گفتی و دُر سفتی، بیا و خوش بخوان حافظ&lt;/p&gt;&lt;p&gt;که بر نظم تو افشاند فلک عِقد ثریّا را&lt;/p&gt;', 'غزل شماره ۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(4, '&lt;p&gt;صبا به لطف بگو آن غزال رعنا را&lt;/p&gt;&lt;p&gt;که سر به کوه و بیابان تو داده‌ای ما را&lt;/p&gt;&lt;p&gt;شکرفروش که عمرش دراز باد چرا&lt;/p&gt;&lt;p&gt;تَفَقُّدی نکند طوطی شکرخا را&lt;/p&gt;&lt;p&gt;غرور حُسنت اجازت مگر نداد ای گل؟&lt;/p&gt;&lt;p&gt;که پرسشی نکنی عَندَلیب شیدا را&lt;/p&gt;&lt;p&gt;به خُلق و لطف توان کرد صید اهل نظر&lt;/p&gt;&lt;p&gt;به بند و دام نگیرند مرغ دانا را&lt;/p&gt;&lt;p&gt;ندانم از چه سبب رنگ آشنایی نیست&lt;/p&gt;&lt;p&gt;سَهی‌ قدانِ سیَه‌ چشمِ ماه‌ سیما را&lt;/p&gt;&lt;p&gt;چو با حبیب نشینی و باده پیمایی&lt;/p&gt;&lt;p&gt;به یاد دار مُحِبّان بادپیما را&lt;/p&gt;&lt;p&gt;جز این قَدَر نتوان گفت در جمال تو عیب&lt;/p&gt;&lt;p&gt;که وضعِ مِهر و وفا نیست روی زیبا را&lt;/p&gt;&lt;p&gt;در آسمان نه عجب گر به گفتهٔ حافظ&lt;/p&gt;&lt;p&gt;سرود زُهره به رقص آورد مسیحا را&lt;/p&gt;', 'غزل شماره ۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(5, '&lt;p&gt;دل می‌رود ز دستم صاحب‌دلان خدا را&lt;/p&gt;&lt;p&gt;دردا که راز پنهان خواهد شد آشکارا&lt;/p&gt;&lt;p&gt;کشتی‌شکستگانیم ای باد شُرطِه برخیز&lt;/p&gt;&lt;p&gt;باشد که باز بینم دیدار آشنا را&lt;/p&gt;&lt;p&gt;ده‌روزه مِهر گردون، افسانه است و افسون&lt;/p&gt;&lt;p&gt;نیکی به جای یاران فرصت شمار یارا&lt;/p&gt;&lt;p&gt;در حلقهٔ گل‌ و مُل خوش خواند دوش بلبل&lt;/p&gt;&lt;p&gt;هاتِ الصَّبُوحَ هُبّوا یا ایُّها السُکارا&lt;/p&gt;&lt;p&gt;ای صاحب کرامت شکرانهٔ سلامت&lt;/p&gt;&lt;p&gt;روزی تَفَقُّدی کن درویش بی‌نوا را&lt;/p&gt;&lt;p&gt;آسایش دو گیتی تفسیر این دو حرف است&lt;/p&gt;&lt;p&gt;با دوستان مروت با دشمنان مدارا&lt;/p&gt;&lt;p&gt;در کوی نیک‌نامی ما را گذر ندادند&lt;/p&gt;&lt;p&gt;گر تو نمی‌پسندی تغییر کن قضا را&lt;/p&gt;&lt;p&gt;آن تلخ‌وَش که صوفی ام‌ُّالخَبائِثَش خواند&lt;/p&gt;&lt;p&gt;اَشهی لَنا و اَحلی مِن قُبلَةِ العَذارا&lt;/p&gt;&lt;p&gt;هنگام تنگ‌دستی در عیش کوش و مستی&lt;/p&gt;&lt;p&gt;کاین کیمیای هستی قارون کُنَد گدا را&lt;/p&gt;&lt;p&gt;سرکش مشو که چون شمع از غیرتت بسوزد&lt;/p&gt;&lt;p&gt;دلبر که در کف او موم است سنگ خارا&lt;/p&gt;&lt;p&gt;آیینهٔ سکندر، جام می است بنگر&lt;/p&gt;&lt;p&gt;تا بر تو عرضه دارد احوال مُلک دارا&lt;/p&gt;&lt;p&gt;خوبان پارسی‌گو، بخشندگان عمرند&lt;/p&gt;&lt;p&gt;ساقی بده بشارت رندان پارسا را&lt;/p&gt;&lt;p&gt;حافظ به خود نپوشید این خرقهٔ مِی‌ْآلود&lt;/p&gt;&lt;p&gt;ای شیخ پاک‌دامن معذور دار ما را&lt;/p&gt;', 'غزل شماره ۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(6, '&lt;p&gt;به ملازمان سلطان که رساند این دعا را؟&lt;/p&gt;&lt;p&gt;که به شُکرِ پادشاهی ز نظر مران گدا را&lt;/p&gt;&lt;p&gt;ز رقیب دیوسیرت به خدای خود پناهم&lt;/p&gt;&lt;p&gt;مگر آن شهاب ثاقب مددی دهد خدا را&lt;/p&gt;&lt;p&gt;مژهٔ سیاهت ار کرد به خون ما اشارت&lt;/p&gt;&lt;p&gt;ز فریب او بیندیش و غلط مکن نگارا&lt;/p&gt;&lt;p&gt;دل عالمی بسوزی چو عِذار برفُروزی&lt;/p&gt;&lt;p&gt;تو از این چه سود داری که نمی‌کنی مدارا&lt;/p&gt;&lt;p&gt;همه شب در این امیدم که نسیم صبحگاهی&lt;/p&gt;&lt;p&gt;به پیام آشنایان بنوازد آشنا را&lt;/p&gt;&lt;p&gt;چه قیامت است جانا که به عاشقان نمودی؟&lt;/p&gt;&lt;p&gt;دل و جان فدای رویت بنما عِذار، ما را&lt;/p&gt;&lt;p&gt;به خدا که جرعه‌ای ده تو به حافظ سحرخیز&lt;/p&gt;&lt;p&gt;که دعای صبحگاهی اثری کند شما را&lt;/p&gt;', 'غزل شماره ۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(7, '&lt;p&gt;صوفی بیا که آینه صافیست جام را&lt;/p&gt;&lt;p&gt;تا بنگری صفای می لعل‌ فام را&lt;/p&gt;&lt;p&gt;راز درون پرده ز رندان مست پرس&lt;/p&gt;&lt;p&gt;کاین حال نیست زاهد عالی‌مقام را&lt;/p&gt;&lt;p&gt;عَنقا شکار کَس نشود دام بازچین&lt;/p&gt;&lt;p&gt;کآنجا همیشه باد به دست است، دام را&lt;/p&gt;&lt;p&gt;در بزم دور، یک‌ دو قدح درکش و برو&lt;/p&gt;&lt;p&gt;یعنی طمع مدار وصال دوام را&lt;/p&gt;&lt;p&gt;ای دل شباب رفت و نچیدی گلی ز عیش&lt;/p&gt;&lt;p&gt;پیرانه‌ سر مکن هنری ننگ و نام را&lt;/p&gt;&lt;p&gt;در عیش نقد کوش که چون آبخور نماند&lt;/p&gt;&lt;p&gt;آدمْ بهشتْ، روضهٔ دارُالسَلام را&lt;/p&gt;&lt;p&gt;ما را بر آستان تو بس حق خدمت است&lt;/p&gt;&lt;p&gt;ای خواجه بازبین به تَرَحُّم غلام را&lt;/p&gt;&lt;p&gt;حافظ مرید جام می است ای صبا برو&lt;/p&gt;&lt;p&gt;وز بنده بندگی برسان شیخ جام را&lt;/p&gt;', 'غزل شماره ۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(8, '&lt;p&gt;ساقیا برخیز و دَردِه جام را&lt;/p&gt;&lt;p&gt;خاک بر سر کن غمِ ایام را&lt;/p&gt;&lt;p&gt;ساغرِ مِی بر کَفَم نِه تا ز بَر&lt;/p&gt;&lt;p&gt;بَرکِشَم این دلقِ اَزرَق‌ فام را&lt;/p&gt;&lt;p&gt;گر چه بدنامی‌ ست نزد عاقلان&lt;/p&gt;&lt;p&gt;ما نمی‌ خواهیم ننگ و نام را&lt;/p&gt;&lt;p&gt;باده دَردِه چند از این بادِ غرور&lt;/p&gt;&lt;p&gt;خاک بر سر، نفسِ نافرجام را&lt;/p&gt;&lt;p&gt;دودِ آهِ سینهٔ نالانِ من&lt;/p&gt;&lt;p&gt;سوخت این افسردگانِ خام را&lt;/p&gt;&lt;p&gt;محرمِ رازِ دلِ شیدایِ خود&lt;/p&gt;&lt;p&gt;کس نمی‌بینم ز خاص و عام را&lt;/p&gt;&lt;p&gt;با دلارامی مرا خاطر خوش است&lt;/p&gt;&lt;p&gt;کز دلم یک باره بُرد آرام را&lt;/p&gt;&lt;p&gt;ننگرد دیگر به سرو اندر چمن&lt;/p&gt;&lt;p&gt;هرکه دید آن سروِ سیم‌اندام را&lt;/p&gt;&lt;p&gt;صبر کن حافظ به سختی روز و شب&lt;/p&gt;&lt;p&gt;عاقبت روزی بیابی کام را&lt;/p&gt;', 'غزل شماره ۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(9, '&lt;p&gt;رونق عهد شباب است دگر بُستان را&lt;/p&gt;&lt;p&gt;می‌ رسد مژدهٔ گل بلبل خوش‌الحان را&lt;/p&gt;&lt;p&gt;ای صبا گر به جوانان چمن باز رَسی&lt;/p&gt;&lt;p&gt;خدمت ما برسان سرو و گل و ریحان را&lt;/p&gt;&lt;p&gt;گر چنین جلوه کند مغبچهٔ باده‌ فروش&lt;/p&gt;&lt;p&gt;خاک‌ روبِ درِ میخانه کنم مژگان را&lt;/p&gt;&lt;p&gt;ای که بر مه کشی از عَنبرِ سارا چوگان&lt;/p&gt;&lt;p&gt;مضطرب‌حال مگردان، من سرگردان را&lt;/p&gt;&lt;p&gt;ترسم این قوم که بر دُردکشان می‌خندند&lt;/p&gt;&lt;p&gt;در سر کار خرابات کنند ایمان را&lt;/p&gt;&lt;p&gt;یار مردان خدا باش که در کشتی نوح&lt;/p&gt;&lt;p&gt;هست خاکی که به آبی نخرد طوفان را&lt;/p&gt;&lt;p&gt;برو از خانهٔ گردون به در و نان مطلب&lt;/p&gt;&lt;p&gt;کـ‌آن سیه‌کاسه در آخر بِکُشد مهمان را&lt;/p&gt;&lt;p&gt;هر که را خوابگه آخر مشتی خاک است&lt;/p&gt;&lt;p&gt;گو چه حاجت که به افلاک کشی ایوان را&lt;/p&gt;&lt;p&gt;ماه کنعانی من! مسند مصر آنِ تو شد&lt;/p&gt;&lt;p&gt;وقت آن است که بدرود کنی زندان را&lt;/p&gt;&lt;p&gt;حافظا می خور و رندی کن و خوش باش ولی&lt;/p&gt;&lt;p&gt;دام تزویر مکن چون دگران قرآن را&lt;/p&gt;', 'غزل شماره ۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(10, '&lt;p&gt;دوش از مسجد سوی میخانه آمد پیر ما&lt;/p&gt;&lt;p&gt;چیست یاران طریقت بعد از این تدبیر ما&lt;/p&gt;&lt;p&gt;ما مریدان روی سوی قبله چون آریم؟ چون&lt;/p&gt;&lt;p&gt;روی سوی خانهٔ خَمّار دارد پیر ما&lt;/p&gt;&lt;p&gt;در خرابات طریقت ما به هم منزل شویم&lt;/p&gt;&lt;p&gt;کـ‌این‌ چنین رفته‌ ست در عهد ازل تقدیر ما&lt;/p&gt;&lt;p&gt;عقل اگر داند که دل در بند زلفش چون خوش است&lt;/p&gt;&lt;p&gt;عاقلان دیوانه گردند از پی زنجیر ما&lt;/p&gt;&lt;p&gt;روی خوبت آیتی از لطف بر ما کشف کرد&lt;/p&gt;&lt;p&gt;زان زمان جز لطف و خوبی نیست در تفسیر ما&lt;/p&gt;&lt;p&gt;با دل سنگینت آیا هیچ درگیرد شبی؟&lt;/p&gt;&lt;p&gt;آه آتشناک و سوز سینهٔ شبگیر ما&lt;/p&gt;&lt;p&gt;تیر آه ما ز گردون بگذرد حافظ خموش&lt;/p&gt;&lt;p&gt;رحم کن بر جان خود پرهیز کن از تیر ما&lt;/p&gt;', 'غزل شماره ۱۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(11, '&lt;p&gt;ساقی به نور باده برافروز جامِ ما&lt;/p&gt;&lt;p&gt;مطرب بگو که کارِ جهان شُد به کامِ ما&lt;/p&gt;&lt;p&gt;ما در پیاله عکس رخ یار دیده‌ایم&lt;/p&gt;&lt;p&gt;ای بی‌خبر ز لذتِ شربِ مدامِ ما&lt;/p&gt;&lt;p&gt;هرگز نمیرد آن که دلش زنده شد به عشق&lt;/p&gt;&lt;p&gt;ثبت است بر جریدهٔ عالم دوام ما&lt;/p&gt;&lt;p&gt;چندان بُوَد کرشمه و نازِ سَهی‌قدان&lt;/p&gt;&lt;p&gt;کآید به جلوه سروِ صنوبرخرام ما&lt;/p&gt;&lt;p&gt;ای باد اگر به گلشن اَحباب بگذری&lt;/p&gt;&lt;p&gt;زِنهار عرضه دِه بَرِ جانان پیام ما&lt;/p&gt;&lt;p&gt;گو نام ما ز یاد به عمداً چه می‌بری؟&lt;/p&gt;&lt;p&gt;خود آید آن که یاد نیاری ز نام ما&lt;/p&gt;&lt;p&gt;مستی به چشمِ شاهدِ دلبندِ ما خوش است&lt;/p&gt;&lt;p&gt;زآن رو سپرده‌اند به مستی زمام ما&lt;/p&gt;&lt;p&gt;ترسم که صَرفه‌ای نَبَرَد روز بازخواست&lt;/p&gt;&lt;p&gt;نانِ حلالِ شیخ، ز آب حرام ما&lt;/p&gt;&lt;p&gt;حافظ ز دیده، دانهٔ اشکی همی‌فشان&lt;/p&gt;&lt;p&gt;باشد که مرغِ وصل کُند قصدِ دام ما&lt;/p&gt;&lt;p&gt;دریای اَخضَر فلک و کَشتی هِلال&lt;/p&gt;&lt;p&gt;هستند غرق نعمتِ حاجی‌قوام ما&lt;/p&gt;', 'غزل شماره ۱۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(12, '&lt;p&gt;ای فروغِ ماهِ حُسن، از روی رخشان شما&lt;/p&gt;&lt;p&gt;آبروی خوبی از چاه زَنَخدان شما&lt;/p&gt;&lt;p&gt;عزم دیدار تو دارد جانِ بر لب آمده&lt;/p&gt;&lt;p&gt;باز گردد یا برآید؟ چیست فرمان شما؟&lt;/p&gt;&lt;p&gt;کَس به دور نرگست طرفی نبست از عافیت&lt;/p&gt;&lt;p&gt;بِه که نفروشند مستوری به مستان شما&lt;/p&gt;&lt;p&gt;بخت خواب آلود ما بیدار خواهد شد مگر&lt;/p&gt;&lt;p&gt;زان که زد بر دیده آبی، روی رخشان شما&lt;/p&gt;&lt;p&gt;با صبا همراه بفرست از رخت گل دسته‌ای&lt;/p&gt;&lt;p&gt;بو که بویی بشنویم از خاک بستان شما&lt;/p&gt;&lt;p&gt;عمرتان باد و مراد ای ساقیانِ بزمِ جم&lt;/p&gt;&lt;p&gt;گر چه جام ما نشد پُر مِی به دوران شما&lt;/p&gt;&lt;p&gt;دل خرابی می‌کند، دلدار را آگه کنید&lt;/p&gt;&lt;p&gt;زینهار ای دوستان جان من و جان شما&lt;/p&gt;&lt;p&gt;کی دهد دست این غرض یا رب که همدستان شوند&lt;/p&gt;&lt;p&gt;خاطر مجموع ما، زلف پریشان شما&lt;/p&gt;&lt;p&gt;دور دار از خاک و خون دامن، چو بر ما بگذری&lt;/p&gt;&lt;p&gt;کَاندَر این ره کشته بسیارند، قربان شما&lt;/p&gt;&lt;p&gt;میکند حافظ دعایی، بشنو، آمینی بگو&lt;/p&gt;&lt;p&gt;روزی ما باد لعل شَکَّرافشان شما&lt;/p&gt;&lt;p&gt;ای صبا با ساکنانِ شهرِ یزد از ما بگو&lt;/p&gt;&lt;p&gt;کِای سر حق ناشناسان گوی چوگان شما&lt;/p&gt;&lt;p&gt;گر چه دوریم از بساط قُرب، همت دور نیست&lt;/p&gt;&lt;p&gt;بندهٔ شاه شماییم و ثناخوان شما&lt;/p&gt;&lt;p&gt;ای شَهنشاه بلند اختر، خدا را همتی&lt;/p&gt;&lt;p&gt;تا ببوسم همچو اختر خاک ایوان شما&lt;/p&gt;', 'غزل شماره ۱۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(13, '&lt;p&gt;می‌دمد صبح و کِلِّه بست سحاب&lt;/p&gt;&lt;p&gt;الصَبوح الصَبوح یا اصحاب&lt;/p&gt;&lt;p&gt;می‌چکد ژاله بر رخِ لاله&lt;/p&gt;&lt;p&gt;المُدام المُدام یا احباب&lt;/p&gt;&lt;p&gt;می‌وزد از چمن نسیمِ بهشت&lt;/p&gt;&lt;p&gt;هان، بنوشید دَم به دَم مِیِ ناب&lt;/p&gt;&lt;p&gt;تخت زُمْرُد زده است گل به چمن&lt;/p&gt;&lt;p&gt;راحِ چون لعلِ آتشین دریاب&lt;/p&gt;&lt;p&gt;درِ میخانه بسته‌اند دگر&lt;/p&gt;&lt;p&gt;اِفتَتِح یا مُفَتِّح الاَبواب&lt;/p&gt;&lt;p&gt;لب و دَندانْت را حقوق نمک&lt;/p&gt;&lt;p&gt;هست بر جان و سینه‌هایِ کباب&lt;/p&gt;&lt;p&gt;این چنین موسِمی عجب باشد&lt;/p&gt;&lt;p&gt;که ببندند میکده به شتاب&lt;/p&gt;&lt;p&gt;بر رخِ ساقیِ پری پیکر&lt;/p&gt;&lt;p&gt;همچو حافظ بنوش بادهٔ ناب&lt;/p&gt;', 'غزل شماره ۱۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(14, '&lt;p&gt;گفتم ای سلطانِ خوبان رحم کن بر این غریب&lt;/p&gt;&lt;p&gt;گفت در دنبالِ دل، رَه گُم کُنَد مسکین غریب&lt;/p&gt;&lt;p&gt;گفتمش مَگذر زمانی، گفت معذورم بدار&lt;/p&gt;&lt;p&gt;خانه پروردی چه تاب آرد غم چندین غریب&lt;/p&gt;&lt;p&gt;خفته بر سنجابِ شاهی نازنینی را چه غم؟&lt;/p&gt;&lt;p&gt;گر ز خار و خاره سازد بستر و بالین غریب&lt;/p&gt;&lt;p&gt;ای که در زنجیرِ زلفت جایِ چندین آشناست&lt;/p&gt;&lt;p&gt;خوش فتاد آن خالِ مشکین بر رخِ رنگین غریب&lt;/p&gt;&lt;p&gt;می‌نماید عکسِ مِی، در رنگِ رویِ مَه وَشَت&lt;/p&gt;&lt;p&gt;همچو برگِ ارغوان بر صفحهٔ نسرین، غریب&lt;/p&gt;&lt;p&gt;بس غریب افتاده است آن مور خَط، گِردِ رُخَت&lt;/p&gt;&lt;p&gt;گر چه نَبوَد در نگارستان، خطِ مشکین غریب&lt;/p&gt;&lt;p&gt;گفتم ای شامِ غریبان طُرِّهٔ شبرنگِ تو&lt;/p&gt;&lt;p&gt;در سحرگاهان حذر کن چون بنالد این غریب&lt;/p&gt;&lt;p&gt;گفت حافظ آشنایان در مقامِ حیرتند&lt;/p&gt;&lt;p&gt;دور نَبوَد گر نشیند خسته و مسکین غریب&lt;/p&gt;', 'غزل شماره ۱۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(15, '&lt;p&gt;ای شاهد قدسی، کِه کَشَد بند نقابت؟&lt;/p&gt;&lt;p&gt;وی مرغ بهشتی، که دهد دانه و آبت؟&lt;/p&gt;&lt;p&gt;خوابم بشد از دیده در این فکر جگرسوز&lt;/p&gt;&lt;p&gt;کآغوشِ که شد منزل آسایش و خوابت؟&lt;/p&gt;&lt;p&gt;درویش نمی‌پرسی و ترسم که نباشد&lt;/p&gt;&lt;p&gt;اندیشهٔ آمرزش و پروای ثوابت&lt;/p&gt;&lt;p&gt;راه دل عشاق زد آن چشم خماری&lt;/p&gt;&lt;p&gt;پیداست از این شیوه که مست است شرابت&lt;/p&gt;&lt;p&gt;تیری که زدی بر دلم از غمزه خطا رفت&lt;/p&gt;&lt;p&gt;تا باز چه اندیشه کند رای صوابت&lt;/p&gt;&lt;p&gt;هر ناله و فریاد که کردم نشنیدی&lt;/p&gt;&lt;p&gt;پیداست نگارا که بلند است جَنابت&lt;/p&gt;&lt;p&gt;دور است سر آب از این بادیه، هش دار&lt;/p&gt;&lt;p&gt;تا غول بیابان نفریبد به سرابت&lt;/p&gt;&lt;p&gt;تا در ره پیری به چه آیین رَوی ای دل&lt;/p&gt;&lt;p&gt;باری به غلط صرف شد ایامِ شبابت&lt;/p&gt;&lt;p&gt;ای قصرِ دل افروز که منزلگهِ انسی&lt;/p&gt;&lt;p&gt;یا رب مَکُناد آفتِ ایام، خرابت&lt;/p&gt;&lt;p&gt;حافظ نه غلامیست که از خواجه گریزد&lt;/p&gt;&lt;p&gt;صلحی کن و بازآ که خرابم ز عِتابت&lt;/p&gt;', 'غزل شماره ۱۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(16, '&lt;p&gt;خَمی که ابروی شوخِ تو در کمان انداخت&lt;/p&gt;&lt;p&gt;به قصد جانِ منِ زارِ ناتوان انداخت&lt;/p&gt;&lt;p&gt;نبود نقش دو عالم، که رنگ الفت بود&lt;/p&gt;&lt;p&gt;زمانه طرح محبت، نه این زمان انداخت&lt;/p&gt;&lt;p&gt;به یک کرشمه که نرگس به خودفروشی کرد&lt;/p&gt;&lt;p&gt;فریبِ چشمِ تو صد فتنه در جهان انداخت&lt;/p&gt;&lt;p&gt;شراب خورده و خِوی کرده می‌روی به چمن&lt;/p&gt;&lt;p&gt;که آبِ روی تو آتش در ارغوان انداخت&lt;/p&gt;&lt;p&gt;به بزمگاهِ چمن، دوش، مست، بگذشتم&lt;/p&gt;&lt;p&gt;چو از دهانِ توام غنچه در گُمان انداخت&lt;/p&gt;&lt;p&gt;بنفشه طُرِّه مفتول خود گره می‌زد&lt;/p&gt;&lt;p&gt;صبا حکایتِ زلفِ تو در میان انداخت&lt;/p&gt;&lt;p&gt;ز شَرمِ آن که به روی تو نسبتش کردم&lt;/p&gt;&lt;p&gt;سمن به دستِ صبا، خاک در دهان انداخت&lt;/p&gt;&lt;p&gt;من از ورع، مِی و مطرب ندیدمی زین پیش&lt;/p&gt;&lt;p&gt;هوای مغبچگانم در این و آن انداخت&lt;/p&gt;&lt;p&gt;کنون به آبِ مِی لعل، خرقه می‌شویم&lt;/p&gt;&lt;p&gt;نصیبهٔ ازل از خود نمی‌توان انداخت&lt;/p&gt;&lt;p&gt;مگر گشایش حافظ در این خرابی بود&lt;/p&gt;&lt;p&gt;که بخششِ ازلش، در میِ مغان انداخت&lt;/p&gt;&lt;p&gt;جهان به کامِ من اکنون شود که دورِ زمان&lt;/p&gt;&lt;p&gt;مرا به بندگیِ خواجهٔ جهان انداخت&lt;/p&gt;', 'غزل شماره ۱۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(17, '&lt;p&gt;سینه از آتش دل، در غمِ جانانه بسوخت&lt;/p&gt;&lt;p&gt;آتشی بود در این خانه که کاشانه بسوخت&lt;/p&gt;&lt;p&gt;تنم از واسطهٔ دوریِ دلبر بگداخت&lt;/p&gt;&lt;p&gt;جانم از آتشِ مهرِ رخِ جانانه بسوخت&lt;/p&gt;&lt;p&gt;سوزِ دل بین که ز بس آتش اشکم، دلِ شمع&lt;/p&gt;&lt;p&gt;دوش بر من ز سرِ مِهر، چو پروانه بسوخت&lt;/p&gt;&lt;p&gt;آشنایی نه غریب است که دلسوزِ من است&lt;/p&gt;&lt;p&gt;چون من از خویش برفتم، دلِ بیگانه بسوخت&lt;/p&gt;&lt;p&gt;خرقهٔ زهدِ مرا، آبِ خرابات ببرد&lt;/p&gt;&lt;p&gt;خانهٔ عقلِ مرا، آتشِ میخانه بسوخت&lt;/p&gt;&lt;p&gt;چون پیاله دلم از توبه که کردم بشکست&lt;/p&gt;&lt;p&gt;همچو لاله، جگرم بی می و خُمخانه بسوخت&lt;/p&gt;&lt;p&gt;ماجرا کم کن و بازآ که مرا مردمِ چشم&lt;/p&gt;&lt;p&gt;خرقه از سر به درآورد و به شکرانه بسوخت&lt;/p&gt;&lt;p&gt;ترک افسانه بگو حافظ و مِی نوش دمی&lt;/p&gt;&lt;p&gt;که نَخُفتیم شب و شمع به افسانه بسوخت&lt;/p&gt;', 'غزل شماره ۱۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(18, '&lt;p&gt;ساقیا آمدنِ عید، مبارک بادت&lt;/p&gt;&lt;p&gt;وان مَواعید که کردی، مَرَواد از یادت&lt;/p&gt;&lt;p&gt;در شگفتم که در این مدّتِ ایّامِ فراق&lt;/p&gt;&lt;p&gt;برگرفتی ز حریفان دل و دل می‌دادت&lt;/p&gt;&lt;p&gt;برسان بندگیِ دختر رَز، گو به درآی&lt;/p&gt;&lt;p&gt;که دَم و همّت ما کرد ز بند، آزادت&lt;/p&gt;&lt;p&gt;شادی مجلسیان در قدم و مقدم توست&lt;/p&gt;&lt;p&gt;جای غم باد، مَر آن دل که نخواهد شادت&lt;/p&gt;&lt;p&gt;شکر ایزد که ز تاراجِ خزان رخنه نیافت&lt;/p&gt;&lt;p&gt;بوستانِ سمن و سرو و گل و شمشادت&lt;/p&gt;&lt;p&gt;چشمِ بد دور کز آن تفرقه‌ات بازآورد&lt;/p&gt;&lt;p&gt;طالعِ ناموَر و دولتِ مادرزادت&lt;/p&gt;&lt;p&gt;حافظ از دست مده دولت این کشتی نوح&lt;/p&gt;&lt;p&gt;ور نه طوفانِ حوادث بِبَرَد بُنیادت&lt;/p&gt;', 'غزل شماره ۱۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(19, '&lt;p&gt;ای نسیمِ سحر، آرامگَهِ یار کجاست؟&lt;/p&gt;&lt;p&gt;منزلِ آن مَهِ عاشق‌کُشِ عَیّار کجاست؟&lt;/p&gt;&lt;p&gt;شبِ تار است و رَهِ وادیِ اَیمَن در پیش&lt;/p&gt;&lt;p&gt;آتشِ طور کجا، موعدِ دیدار کجاست؟&lt;/p&gt;&lt;p&gt;هر که آمد به جهان نقشِ خرابی دارد&lt;/p&gt;&lt;p&gt;در خرابات بگویید که هُشیار کجاست؟&lt;/p&gt;&lt;p&gt;آن‌کس است اهلِ بشارت که اشارت داند&lt;/p&gt;&lt;p&gt;نکته‌ها هست بسی، مَحرمِ اسرار کجاست؟&lt;/p&gt;&lt;p&gt;هر سرِ مویِ مرا با تو هزاران کار است&lt;/p&gt;&lt;p&gt;ما کجاییم و ملامت‌گرِ بی‌کار کجاست؟&lt;/p&gt;&lt;p&gt;باز پرسید ز گیسویِ شِکَن در شِکَنَش&lt;/p&gt;&lt;p&gt;کاین دلِ غم‌زده سرگشته گرفتار، کجاست؟&lt;/p&gt;&lt;p&gt;عقل دیوانه شد آن سلسلهٔ مشکین کو؟&lt;/p&gt;&lt;p&gt;دل ز ما گوشه گرفت، ابرویِ دلدار کجاست؟&lt;/p&gt;&lt;p&gt;ساقی و مُطرب و مِی جمله مُهَیاست ولی&lt;/p&gt;&lt;p&gt;عیش، بی‌یار مُهیّا نشود، یار کجاست؟&lt;/p&gt;&lt;p&gt;حافظ از بادِ خزان در چمنِ دَهر مَرَنج&lt;/p&gt;&lt;p&gt;فکرِ معقول بفرما گُلِ بی‌خار کجاست؟&lt;/p&gt;', 'غزل شماره ۱۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(20, '&lt;p&gt;روزه یک سو شد و عید آمد و دل‌ها برخاست&lt;/p&gt;&lt;p&gt;می ز خُمخانه به جوش آمد و می‌باید خواست&lt;/p&gt;&lt;p&gt;نوبهٔ زهدفروشانِ گران جان بگذشت&lt;/p&gt;&lt;p&gt;وقتِ رندی و طرب کردنِ رندان پیداست&lt;/p&gt;&lt;p&gt;چه ملامت بُوَد آن را که چنین باده خورَد؟&lt;/p&gt;&lt;p&gt;این چه عیب است بدین بی‌خردی، وین چه خطاست؟&lt;/p&gt;&lt;p&gt;باده نوشی که در او روی و ریایی نَبُوَد&lt;/p&gt;&lt;p&gt;بهتر از زهدفروشی که در او روی و ریاست&lt;/p&gt;&lt;p&gt;ما نه رندانِ ریاییم و حریفانِ نفاق&lt;/p&gt;&lt;p&gt;آن که او عالِم سِرّ است، بدین حال گواست&lt;/p&gt;&lt;p&gt;فرضِ ایزد بگزاریم و به کس بد نکنیم&lt;/p&gt;&lt;p&gt;وان چه گویند روا نیست، نگوییم رواست&lt;/p&gt;&lt;p&gt;چه شود گر من و تو چند قدح باده خوریم؟&lt;/p&gt;&lt;p&gt;باده از خونِ رَزان است، نه از خون شماست&lt;/p&gt;&lt;p&gt;این چه عیب است کز آن عیب، خلل خواهد بود&lt;/p&gt;&lt;p&gt;ور بُوَد نیز چه شد؟ مردم بی‌عیب کجاست&lt;/p&gt;', 'غزل شماره ۲۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(21, '&lt;p&gt;دل و دینم شد و دلبر به ملامت برخاست&lt;/p&gt;&lt;p&gt;گفت با ما منشین، کز تو سلامت برخاست&lt;/p&gt;&lt;p&gt;که شنیدی که در این بزم، دمی خوش بنشست؟&lt;/p&gt;&lt;p&gt;که نه در آخرِ صحبت به ندامت برخاست&lt;/p&gt;&lt;p&gt;شمع اگر زان لبِ خندان به زبان، لافی زد&lt;/p&gt;&lt;p&gt;پیشِ عشاقِ تو شب‌ها به غَرامت برخاست&lt;/p&gt;&lt;p&gt;در چمن، بادِ بهاری ز کنار گل و سرو&lt;/p&gt;&lt;p&gt;به هواداریِ آن عارض و قامت برخاست&lt;/p&gt;&lt;p&gt;مست بگذشتی و از خلوتیانِ ملکوت&lt;/p&gt;&lt;p&gt;به تماشایِ تو آشوبِ قیامت برخاست&lt;/p&gt;&lt;p&gt;پیشِ رفتار تو پا برنگرفت از خِجلت&lt;/p&gt;&lt;p&gt;سروِ سرکش که به ناز از قد و قامت برخاست&lt;/p&gt;&lt;p&gt;حافظ این خرقه بینداز مگر جان ببری&lt;/p&gt;&lt;p&gt;کاتش از خرقه ی سالوس و کرامت برخاست&lt;/p&gt;', 'غزل شماره ۲۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(22, '&lt;p&gt;چو بشنوی سخنِ اهلِ دل، مگو که خطاست&lt;/p&gt;&lt;p&gt;سخن شناس نه‌ای جان من، خطا این جاست&lt;/p&gt;&lt;p&gt;سرم به دنیی و عقبی فرو نمی‌آید&lt;/p&gt;&lt;p&gt;تبارک الله از این فتنه‌ها که در سرِ ماست&lt;/p&gt;&lt;p&gt;در اندرونِ منِ خسته دل، ندانم کیست&lt;/p&gt;&lt;p&gt;که من خموشم و او در فغان و در غوغاست&lt;/p&gt;&lt;p&gt;دلم ز پرده برون شد، کجایی ای مطرب&lt;/p&gt;&lt;p&gt;بنال، هان که از این پرده، کارِ ما به نواست&lt;/p&gt;&lt;p&gt;مرا به کارِ جهان، هرگز التفات نبود&lt;/p&gt;&lt;p&gt;رخِ تو در نظرِ من، چنین خوشش آراست&lt;/p&gt;&lt;p&gt;نخفته‌ام ز خیالی که می‌پزد، دلِ من&lt;/p&gt;&lt;p&gt;خمارِ صدشبه دارم، شرابخانه کجاست&lt;/p&gt;&lt;p&gt;چنین که صومعه آلوده شد ز خونِ دلم&lt;/p&gt;&lt;p&gt;گَرَم به باده بشویید، حق به دستِ شماست&lt;/p&gt;&lt;p&gt;از آن به دیرِ مغانم، عزیز می‌دارند&lt;/p&gt;&lt;p&gt;که آتشی که نمیرد، همیشه در دلِ ماست&lt;/p&gt;&lt;p&gt;چه ساز بود که در پرده می‌زد آن مطرب&lt;/p&gt;&lt;p&gt;که رفت عمر و هنوزم، دِماغ پُر ز هواست&lt;/p&gt;&lt;p&gt;ندای عشق تو دیشب در اندرون دادند&lt;/p&gt;&lt;p&gt;فضایِ سینه ی حافظ، هنوز پر ز صداست&lt;/p&gt;', 'غزل شماره ۲۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(23, '&lt;p&gt;خیالِ رویِ تو در هر طریق همرهِ ماست&lt;/p&gt;&lt;p&gt;نسیمِ مویِ تو، پیوندِ جانِ آگهِ ماست&lt;/p&gt;&lt;p&gt;به رَغمِ مدعیانی که منعِ عشق کنند&lt;/p&gt;&lt;p&gt;جمالِ چهرهٔ تو، حجتِ موجهِ ماست&lt;/p&gt;&lt;p&gt;ببین که سیبِ زنخدانِ تو چه می‌گوید&lt;/p&gt;&lt;p&gt;هزار یوسفِ مصری، فتاده در چَهِ ماست&lt;/p&gt;&lt;p&gt;اگر به زلفِ درازِ تو، دستِ ما نرسد&lt;/p&gt;&lt;p&gt;گناهِ بخِتِ پریشان و دستِ کوتهِ ماست&lt;/p&gt;&lt;p&gt;به حاجبِ درِ خلوت سرایِ خاص بگو&lt;/p&gt;&lt;p&gt;فُلان ز گوشه نشینانِ خاکِ درگهِ ماست&lt;/p&gt;&lt;p&gt;به صورت از نظر ما اگر چه محجوب است&lt;/p&gt;&lt;p&gt;همیشه در نظرِ خاطرِ مرفهِ ماست&lt;/p&gt;&lt;p&gt;اگر به سالی حافظ دری زَنَد، بگشای&lt;/p&gt;&lt;p&gt;که سال‌هاست که مشتاقِ روی چون مهِ ماست&lt;/p&gt;', 'غزل شماره ۲۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(24, '&lt;p&gt;مَطَلَب طاعت و پیمان و صَلاح از منِ مست&lt;/p&gt;&lt;p&gt;که به پیمانه کَشی شُهره شدم روزِ الست&lt;/p&gt;&lt;p&gt;من همان دم که وضو ساختم از چشمه عشق&lt;/p&gt;&lt;p&gt;چارتکبیر زدم یکسره بر هر چه که هست&lt;/p&gt;&lt;p&gt;مِی بده تا دَهَمَت آگهی از سِرِّ قضا&lt;/p&gt;&lt;p&gt;که به رویِ که شدم عاشق و از بویِ که مست؟&lt;/p&gt;&lt;p&gt;کمرِ کوه کم است از کمرِ مور این جا&lt;/p&gt;&lt;p&gt;ناامید از درِ رحمت مشو ای باده پرست&lt;/p&gt;&lt;p&gt;به جز آن نرگسِ مستانه که چشمش مَرِساد&lt;/p&gt;&lt;p&gt;زیرِ این طارَمِ فیروزه، کسی خوش ننشست&lt;/p&gt;&lt;p&gt;جان فدایِ دَهَنَش باد که در باغِ نظر&lt;/p&gt;&lt;p&gt;چمن آرایِ جهان، خوشتر از این غنچه نبست&lt;/p&gt;&lt;p&gt;حافظ از دولتِ عشقِ تو سلیمانی شد&lt;/p&gt;&lt;p&gt;یعنی از وصلِ تواش نیست به جز باد به دست&lt;/p&gt;', 'غزل شماره ۲۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(25, '&lt;p&gt;شکفته شد گل حَمرا و گشت بلبل مست&lt;/p&gt;&lt;p&gt;صَلایِ سرخوشی، ای صوفیانِ باده پرست&lt;/p&gt;&lt;p&gt;اساسِ توبه که در محکمی چو سنگ نُمود&lt;/p&gt;&lt;p&gt;ببین که جامِ زُجاجی چه طُرفه‌اش بشکست&lt;/p&gt;&lt;p&gt;بیار باده که در بارگاهِ استغنا&lt;/p&gt;&lt;p&gt;چه پاسبان و چه سلطان، چه هوشیار و چه مست&lt;/p&gt;&lt;p&gt;از این رِباط دو در، چون ضرورت است رَحیل&lt;/p&gt;&lt;p&gt;رِواق و طاقِ معیشت، چه سربلند و چه پست&lt;/p&gt;&lt;p&gt;مقام عیش میسر نمی‌شود بی‌رنج&lt;/p&gt;&lt;p&gt;بلی به حکمِ بلا بسته‌اند عهدِ الست&lt;/p&gt;&lt;p&gt;به هست و نیست مرنجان ضمیر و خوش می‌باش&lt;/p&gt;&lt;p&gt;که نیستی است سرانجامِ هر کمال که هست&lt;/p&gt;&lt;p&gt;شکوهِ آصِفی و اسبِ باد و منطقِ طیر&lt;/p&gt;&lt;p&gt;به باد رفت و از او خواجه هیچ طَرف نبست&lt;/p&gt;&lt;p&gt;به بال و پَر مرو از ره که تیرِ پرتابی&lt;/p&gt;&lt;p&gt;هوا گرفت زمانی، ولی به خاک نشست&lt;/p&gt;&lt;p&gt;زبانِ کِلکِ تو حافظ چه شُکرِ آن گوید&lt;/p&gt;&lt;p&gt;که گفتهٔ سخنت می‌برند دست به دست&lt;/p&gt;', 'غزل شماره ۲۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(26, '&lt;p&gt;زلف‌آشفته و خِوی‌کرده و خندان‌لب و مست&lt;/p&gt;&lt;p&gt;پیرهن‌چاک و غزل‌خوان و صُراحی در دست&lt;/p&gt;&lt;p&gt;نرگسش عَربده‌جوی و لبش افسوس‌کنان&lt;/p&gt;&lt;p&gt;نیم شب، دوش به بالین من آمد، بنشست&lt;/p&gt;&lt;p&gt;سر فرا گوش من آورد به آوازِ حزین&lt;/p&gt;&lt;p&gt;گفت: ای عاشقِ دیرینهٔ من، خوابت هست؟&lt;/p&gt;&lt;p&gt;عاشقی را که چنین بادهٔ شبگیر دهند&lt;/p&gt;&lt;p&gt;کافر عشق بُوَد گر نشود باده پرست&lt;/p&gt;&lt;p&gt;برو ای زاهد و بر دُردکشان خرده مگیر&lt;/p&gt;&lt;p&gt;که ندادند جز این تُحفه به ما روزِ الست&lt;/p&gt;&lt;p&gt;آن چه او ریخت به پیمانهٔ ما نوشیدیم&lt;/p&gt;&lt;p&gt;اگر از خَمرِ بهشت است وگر بادهٔ مست&lt;/p&gt;&lt;p&gt;خندهٔ جامِ می و زلفِ گره‌گیرِ نگار&lt;/p&gt;&lt;p&gt;ای بسا توبه که چون توبه حافظ بشکست&lt;/p&gt;', 'غزل شماره ۲۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(27, '&lt;p&gt;در دیرِ مغان آمد، یارم قدحی در دست&lt;/p&gt;&lt;p&gt;مست از می و میخواران از نرگسِ مستش مست&lt;/p&gt;&lt;p&gt;در نعلِ سمندِ او شکلِ مهِ نو پیدا&lt;/p&gt;&lt;p&gt;وز قدِ بلندِ او بالایِ صنوبر، پست&lt;/p&gt;&lt;p&gt;آخر به چه گویم هست از خود خبرم، چون نیست&lt;/p&gt;&lt;p&gt;وز بَهرِ چه گویم نیست با وی نظرم، چون هست&lt;/p&gt;&lt;p&gt;شمعِ دلِ دمسازم، بنشست چو او برخاست&lt;/p&gt;&lt;p&gt;و افغان ز نظربازان، برخاست چو او بنشست&lt;/p&gt;&lt;p&gt;گر غالیه خوش بو شد، در گیسویِ او پیچید&lt;/p&gt;&lt;p&gt;ور وَسمه کمانکش گشت، در ابروی او پیوست&lt;/p&gt;&lt;p&gt;بازآی که بازآید عمرِ شدهٔ حافظ&lt;/p&gt;&lt;p&gt;هر چند که ناید باز، تیری که بِشُد از شست&lt;/p&gt;', 'غزل شماره ۲۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(28, '&lt;p&gt;به جانِ خواجه و حقِ قدیم و عهدِ درست&lt;/p&gt;&lt;p&gt;که مونسِ دمِ صبحم، دعایِ دولتِ توست&lt;/p&gt;&lt;p&gt;سِرِشک من که ز طوفان نوح دست بَرَد&lt;/p&gt;&lt;p&gt;ز لوح سینه نیارَست نقشِ مهرِ تو شُست&lt;/p&gt;&lt;p&gt;بکن معامله‌ای، وین دل شکسته بخر&lt;/p&gt;&lt;p&gt;که با شکستگی ارزد به صد هزار درست&lt;/p&gt;&lt;p&gt;زبان مور به آصف دراز گشت و رواست&lt;/p&gt;&lt;p&gt;که خواجه خاتَمِ جم، یاوه کرد و باز نَجُست&lt;/p&gt;&lt;p&gt;دلا طَمَع مَبُر از لطفِ بی‌نهایتِ دوست&lt;/p&gt;&lt;p&gt;چو لافِ عشق زدی، سر بباز، چابک و چُست&lt;/p&gt;&lt;p&gt;به صدق کوش، که خورشید زایَد از نَفَسَت&lt;/p&gt;&lt;p&gt;که از دروغ سیه روی گشت صبحِ نخست&lt;/p&gt;&lt;p&gt;شدم ز دست تو شیدای کوه و دشت و هنوز&lt;/p&gt;&lt;p&gt;نمی‌کنی به ترحم، نِطاق سلسله سست&lt;/p&gt;&lt;p&gt;مرنج حافظ و از دلبران حِفاظ مجوی&lt;/p&gt;&lt;p&gt;گناه باغ چه باشد چو این گیاه نَرُست&lt;/p&gt;', 'غزل شماره ۲۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(29, '&lt;p&gt;ما را زِ خیالِ تو چه پروایِ شراب است؟&lt;/p&gt;&lt;p&gt;خُم گو سر خود گیر، که خُمخانه خراب است&lt;/p&gt;&lt;p&gt;گر خَمر بهشت است بریزید که بی دوست&lt;/p&gt;&lt;p&gt;هر شَربَتِ عَذبَم که دهی، عینِ عذاب است&lt;/p&gt;&lt;p&gt;افسوس که شُد دلبر و در دیدهٔ گریان&lt;/p&gt;&lt;p&gt;تحریرِ خیالِ خطِ او نقشِ بر آب است&lt;/p&gt;&lt;p&gt;بیدار شو ای دیده که ایمن نتوان بود&lt;/p&gt;&lt;p&gt;زین سیلِ دمادم که در این منزلِ خواب است&lt;/p&gt;&lt;p&gt;معشوق عیان می‌گذرد بر تو، ولیکن&lt;/p&gt;&lt;p&gt;اغیار همی‌بیند از آن بسته نقاب است&lt;/p&gt;&lt;p&gt;گل بر رخِ رنگین تو تا لطفِ عرق دید&lt;/p&gt;&lt;p&gt;در آتشِ شوق از غمِ دل، غرقِ گلاب است&lt;/p&gt;&lt;p&gt;سبز است در و دشت بیا تا نگذاریم&lt;/p&gt;&lt;p&gt;دست از سرِ آبی که جهان جمله سراب است&lt;/p&gt;&lt;p&gt;در کُنجِ دِماغم مطلب جای نصیحت&lt;/p&gt;&lt;p&gt;کـ‌این گوشه پر از زمزمهٔ چنگ و رَباب است&lt;/p&gt;&lt;p&gt;حافظ چه شد ار عاشق و رند است و نظرباز&lt;/p&gt;&lt;p&gt;بس طُورِ عجب لازمِ ایامِ شباب است&lt;/p&gt;', 'غزل شماره ۲۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(30, '&lt;p&gt;زلفت هزار دل به یکی تار مو ببست&lt;/p&gt;&lt;p&gt;راه هزار چاره‌گر از چار سو ببست&lt;/p&gt;&lt;p&gt;تا عاشقان به بوی نسیمش دهند جان&lt;/p&gt;&lt;p&gt;بگشود نافه‌ایُّ و دَرِ آرزو ببست&lt;/p&gt;&lt;p&gt;شیدا از آن شدم که نگارم چو ماه نو&lt;/p&gt;&lt;p&gt;ابرو نمود و جلوه‌گری کرد و رو ببست&lt;/p&gt;&lt;p&gt;ساقی به چند رنگ، می اندر پیاله ریخت&lt;/p&gt;&lt;p&gt;این نقش‌ها نگر که چه خوش در کدو ببست&lt;/p&gt;&lt;p&gt;یا رب چه غمزه کرد صُراحی که خون خُم&lt;/p&gt;&lt;p&gt;با نعره‌های قُلقُلش اندر گلو ببست&lt;/p&gt;&lt;p&gt;مطرب چه پرده ساخت که در پردهٔ سماع&lt;/p&gt;&lt;p&gt;بر اهل وجد و حال، درِ های و هو ببست&lt;/p&gt;&lt;p&gt;حافظ! هر آن که عشق نَورزید و وصل خواست&lt;/p&gt;&lt;p&gt;احرامِ طوفِ کعبهٔ دل بی وضو ببست&lt;/p&gt;', 'غزل شماره ۳۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(31, '&lt;p&gt;آن شبِ قدری که گویند اهل خلوت امشب است&lt;/p&gt;&lt;p&gt;یا رب این تأثیرِ دولت در کدامین کوکب است؟&lt;/p&gt;&lt;p&gt;تا به گیسوی تو دست ناسزایان کم رسد&lt;/p&gt;&lt;p&gt;هر دلی از حلقه‌ای در ذکر یارب یارب است&lt;/p&gt;&lt;p&gt;کشته چاه زنخدان توام کز هر طرف&lt;/p&gt;&lt;p&gt;صد هزارش گردن جان زیرِ طوقِ غبغب است&lt;/p&gt;&lt;p&gt;شهسوار من که مه آیینه دار روی اوست&lt;/p&gt;&lt;p&gt;تاجِ خورشیدِ بلندش خاکِ نعلِ مرکب است&lt;/p&gt;&lt;p&gt;عکس خِوی بر عارضش بین کآفتاب گرم رو&lt;/p&gt;&lt;p&gt;در هوای آن عرق تا هست هر روزش تب است&lt;/p&gt;&lt;p&gt;من نخواهم کرد ترک لعل یار و جام می&lt;/p&gt;&lt;p&gt;زاهدان معذور داریدم که اینم مذهب است&lt;/p&gt;&lt;p&gt;اندر آن ساعت که بر پشت صبا بندند زین&lt;/p&gt;&lt;p&gt;با سلیمان چون برانم من که مورم مرکب است&lt;/p&gt;&lt;p&gt;آن که ناوک بر دل من زیر چشمی می‌زند&lt;/p&gt;&lt;p&gt;قوتِ جانِ حافظش در خندهٔ زیر لب است&lt;/p&gt;&lt;p&gt;آب حیوانش ز منقار بلاغت می‌چکد&lt;/p&gt;&lt;p&gt;زاغِ کِلکِ من به نام ایزد چه عالی مشرب است&lt;/p&gt;', 'غزل شماره ۳۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(32, '&lt;p&gt;خدا چو صورتِ ابرویِ دلگشای تو بست&lt;/p&gt;&lt;p&gt;گشادِ کارِ من اندر کرشمه‌های تو بست&lt;/p&gt;&lt;p&gt;مرا و سرو چمن را به خاک راه نشاند&lt;/p&gt;&lt;p&gt;زمانه تا قَصَبِ نرگس قبای تو بست&lt;/p&gt;&lt;p&gt;ز کار ما و دل غنچه صد گره بگشود&lt;/p&gt;&lt;p&gt;نسیم گل چو دل اندر پی هوای تو بست&lt;/p&gt;&lt;p&gt;مرا به بندِ تو دوران چرخ راضی کرد&lt;/p&gt;&lt;p&gt;ولی چه سود که سررشته در رضای تو بست&lt;/p&gt;&lt;p&gt;چو نافه بر دلِ مسکینِ من گره مَفِکن&lt;/p&gt;&lt;p&gt;که عهد با سرِ زلفِ گره‌گشایِ تو بست&lt;/p&gt;&lt;p&gt;تو خود وصالِ دگر بودی ای نسیم وصال&lt;/p&gt;&lt;p&gt;خطا نگر که دل امید در وفای تو بست&lt;/p&gt;&lt;p&gt;ز دستِ جورِ تو گفتم ز شهر خواهم رفت&lt;/p&gt;&lt;p&gt;به خنده گفت که حافظ برو، که پای تو بست؟&lt;/p&gt;', 'غزل شماره ۳۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(33, '&lt;p&gt;خلوت گُزیده را به تماشا چه حاجت است&lt;/p&gt;&lt;p&gt;چون کوی دوست هست به صحرا چه حاجت است&lt;/p&gt;&lt;p&gt;جانا به حاجتی که تو را هست با خدا&lt;/p&gt;&lt;p&gt;کآخِر دمی بپرس که ما را چه حاجت است&lt;/p&gt;&lt;p&gt;ای پادشاهِ حُسن خدا را بسوختیم&lt;/p&gt;&lt;p&gt;آخِر سؤال کن که گدا را چه حاجت است&lt;/p&gt;&lt;p&gt;اربابِ حاجتیم و زبان سؤال نیست&lt;/p&gt;&lt;p&gt;در حضرت کریم، تمنا چه حاجت است&lt;/p&gt;&lt;p&gt;محتاج قصه نیست گرت قصدِ خون ماست&lt;/p&gt;&lt;p&gt;چون رخت از آن توست، به یغما چه حاجت است&lt;/p&gt;&lt;p&gt;جامِ جهان نماست ضمیرِ منیرِ دوست&lt;/p&gt;&lt;p&gt;اظهار احتیاج، خود آن جا چه حاجت است&lt;/p&gt;&lt;p&gt;آن شد که بارِ منتِ مَلّاح بردمی&lt;/p&gt;&lt;p&gt;گوهر چو دست داد به دریا چه حاجت است&lt;/p&gt;&lt;p&gt;ای مدعی برو که مرا با تو کار نیست&lt;/p&gt;&lt;p&gt;احباب حاضرند، به اعدا چه حاجت است&lt;/p&gt;&lt;p&gt;ای عاشقِ گدا چو لبِ روح بخش یار&lt;/p&gt;&lt;p&gt;می‌داندت وظیفه، تقاضا چه حاجت است&lt;/p&gt;&lt;p&gt;حافظ! تو ختم کن که هنر خود عیان شود&lt;/p&gt;&lt;p&gt;با مدعی نزاع و مُحاکا چه حاجت است&lt;/p&gt;', 'غزل شماره ۳۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(34, '&lt;p&gt;رَواقِ منظرِ چشمِ من آشیانهٔ توست&lt;/p&gt;&lt;p&gt;کَرَم نما و فرود آ که خانه خانهٔ توست&lt;/p&gt;&lt;p&gt;به لطفِ خال و خط از عارفان ربودی دل&lt;/p&gt;&lt;p&gt;لطیفه‌های عجب زیرِ دام و دانهٔ توست&lt;/p&gt;&lt;p&gt;دلت به وصلِ گل ای بلبل صبا خوش باد&lt;/p&gt;&lt;p&gt;که در چمن همه گلبانگ عاشقانهٔ توست&lt;/p&gt;&lt;p&gt;عِلاج ضعفِ دلِ ما به لب حوالت کن&lt;/p&gt;&lt;p&gt;که این مُفَرِّحِ یاقوت در خزانهٔ توست&lt;/p&gt;&lt;p&gt;به تن مُقصرم از دولتِ ملازمتت&lt;/p&gt;&lt;p&gt;ولی خلاصهٔ جان خاکِ آستانهٔ توست&lt;/p&gt;&lt;p&gt;من آن نِیَم که دهم نقدِ دل به هر شوخی&lt;/p&gt;&lt;p&gt;دَرِ خزانه، به مهر تو و نشانهٔ توست&lt;/p&gt;&lt;p&gt;تو خود چه لعبتی ای شهسوارِ شیرین کار&lt;/p&gt;&lt;p&gt;که توسنی چو فلک، رامِ تازیانهٔ توست&lt;/p&gt;&lt;p&gt;چه جای من، که بلغزد سپهر شعبده‌باز&lt;/p&gt;&lt;p&gt;از این حیَل که در انبانهٔ بهانهٔ توست&lt;/p&gt;&lt;p&gt;سرودِ مجلست اکنون فلک به رقص آرد&lt;/p&gt;&lt;p&gt;که شعرِ حافظِ شیرین سخن ترانهٔ توست&lt;/p&gt;', 'غزل شماره ۳۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(35, '&lt;p&gt;برو به کارِ خود ای واعظ این چه فریادست&lt;/p&gt;&lt;p&gt;مرا فتاد دل از ره، تو را چه افتادست؟&lt;/p&gt;&lt;p&gt;میان او که خدا آفریده است از هیچ&lt;/p&gt;&lt;p&gt;دقیقه‌ایست که هیچ آفریده نگشادست&lt;/p&gt;&lt;p&gt;به کام تا نرساند مرا لبش چون نای&lt;/p&gt;&lt;p&gt;نصیحتِ همه عالم به گوش من بادست&lt;/p&gt;&lt;p&gt;گدای کوی تو از هشت خُلد مستغنیست&lt;/p&gt;&lt;p&gt;اسیرِ عشقِ تو از هر دو عالم آزادست&lt;/p&gt;&lt;p&gt;اگر چه مستی عشقم خراب کرد ولی&lt;/p&gt;&lt;p&gt;اساسِ هستیِ من زان خراب آبادست&lt;/p&gt;&lt;p&gt;دلا منال ز بیداد و جور یار که یار&lt;/p&gt;&lt;p&gt;تو را نصیب همین کرد و این از آن دادست&lt;/p&gt;&lt;p&gt;برو فِسانه مخوان و فسون مدم حافظ&lt;/p&gt;&lt;p&gt;کز این فسانه و افسون مرا بسی یادست&lt;/p&gt;', 'غزل شماره ۳۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(36, '&lt;p&gt;تا سرِ زلفِ تو در دست نسیم افتادست&lt;/p&gt;&lt;p&gt;دلِ سودازده از غصه دو نیم افتادست&lt;/p&gt;&lt;p&gt;چَشمِ جادویِ تو خود عینِ سَوادِ سحْر است&lt;/p&gt;&lt;p&gt;لیکن این هست که این نسخه سَقیم افتادست&lt;/p&gt;&lt;p&gt;در خَم زلف تو آن خال سیه دانی چیست&lt;/p&gt;&lt;p&gt;نقطهٔ دوده که در حلقه جیم افتادست&lt;/p&gt;&lt;p&gt;زلف مِشکین تو در گلشن فردوسِ عِذار&lt;/p&gt;&lt;p&gt;چیست؟ طاووس که در باغ نعیم افتادست&lt;/p&gt;&lt;p&gt;دلِ من در هوسِ رویِ تو ای مونس جان&lt;/p&gt;&lt;p&gt;خاکِ راهیست که در دستِ نسیم افتادست&lt;/p&gt;&lt;p&gt;همچو گَرد این تنِ خاکی نتوانَد برخاست&lt;/p&gt;&lt;p&gt;از سرِ کویِ تو زان رو که عظیم افتادست&lt;/p&gt;&lt;p&gt;سایهٔ قَدِّ تو بر قالبم ای عیسی دم&lt;/p&gt;&lt;p&gt;عکسِ روحیست که بر عَظمِ رَمیم افتادست&lt;/p&gt;&lt;p&gt;آن که جز کعبه مقامش نَبُد از یادِ لبت&lt;/p&gt;&lt;p&gt;بر درِ میکده دیدم که مقیم افتادست&lt;/p&gt;&lt;p&gt;حافظِ گمشده را با غمت ای یارِ عزیز&lt;/p&gt;&lt;p&gt;اتحادیست که در عهد قدیم افتادست&lt;/p&gt;', 'غزل شماره ۳۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(37, '&lt;p&gt;بیا که قصرِ اَمَل سخت سست بنیادست&lt;/p&gt;&lt;p&gt;بیار باده که بنیادِ عمر بر بادست&lt;/p&gt;&lt;p&gt;غلامِ همتِ آنم که زیرِ چرخِ کبود&lt;/p&gt;&lt;p&gt;ز هر چه رنگِ تعلق پذیرد آزادست&lt;/p&gt;&lt;p&gt;چه گویمت که به میخانه دوش مست و خراب&lt;/p&gt;&lt;p&gt;سروشِ عالَمِ غیبم چه مژده‌ها دادست&lt;/p&gt;&lt;p&gt;که ای بلندنظر شاهبازِ سِدره نشین&lt;/p&gt;&lt;p&gt;نشیمن تو نه این کُنجِ محنت آبادست&lt;/p&gt;&lt;p&gt;تو را ز کنگرهٔ عرش می‌زنند صفیر&lt;/p&gt;&lt;p&gt;ندانمت که در این دامگه چه افتادست&lt;/p&gt;&lt;p&gt;نصیحتی کنمت یاد گیر و در عمل آر&lt;/p&gt;&lt;p&gt;که این حدیث، ز پیرِ طریقتم یادست&lt;/p&gt;&lt;p&gt;غمِ جهان مخور و پندِ من مَبَر از یاد&lt;/p&gt;&lt;p&gt;که این لطیفهٔ عشقم ز رهروی یادست&lt;/p&gt;&lt;p&gt;رضا به داده بده وز جبین گره بگشای&lt;/p&gt;&lt;p&gt;که بر من و تو دَرِ اختیار نگشادست&lt;/p&gt;&lt;p&gt;مجو درستیِ عهد از جهانِ سست نهاد&lt;/p&gt;&lt;p&gt;که این عجوز، عروس هزاردامادست&lt;/p&gt;&lt;p&gt;نشان عهد و وفا نیست در تبسمِ گل&lt;/p&gt;&lt;p&gt;بنال بلبل بی دل که جای فریادست&lt;/p&gt;&lt;p&gt;حسد چه می‌بری ای سست نظم بر حافظ؟&lt;/p&gt;&lt;p&gt;قبولِ خاطر و لطفِ سخن خدادادست&lt;/p&gt;', 'غزل شماره ۳۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(38, '&lt;p&gt;بی مهرِ رُخَت روزِ مرا نور نماندست&lt;/p&gt;&lt;p&gt;وز عمر، مرا جز شبِ دیجور نماندست&lt;/p&gt;&lt;p&gt;هنگامِ وداعِ تو ز بس گریه که کردم&lt;/p&gt;&lt;p&gt;دور از رخِ تو چشمِ مرا نور نماندست&lt;/p&gt;&lt;p&gt;می‌رفت خیالِ تو ز چشمِ من و می‌گفت&lt;/p&gt;&lt;p&gt;هیهات از این گوشه که معمور نماندست&lt;/p&gt;&lt;p&gt;وصلِ تو اجل را ز سرم دور همی‌داشت&lt;/p&gt;&lt;p&gt;از دولتِ هجرِ تو کنون دور نماندست&lt;/p&gt;&lt;p&gt;نزدیک شد آن دم که رقیب تو بگوید&lt;/p&gt;&lt;p&gt;دور از رُخَت این خستهٔ رنجور نماندست&lt;/p&gt;&lt;p&gt;صبر است مرا چارهٔ هجرانِ تو لیکن&lt;/p&gt;&lt;p&gt;چون صبر توان کرد که مقدور نماندست؟&lt;/p&gt;&lt;p&gt;در هجرِ تو گر چشمِ مرا آبِ روان است&lt;/p&gt;&lt;p&gt;گو خونِ جگر ریز که معذور نماندست&lt;/p&gt;&lt;p&gt;حافظ، ز غم از گریه نپرداخت به خنده&lt;/p&gt;&lt;p&gt;ماتم زده را داعیهٔ سور نماندست&lt;/p&gt;', 'غزل شماره ۳۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(39, '&lt;p&gt;باغِ مرا چه حاجتِ سرو و صنوبر است؟&lt;/p&gt;&lt;p&gt;شمشادِ خانه‌پرورِ ما از که کمتر است؟&lt;/p&gt;&lt;p&gt;ای نازنین‌پسر، تو چه مذهب گرفته‌ای؟&lt;/p&gt;&lt;p&gt;کِت خونِ ما حلال‌تر از شیرِ مادر است&lt;/p&gt;&lt;p&gt;چون نقشِ غم ز دور ببینی شراب خواه&lt;/p&gt;&lt;p&gt;تشخیص کرده‌ایم و مداوا مقرّر است&lt;/p&gt;&lt;p&gt;از آستانِ پیرِ مغان سر چرا کشیم؟&lt;/p&gt;&lt;p&gt;دولت در آن سرا و گشایش در آن در است&lt;/p&gt;&lt;p&gt;یک قصّه بیش نیست غمِ عشق، وین عجب&lt;/p&gt;&lt;p&gt;کز هر زبان که می‌شنوم نامکرّر است&lt;/p&gt;&lt;p&gt;دی وعده داد وصلم و در سر شراب داشت&lt;/p&gt;&lt;p&gt;امروز تا چه گوید و بازش چه در سر است&lt;/p&gt;&lt;p&gt;شیراز و آبِ رکنی و این بادِ خوش نسیم&lt;/p&gt;&lt;p&gt;عیبش مکن که خالِ رُخِ هفت کشور است&lt;/p&gt;&lt;p&gt;فرق است از آبِ خضر که ظلمات جای او است&lt;/p&gt;&lt;p&gt;تا آبِ ما که منبعش الله اکبر است&lt;/p&gt;&lt;p&gt;ما آبرویِ فقر و قناعت نمی‌بریم&lt;/p&gt;&lt;p&gt;با پادشه بگوی که روزی مقدّر است&lt;/p&gt;&lt;p&gt;حافظ چه طُرفه شاخ نباتیست کِلکِ تو&lt;/p&gt;&lt;p&gt;کِش میوه دلپذیرتر از شهد و شکّر است&lt;/p&gt;', 'غزل شماره ۳۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(40, '&lt;p&gt;اَلمِنَّةُ لِلَّه که درِ میکده باز است&lt;/p&gt;&lt;p&gt;زان رو که مرا بر در او روی نیاز است&lt;/p&gt;&lt;p&gt;خُم‌ها همه در جوش و خروشند ز مستی&lt;/p&gt;&lt;p&gt;وان می که در آن جاست حقیقت نه مجاز است&lt;/p&gt;&lt;p&gt;از وی همه مستی و غرور است و تکبر&lt;/p&gt;&lt;p&gt;وز ما همه بیچارگی و عجز و نیاز است&lt;/p&gt;&lt;p&gt;رازی که بَرِ غیر نگفتیم و نگوییم&lt;/p&gt;&lt;p&gt;با دوست بگوییم که او محرم راز است&lt;/p&gt;&lt;p&gt;شرحِ شِکَنِ زلفِ خَم اندر خَم جانان&lt;/p&gt;&lt;p&gt;کوته نتوان کرد که این قصه دراز است&lt;/p&gt;&lt;p&gt;بارِ دلِ مجنون و خَمِ طُرِّهٔ لیلی&lt;/p&gt;&lt;p&gt;رخسارهٔ محمود و کفِ پایِ ایاز است&lt;/p&gt;&lt;p&gt;بردوخته‌ام دیده چو باز از همه عالم&lt;/p&gt;&lt;p&gt;تا دیدهٔ من بر رُخِ زیبایِ تو باز است&lt;/p&gt;&lt;p&gt;در کعبهٔ کوی تو هر آن کس که بیاید&lt;/p&gt;&lt;p&gt;از قبلهٔ ابروی تو در عینِ نماز است&lt;/p&gt;&lt;p&gt;ای مجلسیان، سوزِ دلِ حافظِ مسکین&lt;/p&gt;&lt;p&gt;از شمع بپرسید که در سوز و گداز است&lt;/p&gt;', 'غزل شماره ۴۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(41, '&lt;p&gt;اگر چه باده فرح بخش و باد گل‌بیز است&lt;/p&gt;&lt;p&gt;به بانگ چنگ مخور می که محتسب تیز است&lt;/p&gt;&lt;p&gt;صراحی‌ای و حریفی گرت به چنگ افتد&lt;/p&gt;&lt;p&gt;به عقل نوش که ایام فتنه‌ انگیز است&lt;/p&gt;&lt;p&gt;در آستین مرقع پیاله پنهان کن&lt;/p&gt;&lt;p&gt;که همچو چشم صراحی زمانه خونریز است&lt;/p&gt;&lt;p&gt;به آب دیده بشوییم خرقه‌ها از می&lt;/p&gt;&lt;p&gt;که موسم ورع و روزگار پرهیز است&lt;/p&gt;&lt;p&gt;مجوی عیش خوش از دور باژگون سپهر&lt;/p&gt;&lt;p&gt;که صاف این سر خم جمله دُردی آمیز است&lt;/p&gt;&lt;p&gt;سپهرِ بر شده پرویزنی‌ست خون افشان&lt;/p&gt;&lt;p&gt;که ریزه‌اش سر کسری و تاجِ پرویز است&lt;/p&gt;&lt;p&gt;عراق و فارس گرفتی به شعر خوش حافظ&lt;/p&gt;&lt;p&gt;بیا که نوبت بغداد و وقت تبریز است&lt;/p&gt;', 'غزل شماره ۴۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(42, '&lt;p&gt;حالِ دل با تو گفتنم هوس است&lt;/p&gt;&lt;p&gt;خبرِ دل شِنُفتَنَم هوس است&lt;/p&gt;&lt;p&gt;طمعِ خام بین که قصهٔ فاش&lt;/p&gt;&lt;p&gt;از رقیبان نهفتنم هوس است&lt;/p&gt;&lt;p&gt;شبِ قدری چنین عزیزِ شریف&lt;/p&gt;&lt;p&gt;با تو تا روز خُفتنم هوس است&lt;/p&gt;&lt;p&gt;وه که دُردانه‌ای چنین نازک&lt;/p&gt;&lt;p&gt;در شبِ تار سُفتنم هوس است&lt;/p&gt;&lt;p&gt;ای صبا امشبم مدد فرمای&lt;/p&gt;&lt;p&gt;که سحرگه شکفتنم هوس است&lt;/p&gt;&lt;p&gt;از برای شرف به نوُکِ مژه&lt;/p&gt;&lt;p&gt;خاکِ راهِ تو رفتنم هوس است&lt;/p&gt;&lt;p&gt;همچو حافظ به رغمِ مدعیان&lt;/p&gt;&lt;p&gt;شعرِ رندانه گفتنم هوس است&lt;/p&gt;', 'غزل شماره ۴۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(43, '&lt;p&gt;صحنِ بُستان ذوق بخش و صحبتِ یاران خوش است&lt;/p&gt;&lt;p&gt;وقت گل خوش باد کز وی وقتِ میخواران خوش است&lt;/p&gt;&lt;p&gt;از صبا هر دم مشامِ جانِ ما خوش می‌شود&lt;/p&gt;&lt;p&gt;آری آری طیبِ انفاسِ هواداران خوش است&lt;/p&gt;&lt;p&gt;ناگشوده گل نقاب، آهنگِ رحلت ساز کرد&lt;/p&gt;&lt;p&gt;ناله کن بلبل که گلبانگِ دل افکاران خوش است&lt;/p&gt;&lt;p&gt;مرغِ خوشخوان را بشارت باد کاندر راهِ عشق&lt;/p&gt;&lt;p&gt;دوست را با نالهٔ شب‌های بیداران خوش است&lt;/p&gt;&lt;p&gt;نیست در بازارِ عالَم خوشدلی ور زان که هست&lt;/p&gt;&lt;p&gt;شیوهٔ رندی و خوش باشیِ عیاران خوش است&lt;/p&gt;&lt;p&gt;از زبانِ سوسنِ آزاده‌ام آمد به گوش&lt;/p&gt;&lt;p&gt;کاندر این دیرِ کهن، کارِ سبکباران خوش است&lt;/p&gt;&lt;p&gt;حافظا! تَرکِ جهان گفتن طریقِ خوشدلیست&lt;/p&gt;&lt;p&gt;تا نپنداری که احوالِ جهان داران خوش است&lt;/p&gt;', 'غزل شماره ۴۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(44, '&lt;p&gt;کنون که بر کفِ گل جامِ بادهٔ صاف است&lt;/p&gt;&lt;p&gt;به صدهزار زبان بلبلش در اوصاف است&lt;/p&gt;&lt;p&gt;بخواه دفتر اشعار و راهِ صحرا گیر&lt;/p&gt;&lt;p&gt;چه وقتِ مدرسه و بحث کشف کَشّاف است؟&lt;/p&gt;&lt;p&gt;فقیه مدرسه دی مست بود و فتوی داد&lt;/p&gt;&lt;p&gt;که می حرام ولی بِه ز مالِ اوقاف است&lt;/p&gt;&lt;p&gt;به دُرد و صاف تو را حکم نیست خوش دَرکَش&lt;/p&gt;&lt;p&gt;که هر چه ساقی ما کرد عینِ الطاف است&lt;/p&gt;&lt;p&gt;بِبُر ز خلق و چو عَنقا قیاس کار بگیر&lt;/p&gt;&lt;p&gt;که صیت گوشه‌نشینان ز قاف تا قاف است&lt;/p&gt;&lt;p&gt;حدیثِ مدّعیان و خیالِ همکاران&lt;/p&gt;&lt;p&gt;همان حکایت زردوز و بوریاباف است&lt;/p&gt;&lt;p&gt;خموش حافظ و این نکته‌های چون زر سرخ&lt;/p&gt;&lt;p&gt;نگاه‌دار که قَلّابِ شهر، صرّاف است&lt;/p&gt;', 'غزل شماره ۴۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(45, '&lt;p&gt;در این زمانه رفیقی که خالی از خِلَل است&lt;/p&gt;&lt;p&gt;صُراحیِ میِ ناب و سفینهٔ غزل است&lt;/p&gt;&lt;p&gt;جریده رو که گذرگاهِ عافیت تنگ است&lt;/p&gt;&lt;p&gt;پیاله گیر که عمرِ عزیز بی‌بدل است&lt;/p&gt;&lt;p&gt;نه من ز بی‌عملی در جهان ملولم و بس&lt;/p&gt;&lt;p&gt;ملالتِ عُلما هم ز علمِ بی‌عمل است&lt;/p&gt;&lt;p&gt;به چشمِ عقل در این رهگذار پرآشوب&lt;/p&gt;&lt;p&gt;جهان و کار جهان بی‌ثبات و بی‌محل است&lt;/p&gt;&lt;p&gt;بگیر طُرِّهٔ مه‌چهره‌ای و قصّه مخوان&lt;/p&gt;&lt;p&gt;که سعد و نحس ز تأثیر زهره و زحل است&lt;/p&gt;&lt;p&gt;دلم امید فراوان به وصلِ رویِ تو داشت&lt;/p&gt;&lt;p&gt;ولی اجل به رَهِ عمر رهزن امل است&lt;/p&gt;&lt;p&gt;به هیچ دور نخواهند یافت هشیارش&lt;/p&gt;&lt;p&gt;چنین که حافظ ما مستِ بادهٔ ازل است&lt;/p&gt;', 'غزل شماره ۴۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(46, '&lt;p&gt;گل در بر و می در کف و معشوق به کام است&lt;/p&gt;&lt;p&gt;سلطانِ جهانم به چنین روز غلام است&lt;/p&gt;&lt;p&gt;گو شمع میارید در این جمع که امشب&lt;/p&gt;&lt;p&gt;در مجلس ما ماهِ رخِ دوست تمام است&lt;/p&gt;&lt;p&gt;در مذهبِ ما باده حلال است ولیکن&lt;/p&gt;&lt;p&gt;بی‌روی تو ای سرو گل‌اندام حرام است&lt;/p&gt;&lt;p&gt;گوشم همه بر قولِ نی و نغمهٔ چنگ است&lt;/p&gt;&lt;p&gt;چشمم همه بر لعلِ لب و گردش جام است&lt;/p&gt;&lt;p&gt;در مجلسِ ما عطر میامیز که ما را&lt;/p&gt;&lt;p&gt;هر لحظه ز گیسوی تو خوش بوی مشام است&lt;/p&gt;&lt;p&gt;از چاشنی قند مگو هیچ و زِ شِکَّر&lt;/p&gt;&lt;p&gt;زان رو که مرا از لبِ شیرینِ تو کام است&lt;/p&gt;&lt;p&gt;تا گنجِ غمت در دلِ ویرانه مقیم است&lt;/p&gt;&lt;p&gt;همواره مرا کوی خرابات مقام است&lt;/p&gt;&lt;p&gt;از ننگ چه گویی که مرا نام ز ننگ است&lt;/p&gt;&lt;p&gt;وز نام چه پرسی که مرا ننگ ز نام است&lt;/p&gt;&lt;p&gt;مِی‌خواره و سرگشته و رندیم و نظرباز&lt;/p&gt;&lt;p&gt;وان کس که چو ما نیست در این شهر کدام است؟&lt;/p&gt;&lt;p&gt;با محتسبم عیب مگویید که او نیز&lt;/p&gt;&lt;p&gt;پیوسته چو ما در طلبِ عیشِ مدام است&lt;/p&gt;&lt;p&gt;حافظ منشین بی‌می و معشوق زمانی&lt;/p&gt;&lt;p&gt;کایّام گل و یاسمن و عید صیام است&lt;/p&gt;', 'غزل شماره ۴۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(47, '&lt;p&gt;به کوی میکده هر سالِکی که ره دانست&lt;/p&gt;&lt;p&gt;دری دگر زدن اندیشهٔ تَبَه دانست&lt;/p&gt;&lt;p&gt;زمانه افسر رندی نداد جز به کسی&lt;/p&gt;&lt;p&gt;که سرفرازیِ عالم در این کُلَه دانست&lt;/p&gt;&lt;p&gt;بر آستانهٔ میخانه هر که یافت رهی&lt;/p&gt;&lt;p&gt;ز فیضِ جامِ می اسرار خانقَه دانست&lt;/p&gt;&lt;p&gt;هر آن که رازِ دو عالم ز خطِ ساغر خواند&lt;/p&gt;&lt;p&gt;رُموزِ جامِ جم از نقشِ خاکِ ره دانست&lt;/p&gt;&lt;p&gt;ورای طاعتِ دیوانگان ز ما مطلب&lt;/p&gt;&lt;p&gt;که شیخِ مذهبِ ما عاقلی گنه دانست&lt;/p&gt;&lt;p&gt;دلم ز نرگسِ ساقی امان نخواست به جان&lt;/p&gt;&lt;p&gt;چرا که شیوهٔ آن تُرک دل سیه دانست&lt;/p&gt;&lt;p&gt;ز جورِ کوکبِ طالع ،سحرگهان چشمم&lt;/p&gt;&lt;p&gt;چنان گریست که ناهید دید و مه دانست&lt;/p&gt;&lt;p&gt;حدیثِ حافظ و ساغر که می‌زند پنهان&lt;/p&gt;&lt;p&gt;چه جایِ محتسب و شَحنه، پادشه دانست&lt;/p&gt;&lt;p&gt;بلندمرتبه شاهی که نُه رواق سِپهر&lt;/p&gt;&lt;p&gt;نمونه‌ای ز خَمِ طاقِ بارگه دانست&lt;/p&gt;', 'غزل شماره ۴۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(48, '&lt;p&gt;صوفی از پرتو می راز نهانی دانست&lt;/p&gt;&lt;p&gt;گوهر هر کس از این لعل، توانی دانست&lt;/p&gt;&lt;p&gt;قدر مجموعهٔ گل، مرغِ سحر داند و بس&lt;/p&gt;&lt;p&gt;که نه هر کو ورقی خواند، معانی دانست&lt;/p&gt;&lt;p&gt;عرضه کردم دو جهان بر دلِ کارافتاده&lt;/p&gt;&lt;p&gt;بجز از عشقِ تو باقی همه فانی دانست&lt;/p&gt;&lt;p&gt;آن شد اکنون که ز ابنای عوام اندیشم&lt;/p&gt;&lt;p&gt;مُحتَسِب نیز در این عیشِ نهانی دانست&lt;/p&gt;&lt;p&gt;دلبر آسایش ما مَصلحت وقت ندید&lt;/p&gt;&lt;p&gt;ور نه از جانبِ ما دل نگرانی دانست&lt;/p&gt;&lt;p&gt;سنگ و گِل را کُنَد از یُمنِ نظر لعل و عقیق&lt;/p&gt;&lt;p&gt;هر که قدرِ نفسِ بادِ یمانی دانست&lt;/p&gt;&lt;p&gt;ای که از دفترِ عقل آیت عشق آموزی&lt;/p&gt;&lt;p&gt;ترسم این نکته به تَحقیق ندانی دانست&lt;/p&gt;&lt;p&gt;می بیاور که ننازد به گلِ باغِ جهان&lt;/p&gt;&lt;p&gt;هر که غارتگریِ بادِ خزانی دانست&lt;/p&gt;&lt;p&gt;حافظ این گوهر منظوم که از طبع انگیخت&lt;/p&gt;&lt;p&gt;ز اثرِ تربیتِ آصفِ ثانی دانست&lt;/p&gt;', 'غزل شماره ۴۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(49, '&lt;p&gt;روضهٔ خُلدِ برین خلوت درویشان است&lt;/p&gt;&lt;p&gt;مایهٔ محتشمی خدمتِ درویشان است&lt;/p&gt;&lt;p&gt;گَنج عُزلت که طلسماتِ عجایب دارد&lt;/p&gt;&lt;p&gt;فتحِ آن در نظرِ رحمتِ درویشان است&lt;/p&gt;&lt;p&gt;قصرِ فردوس که رضوانش به دَربانی رفت&lt;/p&gt;&lt;p&gt;مَنظَری از چمنِ نُزهَتِ درویشان است&lt;/p&gt;&lt;p&gt;آن چه زر می‌شود از پرتو آن قلبِ سیاه&lt;/p&gt;&lt;p&gt;کیمیاییست که در صحبتِ درویشان است&lt;/p&gt;&lt;p&gt;آن که پیشش بِنَهَد تاجِ تکبر خورشید&lt;/p&gt;&lt;p&gt;کبریاییست که در حِشمتِ درویشان است&lt;/p&gt;&lt;p&gt;دولتی را که نباشد غم از آسیبِ زَوال&lt;/p&gt;&lt;p&gt;بی تکلف بشنو دولتِ درویشان است&lt;/p&gt;&lt;p&gt;خسروان، قبلهٔ حاجاتِ جهانند ولی&lt;/p&gt;&lt;p&gt;سببش بندگیِ حضرتِ درویشان است&lt;/p&gt;&lt;p&gt;روی مقصود که شاهان به دعا می‌طَلبند&lt;/p&gt;&lt;p&gt;مَظهَرَش آینهٔ طَلعَتِ درویشان است&lt;/p&gt;&lt;p&gt;از کران تا به کران لشکر ظُلم است ولی&lt;/p&gt;&lt;p&gt;از ازل تا به ابد، فرصتِ درویشان است&lt;/p&gt;&lt;p&gt;ای توانگر مَفُروش این همه نِخوَت که تو را&lt;/p&gt;&lt;p&gt;سر و زر در کَنفِ همتِ درویشان است&lt;/p&gt;&lt;p&gt;گنجِ قارون که فرو می‌شود از قَهر هنوز&lt;/p&gt;&lt;p&gt;خوانده باشی که هم از غیرتِ درویشان است&lt;/p&gt;&lt;p&gt;حافظ ار آبِ حیاتِ اَزَلی می‌خواهی&lt;/p&gt;&lt;p&gt;منبعش خاکِ درِ خلوتِ درویشان است&lt;/p&gt;&lt;p&gt;من غلامِ نظرِ آصِفِ عهدم کو را&lt;/p&gt;&lt;p&gt;صورتِ خواجگی و سیرتِ درویشان است&lt;/p&gt;', 'غزل شماره ۴۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(50, '&lt;p&gt;به دامِ زلفِ تو دل مبتلایِ خویشتن است&lt;/p&gt;&lt;p&gt;بکُش به غمزه که اینش سزایِ خویشتن است&lt;/p&gt;&lt;p&gt;گرت ز دست برآید مُرادِ خاطرِ ما&lt;/p&gt;&lt;p&gt;به دست باش که خیری به جایِ خویشتن است&lt;/p&gt;&lt;p&gt;به جانت ای بتِ شیرین دهن که همچون شمع&lt;/p&gt;&lt;p&gt;شبانِ تیره، مُرادم فنای خویشتن است&lt;/p&gt;&lt;p&gt;چو رای عشق زدی با تو گفتم ای بلبل&lt;/p&gt;&lt;p&gt;مَکُن که آن گلِ خندان به رای خویشتن است&lt;/p&gt;&lt;p&gt;به مشکِ چین و چِگِل نیست بوی گل مُحتاج&lt;/p&gt;&lt;p&gt;که نافه‌هاش ز بندِ قَبایِ خویشتن است&lt;/p&gt;&lt;p&gt;مرو به خانهٔ اربابِ بی‌مُروتِ دهر&lt;/p&gt;&lt;p&gt;که گنجِ عافیتت در سرای خویشتن است&lt;/p&gt;&lt;p&gt;بسوخت حافظ و در شرطِ عشقبازی او&lt;/p&gt;&lt;p&gt;هنوز بر سرِ عهد و وفایِ خویشتن است&lt;/p&gt;', 'غزل شماره ۵۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(51, '&lt;p&gt;لعلِ سیرابِ به خون تشنه لب یار من است&lt;/p&gt;&lt;p&gt;وز پی دیدنِ او دادنِ جان کار من است&lt;/p&gt;&lt;p&gt;شرم از آن چشمِ سیه بادش و مژگان دراز&lt;/p&gt;&lt;p&gt;هر که دل بردنِ او دید و در انکارِ من است&lt;/p&gt;&lt;p&gt;ساروان رَخت به دروازه مَبَر کان سرِ کو&lt;/p&gt;&lt;p&gt;شاهراهیست که منزلگهِ دلدارِ من است&lt;/p&gt;&lt;p&gt;بندهٔ طالعِ خویشم که در این قحطِ وفا&lt;/p&gt;&lt;p&gt;عشق آن لولیِ سرمست خریدار من است&lt;/p&gt;&lt;p&gt;طَبلِهٔ عطرِ گل و زلفِ عبیر افشانش&lt;/p&gt;&lt;p&gt;فیضِ یک شَمِّه ز بوی خوشِ عطارِ من است&lt;/p&gt;&lt;p&gt;باغبان همچو نسیمم ز درِ خویش مران&lt;/p&gt;&lt;p&gt;کآبِ گلزارِ تو از اشکِ چو گلنارِ من است&lt;/p&gt;&lt;p&gt;شربتِ قند و گلاب از لبِ یارم فرمود&lt;/p&gt;&lt;p&gt;نرگس او که طبیبِ دلِ بیمارِ من است&lt;/p&gt;&lt;p&gt;آن که در طرزِ غزل نکته به حافظ آموخت&lt;/p&gt;&lt;p&gt;یارِ شیرین‌سخنِ نادره‌گفتارِ من است&lt;/p&gt;', 'غزل شماره ۵۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(52, '&lt;p&gt;روزگاریست که سودایِ بتان دینِ من است&lt;/p&gt;&lt;p&gt;غمِ این کار نشاطِ دلِ غمگینِ من است&lt;/p&gt;&lt;p&gt;دیدنِ رویِ تو را دیدهٔ جان بین باید&lt;/p&gt;&lt;p&gt;وین کجا مرتبهٔ چشمِ جهان بینِ من است؟&lt;/p&gt;&lt;p&gt;یارِ من باش که زیبِ فلک و زینتِ دهر&lt;/p&gt;&lt;p&gt;از مه روی تو و اشکِ چو پروینِ من است&lt;/p&gt;&lt;p&gt;تا مرا عشقِ تو تعلیمِ سخن گفتن کرد&lt;/p&gt;&lt;p&gt;خلق را وردِ زبان، مدحت و تحسینِ من است&lt;/p&gt;&lt;p&gt;دولت فقر خدایا به من ارزانی دار&lt;/p&gt;&lt;p&gt;کاین کرامت سببِ حشمت و تمکینِ من است&lt;/p&gt;&lt;p&gt;واعظِ شَحنه شناس این عظمت گو مفروش&lt;/p&gt;&lt;p&gt;زان که منزلگهِ سلطان، دلِ مسکینِ من است&lt;/p&gt;&lt;p&gt;یا رب این کعبهٔ مقصود تماشاگه کیست؟&lt;/p&gt;&lt;p&gt;که مغیلانِ طریقش گل و نسرینِ من است&lt;/p&gt;&lt;p&gt;حافظ از حشمتِ پرویز دگر قصه مخوان&lt;/p&gt;&lt;p&gt;که لبش جرعه کش خسرو شیرین من است&lt;/p&gt;', 'غزل شمارهٔ ۵۲', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(54, '&lt;p&gt;منم که گوشهٔ میخانه خانقاهِ من است&lt;/p&gt;&lt;p&gt;دعایِ پیرِ مغان وردِ صبحگاهِ من است&lt;/p&gt;&lt;p&gt;گَرَم ترانهٔ چنگ صَبوح نیست چه باک&lt;/p&gt;&lt;p&gt;نوایِ من به سحر آهِ عذرخواهِ من است&lt;/p&gt;&lt;p&gt;ز پادشاه و گدا فارغم بحمدالله&lt;/p&gt;&lt;p&gt;گدایِ خاکِ درِ دوست، پادشاه من است&lt;/p&gt;&lt;p&gt;غرض ز مسجد و میخانه‌ام وصال شماست&lt;/p&gt;&lt;p&gt;جز این خیال ندارم خدا گواهِ من است&lt;/p&gt;&lt;p&gt;مگر به تیغ اجل خیمه بَرکَنَم ور نی&lt;/p&gt;&lt;p&gt;رمیدن از درِ دولت نه رسم و راهِ من است&lt;/p&gt;&lt;p&gt;از آن زمان که بر این آستان نهادم روی&lt;/p&gt;&lt;p&gt;فرازِ مسندِ خورشید، تکیه‌گاهِ من است&lt;/p&gt;&lt;p&gt;گناه اگر چه نبود اختیارِ ما حافظ&lt;/p&gt;&lt;p&gt;تو در طریقِ ادب باش، گو گناهِ من است&lt;/p&gt;', 'غزل شمارهٔ ۵۳', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(55, '&lt;p&gt;ز گریه مَردُمِ چشمم نشسته در خون است&lt;/p&gt;&lt;p&gt;ببین که در طلبت حالِ مَردُمان چون است&lt;/p&gt;&lt;p&gt;به یادِ لعلِ تو و چشمِ مستِ میگونت&lt;/p&gt;&lt;p&gt;ز جامِ غم، می لعلی که می‌خورم خون است&lt;/p&gt;&lt;p&gt;ز مشرقِ سرِ کو آفتابِ طلعتِ تو&lt;/p&gt;&lt;p&gt;اگر طلوع کند، طالعم همایون است&lt;/p&gt;&lt;p&gt;حکایتِ لبِ شیرین، کلام فرهاد است&lt;/p&gt;&lt;p&gt;شِکَنجِ طُرِّهٔ لیلی مقام مجنون است&lt;/p&gt;&lt;p&gt;دلم بجو که قدت همچو سرو دلجوی است&lt;/p&gt;&lt;p&gt;سخن بگو که کلامت لطیف و موزون است&lt;/p&gt;&lt;p&gt;ز دورِ باده به جان، راحتی رسان ساقی&lt;/p&gt;&lt;p&gt;که رنجِ خاطرم از جورِ دورِ گردون است&lt;/p&gt;&lt;p&gt;از آن دمی که ز چشمم برفت رودِ عزیز&lt;/p&gt;&lt;p&gt;کنارِ دامنِ من همچو رودِ جیحون است&lt;/p&gt;&lt;p&gt;چگونه شاد شود اندرونِ غمگینم؟&lt;/p&gt;&lt;p&gt;به اختیار، که از اختیار بیرون است&lt;/p&gt;&lt;p&gt;ز بیخودی طلبِ یار می‌کند حافظ&lt;/p&gt;&lt;p&gt;چو مفلسی که طلبکارِ گنجِ قارون است&lt;/p&gt;', 'غزل شمارهٔ ۵۴', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(56, '&lt;p&gt;خَمِ زلفِ تو دامِ کفر و دین است&lt;/p&gt;&lt;p&gt;ز کارستانِ او یک شمه این است&lt;/p&gt;&lt;p&gt;جمالت مُعجِزِ حُسن است لیکن&lt;/p&gt;&lt;p&gt;حدیثِ غمزه‌ات سحرِ مبین است&lt;/p&gt;&lt;p&gt;ز چشمِ شوخِ تو جان کی توان برد؟&lt;/p&gt;&lt;p&gt;که دایم با کمان اندر کمین است&lt;/p&gt;&lt;p&gt;بر آن چشمِ سیه صد آفرین باد&lt;/p&gt;&lt;p&gt;که در عاشق کُشی سِحرآفرین است&lt;/p&gt;&lt;p&gt;عجب عِلمیست عِلم هیئت عشق&lt;/p&gt;&lt;p&gt;که چرخِ هشتمش، هفتم زمین است&lt;/p&gt;&lt;p&gt;تو پنداری که بدگو رفت و جان برد؟&lt;/p&gt;&lt;p&gt;حسابش با کرام الکاتبین است&lt;/p&gt;&lt;p&gt;مشو حافظ ز کیدِ زلفش ایمن&lt;/p&gt;&lt;p&gt;که دل برد و کنون در بندِ دین است&lt;/p&gt;', 'غزل شمارهٔ ۵۵', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(57, '&lt;p&gt;دل سراپردهٔ محبتِ اوست&lt;/p&gt;&lt;p&gt;دیده آیینه دارِ طلعت اوست&lt;/p&gt;&lt;p&gt;من که سر درنیاورم به دو کون&lt;/p&gt;&lt;p&gt;گردنم زیرِ بارِ منتِ اوست&lt;/p&gt;&lt;p&gt;تو و طوبی و ما و قامتِ یار&lt;/p&gt;&lt;p&gt;فکرِ هر کس به قدرِ همتِ اوست&lt;/p&gt;&lt;p&gt;گر من آلوده دامنم چه عجب&lt;/p&gt;&lt;p&gt;همه عالم گواهِ عصمتِ اوست&lt;/p&gt;&lt;p&gt;من که باشم در آن حرم که صبا&lt;/p&gt;&lt;p&gt;پرده دارِ حریمِ حُرمتِ اوست&lt;/p&gt;&lt;p&gt;بی خیالش مباد منظرِ چشم&lt;/p&gt;&lt;p&gt;زان که این گوشه جایِ خلوتِ اوست&lt;/p&gt;&lt;p&gt;هر گلِ نو که شد چمن آرای&lt;/p&gt;&lt;p&gt;ز اثر رنگ و بویِ صحبتِ اوست&lt;/p&gt;&lt;p&gt;دورِ مجنون گذشت و نوبتِ ماست&lt;/p&gt;&lt;p&gt;هر کسی پنج روز، نوبتِ اوست&lt;/p&gt;&lt;p&gt;مُلکَتِ عاشقی و گنجِ طرب&lt;/p&gt;&lt;p&gt;هر چه دارم ز یُمنِ همتِ اوست&lt;/p&gt;&lt;p&gt;من و دل گر فدا شدیم چه باک؟&lt;/p&gt;&lt;p&gt;غرض اندر میان سلامتِ اوست&lt;/p&gt;&lt;p&gt;فقرِ ظاهر مبین که حافظ را&lt;/p&gt;&lt;p&gt;سینه گنجینهٔ محبتِ اوست&lt;/p&gt;', 'غزل شمارهٔ ۵۶', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(58, '&lt;p&gt;آن سیه‌چرده که شیرینیِ عالم با اوست&lt;/p&gt;&lt;p&gt;چشمِ میگون لبِ خندان دلِ خرم با اوست&lt;/p&gt;&lt;p&gt;گرچه شیرین‌ دهنان پادشهانند ولی&lt;/p&gt;&lt;p&gt;او سلیمانِ زمان است که خاتم با اوست&lt;/p&gt;&lt;p&gt;رویِ خوب است و کمالِ هنر و دامنِ پاک&lt;/p&gt;&lt;p&gt;لاجرم همتِ پاکان دو عالم با اوست&lt;/p&gt;&lt;p&gt;خال مُشکین که بدان عارض گندمگون است&lt;/p&gt;&lt;p&gt;سِرِّ آن دانه که شد رهزنِ آدم با اوست&lt;/p&gt;&lt;p&gt;دلبرم عزم سفر کرد خدا را یاران&lt;/p&gt;&lt;p&gt;چه کنم با دل مجروح که مرهم با اوست&lt;/p&gt;&lt;p&gt;با که این نکته توان گفت که آن سنگین‌ دل&lt;/p&gt;&lt;p&gt;کُشت ما را و دمِ عیسیِ مریم با اوست&lt;/p&gt;&lt;p&gt;حافظ از معتقدان است گرامی دارش&lt;/p&gt;&lt;p&gt;زان که بخشایشِ بس روحِ مکرم با اوست&lt;/p&gt;', 'غزل شمارهٔ ۵۷', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(59, '&lt;p&gt;سرِ ارادتِ ما و آستانِ حضرت دوست&lt;/p&gt;&lt;p&gt;که هر چه بر سرِ ما می‌رود ارادتِ اوست&lt;/p&gt;&lt;p&gt;نظیرِ دوست ندیدم اگر چه از مَه و مِهر&lt;/p&gt;&lt;p&gt;نهادم آینه‌ها در مقابلِ رخِ دوست&lt;/p&gt;&lt;p&gt;صبا ز حالِ دلِ تنگِ ما چه شرح دهد؟&lt;/p&gt;&lt;p&gt;که چون شِکَنجِ ورق‌هایِ غنچه تو بر توست&lt;/p&gt;&lt;p&gt;نه من سَبوکش این دیرِ رندسوزم و بس&lt;/p&gt;&lt;p&gt;بسا سَرا که در این کارخانه سنگ و سبوست&lt;/p&gt;&lt;p&gt;مگر تو شانه زدی زلفِ عنبرافشان را؟&lt;/p&gt;&lt;p&gt;که بادْ غالیه‌سا گشت و خاکْ عنبربوست&lt;/p&gt;&lt;p&gt;نثارِ رویِ تو هر برگِ گل که در چمن است&lt;/p&gt;&lt;p&gt;فدای قَدِّ تو هر سروبُن که بر لبِ جوست&lt;/p&gt;&lt;p&gt;زبانِ ناطقه در وصفِ شوق نالان است&lt;/p&gt;&lt;p&gt;چه جای کِلکِ بریده‌زبانِ بیهُده‌گوست؟&lt;/p&gt;&lt;p&gt;رخِ تو در دلم آمد مراد خواهم یافت&lt;/p&gt;&lt;p&gt;چرا که حالِ نکو در قَفایِ فالِ نکوست&lt;/p&gt;&lt;p&gt;نه این زمان دلِ حافظ در آتشِ هوس است&lt;/p&gt;&lt;p&gt;که داغدار ازل همچو لالهٔ خودروست&lt;/p&gt;', 'غزل شمارهٔ ۵۸', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(60, '&lt;p&gt;دارم امید عاطفتی از جناب دوست&lt;/p&gt;&lt;p&gt;کردم جنایتی و امیدم به عفو اوست&lt;/p&gt;&lt;p&gt;دانم که بُگذرد ز سرِ جرمِ من که او&lt;/p&gt;&lt;p&gt;گر چه پریوش است ولیکن فرشته خوست&lt;/p&gt;&lt;p&gt;چندان گریستیم که هر کس که برگذشت&lt;/p&gt;&lt;p&gt;در اشکِ ما چو دید روان گفت کـ‌این چه جوست؟&lt;/p&gt;&lt;p&gt;هیچ است آن دهان و نبینم از او نشان&lt;/p&gt;&lt;p&gt;موی است آن میان و ندانم که آن چه موست&lt;/p&gt;&lt;p&gt;دارم عجب ز نقشِ خیالش که چون نرفت&lt;/p&gt;&lt;p&gt;از دیده‌ام که دَم به دَمش کار شُست و شوست&lt;/p&gt;&lt;p&gt;بی گفت و گوی زلفِ تو دل را همی‌کشد&lt;/p&gt;&lt;p&gt;با زلف دلکَش تو که را روی گفت و گوست؟&lt;/p&gt;&lt;p&gt;عمریست تا ز زلفِ تو بویی شنیده‌ام&lt;/p&gt;&lt;p&gt;زان بوی در مشامِ دلِ من هنوز بوست&lt;/p&gt;&lt;p&gt;حافظ بد است حالِ پریشانِ تو، ولی&lt;/p&gt;&lt;p&gt;بر بویِ زلفِ یار پریشانیَت نکوست&lt;/p&gt;', 'غزل شمارهٔ ۵۹', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(61, '&lt;p&gt;آن پیکِ ناموَر که رسید از دیارِ دوست&lt;/p&gt;&lt;p&gt;آورد حِرزِ جان ز خطِ مُشکبارِ دوست&lt;/p&gt;&lt;p&gt;خوش می‌دهد نشانِ جلال و جمالِ یار&lt;/p&gt;&lt;p&gt;خوش می‌کند حکایتِ عِزّ و وقارِ دوست&lt;/p&gt;&lt;p&gt;دل دادمش به مژده و خِجلَت همی‌برم&lt;/p&gt;&lt;p&gt;زین نقدِ قلبِ خویش که کردم نثارِ دوست&lt;/p&gt;&lt;p&gt;شکرِ خدا که از مددِ بختِ کارساز&lt;/p&gt;&lt;p&gt;بر حسبِ آرزوست همه کار و بارِ دوست&lt;/p&gt;&lt;p&gt;سیرِ سپهر و دورِ قمر را چه اختیار؟&lt;/p&gt;&lt;p&gt;در گردشند بر حَسَبِ اختیار دوست&lt;/p&gt;&lt;p&gt;گر بادِ فتنه هر دو جهان را به هم زند&lt;/p&gt;&lt;p&gt;ما و چراغِ چَشم و رهِ انتظارِ دوست&lt;/p&gt;&lt;p&gt;کُحلُ الجَواهری به من آر ای نسیمِ صبح&lt;/p&gt;&lt;p&gt;زان خاکِ نیکبخت که شد رهگذارِ دوست&lt;/p&gt;&lt;p&gt;ماییم و آستانهٔ عشق و سرِ نیاز&lt;/p&gt;&lt;p&gt;تا خوابِ خوش که را بَرَد اندر کنارِ دوست&lt;/p&gt;&lt;p&gt;دشمن به قصدِ حافظ اگر دم زند چه باک؟&lt;/p&gt;&lt;p&gt;مِنَّت خدای را که نیَم شرمسارِ دوست&lt;/p&gt;', 'غزل شمارهٔ ۶۰', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(62, '&lt;p&gt;صبا اگر گذری اُفتَدَت به کشور دوست&lt;/p&gt;&lt;p&gt;بیار نَفحِه‌ای از گیسوی مُعَنبَر دوست&lt;/p&gt;&lt;p&gt;به جانِ او که به شکرانه جان برافشانم&lt;/p&gt;&lt;p&gt;اگر به سویِ من آری پیامی از برِ دوست&lt;/p&gt;&lt;p&gt;و گر چنان که در آن حضرتت نباشد بار&lt;/p&gt;&lt;p&gt;برایِ دیده بیاور غباری از درِ دوست&lt;/p&gt;&lt;p&gt;منِ گدا و تمنایِ وصلِ او هیهات&lt;/p&gt;&lt;p&gt;مگر به خواب ببینم خیالِ منظرِ دوست&lt;/p&gt;&lt;p&gt;دل صِنوبَریَم همچو بید لرزان است&lt;/p&gt;&lt;p&gt;ز حسرتِ قد و بالای چون صنوبرِ دوست&lt;/p&gt;&lt;p&gt;اگر چه دوست به چیزی نمی‌خرد ما را&lt;/p&gt;&lt;p&gt;به عالمی نفروشیم مویی از سرِ دوست&lt;/p&gt;&lt;p&gt;چه باشد ار شود از بندِ غم دلش آزاد&lt;/p&gt;&lt;p&gt;چو هست حافظِ مسکین غلام و چاکر دوست&lt;/p&gt;', 'غزل شمارهٔ ۶۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(63, '&lt;p&gt;صبا اگر گذری اُفتَدَت به کشور دوست&lt;/p&gt;&lt;p&gt;بیار نَفحِه‌ای از گیسوی مُعَنبَر دوست&lt;/p&gt;&lt;p&gt;به جانِ او که به شکرانه جان برافشانم&lt;/p&gt;&lt;p&gt;اگر به سویِ من آری پیامی از برِ دوست&lt;/p&gt;&lt;p&gt;و گر چنان که در آن حضرتت نباشد بار&lt;/p&gt;&lt;p&gt;برایِ دیده بیاور غباری از درِ دوست&lt;/p&gt;&lt;p&gt;منِ گدا و تمنایِ وصلِ او هیهات&lt;/p&gt;&lt;p&gt;مگر به خواب ببینم خیالِ منظرِ دوست&lt;/p&gt;&lt;p&gt;دل صِنوبَریَم همچو بید لرزان است&lt;/p&gt;&lt;p&gt;ز حسرتِ قد و بالای چون صنوبرِ دوست&lt;/p&gt;&lt;p&gt;اگر چه دوست به چیزی نمی‌خرد ما را&lt;/p&gt;&lt;p&gt;به عالمی نفروشیم مویی از سرِ دوست&lt;/p&gt;&lt;p&gt;چه باشد ار شود از بندِ غم دلش آزاد&lt;/p&gt;&lt;p&gt;چو هست حافظِ مسکین غلام و چاکر دوست&lt;/p&gt;', 'غزل شمارهٔ ۶۱', 'حافظ', 'دیوان حافظ', 'غزل', 1, 1),
(64, '&lt;p&gt;برخیز بتا بیا ز بهر دل ما&lt;/p&gt;&lt;p&gt;حل کن به جمال خویشتن مشکل ما&lt;/p&gt;&lt;p&gt;یک کوزه شراب تا به هم نوش کنیم&lt;/p&gt;&lt;p&gt;زآن پیش که کوزه‌ها کنند از گل ما&lt;/p&gt;', 'رباعی شماره ۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(65, '&lt;p&gt;چون عهده نمی‌شود کسی فردا را&lt;/p&gt;&lt;p&gt;حالی خوش دار این دل پر سودا را&lt;/p&gt;&lt;p&gt;می نوش به ماهتاب ای ماه که ماه&lt;/p&gt;&lt;p&gt;بسیار بتابد و نیابد ما را&lt;/p&gt;', 'رباعی شماره ۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(66, '&lt;p&gt;قرآن که مهین کلام خوانند آن را&lt;/p&gt;&lt;p&gt;گهگاه نه بر دوام خوانند آن را&lt;/p&gt;&lt;p&gt;بر گرد پیاله آیتی هست مقیم&lt;/p&gt;&lt;p&gt;کاندر همه جا مدام خوانند آن را&lt;/p&gt;', 'رباعی شماره ۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(67, '&lt;p&gt;گر می نخوری طعنه مزن مستان را&lt;/p&gt;&lt;p&gt;بنیاد مکن تو حیله و دستان را&lt;/p&gt;&lt;p&gt;تو غره بدان مشو که می می‌نخوری&lt;/p&gt;&lt;p&gt;صد لقمه خوری که می غلام است آن را&lt;/p&gt;', 'رباعی شماره ۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(68, '&lt;p&gt;هر چند که رنگ و بوی زیباست مرا&lt;/p&gt;&lt;p&gt;چون لاله رخ و چو سرو بالاست مرا&lt;/p&gt;&lt;p&gt;معلوم نشد که در طربخانۀ خاک&lt;/p&gt;&lt;p&gt;نقاش ازل بهر چه آراست مرا&lt;/p&gt;', 'رباعی شماره ۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(69, '&lt;p&gt;ماییم و می و مطرب و این کنج خراب&lt;/p&gt;&lt;p&gt;جان و دل و جام و جامه پر درد شراب&lt;/p&gt;&lt;p&gt;فارغ ز امید رحمت و بیم عذاب&lt;/p&gt;&lt;p&gt;آزاد ز خاک و باد و از آتش و آب&lt;/p&gt;', 'رباعی شماره ۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(70, '&lt;p&gt;آن قصر که جمشید در او جام گرفت&lt;/p&gt;&lt;p&gt;آهو بچه کرد و روبه آرام گرفت&lt;/p&gt;&lt;p&gt;بهرام که گور می‌گرفتی همه عمر&lt;/p&gt;&lt;p&gt;دیدی که چگونه گور بهرام گرفت&lt;/p&gt;', 'رباعی شماره ۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(71, '&lt;p&gt;ابر آمد و باز بر سر سبزه گریست&lt;/p&gt;&lt;p&gt;بی بادهٔ گلرنگ نمی‌باید زیست&lt;/p&gt;&lt;p&gt;این سبزه که امروز تماشاگه ماست&lt;/p&gt;&lt;p&gt;تا سبزهٔ خاک ما تماشاگه کیست&lt;/p&gt;', 'رباعی شماره ۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(72, '&lt;p&gt;اکنون که گل سعادتت پربار است&lt;/p&gt;&lt;p&gt;دست تو ز جام می چرا بیکار است&lt;/p&gt;&lt;p&gt;می خور که زمانه دشمنی غدار است&lt;/p&gt;&lt;p&gt;دریافتن روز چنین دشوار است&lt;/p&gt;', 'رباعی شماره ۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(73, '&lt;p&gt;امروز تو را دسترس فردا نیست&lt;/p&gt;&lt;p&gt;واندیشهٔ فردات به جز سودا نیست&lt;/p&gt;&lt;p&gt;ضایع مکن این دم ار دلت شیدا نیست&lt;/p&gt;&lt;p&gt;کاین باقی عمر را بها پیدا نیست&lt;/p&gt;', 'رباعی شماره ۱۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(74, '&lt;p&gt;ای آمده از عالم روحانی تفت&lt;/p&gt;&lt;p&gt;حیران شده در پنج و چهار و شش و هفت&lt;/p&gt;&lt;p&gt;می نوش ندانی ز کجا آمده‌ای&lt;/p&gt;&lt;p&gt;خوش باش ندانی به کجا خواهی رفت&lt;/p&gt;', 'رباعی شماره  ۱۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(75, '&lt;p&gt;ای چرخ فلک خرابی از کینهٔ توست&lt;/p&gt;&lt;p&gt;بیدادگری شیوهٔ دیرینهٔ توست&lt;/p&gt;&lt;p&gt;ای خاک اگر سینه تو بشکافند&lt;/p&gt;&lt;p&gt;بس گوهر قیمتی که در سینهٔ توست&lt;/p&gt;', 'رباعی شماره  ۱۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(76, '&lt;p&gt;ای دل چو زمانه می‌کند غمناکت&lt;/p&gt;&lt;p&gt;ناگه برود ز تن روان پاکت&lt;/p&gt;&lt;p&gt;بر سبزه نشین و خوش بزی روزی چند&lt;/p&gt;&lt;p&gt;زآن پیش که سبزه بر دمد از خاکت&lt;/p&gt;', 'رباعی شماره  ۱۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(77, '&lt;p&gt;این بحر وجود آمده بیرون ز نهفت&lt;/p&gt;&lt;p&gt;کس نیست که این گوهر تحقیق بسفت&lt;/p&gt;&lt;p&gt;هر کس سخنی از سر سودا گفتند&lt;/p&gt;&lt;p&gt;زآن روی که هست کس نمی‌داند گفت&lt;/p&gt;', 'رباعی شماره  ۱۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(78, '&lt;p&gt;این کوزه چو من عاشق زاری بوده‌ست&lt;/p&gt;&lt;p&gt;در بند سر زلف نگاری بوده‌ست&lt;/p&gt;&lt;p&gt;این دسته که بر گردن او می‌بینی&lt;/p&gt;&lt;p&gt;دستی‌ست که بر گردن یاری بوده‌ست&lt;/p&gt;', 'رباعی شماره  ۱۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(79, '&lt;p&gt;این کوزه که آبخوارهٔ مزدوری‌ست&lt;/p&gt;&lt;p&gt;از دیدهٔ شاهی و دل دستوری‌ست&lt;/p&gt;&lt;p&gt;هر کاسهٔ می که بر کف مخموری‌ست&lt;/p&gt;&lt;p&gt;از عارض مستی و لب مستوری‌ست&lt;/p&gt;', 'رباعی شماره  ۱۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(80, '&lt;p&gt;این کهنه رباط را که عالم نام است&lt;/p&gt;&lt;p&gt;وآرامگه ابلق صبح و شام است&lt;/p&gt;&lt;p&gt;بزمی‌ست که واماندۀ صد جمشید است&lt;/p&gt;&lt;p&gt;قصری‌ست که تکیه‌گاه صد بهرام است&lt;/p&gt;', 'رباعی شماره  ۱۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(81, '&lt;p&gt;این یک دو سه روز نوبت عمر گذشت&lt;/p&gt;&lt;p&gt;چون آب به جویبار و چون باد به دشت&lt;/p&gt;&lt;p&gt;هرگز غم دو روز مرا یاد نگشت&lt;/p&gt;&lt;p&gt;روزی که نیامده‌ست و روزی که گذشت&lt;/p&gt;', 'رباعی شماره  ۱۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(82, '&lt;p&gt;بر چهرۀ گل نسیم نوروز خوش است&lt;/p&gt;&lt;p&gt;در صحن چمن روی دل‌افروز خوش است&lt;/p&gt;&lt;p&gt;از دی که گذشت هر چه گویی خوش نیست&lt;/p&gt;&lt;p&gt;خوش باش و ز دی مگو که امروز خوش است&lt;/p&gt;', 'رباعی شماره  ۱۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(83, '&lt;p&gt;پیش از من و تو لیل و نهاری بوده‌ست&lt;/p&gt;&lt;p&gt;گردنده فلک نیز بکاری بوده است&lt;/p&gt;&lt;p&gt;هرجا که قدم نهی تو بر روی زمین&lt;/p&gt;&lt;p&gt;آن مردمک چشم نگاری بوده‌ست&lt;/p&gt;', 'رباعی شماره  ۲۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(84, '&lt;p&gt;تا چند زنم به روی دریاها خشت&lt;/p&gt;&lt;p&gt;بیزار شدم ز بت‌پرستان کنشت&lt;/p&gt;&lt;p&gt;خیام ، که گفت دوزخی خواهد بود&lt;/p&gt;&lt;p&gt;که رفت به دوزخ و که آمد ز بهشت&lt;/p&gt;', 'رباعی شماره  ۲۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(85, '&lt;p&gt;ترکیب پیاله‌ای که در هم پیوست&lt;/p&gt;&lt;p&gt;بشکستن آن روا نمی‌دارد مست&lt;/p&gt;&lt;p&gt;چندین سر و پای نازنین از سر دست&lt;/p&gt;&lt;p&gt;از مهر که پیوست و به کین که شکست&lt;/p&gt;', 'رباعی شماره  ۲۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(86, '&lt;p&gt;ترکیب طبایع چو به کام تو دمی‌ست&lt;/p&gt;&lt;p&gt;رو شاد بزی اگرچه بر تو ستمی‌ست&lt;/p&gt;&lt;p&gt;با اهل خرد باش که اصل تن تو&lt;/p&gt;&lt;p&gt;گردی و نسیمی و غباری و دمی‌ست&lt;/p&gt;', 'رباعی شماره  ۲۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(87, '&lt;p&gt;چون ابر به نوروز رخ لاله بشست&lt;/p&gt;&lt;p&gt;برخیز و به جام باده کن عزم درست&lt;/p&gt;&lt;p&gt;کاین سبزه که امروز تماشاگه توست&lt;/p&gt;&lt;p&gt;فردا همه از خاک تو برخواهد رست&lt;/p&gt;', 'رباعی شماره  ۲۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(88, '&lt;p&gt;چون بلبل مست راه در بستان یافت&lt;/p&gt;&lt;p&gt;روی گل و جام باده را خندان یافت&lt;/p&gt;&lt;p&gt;آمد به زبان حال در گوشم گفت&lt;/p&gt;&lt;p&gt;دریاب که عمر رفته را نتوان یافت&lt;/p&gt;', 'رباعی شماره  ۲۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(89, '&lt;p&gt;چون چرخ به کام یک خردمند نگشت&lt;/p&gt;&lt;p&gt;خواهی تو فلک هفت شمر خواهی هشت&lt;/p&gt;&lt;p&gt;چون باید مرد و آرزوها همه هشت&lt;/p&gt;&lt;p&gt;چه مور خورد به گور و چه گرگ به دشت&lt;/p&gt;', 'رباعی شماره  ۲۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(90, '&lt;p&gt;چون لاله به نوروز قدح گیر به دست&lt;/p&gt;&lt;p&gt;با لاله‌رخی اگر تو را فرصت هست&lt;/p&gt;&lt;p&gt;می نوش به خرمی که این چرخ کهن&lt;/p&gt;&lt;p&gt;ناگاه تو را چو خاک گرداند پست&lt;/p&gt;', 'رباعی شماره  ۲۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(91, '&lt;p&gt;چون نیست حقیقت و یقین اندر دست&lt;/p&gt;&lt;p&gt;نتوان به امید شک همه عمر نشست&lt;/p&gt;&lt;p&gt;هان تا ننهیم جام می از کف دست&lt;/p&gt;&lt;p&gt;در بی‌خبری مرد چه هشیار و چه مست&lt;/p&gt;', 'رباعی شماره  ۲۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(92, '&lt;p&gt;چون نیست ز هرچه هست جز باد به دست&lt;/p&gt;&lt;p&gt;چون هست به هرچه هست نقصان و شکست&lt;/p&gt;&lt;p&gt;انگار که هرچه هست در عالم نیست&lt;/p&gt;&lt;p&gt;پندار که هرچه نیست در عالم هست&lt;/p&gt;', 'رباعی شماره  ۲۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(93, '&lt;p&gt;خاکی که به زیر پای هر نادانی‌ست&lt;/p&gt;&lt;p&gt;کفّ صنمیّ و چهرهٔ جانانی‌ست&lt;/p&gt;&lt;p&gt;هر خشت که بر کنگرهٔ ایوانی‌ست&lt;/p&gt;&lt;p&gt;انگشت وزیر یا سر سلطانی‌ست&lt;/p&gt;', 'رباعی شماره  ۳۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(94, '&lt;p&gt;دارنده چو ترکیب طبایع آراست&lt;/p&gt;&lt;p&gt;از بهر چه اوفکندش اندر کم و کاست&lt;/p&gt;&lt;p&gt;گر نیک آمد شکستن از بهر چه بود&lt;/p&gt;&lt;p&gt;ور نیک نیامد این صور عیب که راست&lt;/p&gt;', 'رباعی شماره  ۳۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(95, '&lt;p&gt;در پردۀ اسرار کسی را ره نیست&lt;/p&gt;&lt;p&gt;زین تعبیه جان هیچ‌کس آگه نیست&lt;/p&gt;&lt;p&gt;جز در دل خاک هیچ منزلگه نیست&lt;/p&gt;&lt;p&gt;می خور که چنین فسانه‌ها کوته نیست&lt;/p&gt;', 'رباعی شماره ۳۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(96, '&lt;p&gt;در خواب بدم مرا خردمندی گفت&lt;/p&gt;&lt;p&gt;کز خواب کسی را گل شادی نشکفت&lt;/p&gt;&lt;p&gt;کاری چه کنی که با اجل باشد جفت؟&lt;/p&gt;&lt;p&gt;می خور که به زیر خاک می‌باید خفت&lt;/p&gt;', 'رباعی شماره ۳۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(97, '&lt;p&gt;در دایره‌ای که آمد و رفتن ماست&lt;/p&gt;&lt;p&gt;او را نه بدایت نه نهایت پیداست&lt;/p&gt;&lt;p&gt;کس می‌نزند دمی در این معنی راست&lt;/p&gt;&lt;p&gt;کاین آمدن از کجا و رفتن به کجاست&lt;/p&gt;', 'رباعی شماره ۳۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(98, '&lt;p&gt;در فصل بهار اگر بتی حور سرشت&lt;/p&gt;&lt;p&gt;یک ساغر می دهد مرا بر لب کشت&lt;/p&gt;&lt;p&gt;هر چند به نزد عامه این باشد زشت&lt;/p&gt;&lt;p&gt;سگ به ز من است اگر برم نام بهشت&lt;/p&gt;', 'رباعی شماره ۳۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(99, '&lt;p&gt;دریاب که از روح جدا خواهی رفت&lt;/p&gt;&lt;p&gt;در پردۀ اسرار فنا خواهی رفت&lt;/p&gt;&lt;p&gt;می نوش ندانی از کجا آمده‌ای&lt;/p&gt;&lt;p&gt;خوش باش ندانی به کجا خواهی رفت&lt;/p&gt;', 'رباعی شماره ۳۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(100, '&lt;p&gt;ساقی گل و سبزه بس طربناک شده‌ست&lt;/p&gt;&lt;p&gt;دریاب که هفته دگر خاک شده‌ست&lt;/p&gt;&lt;p&gt;می نوش و گلی بچین که تا درنگری&lt;/p&gt;&lt;p&gt;گل خاک شده‌ست و سبزه خاشاک شده‌ست&lt;/p&gt;', 'رباعی شماره ۳۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(101, '&lt;p&gt;عمری‌ست مرا تیره و کاری‌ست نه راست&lt;/p&gt;&lt;p&gt;محنت همه افزوده و راحت کم و کاست&lt;/p&gt;&lt;p&gt;شکر ایزد را که آنچه اسباب بلاست&lt;/p&gt;&lt;p&gt;ما را ز کس دگر نمی‌باید خواست&lt;/p&gt;', 'رباعی شماره ۳۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(102, '&lt;p&gt;فصل گل و طرف جویبار و لب کشت&lt;/p&gt;&lt;p&gt;با یک دو سه اهل و لعبتی حورسرشت&lt;/p&gt;&lt;p&gt;پیش آر قدح که باده‌نوشان صبوح&lt;/p&gt;&lt;p&gt;آسوده ز مسجدند و فارغ ز کنشت&lt;/p&gt;', 'رباعی شماره ۳۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(103, '&lt;p&gt;گر شاخ بقا ز بیخ بختت رستست&lt;/p&gt;&lt;p&gt;ور بر تن تو عمر لباسی چستست&lt;/p&gt;&lt;p&gt;در خیمه تن که سایبانی‌ست ترا&lt;/p&gt;&lt;p&gt;هان تکیه مکن که چارمیخش سستست&lt;/p&gt;', 'رباعی شماره ۴۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(104, '&lt;p&gt;گویند کسان بهشت با حور خوش است&lt;/p&gt;&lt;p&gt;من می‌گویم که آب انگور خوش است&lt;/p&gt;&lt;p&gt;این نقد بگیر و دست از آن نسیه بدار&lt;/p&gt;&lt;p&gt;کآواز دهل شنیدن از دور خوش است&lt;/p&gt;', 'رباعی شماره ۴۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(105, '&lt;p&gt;گویند مرا که دوزخی باشد مست&lt;/p&gt;&lt;p&gt;قولی‌ست خلاف ، دل در آن نتوان بست&lt;/p&gt;&lt;p&gt;گر عاشق و میخواره به دوزخ باشند&lt;/p&gt;&lt;p&gt;فردا بینی بهشت همچون کف دست&lt;/p&gt;', 'رباعی شماره ۴۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(106, '&lt;p&gt;من هیچ ندانم که مرا آن‌که سرشت&lt;/p&gt;&lt;p&gt;از اهل بهشت کرد یا دوزخ زشت&lt;/p&gt;&lt;p&gt;جامی و بتی و بربطی بر لب کشت&lt;/p&gt;&lt;p&gt;این هرسه مرا نقد و تو را نسیه بهشت&lt;/p&gt;', 'رباعی شماره ۴۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(107, '&lt;p&gt;مهتاب به نور دامن شب بشکافت&lt;/p&gt;&lt;p&gt;می نوش دمی بهتر از این نتوان یافت&lt;/p&gt;&lt;p&gt;خوش باش و میندیش که مهتاب بسی&lt;/p&gt;&lt;p&gt;اندر سر خاک یک به یک خواهد تافت&lt;/p&gt;', 'رباعی شماره ۴۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(108, '&lt;p&gt;می خوردن و شاد بودن آیین من است&lt;/p&gt;&lt;p&gt;فارغ بودن ز کفر و دین دین من است&lt;/p&gt;&lt;p&gt;گفتم به عروس دهر «کابین تو چیست؟»&lt;/p&gt;&lt;p&gt;گفتا «دل خرم تو کابین من است»&lt;/p&gt;', 'رباعی شماره ۴۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(109, '&lt;p&gt;می لعل مذاب است و صراحی کان است&lt;/p&gt;&lt;p&gt;جسم است پیاله و شرابش جان است&lt;/p&gt;&lt;p&gt;آن جام بلورین که ز می خندان است&lt;/p&gt;&lt;p&gt;اشکی است که خون دل در او پنهان است&lt;/p&gt;', 'رباعی شماره ۴۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(110, '&lt;p&gt;می نوش که عمر جاودانی این است&lt;/p&gt;&lt;p&gt;خود حاصلت از دور جوانی این است&lt;/p&gt;&lt;p&gt;هنگام گل و باده و یاران سرمست&lt;/p&gt;&lt;p&gt;خوش باش دمی که زندگانی این است&lt;/p&gt;', 'رباعی شماره ۴۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(111, '&lt;p&gt;نیکی و بدی که در نهاد بشر است&lt;/p&gt;&lt;p&gt;شادی و غمی که در قضا و قدر است&lt;/p&gt;&lt;p&gt;با چرخ مکن حواله کاندر ره عقل&lt;/p&gt;&lt;p&gt;چرخ از تو هزار بار بیچاره‌تر است&lt;/p&gt;', 'رباعی شماره ۴۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(112, '&lt;p&gt;در هر دشتی که لاله‌زاری بوده‌ست&lt;/p&gt;&lt;p&gt;از سرخی خون شهریاری بوده‌ست&lt;/p&gt;&lt;p&gt;هر شاخ بنفشه کز زمین می‌روید&lt;/p&gt;&lt;p&gt;خالی‌ست که بر رخ نگاری بوده‌ست&lt;/p&gt;', 'رباعی شماره ۴۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(113, '&lt;p&gt;هر ذره که در خاک زمینی بوده‌ست&lt;/p&gt;&lt;p&gt;پیش از من و تو تاج و نگینی بوده‌ست&lt;/p&gt;&lt;p&gt;گرد از رخ نازنین به آزرم فشان&lt;/p&gt;&lt;p&gt;کآن هم رخ خوب نازنینی بوده‌ست&lt;/p&gt;', 'رباعی شماره ۵۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(114, '&lt;p&gt;هر سبزه که بر کنار جویی رسته‌ست&lt;/p&gt;&lt;p&gt;گویی ز لب فرشته‌خویی رسته‌ست&lt;/p&gt;&lt;p&gt;پا بر سر سبزه تا به خواری ننهی&lt;/p&gt;&lt;p&gt;کآن سبزه ز خاک لاله‌رویی رسته‌ست&lt;/p&gt;', 'رباعی شماره ۵۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(115, '&lt;p&gt;یک جرعهٔ می ز ملک کاووس به است&lt;/p&gt;&lt;p&gt;از تخت قباد و ملکت طوس به است&lt;/p&gt;&lt;p&gt;هر ناله که رندی به سحرگاه زند&lt;/p&gt;&lt;p&gt;از طاعت زاهدان سالوس به است&lt;/p&gt;', 'رباعی شماره ۵۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(116, '&lt;p&gt;چون عمر به سر رسد چه شیرین و چه تلخ&lt;/p&gt;&lt;p&gt;پیمانه چو پر شود چه بغداد و چه بلخ&lt;/p&gt;&lt;p&gt;می نوش که بعد از من و تو ماه بسی&lt;/p&gt;&lt;p&gt;از سَلخ به غٌرّه آید از غره به سلخ&lt;/p&gt;', 'رباعی شماره ۵۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(117, '&lt;p&gt;آنان که محیط فضل و آداب شدند&lt;/p&gt;&lt;p&gt;در جمع کمال شمع اصحاب شدند&lt;/p&gt;&lt;p&gt;ره زین شب تاریک نبردند برون&lt;/p&gt;&lt;p&gt;گفتند فسانه‌ای و در خواب شدند&lt;/p&gt;', 'رباعی شماره ۵۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(118, '&lt;p&gt;آن را که به صحرای علل تاخته‌اند&lt;/p&gt;&lt;p&gt;بی او همه کارها بپرداخته‌اند&lt;/p&gt;&lt;p&gt;امروز بهانه‌ای درانداخته‌اند&lt;/p&gt;&lt;p&gt;فردا همه آن بود که درساخته‌اند&lt;/p&gt;', 'رباعی شماره ۵۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(119, '&lt;p&gt;آن‌ها که کهن شدند و این‌ها که نوند&lt;/p&gt;&lt;p&gt;هر کس به مراد خویش یک تک به دوند&lt;/p&gt;&lt;p&gt;این کهنه‌جهان به کس نماند باقی&lt;/p&gt;&lt;p&gt;رفتند و رویم دیگر آیند و روند&lt;/p&gt;', 'رباعی شماره ۵۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(120, '&lt;p&gt;آن‌کس که زمین و چرخ و افلاک نهاد&lt;/p&gt;&lt;p&gt;بس داغ که او بر دل غمناک نهاد&lt;/p&gt;&lt;p&gt;بسیار لب چو لعل و زلفین چو مشک&lt;/p&gt;&lt;p&gt;در طبل زمین و حقهٔ خاک نهاد&lt;/p&gt;', 'رباعی شماره  ۵۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(121, '&lt;p&gt;آرند یکی و دیگری بربایند&lt;/p&gt;&lt;p&gt;بر هیچ‌کسی راز همی‌نگشایند&lt;/p&gt;&lt;p&gt;ما را ز قضا جز این قدر ننمایند&lt;/p&gt;&lt;p&gt;پیمانهٔ عمر ماست می‌پیمایند&lt;/p&gt;', 'رباعی شماره  ۵۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(122, '&lt;p&gt;اجرام که ساکنان این ایوانند&lt;/p&gt;&lt;p&gt;اسباب تردد خردمندانند&lt;/p&gt;&lt;p&gt;هان تا سر رشتهٔ خرد گم نکنی&lt;/p&gt;&lt;p&gt;کآنان که مدبرند سرگردانند&lt;/p&gt;', 'رباعی شماره   ۵۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(123, '&lt;p&gt;از آمدنم نبود گردون را سود&lt;/p&gt;&lt;p&gt;وز رفتن من جلال و جاهش نفزود&lt;/p&gt;&lt;p&gt;وز هیچ کسی نیز دو گوشم نشنود&lt;/p&gt;&lt;p&gt;کاین آمدن و رفتنم از بهر چه بود&lt;/p&gt;', 'رباعی شماره  ۶۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(124, '&lt;p&gt;از رنج کشیدن آدمی حر گردد&lt;/p&gt;&lt;p&gt;قطره چو کشد حبس صدف در گردد&lt;/p&gt;&lt;p&gt;گر مال نماند سر بماناد به جای&lt;/p&gt;&lt;p&gt;پیمانه چو شد تهی دگر پر گردد&lt;/p&gt;', 'رباعی شماره  ۶۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(125, '&lt;p&gt;افسوس که سرمایه ز کف بیرون شد&lt;/p&gt;&lt;p&gt;وز دست اجل بسی جگرها خون شد&lt;/p&gt;&lt;p&gt;کس نآمد از آن جهان که پرسم از وی&lt;/p&gt;&lt;p&gt;کاحوال مسافران دنیا چون شد&lt;/p&gt;', 'رباعی شماره ۶۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(126, '&lt;p&gt;افسوس که نامهٔ جوانی طی شد&lt;/p&gt;&lt;p&gt;و آن تازه بهار زندگانی دی شد&lt;/p&gt;&lt;p&gt;آن مرغ طرب که نام او بود شباب&lt;/p&gt;&lt;p&gt;افسوس ندانم که کی آمد کی شد&lt;/p&gt;', 'رباعی شماره ۶۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(127, '&lt;p&gt;ای بس که نباشیم و جهان خواهد بود&lt;/p&gt;&lt;p&gt;نی نام ز ما و نی‌ نشان خواهد بود&lt;/p&gt;&lt;p&gt;زین پیش نبودیم و نبد هیچ خلل&lt;/p&gt;&lt;p&gt;زین پس چو نباشیم همان خواهد بود&lt;/p&gt;', 'رباعی شماره ۶۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(128, '&lt;p&gt;این عقل که در ره سعادت پوید&lt;/p&gt;&lt;p&gt;روزی صد بار خود تو را می‌گوید&lt;/p&gt;&lt;p&gt;دریاب تو این یک دم وقتت که نه‌ای&lt;/p&gt;&lt;p&gt;آن تره که بدروند و دیگر روید&lt;/p&gt;', 'رباعی شماره ۶۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(129, '&lt;p&gt;این قافلهٔ عمر عجب می‌گذرد&lt;/p&gt;&lt;p&gt;دریاب دمی که با طرب می‌گذرد&lt;/p&gt;&lt;p&gt;ساقی غم فردای حریفان چه خوری&lt;/p&gt;&lt;p&gt;پیش آر پیاله را که شب می‌گذرد&lt;/p&gt;', 'رباعی شماره ۶۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(130, '&lt;p&gt;بر پشت من از زمانه تو می‌آید&lt;/p&gt;&lt;p&gt;وز من همه کار نانکو&amp;nbsp;می‌آید&lt;/p&gt;&lt;p&gt;جان عزم رحیل کرد و گفتم بمرو&lt;/p&gt;&lt;p&gt;گفتا چه کنم خانه فرومی‌آید&lt;/p&gt;', 'رباعی شماره ۶۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(131, '&lt;p&gt;بر چرخ فلک هیچ کسی چیر نشد&lt;/p&gt;&lt;p&gt;وز خوردن آدمی زمین سیر نشد&lt;/p&gt;&lt;p&gt;مغرور بدانی که نخورده‌ست تو را&lt;/p&gt;&lt;p&gt;تعجیل مکن هم بخورَد دیر نشد&lt;/p&gt;', 'رباعی شماره ۶۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(132, '&lt;p&gt;بر چشم تو عالم ارچه می‌آرایند&lt;/p&gt;&lt;p&gt;مگرای بدان که عاقلان نگرایند&lt;/p&gt;&lt;p&gt;بسیار چو تو روند و بسیار آیند&lt;/p&gt;&lt;p&gt;بربای نصیب خویش کت بربایند&lt;/p&gt;', 'رباعی شماره  ۶۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(133, '&lt;p&gt;بر من قلم قضا چو بی من رانند&lt;/p&gt;&lt;p&gt;پس نیک و بدش ز من چرا می‌دانند&lt;/p&gt;&lt;p&gt;دی بی من و امروز چو دی بی من و تو&lt;/p&gt;&lt;p&gt;فردا به چه حجتم به داور خوانند&lt;/p&gt;', 'رباعی شماره  ۷۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(134, '&lt;p&gt;تا چند اسیر رنگ و بو خواهی شد&lt;/p&gt;&lt;p&gt;چند از پی هر زشت و نکو خواهی شد&lt;/p&gt;&lt;p&gt;گر چشمهٔ زمزمی و گر آب حیات&lt;/p&gt;&lt;p&gt;آخر به دل خاک فروخواهی شد&lt;/p&gt;', 'رباعی شماره  ۷۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(135, '&lt;p&gt;تا راهِ قلندری نپویی نشود&lt;/p&gt;&lt;p&gt;رخساره به خونِ دل نشویی نشود&lt;/p&gt;&lt;p&gt;سودا چه پزی؟ تا که چو دل‌سوختگان&lt;/p&gt;&lt;p&gt;آزاد به تَرکِ خود نگویی نشود&lt;/p&gt;', 'رباعی شماره ۷۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(136, '&lt;p&gt;تا زهره و مه در آسمان گشت پدید&lt;/p&gt;&lt;p&gt;بهتر ز می ناب کسی هیچ ندید&lt;/p&gt;&lt;p&gt;من در عجبم ز می‌فروشان کایشان&lt;/p&gt;&lt;p&gt;به زآن‌که فروشند چه خواهند خرید&lt;/p&gt;', 'رباعی شماره ۷۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(137, '&lt;p&gt;چون روزی و عمر بیش و کم نتوان کرد&lt;/p&gt;&lt;p&gt;دل را به کم و بیش دژم نتوان کرد&lt;/p&gt;&lt;p&gt;کار من و تو چنان‌که رای من و توست&lt;/p&gt;&lt;p&gt;از موم به دست خویش هم نتوان کرد&lt;/p&gt;', 'رباعی شماره  ۷۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(138, '&lt;p&gt;حیی که به قدرت سر و رو می‌سازد&lt;/p&gt;&lt;p&gt;همواره همو کار عدو می‌سازد&lt;/p&gt;&lt;p&gt;گویند قرابه‌گر مسلمان نبود&lt;/p&gt;&lt;p&gt;او را تو چه گویی که کدو می‌سازد&lt;/p&gt;', 'رباعی شماره  ۷۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(139, '&lt;p&gt;در دهر چو آواز گل تازه دهند&lt;/p&gt;&lt;p&gt;فرمای بتا که می به‌اندازه دهند&lt;/p&gt;&lt;p&gt;از حور و قصور و ز بهشت و دوزخ&lt;/p&gt;&lt;p&gt;فارغ بنشین که آن هر آوازه دهند&lt;/p&gt;', 'رباعی شماره ۷۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(140, '&lt;p&gt;در دهر هر آن‌که نیم‌نانی دارد&lt;/p&gt;&lt;p&gt;از بهر نشست آشیانی دارد&lt;/p&gt;&lt;p&gt;نه خادم کس بود نه مخدوم کسی&lt;/p&gt;&lt;p&gt;گو شاد بزی که خوش‌جهانی دارد&lt;/p&gt;', 'رباعی شماره ۷۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(141, '&lt;p&gt;دهقان قضا بسی چو ما کشت و درود&lt;/p&gt;&lt;p&gt;غم خوردن بیهوده نمی‌دارد سود&lt;/p&gt;&lt;p&gt;پر کن قدح می به کفم درنه زود&lt;/p&gt;&lt;p&gt;تا باز خورم که بودنی‌ها همه بود&lt;/p&gt;', 'رباعی شماره ۷۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(142, '&lt;p&gt;روزی‌ست خوش و هوا نه گرم است و نه سرد&lt;/p&gt;&lt;p&gt;ابر از رخ گلزار همی‌شوید گرد&lt;/p&gt;&lt;p&gt;بلبل به زبان حال خود با گل زرد&lt;/p&gt;&lt;p&gt;فریاد همی‌کند که می باید خورد&lt;/p&gt;', 'رباعی شماره ۷۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(143, '&lt;p&gt;زآن پیش که بر سرت شبیخون آرند&lt;/p&gt;&lt;p&gt;فرمای که تا بادهٔ گلگون آرند&lt;/p&gt;&lt;p&gt;تو زر نه‌ای ای غافل نادان که تو را&lt;/p&gt;&lt;p&gt;در خاک نهند و باز بیرون آرند&lt;/p&gt;', 'رباعی شماره ۸۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(144, '&lt;p&gt;عمرت تا کی به خودپرستی گذرد&lt;/p&gt;&lt;p&gt;یا در پی نیستی و هستی گذرد&lt;/p&gt;&lt;p&gt;می نوش که عمری که اجل در پی اوست&lt;/p&gt;&lt;p&gt;آن به که به خواب یا به مستی گذرد&lt;/p&gt;', 'رباعی شماره ۸۱', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(145, '&lt;p&gt;کس مشکل اسرار اجل را نگشاد&lt;/p&gt;&lt;p&gt;کس یک قدم از دایره بیرون ننهاد&lt;/p&gt;&lt;p&gt;من می‌نگرم ز مبتدی تا استاد&lt;/p&gt;&lt;p&gt;عجز است به دست هرکه از مادر زاد&lt;/p&gt;', 'رباعی شماره ۸۲', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(146, '&lt;p&gt;کم کن طمع از جهان و می‌زی خرسند&lt;/p&gt;&lt;p&gt;از نیک و بد زمانه بگسل پیوند&lt;/p&gt;&lt;p&gt;می در کف و زلف دلبری گیر که زود&lt;/p&gt;&lt;p&gt;هم بگذرد و نمانَد این روزی چند&lt;/p&gt;', 'رباعی شماره ۸۳', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(147, '&lt;p&gt;گرچه غم و رنج من درازی دارد&lt;/p&gt;&lt;p&gt;عیش و طرب تو سرفرازی دارد&lt;/p&gt;&lt;p&gt;بر هر دو مکن تکیه که دوران فلک&lt;/p&gt;&lt;p&gt;در پرده هزار گونه بازی دارد&lt;/p&gt;', 'رباعی شماره ۸۴', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(148, '&lt;p&gt;گردون ز زمین هیچ گلی برنارد&lt;/p&gt;&lt;p&gt;کش نشکند و هم به زمین نسپارد&lt;/p&gt;&lt;p&gt;گر ابر چو آب، خاک را بردارد&lt;/p&gt;&lt;p&gt;تا حشر همه خون عزیزان بارد&lt;/p&gt;', 'رباعی شماره ۸۵', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(149, '&lt;p&gt;گر یک نفست ز زندگانی گذرد&lt;/p&gt;&lt;p&gt;مگذار که جز به شادمانی گذرد&lt;/p&gt;&lt;p&gt;هشدار که سرمایهٔ سودای جهان&lt;/p&gt;&lt;p&gt;عمر است چنان کش گذرانی گذرد&lt;/p&gt;', 'رباعی شماره ۸۶', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(150, '&lt;p&gt;گویند بهشت و حورعین خواهد بود&lt;/p&gt;&lt;p&gt;آنجا می و شیر و انگبین خواهد بود&lt;/p&gt;&lt;p&gt;گر ما می و معشوق گزیدیم چه باک&lt;/p&gt;&lt;p&gt;چون عاقبت کار چنین خواهد بود&lt;/p&gt;', 'رباعی شماره ۸۷', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(151, '&lt;p&gt;گویند بهشت و حور و کوثر باشد&lt;/p&gt;&lt;p&gt;جوی می و شیر و شهد و شکر باشد&lt;/p&gt;&lt;p&gt;پر کن قدح باده و بر دستم نه&lt;/p&gt;&lt;p&gt;نقدی ز هزار نسیه خوش‌تر باشد&lt;/p&gt;', 'رباعی شماره ۸۸', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(152, '&lt;p&gt;گویند هر آن کسان که با پرهیزند&lt;/p&gt;&lt;p&gt;زآن‌سان که بمیرند چنان برخیزند&lt;/p&gt;&lt;p&gt;ما با می و معشوقه از آنیم مدام&lt;/p&gt;&lt;p&gt;باشد که به حشرمان چنان انگیزند&lt;/p&gt;', 'رباعی شماره ۸۹', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(153, '&lt;p&gt;می خور که ز دل کثرت و قلت ببرد&lt;/p&gt;&lt;p&gt;و اندیشه هفتاد و دو ملت ببرد&lt;/p&gt;&lt;p&gt;پرهیز مکن ز کیمیایی که از او&lt;/p&gt;&lt;p&gt;یک جرعه خوری هزار علت ببرد&lt;/p&gt;', 'رباعی شماره ۹۰', 'خیام', 'رباعیات خیام', 'رباعی', 2, 2),
(443, '&lt;p&gt;دود می‌خیزد ز خلوتگاه من.&lt;/p&gt;&lt;p&gt;کس خبر کی یابد از ویرانه‌ام؟&lt;/p&gt;&lt;p&gt;با درون سوخته دارم سخن.&lt;/p&gt;&lt;p&gt;کی به پایان می‌رسد افسانه‌ام؟&lt;/p&gt;&lt;p&gt;&lt;br&gt;دست از دامان شب برداشتم&lt;/p&gt;&lt;p&gt;تا بیاویزم به گیسوی سحر.&lt;/p&gt;&lt;p&gt;خویش را از ساحل افکندم در آب،&lt;/p&gt;&lt;p&gt;لیک از ژرفای دریا بی‌خبر.&lt;/p&gt;&lt;p&gt;&lt;br&gt;بر تن دیوارها طرح شکست.&lt;/p&gt;&lt;p&gt;کس دگر رنگی در این سامان ندید.&lt;/p&gt;&lt;p&gt;چشم میدوزد خیال روز و شب&lt;/p&gt;&lt;p&gt;از درون دل به تصویر امید.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تا بدین منزل نهادم پای را&lt;/p&gt;&lt;p&gt;از درای کاروان بگسسته‌ام.&lt;/p&gt;&lt;p&gt;گرچه می‌سوزم از این آتش به جان،&lt;/p&gt;&lt;p&gt;لیک بر این سوختن دل بسته ام.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تیرگی پا می‌کشد از بام‌ها:&lt;/p&gt;&lt;p&gt;صبح می‌خندد به راه شهر من.&lt;/p&gt;&lt;p&gt;دود می‌خیزد هنوز از خلوتم.&lt;/p&gt;&lt;p&gt;با درون سوخته دارم سخن.&lt;/p&gt;', 'دود می‌خیزد', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(444, '&lt;p&gt;دیرگاهی است که در این تنهایی&lt;/p&gt;&lt;p&gt;رنگ خاموشی در طرح لب است.&lt;/p&gt;&lt;p&gt;بانگی از دور مرا می‌خواند&lt;/p&gt;&lt;p&gt;لیک پاهایم در قیر شب است&lt;/p&gt;&lt;p&gt;رخنه‌ای نیست در این تاریکی:&lt;/p&gt;&lt;p&gt;در و دیوار به هم پیوسته.&lt;/p&gt;&lt;p&gt;سایه‌ای لغزد اگر روی زمین&lt;/p&gt;&lt;p&gt;نقش وهمی است ز بندی رسته.&lt;/p&gt;&lt;p&gt;نفس آدم‌ها&lt;/p&gt;&lt;p&gt;سر بسر افسرده است&lt;/p&gt;&lt;p&gt;روزگاری است در این گوشه پژمرده هوا&lt;/p&gt;&lt;p&gt;هر نشاطی مرده است.&lt;/p&gt;&lt;p&gt;دست جادویی شب&lt;/p&gt;&lt;p&gt;در به روی من و غم می‌بندد&lt;/p&gt;&lt;p&gt;می‌کنم هر چه تلاش،&lt;/p&gt;&lt;p&gt;او به من می‌خندد.&lt;/p&gt;&lt;p&gt;نقش‌هایی که کشیدم در روز،&lt;/p&gt;&lt;p&gt;شب ز راه آمد و با دود اندود.&lt;/p&gt;&lt;p&gt;طرح‌هایی که فکندم در شب،&lt;/p&gt;&lt;p&gt;روز پیدا شد و با پنبه زدود.&lt;/p&gt;&lt;p&gt;دیرگاهی است که چون من همه را&lt;/p&gt;&lt;p&gt;رنگ خاموشی در طرح لب است.&lt;/p&gt;&lt;p&gt;جنبشی نیست در این خاموشی&lt;/p&gt;&lt;p&gt;دست‌ها پاها در قیر شب است&lt;/p&gt;', 'در قیر شب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(156, '&lt;p&gt;درین سرای بی کسی، کسی به در نمی زند&lt;br&gt;به دشت پُر ملال ما پرنده پر نمی زند&lt;br&gt;&lt;br&gt;یکی ز شب گرفتگان چراغ بر نمی کند&lt;br&gt;کسی به کوچه سار شب در سحر نمی زند&lt;br&gt;&lt;br&gt;نشسته ام در انتظار این غبار بی سوار&lt;br&gt;دریغ کز شبی چنین سپیده سر نمی زند&lt;br&gt;&lt;br&gt;گذر گهی است پر ستم که اندر او به غیر غم&lt;br&gt;یکی صلای آشنا به رهگذر نمی زند&lt;br&gt;&lt;br&gt;دل خراب من دگر خراب تر نمی شود&lt;br&gt;که خنجر غمت ازین خراب تر نمی زند&lt;br&gt;&lt;br&gt;چه چشم پاسخ است ازین دریچه های بسته ات ؟&lt;br&gt;برو که هیچ کس ندا به گوش کر نمی زند&lt;br&gt;&lt;br&gt;نه سایه دارم و نه بر، بیفکنندم و سزاست&lt;br&gt;اگر نه بر درخت تر کسی تبر نمی زند&lt;br&gt;&amp;nbsp;&lt;/p&gt;', ' غبار بی سوار', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(157, '&lt;p&gt;نشود فاش کسی آنچه میان من و توست&lt;br&gt;تا اشارات نظر نامه رسان من توست&lt;br&gt;&lt;br&gt;گوش کن با لب خاموش سخن می گویم&lt;br&gt;پاسخم گو به نگاهی که زبان من و توست&lt;br&gt;&lt;br&gt;روزگاری شد و کس مرد ره عشق ندید&lt;br&gt;حالیا چشم جهانی نگران من و توست&lt;br&gt;&lt;br&gt;گرچه در خلوت راز دل ما کس نرسید&lt;br&gt;همه جا زمزمه عشق نهان من و توست&lt;br&gt;&lt;br&gt;گو بهار دل و جان باش و خزان باش، ارنه&lt;br&gt;ای بسا باغ و بهاران که خزان من و توست&lt;br&gt;&lt;br&gt;این همه قصه فردوس و تمنای بهشت&lt;br&gt;گفت و گویی و خیالی ز جهان من و توست&lt;br&gt;&lt;br&gt;نقش ما گو ننگارند به دیباچه عقل&lt;br&gt;هرکجا نامه عشق است نشان من و توست&lt;br&gt;&lt;br&gt;سایه زآتشکده ماست فروغ مه و مهر&lt;br&gt;وه از این آتش روشن که به جان من و توست&lt;/p&gt;', ' اشارات نظر', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(158, '&lt;p&gt;هنوز عشق تو امید بخش جان من است&lt;br&gt;خوشا غمی که ازو شادی جهان من است&lt;br&gt;&lt;br&gt;چه شکر گویمت ای هستی یگانه ی عشق&lt;br&gt;که سوز سینه ی خورشید در زبان من است&lt;br&gt;&lt;br&gt;اگر چه فرصت عمرم ز دست رفت بیا&lt;br&gt;که همچنان به رهت چشم خون فشان من است&lt;br&gt;&lt;br&gt;نمی رود ز سرم این خیال خون آلود&lt;br&gt;که داس حادثه در قصد ارغوان من است&lt;br&gt;&lt;br&gt;بیا بیا که درین ظلمت دروغ و ریا&lt;br&gt;فروغ روی تو آرایش روان من است&lt;br&gt;&amp;nbsp;&lt;br&gt;حکایت غم دیرین به عشق گفتم، گفت:&lt;br&gt;هنوز این همه آغاز داستان من است&lt;br&gt;&lt;br&gt;بدین نشان که تویی ای دل نشسته به خون&lt;br&gt;بمان که تیر امان تو در کمان من است&lt;br&gt;&lt;br&gt;اگر ز ورطه بترسی چه طرف خواهی بست&lt;br&gt;ز طرفه ها که درین بحر بی کران من است&lt;br&gt;&amp;nbsp;&lt;br&gt;زمان به دست پریشانی اش نخواهد داد&lt;br&gt;دلی که در گرو حسن جاودان من است&lt;br&gt;&lt;br&gt;به شادی غزل سایه نوش و بخشش عشق&lt;br&gt;که مرغ خوش سخن غم هم آشیان من است&lt;/p&gt;', ' امید بخش جان', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(159, '&lt;p&gt;دلی که پیش تو ره یافت باز پس نرود&lt;br&gt;هواگرفته ی عشق از پی هوس نرود&lt;br&gt;&lt;br&gt;به بوی زلف تو دم می زنم درین شب تار&lt;br&gt;وگرنه چون سحرم بی تو یک نفس نرود&lt;br&gt;&lt;br&gt;چنان به دام غمت خو گرفت مرغ دلم&amp;nbsp;&lt;br&gt;که یاد باغ بهشتش درین قفس نرود&lt;br&gt;&lt;br&gt;نثار آه سحر می کنم سرشک نیاز&lt;br&gt;که دامن توام ای گل ز دسترس نرود&lt;br&gt;&lt;br&gt;دلا بسوز و به جان برفروز آتش عشق&lt;br&gt;کزین چراغ تو دودی به چشم کس نرود&lt;br&gt;&lt;br&gt;فغانِ بلبل طبعم به گلشن تو خوش است&lt;br&gt;که کار دلبری گل ز خار و خس نرود&lt;br&gt;&lt;br&gt;دلی که نغمه ی ناقوس معبد تو شنید&lt;br&gt;چو کودکان ز پی بانگ هر جرس نرود&lt;br&gt;&lt;br&gt;بر آستان تو چون سایه سر نهم همه عمر&lt;br&gt;که هرکه پیش تو ره یافت باز پس نرود&lt;/p&gt;', 'دلی که پیش تو ره یافت ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(160, '&lt;p&gt;بر آستان تو، دل پایمال صد درد است&lt;br&gt;ببین که دست غمت بر سرم چه آورده است&lt;br&gt;&lt;br&gt;هوای باغ سرخ داشتیم و دریغ&lt;br&gt;که بلبلان همه زارند و برگ ها زرد است&lt;br&gt;&lt;br&gt;شب است و آینه خواب سپیده می بیند&lt;br&gt;بیا که روز خوش ما خیال پرورده است&lt;br&gt;&lt;br&gt;دهان غنچه فرو بسته ماند در شب باغ&lt;br&gt;که صبح خنده گشا روی ازو نهان کرده است&lt;br&gt;&lt;br&gt;چه ها که بر سر ما رفت و کس نزد آهی&lt;br&gt;به مردمی که جهان سخت ناجوانمرد است&lt;br&gt;&lt;br&gt;به سوز دل نفسی آتشین بر آر، ای عشق&lt;br&gt;که سینه ها سیه از روزگار دم سرد است&lt;br&gt;&lt;br&gt;غم تو با دل من پنجه درفکند و رواست&lt;br&gt;که این دلیر به بازوی آن هماورد است&lt;br&gt;&lt;br&gt;دلا منال و ببین هستی یگانه ی عشق&lt;br&gt;که آسمان و زمین با من و تو همدرد است&lt;br&gt;&lt;br&gt;ز خواب زلف سیاهت چه دم زنم که هنوز&lt;br&gt;خیال سایه پریشان ز فکر شبگرد است&lt;/p&gt;', ' خواب سپیده ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(161, '&lt;p&gt;با من بی کسِ تنها شده، یارا تو بمان&lt;br&gt;همه رفتند از این خانه، خدا را تو بمان&lt;br&gt;&lt;br&gt;منِ بی برگِ خزان دیده، دگر رفتنی ام&lt;br&gt;تو همه بار و بری، تازه بهارا تو بمان&lt;br&gt;&lt;br&gt;داغ و درد است همه نقش و نگار دل من&lt;br&gt;بنگر این نقشِ به خون شسته، نگارا تو بمان&lt;br&gt;&lt;br&gt;زین بیابان گذری نیست سواران را، لیک&lt;br&gt;دل ما خوش به فریبی است، غبارا تو بمان&lt;br&gt;&lt;br&gt;هر دم از حلقه ی عشاق، پریشانی رفت&lt;br&gt;به سرِ زلف بتان، سلسله دارا تو بمان&lt;br&gt;&lt;br&gt;شهریارا تو بمان بر سر این خیل یتیم&lt;br&gt;پدرا! یارا! اندوهگسارا تو بمان&lt;br&gt;&lt;br&gt;سایه در پای تو چون موج چه خوش زار گریست&lt;br&gt;که سرِ سبزِ تو خوش باد، کنارا تو بمان&lt;/p&gt;', 'خدا را تو بمان', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(162, '&lt;p&gt;فریاد که از عمر جهان هر نفسی رفت&lt;br&gt;دیدیم کزین جمعِ پراکنده کسی رفت&lt;br&gt;&lt;br&gt;شادی مکن از زادن و شیون مکن از مرگ&lt;br&gt;زین گونه بسی آمد و زین گونه بسی رفت&lt;br&gt;&lt;br&gt;آن طفل که چون پیر از این قافله درماند&lt;br&gt;وآن پیر که چون طفل به بانگ جرسی رفت&lt;br&gt;&lt;br&gt;از پیش و پس قافله ی عمر میندیش&lt;br&gt;گه پیشروی پی شد و گه بازپسی رفت&lt;br&gt;&lt;br&gt;ما همچو خسی بر سر دریای وجودیم&lt;br&gt;دریاست چه سنجد که بر این موج خسی رفت&lt;br&gt;&lt;br&gt;رفتی و فراموش شدی از دل دنیا&lt;br&gt;چون ناله ی مرغی که ز یاد قفسی رفت&lt;br&gt;&lt;br&gt;رفتی و غم آمد به سر جای تو ای داد&lt;br&gt;بیدادگری آمد و فریادرسی رفت&lt;br&gt;&lt;br&gt;این عمر سبکسایه ی ما بسته به آهی است&lt;br&gt;دودی ز سر شمع پرید و نفسی رفت&lt;/p&gt;', ' عمر جهان ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(163, '&lt;p&gt;می شنوم، می شنوم آشناست&lt;br&gt;موسقی چشم تو در گوش من&lt;br&gt;موج نگاه تو همآواز ناز&lt;br&gt;ریخت چو مهتاب در آغوش من&lt;br&gt;&lt;br&gt;می شنوم در نگه گرم توست&lt;br&gt;گمشده گلبانگ بهشت امید&lt;br&gt;این همه گشتم من و دلخواه من&lt;br&gt;در نگه گرم تو می آرمید&lt;br&gt;&lt;br&gt;زمزمه ی شعر نگاه تو را&lt;br&gt;می شنوم با دل و جان آشناست&lt;br&gt;اشک زلال غزل حافظ است&lt;br&gt;نغمه ی مرغان بهشتی نواست&lt;br&gt;&lt;br&gt;می شنوم در نگه گرم توست&lt;br&gt;نغمه ی آن شاهد رویانشین&lt;br&gt;باز ز گلبانگ تو سر می کشد&lt;br&gt;شعله ی این آرزوی آتشین&lt;br&gt;&lt;br&gt;موسقی چشم تو گویاتر است&lt;br&gt;از لب پر ناله و آواز من&lt;br&gt;وه که تو هم گر بتوانی شنید&lt;br&gt;زین نگه نغمه سرا راز من!&lt;/p&gt;', 'موسیقی چشم تو ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(164, '&lt;p&gt;حکایت از چه کنم! سینه سینه، درد اینجاست&lt;br&gt;هزار شعله ی سوزان و آه سرد اینجاست&lt;br&gt;&lt;br&gt;نگاه کن که ز هر بیشه در قفس شیری ست&lt;br&gt;بلوچ و کرد و لر و ترک و گیله مرد اینجاست&lt;br&gt;&lt;br&gt;بیا که مسئله ی بودن و نبودن نیست&lt;br&gt;حدیث عهد و وفا می رود نبرد اینجاست&lt;br&gt;&lt;br&gt;بهار آن سوی دیوار ماند و یاد خوشش&lt;br&gt;هنوز با غم این برگ های زرد اینجاست&lt;br&gt;&lt;br&gt;به روزگار شبی بی سحر نخواهد ماند&lt;br&gt;چو چشم باز کنی صبح شب نورد اینجاست&lt;br&gt;&lt;br&gt;جدایی از زن و فرزند سایه جان! سهل است&lt;br&gt;تو را ز خویش جدا می کنند، درد اینجاست!&lt;/p&gt;', ' مسئله ی بودن و نبودن ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(165, '&lt;p&gt;نازنین آمد و دستی به دل ما زد و رفت&lt;br&gt;پرده ی خلوت این غمکده بالا زد و رفت&lt;br&gt;&lt;br&gt;کنج تنهایی ما را به خیالی خوش کرد&lt;br&gt;خواب خورشید به چشم شب یلدا زد و رفت&lt;br&gt;&lt;br&gt;درد بی عشقی ما دید و دریغش آمد&lt;br&gt;آتش شوق درین جان شکیبا زد و رفت&lt;br&gt;&lt;br&gt;خرمن سوخته ی ما به چه کارش می خورد&lt;br&gt;که چو برق آمد و در خشک و تر ما زد و رفت&lt;br&gt;&lt;br&gt;رفت و از گریه ی طوفانی ام اندیشه نکرد&lt;br&gt;چه دلی داشت خدایا که به دریا زد و رفت&lt;br&gt;&lt;br&gt;بود آیا که ز دیوانه ی خود یاد کند&lt;br&gt;آن که زنجیر به پای دل شیدا زد و رفت&lt;br&gt;&lt;br&gt;سایه آن چشم سیه با تو چه می گفت که دوش&lt;br&gt;عقل فریاد برآورد و به صحرا زد و رفت!&lt;/p&gt;', 'نازنین آمد  ', 'هوشنگ ابتهاج', 'آینه در آینه', 'غزل', 3, 3),
(166, '&lt;p&gt;به کویت با دل شاد آمدم با چشم تر رفتم&lt;/p&gt;&lt;p&gt;به دل امید درمان داشتم درمانده تر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو کوته دستی ام می خواستی ورنه من مسکین&lt;/p&gt;&lt;p&gt;به راه عشق اگر از پا در افتادم به سر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نیامد دامن وصلت به دستم هر چه کوشیدم&lt;/p&gt;&lt;p&gt;ز کویت عاقبت با دامنی خون جگر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;حریفان هر یک آوردند از سودای خود سودی&lt;/p&gt;&lt;p&gt;زیان آورده من بودم که دنبال هنر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ندانستم که تو کی آمدی ای دوست کی رفتی&lt;/p&gt;&lt;p&gt;به من تا مژده آوردند من از خود به در رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرا آزردی و گفتم که خواهم رفت از کویت&lt;/p&gt;&lt;p&gt;بلی رفتم ولی هر جا که رفتم دربدر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به پایت ریختم اشکی و رفتم در گذر از من&lt;/p&gt;&lt;p&gt;ازین ره بر نمی گردم که چون شمع سحر رفتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو رشک آفتابی کی به دست سایه می آیی&lt;/p&gt;&lt;p&gt;دریغا آخر از کوی تو با غم همسفر رفتم&lt;/p&gt;', 'اشک واپسین', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(167, '&lt;p&gt;چون خواب  بود که باز از سرم گذشت&lt;/p&gt;&lt;p&gt;نامهربان من که به ناز از برم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون ابر نوبهار بگریم درین چمن&lt;/p&gt;&lt;p&gt;از حسرت گلی که ز چشم ترم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;منظور من که منظره افروز عالمی ست&lt;/p&gt;&lt;p&gt;چون برق خنده ای زد و از منظرم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آخر به عزم پرسش پروانه شمع بزم&lt;/p&gt;&lt;p&gt;آمد ولی چو باد به خاکسترم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دریای لطف بودی و من مانده با سراب&lt;/p&gt;&lt;p&gt;دل آنگهت شناخت که آب از سرم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;منت کش خیال توام کز سر کرم&lt;/p&gt;&lt;p&gt;همخوابه ی شبم شد و بر بسترم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جان پرورست لطف تو ای اشک ژاله، لیک&lt;/p&gt;&lt;p&gt;دیر آمدی و کار گل پرپرم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خوناب درد گشت و ز چشمم فرو چکید&lt;/p&gt;&lt;p&gt;هر آرزو که از دل خوش باورم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;صد چشمه اشک غم شد و صد باغ لاله داغ&lt;/p&gt;&lt;p&gt;هر دم که خاطرات تو از خاطرم گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خوش سایه روشنی است تماشای یار را&lt;/p&gt;&lt;p&gt;این دود آه و شعله که بر دفترم گذشت&lt;/p&gt;', 'خاکستر', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(168, '&lt;p&gt;یاری کن ای نفس که درین گوشه ی قفس&lt;/p&gt;&lt;p&gt;بانگی بر آورم ز دل خسته ی یک نفس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنگ غروب و هول بیابان و راه دور&lt;/p&gt;&lt;p&gt;نه پرتو ستاره و نه ناله ی جرس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خونابه گشت دیده ی کارون و زنده رود&lt;/p&gt;&lt;p&gt;ای پیک آشنا برس از ساحل ارس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;صبر پیمبرانه ام آخر تمام شد&lt;/p&gt;&lt;p&gt;ای آیت امید به فریاد من برس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از بیم محتسب مشکن ساغر ای حریف&lt;/p&gt;&lt;p&gt;می خواره را دریغ بود خدمت عسس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جز مرگ دیگرم چه کس آید به پیشباز&lt;/p&gt;&lt;p&gt;رفتیم و همچنان نگران تو باز پس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ما را هوای چشمه ی خورشید در سر است&lt;/p&gt;&lt;p&gt;سهل است سایه گر برود سر در این هوس&lt;/p&gt;', 'تنگ غروب', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(169, '&lt;p&gt;بیایید، بیایید که جان دل ما رفت&lt;/p&gt;&lt;p&gt;بگریید، بگریید که آن خنده گشا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;برین خاک بیفتید که آن آلاله فرو ریخت&lt;/p&gt;&lt;p&gt;برین باغ بگریید که آن سرو فرا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;درین غم بنشینید که غم خوار سفر کرد&lt;/p&gt;&lt;p&gt;درین درد بمانید که امید دوا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دگر شمع میارید که این جمع پراکند&lt;/p&gt;&lt;p&gt;دگر عود مسوزید کزین بزم صفا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;لب جام مبوسید که آن ساقی ما خفت&lt;/p&gt;&lt;p&gt;رگ چنگ ببرید که آن نغمه سرا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;رخ حسن مجویید که آن آینه بشکست&lt;/p&gt;&lt;p&gt;گل عشق مبویید که آن بوی وفا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نوای نی او بود که سوط غزلم داد&lt;/p&gt;&lt;p&gt;غزل باز مخوانید که نی سوخت، نوا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ازین چشمه منوشید که پر خون جگر گشت&lt;/p&gt;&lt;p&gt;بدین تشنه بگویید که آن آب بقا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سر راه نشستیم و نشستیم و شب افتاد&lt;/p&gt;&lt;p&gt;بپرسید، بپرسید که آن ماه کجا رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زهی سایه ی اقبال کزو بر سر ما بود&lt;/p&gt;&lt;p&gt;سر و سایه مخواهید که آن فر هما رفت&lt;/p&gt;', 'آینه ی شکسته', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(170, '&lt;p&gt;دلی که در دو جهان جز تو هیچ یارش نیست&lt;/p&gt;&lt;p&gt;گرش تو یار نباشی جهان به کارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چنان ز لذت دریا پر است کشتی ما&lt;/p&gt;&lt;p&gt;که بیم ورطه و اندیشه ی کنارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کسی به سان صدف واکند دهان نیاز&lt;/p&gt;&lt;p&gt;که نازنین گوهری چون تو در کنارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خیال دوست گل افشان اشک من دیده ست&lt;/p&gt;&lt;p&gt;هزار شکر که این دیده شرمسارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نه من ز حلقه ی دیوانگان عشقم و بس&lt;/p&gt;&lt;p&gt;کدام سلسله دیدی که بی قرارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سوار من که ازل تا ابد گذرگه اوست&lt;/p&gt;&lt;p&gt;سری نماند که بر خاک رهگذارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ز تشنه کامی خود آب می خورد دل من&lt;/p&gt;&lt;p&gt;کویر سوخته جان منت بهارش نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;عروس طبع من ای سایه هر چه دل ببرد&lt;/p&gt;&lt;p&gt;هنوز دلیری شعر شهریارش نیست&lt;/p&gt;', 'لذت دریا', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(171, '&lt;p&gt;نامدگان و رفتگان، از دو کرانه ی زمان&lt;/p&gt;&lt;p&gt;سوی تو می دوند، هان ای تو همیشه در میان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در چمن تو می چرد آهوی دشت آسمان&lt;/p&gt;&lt;p&gt;گرد سر تو می پرد باز سپید کهکشان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر چه به گرد خویشتن می نگرم درین چمن&lt;/p&gt;&lt;p&gt;آینه ی ضمیر من جز تو نمی دهد نشان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای گل بوستان سرا از پس پرده ها در آ&lt;/p&gt;&lt;p&gt;بوی تو می کشد مرا وقت سحر به بوستان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای که نهان نشسته ای باغ درون هسته ای&lt;/p&gt;&lt;p&gt;هسته فروشکسته ای کاین همه باغ شد روان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مست نیاز من شدی، پرده ی ناز پس زدی&lt;/p&gt;&lt;p&gt;از دل خود بر آمدی، آمدن تو شد جهان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه که می زند برون، از سر و سینه موج خون&lt;/p&gt;&lt;p&gt;من چه کنم که از درون دست تو می کشد کمان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیش وجودت از عدم زنده و مرده را چه غم؟&lt;/p&gt;&lt;p&gt;کز نفس تو دم به دم می شنویم بوی جان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیش تو، جامه در برم نعره زند که بر درم&lt;/p&gt;&lt;p&gt;آمدمت که بنگرم گریه نمی دهد امان&lt;/p&gt;', 'همیشه در میان', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(172, '&lt;p&gt;شبی رسید که در آرزوی صبح امید&lt;/p&gt;&lt;p&gt;هزار عمر دگر باید انتظار کشید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آسمان سحر ایستاده بود گمان&lt;/p&gt;&lt;p&gt;سیاه کرد مرا آسمان بی خورشید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هزار سال ز من دور شدستاره ی صبح&lt;/p&gt;&lt;p&gt;ببین کزین شب ظلمت جهان چه خواهد دید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دریغ جان فرورفتگان این دریا&lt;/p&gt;&lt;p&gt;که رفت در سر سودای صید مروارید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نبود در صدفی آن گوهر که می جستیم&lt;/p&gt;&lt;p&gt;صفای اشک تو باد ای خراب گنج امید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ندانم آن که دل و دین ما به سودا داد&lt;/p&gt;&lt;p&gt;بهای آن چه گرفت و به جای آن چه خرید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سیاه دستی آن ساقی منافق بین&lt;/p&gt;&lt;p&gt;که زهر ریخت به جام کسان به جای نبید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سزاست گر برود رود خون ز سینه ی دوست&lt;/p&gt;&lt;p&gt;که برق دشنه ی دشمن ندید و دست پلید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه نقش باختی ای روزگار رنگ آمیز&lt;/p&gt;&lt;p&gt;که این سپید سیه گشت و آن سیاه سپید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کجاست آن که دگر ره صلای عشق زند&lt;/p&gt;&lt;p&gt;که جان ماست گروگان آن نوا و نوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بیا که طبع جهان ناگزیر این عشق است&lt;/p&gt;&lt;p&gt;به جادویی نتوان کشت آتش جاوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;روان سیاه که آیینه دار خورشید است&lt;/p&gt;&lt;p&gt;ببین که از شب عمرش سپیده ای ندمید&lt;/p&gt;', 'سیاه و سپید', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(173, '&lt;p&gt;دگر نگاه مگردان در آسمان کبود&lt;/p&gt;&lt;p&gt;کبوتران تو پر خسته آمدند فرود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به هر چه می نگرم با دریغ و بدرود است&lt;/p&gt;&lt;p&gt;شد آن زمان که جهان جمله مژده بود و درود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دریغ عهد شکر خواب و روزگار شباب&lt;/p&gt;&lt;p&gt;چنان گذشت که انگار هر چه بود نبود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه نقش ها که به خون جگر زدیم و دریغ&lt;/p&gt;&lt;p&gt;کز آن پرند نگارین نه تار ماند و نه پود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سخن به سینه ی تنگم نمی زند چنگی&lt;/p&gt;&lt;p&gt;که گور گریه ی خاموش شد سرای سرود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه رفت بر سر آن شهسوار دشت شفق&lt;/p&gt;&lt;p&gt;که خون همی چکد از سم این سمند کبود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بود که خرمن خاکسترش به باد رود&lt;/p&gt;&lt;p&gt;چو تنگ شد نفس آتش از تباهی دود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مباد سایه که جانت بماند از رفتار&lt;/p&gt;&lt;p&gt;که در روندگی دایم است هستی رود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو را که گوش دل است و زبان جان خوش باش&lt;/p&gt;&lt;p&gt;که نازکان جهان راست با تو گفت و شنود&lt;/p&gt;', 'سرای سرود', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(174, '&lt;p&gt;من آن صبحم که ناگاهان چو آتش در شب افتادم&lt;/p&gt;&lt;p&gt;بیا ای چشم روشن بین که خورشیدی عجب زادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ز هر چاک گریبانم چراغی تازه می تابد&lt;/p&gt;&lt;p&gt;که در پیراهن خود آذرخش آسا درافتادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چو از هر ذره ی من آفتابی نو به چرخ آمد&lt;/p&gt;&lt;p&gt;چه باک از آتش دوران که خواهد داد بر بادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنم افتاده خونین زیر این آوار شب، اما&lt;/p&gt;&lt;p&gt;دری زین دخمه سوی خانه ی خورشید بگشادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;الا ای صبح آزادی به یاد آور در آن شادی&lt;/p&gt;&lt;p&gt;کزین شب های ناباور منت آواز می دادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آن روی و بد حالی نبودم از رخت خالی&lt;/p&gt;&lt;p&gt;به دل می دیدمت وز جان سلامت می فرستادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سزد کز خون من نقشی بر آرد لعل پیروزت&lt;/p&gt;&lt;p&gt;که من بر درج دل مهری به جز مهر تو ننهادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به جز دام سر زلفت که آرام دل سایه ست&lt;/p&gt;&lt;p&gt;به بندی تن نخواهد داد هرگز جان آزادم&lt;/p&gt;', 'از این شب های ناباور', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(175, '&lt;p&gt;من آن صبحم که ناگاهان چو آتش در شب افتادم&lt;/p&gt;&lt;p&gt;بیا ای چشم روشن بین که خورشیدی عجب زادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ز هر چاک گریبانم چراغی تازه می تابد&lt;/p&gt;&lt;p&gt;که در پیراهن خود آذرخش آسا درافتادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چو از هر ذره ی من آفتابی نو به چرخ آمد&lt;/p&gt;&lt;p&gt;چه باک از آتش دوران که خواهد داد بر بادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنم افتاده خونین زیر این آوار شب، اما&lt;/p&gt;&lt;p&gt;دری زین دخمه سوی خانه ی خورشید بگشادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;الا ای صبح آزادی به یاد آور در آن شادی&lt;/p&gt;&lt;p&gt;کزین شب های ناباور منت آواز می دادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آن روی و بد حالی نبودم از رخت خالی&lt;/p&gt;&lt;p&gt;به دل می دیدمت وز جان سلامت می فرستادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سزد کز خون من نقشی بر آرد لعل پیروزت&lt;/p&gt;&lt;p&gt;که من بر درج دل مهری به جز مهر تو ننهادم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به جز دام سر زلفت که آرام دل سایه ست&lt;/p&gt;&lt;p&gt;به بندی تن نخواهد داد هرگز جان آزادم&lt;/p&gt;', 'غروب چمن', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(176, '&lt;p&gt;آن که مست آمد و دستی به دل ما زد و رفت&lt;/p&gt;&lt;p&gt;در این خانه ندانم به چه سودا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خواست تنهایی ما را به رخ ما بکشد&lt;/p&gt;&lt;p&gt;تنه ای بر در این خانه ی تنها زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل تنگش سر گل چیدن ازین باغ نداشت&lt;/p&gt;&lt;p&gt;قدمی چند به آهنگ تماشا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغ دریا خبر از یک شب توفانی داشت&lt;/p&gt;&lt;p&gt;گشت و فریاد کشان بال به دریا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه هوایی به سرش بود که با دست تهی&lt;/p&gt;&lt;p&gt;پشت پا بر هوس دولت دنیا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بس که اوضاع جهان در هم و ناموزون دید&lt;/p&gt;&lt;p&gt;قلم نسخ برین خط چلیپا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل خورشیدی اش از ظلمت ما گشت ملول&lt;/p&gt;&lt;p&gt;چون شفق بال به بام شب یلدا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همنوای دل من بود به تنگام قفس&lt;/p&gt;&lt;p&gt;ناله ای در غم مرغان هم آوا زد و رفت&lt;/p&gt;', 'مرغ دریا', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(177, '&lt;p&gt;آن که مست آمد و دستی به دل ما زد و رفت&lt;/p&gt;&lt;p&gt;در این خانه ندانم به چه سودا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خواست تنهایی ما را به رخ ما بکشد&lt;/p&gt;&lt;p&gt;تنه ای بر در این خانه ی تنها زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل تنگش سر گل چیدن ازین باغ نداشت&lt;/p&gt;&lt;p&gt;قدمی چند به آهنگ تماشا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغ دریا خبر از یک شب توفانی داشت&lt;/p&gt;&lt;p&gt;گشت و فریاد کشان بال به دریا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه هوایی به سرش بود که با دست تهی&lt;/p&gt;&lt;p&gt;پشت پا بر هوس دولت دنیا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بس که اوضاع جهان در هم و ناموزون دید&lt;/p&gt;&lt;p&gt;قلم نسخ برین خط چلیپا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل خورشیدی اش از ظلمت ما گشت ملول&lt;/p&gt;&lt;p&gt;چون شفق بال به بام شب یلدا زد و رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همنوای دل من بود به تنگام قفس&lt;/p&gt;&lt;p&gt;ناله ای در غم مرغان هم آوا زد و رفت&lt;/p&gt;', 'گهواره خالی', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(178, '&lt;p&gt;بخت اگر بیدار باشد خواب بردارد مرا&lt;/p&gt;&lt;p&gt;یکسر از بستر در آغوش تو بگذارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از چه دریا آمدم با ابر بی پایان غم&lt;/p&gt;&lt;p&gt;کاسمان عمری ست تا یکریز می بارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آخرین پیمانه ی شبگیر این خمخانه ام&lt;/p&gt;&lt;p&gt;تا کدامین مست درد آشام بگسارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گنج بی قدرم به دست روزگار مرده دوست&lt;/p&gt;&lt;p&gt;آن گهم داند که خود در خاک بسپارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گرچه مرگم پیش تر از فرصت دیدار توست&lt;/p&gt;&lt;p&gt;همچنان شوق وصالت زنده می دارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سینه ی صافی گفتم پیش چشم روزگار&lt;/p&gt;&lt;p&gt;تا درین آیینه هر کسی خود چه انگارد مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سایه گر خود در هوایت خاک گردد باک نیست&lt;/p&gt;&lt;p&gt;عاقبت روزی به کویت باد می آرد کرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یاد آن فرزانه ی آزرده خاطر خوش که گفت&lt;/p&gt;&lt;p&gt;خامشی جستم که حاسد مرده پندارد مرا&lt;/p&gt;', 'خواب', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(179, '&lt;p&gt;ای عشق مشو در خط خلق ندانندت&lt;/p&gt;&lt;p&gt;تو حرف معمایی خواندن نتوانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بیگانه گرت خواند چون خویشتنت داند&lt;/p&gt;&lt;p&gt;خوش باش و کرامت دان کز خویش برانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;درد تو سرشت توست درمان ز که خواهی جست&lt;/p&gt;&lt;p&gt;تو دام خودی ای دل تا چون برهانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از بزم سیه دستان هرگز قدحی مستان&lt;/p&gt;&lt;p&gt;زهر است اگر آبی در کام چکانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در گردنت از هر سو پیچیده غمی گیسو&lt;/p&gt;&lt;p&gt;تا در شب سرگردان هر سو بکشانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو آب گوارایی جوشیده ز خارایی&lt;/p&gt;&lt;p&gt;ای چشمه مکن تلخی ور زهر چشانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یک عمر غمت خوردم تا در برت آوردم&lt;/p&gt;&lt;p&gt;گر جان بدهند ای غم از من نستانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گر دست بیفشانند بر سایه، نمی دانند&lt;/p&gt;&lt;p&gt;جان تو که ارزانی گر جان بفشانندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون مشک پراکنده عالم ز تو آکنده&lt;/p&gt;&lt;p&gt;گر نافه نهان داری از بوی بدانندت&lt;/p&gt;', 'چشمه خارا', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(180, '&lt;p&gt;چو شبروان سرآسیمه، گرد خانه مگرد&lt;/p&gt;&lt;p&gt;تو خود بهانه ی خویشی پی بهانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو نور دیده ی مایی به جای خویش در آی&lt;/p&gt;&lt;p&gt;چنین چو مردم بیگانه گرد خانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تویی که خانه خدایی بیا و خود را باش&lt;/p&gt;&lt;p&gt;برون در منشین و بر آستانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زمانه گشت و دگر بر مدار بی مهری ست&lt;/p&gt;&lt;p&gt;تو بر مدار دل از مهر و چون زمانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چو تیر گذشتی ز هفت پرده ی چشم&lt;/p&gt;&lt;p&gt;کنون که در بن جانی پی نشانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بهوش باش که هرنقطه دام دایره ای ست&lt;/p&gt;&lt;p&gt;تو در هوای رهایی درین میانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کمند مهر نکردی ز گیسوان بلند&lt;/p&gt;&lt;p&gt;دگر به گرد سر من چو تازیانه مگرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو شعر گمشده ی سایه ای، شناختمت&lt;/p&gt;&lt;p&gt;به سایه روشن مهتاب خامشانه مگرد&lt;/p&gt;', 'کمند مهر', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(181, '&lt;p&gt;شکوه جام جهان بین شکست ای ساقی&lt;/p&gt;&lt;p&gt;نماند جز من و چشم تو مست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من شکسته سبو چاره از کجا جویم&lt;/p&gt;&lt;p&gt;که سنگ فتنه سر خم شکست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;صفای خاطر دردی کشان ببین که هنوز&lt;/p&gt;&lt;p&gt;ز داشت نکشیدند دست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ز رنگ خون دل ما که آب روی تو بود&lt;/p&gt;&lt;p&gt;چه نقش ها که به دل می نشست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;درین دو دم مددی کن مگر که برگذریم&lt;/p&gt;&lt;p&gt;به سر بلندی ازین دیر پست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شبی که ساغرت از می پر است و وقت خوش است&lt;/p&gt;&lt;p&gt;بزن به شادی این غم پرست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه خون که می رود اینجا ز پای خسته هنوز&lt;/p&gt;&lt;p&gt;مگو که مرد رهی نیست، هست ای ساقی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;روا مدار که پیوسته دل شکسته بود&lt;/p&gt;&lt;p&gt;دلی که سایه به زلف تو بست ای ساقی&lt;/p&gt;', 'هست ای ساقی', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(182, '&lt;p&gt;کجایی ای که دلم بی تو در تب و تاب است&lt;/p&gt;&lt;p&gt;چه بس خیال پریشان به چشم بی خواب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به ساکنان سلامت خبر که خواهد برد&lt;/p&gt;&lt;p&gt;که باز کشتی ما در میان غرقاب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ز چشم خویش گرفتم قیاس کار جهان&lt;/p&gt;&lt;p&gt;که نقش مردم حق بین همیشه بر آب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به سینه سر محبت نهان کنید که باز&lt;/p&gt;&lt;p&gt;هزار تیر بلا در کمین احباب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ببین در آینه داری ثبات سینهی ما&lt;/p&gt;&lt;p&gt;اگر چه با دل لرزان به سان سیماب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر آستان وفا سر نهاده ایم و هنوز&lt;/p&gt;&lt;p&gt;اگر امید گشایش بود ازین باب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;قدح ز هر که گرفتم به جز خمار نداشت&lt;/p&gt;&lt;p&gt;مرید ساقی خویشم که باده اش ناب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مدار چشم امید از چراغدار سپهر&lt;/p&gt;&lt;p&gt;سیاه گوشه ی زندان چه جای مهتاب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زمانه کیفر بیداد سخت خواهد داد&lt;/p&gt;&lt;p&gt;سزای رستم بد روز مرگ سهراب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;عقاب ها به هوا پر گشاده اند و دریغ&lt;/p&gt;&lt;p&gt;که این نمایش پرواز نقش در قاب است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آرزوی تو آخر به باد خواهد رفت&lt;/p&gt;&lt;p&gt;چنین که جان پریشان سایه بی تاب است&lt;/p&gt;', 'بر آستان وفا', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(183, '&lt;p&gt;ای عشق تو ما را به کجا می کشی ای عشق&lt;/p&gt;&lt;p&gt;جز محنت و غم نیستی، اما خوشی ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این شوری و شیرینی من خود ز لب توست&lt;/p&gt;&lt;p&gt;صد بار مرا می پزی و می چشی ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون زر همه در حسرت مس گشتنم امروز&lt;/p&gt;&lt;p&gt;تا باز تو دستی به سر من می کشی ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دین و دل و حسن و هنر و دولت و دانش&lt;/p&gt;&lt;p&gt;چندان که نگه می کنمت هر ششی ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;رخساره ی مردان نگر آراسته ی خون&lt;/p&gt;&lt;p&gt;هنگامه ی حسن است چرا خامشی ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آواز خوشت بوی دل سوخته دارد&lt;/p&gt;&lt;p&gt;پیداست که مرغ چمن آتش ای عشق&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بگذار که چون سایه هنوزت بگدازند&lt;/p&gt;&lt;p&gt;از بوته ی ایام چه غم؟ بی غشی ای عشق&lt;/p&gt;', 'مرغ چمن آتش', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(184, '&lt;p&gt;شب های ملال آور پاییز است&lt;/p&gt;&lt;p&gt;هنگام غزل های غم انگیز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گویی همه غم های جهان امشب&lt;/p&gt;&lt;p&gt;در زاری این بارش یکریز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای مرغ سحر ناله به دل بشکن&lt;/p&gt;&lt;p&gt;هنگامه ی آواز شباویز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دورست ازین باغ خزان خورده&lt;/p&gt;&lt;p&gt;آن باد فرح بخش که گلبیز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ساقی سبک آن رطل گران پیش آر&lt;/p&gt;&lt;p&gt;کاین عمر گران مایه سبک خیز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خاکستر خاموش مبین ما را&lt;/p&gt;&lt;p&gt;باز آ که هنوز آتش ما تیز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این دست که در گردن ما کردند&lt;/p&gt;&lt;p&gt;هش دار که با دشنه ی خونریز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;برخیز و بزن بر دف رسوایی&lt;/p&gt;&lt;p&gt;فسقی که در این پرده ی پرهیز است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سهل است که با سایه نیامیزند&lt;/p&gt;&lt;p&gt;ماییم و همین غم که خوش آمیز است&lt;/p&gt;', 'پاییز', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(185, '&lt;p&gt;هزار سال درین آرزو توانم بود&lt;/p&gt;&lt;p&gt;تو هر چه دیر بیایی هنوز باشد زود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو سخت ساخته می آیی و نمی دانم&lt;/p&gt;&lt;p&gt;که روز آمدنت روزی که خواهد بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زهی امید شکیب آفرین که در غم تو&lt;/p&gt;&lt;p&gt;ز عمر خسته ی من هر چه کاست عشق افزود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بدان دو دیده که برخیز و دست خون بگشای&lt;/p&gt;&lt;p&gt;کزین بد آمده راه برون شدی نگشود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;برون کشیدم از آن ورطه رخت و سود نداشت&lt;/p&gt;&lt;p&gt;که بر کرانه ی طوفان نمی توان آسود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دلی به دست تو دادیم و این ندانستیم&lt;/p&gt;&lt;p&gt;که دشنه هاست در آن آستین خون آلود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه نقش می زند این پیر پرنیان اندیش&lt;/p&gt;&lt;p&gt;که بس گره ز دل و جان سایه بست و گشود&lt;/p&gt;', 'نقش پرنیان', 'هوشنگ ابتهاج', 'سیاه مشق', 'غزل', 3, 5),
(186, '&lt;p&gt;شب آمد و چیره شد سیاهی&lt;/p&gt;&lt;p&gt;آرام گرفت مرغ و ماهی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنها منم اشک بار و بیدار&lt;/p&gt;&lt;p&gt;ای شب تو زجان ِ من چه خواهی !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بشکیب و منال ای شباهنگ&lt;/p&gt;&lt;p&gt;انده مفزا بر این دل ِ تنگ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بگذار به درد ِ خود بگریم&lt;/p&gt;&lt;p&gt;در خون ِ دلم فرو مزن چنگ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای ماه متاب بر من امشب&lt;/p&gt;&lt;p&gt;آتش به دلم میفکن امشب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چشمک مزن ای ستاره ، خاموش&lt;/p&gt;&lt;p&gt;خونم مفشان به دامن امشب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای باد به کوی من چه پویی&lt;/p&gt;&lt;p&gt;آزار ِ دل ِ مرا چه جویی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زان دختر ِ شوخ چشم ِ طناز&lt;/p&gt;&lt;p&gt;با من سخنی دگر نگویی !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نزدیک میا ، خیال ِ او ، دور !&lt;/p&gt;&lt;p&gt;دور از من ِ دردمند ِ مهجور !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بگریز ازین شکسته ی درد&lt;/p&gt;&lt;p&gt;بگذار مرا درین شب ِ گور !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ای شب ِ تب گرغته پیش آی&lt;/p&gt;&lt;p&gt;و آغوش ِ سیاه ِ خویش بگشای&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بفشار مرا به سینه ، بفشار&lt;/p&gt;&lt;p&gt;بربای مرا ز خویش ، بربای&lt;/p&gt;', 'گور شب', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(187, '&lt;p&gt;می شنوم می شنوم آشناست&lt;/p&gt;&lt;p&gt;موسقی چشم ِ تو در گوش ِ من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;موج ِ نگاه ِ تو هماواز ِ ناز&lt;/p&gt;&lt;p&gt;ریخت چو مهتاب در آغوش ِ من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می شنوم در نگه ِ گرم ِ توست&lt;/p&gt;&lt;p&gt;گم شده گلبانگ ِ بهشت ِ امید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این همه گشتم من و ، دلخواه ِ من&lt;/p&gt;&lt;p&gt;در نگه ِ گرم ِ تو می آرمید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زمزمه ی شعر ِ نگاه ِ تو را&lt;/p&gt;&lt;p&gt;می شنوم ، با دل و جان آشناست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اشک ِ زلال ِ غزل حافظ است&lt;/p&gt;&lt;p&gt;نغمه ی مرغان ِ بهشتی نواست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می شنوم ، در نگه ِ گرم توست&lt;/p&gt;&lt;p&gt;نغمه ی آن شاهد رؤیانشین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باز ز گلبانگ ِ تو سر می کشد&lt;/p&gt;&lt;p&gt;شعله ی این آرزوی آتشین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;موسقی چشم ِ تو گویاتر است&lt;/p&gt;&lt;p&gt;از لب ِ پر ناله و آواز ِ من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وه که تو هم گر بتوانی شنید&lt;/p&gt;&lt;p&gt;زین نگه ِ نغمه سرا راز ِ من !&lt;/p&gt;', 'آواز نگاه', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(188, '&lt;p&gt;می خوانم و می ستایمت پرشور&lt;/p&gt;&lt;p&gt;ای پرده ی دل فریب ِ رؤیا رنگ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می بوسمت ای سپیده ی گلگون&lt;/p&gt;&lt;p&gt;ای فردا ای امید ِ بی نیرنگ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دیری ست که من پی ِ تو می پویم&lt;/p&gt;&lt;p&gt;هر سو که نگاه می کنم ، آوخ !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;غرق است در اشک و خون نگاه ِ من&lt;/p&gt;&lt;p&gt;هر گام که پیش می روم برپاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سر نیزه ی خون فشان به راه ِ من&lt;/p&gt;&lt;p&gt;وین راه ِ یگانه ، راه ِ بی برگشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ره می سپریک همره ِ امید&lt;/p&gt;&lt;p&gt;آگاه ز رنج و آشنا با درد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یک مرد اگر به خاک می افتد&lt;/p&gt;&lt;p&gt;بر می خیزد به جای او صد مرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این است که کاروان نمی ماند&lt;/p&gt;&lt;p&gt;آری ز درون ِ این شب ِ تاریک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای فردا من سوی تو می رانم&lt;/p&gt;&lt;p&gt;رنج است و درنگ نیست می تازم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرگ است و شکست نیست می دانم&lt;/p&gt;&lt;p&gt;آبستن ِ فتح ِ ماست این پیکار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می دانمت ای سپیده ی نزدیک&lt;/p&gt;&lt;p&gt;ای چشمه ی تابناک ِ جان افروز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کز این شب ِ شوم بخت ِ بدفرجام&lt;/p&gt;&lt;p&gt;برمی آیی شکفته و پیروز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وز آمدن ِ تو زندگی خندان&lt;/p&gt;&lt;p&gt;می آیی و بر لب ِ تو صد لبخند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می آیی و در دل ِ تو صد امید&lt;/p&gt;&lt;p&gt;می آیی و از فروغ ِ شادی ها&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تابنده به دامن ِ تو صد خورشید&lt;/p&gt;&lt;p&gt;وز بهر ِ تو بازگشته صد آغوش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در سینه ی گرم توست ای فردا&lt;/p&gt;&lt;p&gt;درمان ِ امید های غم فرسود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در دامن ِ پاک ِ توست ای فردا&lt;/p&gt;&lt;p&gt;پایان ِ شکنجه های خون آلود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای فردا ای امید ِ بی نیرنگ&lt;/p&gt;', 'ای فردا', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(189, '&lt;p&gt;ای کدامین شب !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یک نفس بگشای&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جنگل ِ انبوه ِ مژگان ِ سیاهت را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا بلغزد بر بلور ِ برکه ی چشم ِ کبود ِ تو&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیکر ِ مهتابگون ِ دختری کز دور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با نگاه ِ خویش می جوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بوسه ی شیرین ِ روزی آفتابی را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از نوازش های گرم ِ دست های من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دختری نیلوفرین ، شبرنگ ، مهتابی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می تپد بی تاب در خواب ِ هوسناک ِ امید ِ خویش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پای تا سر یک هوس : آغوش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و تنش لغزان و خواهش بار می جوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون مِهِ پیچان به روی دره های خواب آلود ِ سپیده دم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بسترم را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا بلغزد از طلب سرشار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همچو موج ِ بوسه ی مهتاب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;روی گندمزار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا بنوشد در نوازش های گرم ِ دست های من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شبنم ِ یک عشق ِ وحشی را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای کدامین شب !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یک نفس بگشای مژگان ِ سیاهت را&lt;/p&gt;', 'نیلوفر', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(190, '&lt;p&gt;سنگی ست زیر ِ آب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در گود ِ شب گرفته ی دریای ِ نیلگون&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنها نشسته در تک ِ آن گور ِ سهمناک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خاموش مانده در دل ِ آن سردی و سکون&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;او با سکوت ِ خویش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از یاد رفته ای ست در آن دخمه ی سیاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هرگز بر او نتافته خورشید ِ نیم روز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هرگز بر او نتافته مهتاب ِ شامگاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بسیار شب که ناله برآورد و کس نبود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کان ناله بشنود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بسیار شب که اشک بر افشاند و یاوه گشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در گود ِ آن کبود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سنگی ست زیر ِ آب ولی آن شکسته سنگ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زنده ست ، می تپد به امیدی در آن نهفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل بود اگر به سینه ی دلدار می نشست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گل بود اگر به سایه ی خورشید می شکفت&lt;/p&gt;', 'مرجان', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(191, '&lt;p&gt;باز باران است و شب چون جنگلی انبوه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از زمین آهسته می روید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با نواهایی به هم پیچیده زیر ِ ریزش ِ باران&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با خود او را زیر ِ لب نجواست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سرگذشتی تلخ می گوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کوچه تاریک است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بانگ ِ پایی می شود نزدیک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شاخه ای بر پنجره انگشت می ساید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اشک ِ باران می چکد بر شیشه ی تاریک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من نشسته پیش ِ آتش ، در اجاقم هیمه می سوزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دخترم یلدا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خفته در گهواره می جنباندش مادر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شب گران بار ست و باران همچنان یکریز می بارد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سایه ی باریک ِ اندام زنی افتاده بر دیوار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بچه اش را می فشارد در بغل نومید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در دلش انگار چیزی را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می کنند از ریشه خون آلود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;لحظه ای می ایستد خم می شود آهسته با تردید ...&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;رعد می غرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سیل می بارد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آخرین اندیشه ی مادر :&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;- &quot; چه می خواهی شد ؟ ... &quot;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آسمان گویی ز چشم ِ او فرو می بارد این باران ...&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باز باران است و شب چون جنگلی انبوه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر زمین گسترده هر سو شاخ و برگش را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با صداهایی به هم پیچیده دارد زیر ِ لب نجوا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من نشسته تنگ دل پیش ِ اجاق ِ سرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دخترم یلدا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خفته در گهواره اش آرام ...&lt;/p&gt;', 'سرگذشت', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(192, '&lt;p&gt;ما از نژاد ی آتش بودیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همزاد ِ آفتاب ِ بلند اما&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با سرنوشت ِ تیره ی خاکستر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;عمری میان کوره ی بیداد سوختیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;او چون شراره رفت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من با شکیب ِ خاکستر ماندم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کیوان ستاره شد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا بر فراز ِ این شب ِ غمناک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;امید ِ روشنی را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با ما نگاه دارد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کیوان ستاره شد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا شب گرفتگان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;راه ِ سپیده را بشناسند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کیوان ستاره شد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که بگوید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آتش&lt;/p&gt;&lt;p&gt;آنگاه آتش است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کز اندرون ِ خویش بسوزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وین شام ِ تیره را بفروزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من در تمام ِ این شب ِ یلدا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دست ِ امید ِ خسته ی خود را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در دست های روشن ِ او می گذاشتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من در تمام ِ این شب ِ یلدا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ایمان ِ آفتابی ِ خود را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از پرتو ِ ستاره ی او گرم داشتم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کیوان ستاره بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با نور زندگانی می کرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با نور در گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;او در میان ِ مردمک ِ چشم ِ ما نشست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا این ودیعه را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;روزی به صبحدم بسپاریم&lt;/p&gt;', 'کیوان ستاره بود', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(193, '&lt;p&gt;ارغوان ! شاخه ی همخون ِ جدامانده ی من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آسمان ِ تو چه رنگ است امروز ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آفتابی ست هوا ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یا گرفته ست هنوز ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من در این گوشه که از دنیا بیرون است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آسمانی به سرم نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از بهاران خبرم نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آنچه می بینم دیوارست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ، این سخت ِ سیاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آن چنان نزدیک است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که چو بر می کشم از سینه نفس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نفسم را بر می گرداند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ره چنان بسته که پروازِ نگه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در همین یک قدمی می ماند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کورسویی ز چراغی رنجور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;قصه پرداز ِ شبِ ظلمانی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نفسم می گیرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که هوا هم اینجا زندانی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر چه با من اینجاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;رنگ ِ رخ باخته است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آفتابی هرگز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گوشه ی چشمی هم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر فراموشی ِ این دخمه نینداختهاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اندرین گوشه ی خاموش ِ فراموش شده&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کز دم ِ سردش هر شمعی خاموش شده&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یاد ِرنگینی در خاطر ِ من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گریه می انگیزد :&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوانم آنجاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوانم تنهاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوانم دارد می گرید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون دل ِ من که چنین خون آلود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر دم از دیده فرو می ریزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوان !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این چه رازی ست که هر بار بهار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با عزای دل ِ ما می آید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که زمین هر سال از خون ِ پرستوها رنگین است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وین چنین بر جگر سوختگان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;داغ برداغ می افزاید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوان ، پنجه ی خونین ِ زمین !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دامن ِ صبح بگیر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وز سواران ِ خرامنده ی خورشید بپرس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کی بر این دره ی غم می گذرند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوان ، خوشه ی خون !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بامدادان که کبوترها&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر لب ِ پنجره ی باز ِ سحر غلغله می آغازند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جان ِ گل رنگ ِ مرا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر سر ِ دست بگیر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به تماشاگه ِ پرواز ببر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ، بشتاب که همپروازان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نگران ِ غم ِ همپروازند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوان ، بیرق ِ گلگون ِ بهار !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو برافراشته باش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شعر ِ خونبار ِ منی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;یاد ِ رنگین ِ رفیقانم را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر زبان داشته باش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو بخوان نغمه ی ناخوانده ی من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ارغوان ، شاخه ی همخون ِ جدامانده ی من !&lt;/p&gt;', 'ارغوان', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(194, '&lt;p&gt;خانه دل تنگ ِ غروبی خفه بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مثل ِ امروز که تنگ است دلم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پدرم گفت چراغ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و شب از شب پر شد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من به خود گفتم یک روز گذشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مادرم آه کشید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زود بر خواهد گشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ابری آهسته به چشمم لغزید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و سپس خوابم برد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که گمان داشت که هست اینهمه درد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در کمین ِ دل ِ آن کودک ِ خرد ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آری آن روز چو می رفت کسی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;داشتم آمدنش را باور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من نمی دانستم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;معنی هرگز را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو چرا بازنگشتی دیگر ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ای واژه ی شوم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خو نکرده ست دلم با تو هنوز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من پس از این همه سال&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چشم دارم در راه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که بیایند عزیزانم، آه !&lt;/p&gt;', 'تاسیان', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(195, '&lt;p&gt;او راز گیسوان ِ بلندش شناختند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای خاک این همان تن ِ پاک است ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;انسان همین خلاصه ی خاک است ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وقتی که شانه می زد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;انبوه ِ گیسوان ِ بلندش را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا دوردست ِ آینه می راند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اندیشه ی خیال پسندش را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;او با سلام صبح&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خندان ، گلی ز آینه می چید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دستی به گیسوانش می برد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شب را کنار می زد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خورشید را در آینه می دید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اندیشه ی برآمدن ِ روز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بارانی از ستاره فرو می ریخت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آسمان ِ چشم ِ جوانش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آنگاه آن تبسم ِ شیرین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در می گشود بر رخ ِ آیینه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از باغ ِ آفتابی ِ جانش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دزدان ِ کور ِ آینه ، افسوس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آن چشم ِ مهربان را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از آستان ِ صبح ربودند !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ای بهار ِ سوخته&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خاکستر ِ جوانی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تصویر ِ پر کشیده ی آیینه ی تهی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با یاد ِ گیسوان بلندت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آیینه در غبار ِ سحر آه می کشد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغان باغ بیهده خواندند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هنگام ِ گل نبود&lt;/p&gt;', 'آه آینه', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(196, '&lt;p&gt;ره در جنگل ِ اوهام گم است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سینه بگشای چو دشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اگرت پرتو ِ خورشید ِ حقیقت یابد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وقتی از جنگل ِ گم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پا نهادی بیرون&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و رها گشتی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از آن گره ِ کور ِ گمار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ناگهان آبشاری از نور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر سرت می ریزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و آسمان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با همه پهناوری ِ بی مرزش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در تو می آمیزد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای فراز آمده از جنگل ِ کور !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هستی ِ روشن ِ دشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آشکارا بادت !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر لب ِ چشمه ی خورشید ِ زلال&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جرعه ی نور گوارا بادت !&lt;/p&gt;', 'بیرون شد از گُمار', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(197, '&lt;p&gt;بانگ ِ خروس از سرای دوست برآمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خیز و صفا کن که مژده ی سحر آمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چشم ِ تو روشن&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باغ ِ تو آباد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دست مریزاد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همت ِ حافظ به همره ِ تو که آخر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دست به کاری زدی و غصه سر آمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بخت ِ تو برخاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;صبح ِ تو خندید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از نفست تازه گشت آتش ِ امید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وه که به زندانِ ظلمت ِ شب ِ یلدا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نور ز خورشید خواستی و برآمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گل به کنارست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باده به کارست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گلشن و کاشانه پر ز شور ِ بهارست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بلبل ِ عاشق بخوان به کام ِ دل ِ خویش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باغ ِ تو شد سبز و سرخ گل به بر آمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جام ِ تو پرنوش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کام ِ تو شیرین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;روز ِ تو خوش باد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کز پس ِ آن روزگار ِ تلخ تر از زهر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بار ِ دگر روزگار ِ چون شکر آمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;رزم ِ تو پیروز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بزم ِ تو پر نور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جام به جام ِ تو می زنم زره ژ دور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شادی ِ آن صبح ِ آرزو که ببینم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بوم ازین بام رفت و خوش خبر آمد&lt;/p&gt;', 'شاد باش', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(198, '&lt;p&gt;شب فرو می افتاد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به درون آمدم و پنجره ها را بستم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;باد با شاخه در آویخته بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من در این خانه ی تنها تنها&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;غم ِ عالم به دلم ریخته بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ناگهان حس کردم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;که کسی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آنجا بیرون در باغ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در پس ِ پنجره ام&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می گرید ...&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;صبحگاهان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شبنم&lt;/p&gt;&lt;p&gt;می چکید از گل ِ سیب&lt;/p&gt;', 'گریهٔ سیب', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(199, '&lt;p&gt;امشب همه غم های عالم را خبر کن !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بنشین و با من گریه سر کن&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گریه سر کن !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ، ای انبوه ِ اندوهان ِ دیرین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای چون دل ِ من ، ای خموش ِ گریه آگین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سر در گریبان ، در پس ِ زانو نشسته&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ابرو گره افکنده چشم از درد بسته&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در پرده های اشک ِ پنهان ، کرده بالین !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای داد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از آشیانت بوی خون می آورد باد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر بال ِ سرخ ِ کشکرک پیغام شومی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آنجا چه آمد بر سر ِ آنسرو ِ آزاد ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای شب !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای بی ستاره !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خورشید ِ تاریک !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اشک ِ سیاه ِ کهکشان های گسسته !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آیینه ی دیرینه ی زنگار بسته !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دیدی چراغی را که در چشمت شکستند ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای غم !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چنگ ِ هزار آوای باران های ماتم !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در سایه افکند ِ کدامین ناربُن ریخت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خون از گلوی مرغ عاشق ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغی که می خواند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغی که با آوازش از کنج ِ قفس پرواز می کرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مرغی که می خواست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پرواز باشد ...&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از جنگل ای حیف !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;همسایه ی شب های تلخ ِ نامرادی !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در آستان ِ سبز ِ فروردین دریغا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آن غنچه های سرخ را بر باد دادی !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای پیسوته پاییز !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای آتش ِ خیس !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای سرخ و زرد ، ای شعله ی سرد !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای در گلوی ابر و مه فریاد ِ خورشید !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تا کی ستم با مرد خواهد کرد نامرد ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای در خود نشسته !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیچیده با خاموشی ِ سبز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خوابیده با رؤیای رنگین ِ بهار ِ نغمه پرداز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زین پیله کی آن نازنین پروانه خواهد کرد پرواز ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای همراز ِ کوچک خان ِ سردار !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هم عهد ِ سر های بریده !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پر کرده دامن&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از میوه های کال چیده !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کی می نشیند دُرد ِ شیرین ِ رسیدن&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در شیر ِ پستان های سبزت ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای خشم !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای شعله ور چون آذرخش ِ پیرهن چاک !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با من بگو از سرگذشت ِ آن سپیدار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آن سهمگین پیکر ، که با فریاد ِ تندر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چون پاره ای از آسمان افتاد بر خاک !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ای پیر !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بالنده ی افتاده ، آزاد ِ زمینگیر !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خون می چکد ینجا هنوز از زخم ِ دیرین ِ تبر ها&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای جنگل ! اینجا سینه ی من چون تو زخمی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اینجا دمادم دارکوبی بر درخت ِ پیر می کوبد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دمادم&lt;/p&gt;', 'مرثیه جنگل', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(200, '&lt;p&gt;بنشینیم و بیندیشیم !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این همه با هم بیگانه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این همه دوری و بیزاری&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به کجا آیا خواهیم رسید آخر ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و چه خواهد آمد بر سر ِ ما با این دل های پراکنده ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جنگلی بودیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شاخه در شاخه همه آغوش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ریشه در ریشه همه پیوند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وینک ، انبوه ِ درختانی تنهاییم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مهربانی ، به دل ِ بسته ی ما ،مرغی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;کز قفس در نگشادیمش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و به عذری که فضایی نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وندرین باغ ِ خزان خورده&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جز سموم ِ ستم آورده هوایی نیست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ره ِ پرواز ندادیمش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هستی ِ ما که چو آیینه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تنگ بر سینه فشردیمش از وحشت ِ سنگ انداز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نه صفا و نه تماشا به چه کار آمد ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دشمنی دل ها را با کین خوگر کرد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دست ها با دشنه همدستان گشتند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و زمین از بدخواهی به ستوه آمد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای دریغا که دگر دشمن رفت از یاد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وینک از سینه ی دوست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خون فرو می ریزد !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دوست کاندر بر ِ وی گریه ی انباشته را نتوانی سر داد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه توان گفتمش ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بیگانه ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و سرایی که به چشم انداز ِ پنجره اش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;نیست درختی که بر او مرغی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به فغان ِ تو دهد پاسخ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زندان است&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من به عهدی که بدی مقبول&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و توانایی دانایی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با تو از خوبی می گویم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از تو دانایی می جویم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خوب ِ من ! دانایی را بنشان بر تخت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و توانایی را حلقه به گوشش کن !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من به عهدی که وفاداری&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;داستانی ست ملال آور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و ابلهی نیست دگر افسوس !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;داشتن جنگ ِ برادرها را باور&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آشتی را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به امیدی که خرد فرمان خواهد راند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;می کنم تلقین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وندر این فتنه ی بی تدبیر&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با چه دلشوره و بیمی نگرانم من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این همه با هم بیگانه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;این همه دوری و بیزاری&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;به کجا آیا خواهیم رسید آخر ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و چه خواهد آمد بر سر ِ ما با این دل ِ های پراکنده ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بنشینیم و بیندیشیم !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'تشویش', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(201, '&lt;p&gt;از هم گریختیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وان نازنین پیاله ی دلخواه را ، دریغ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر خاک ریختیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جان ِ من و تو تشنه ی پیوند ِ مهر بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دردا که جان ِ تشنه ی خود را گداختیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بس دردناک بود جدایی میان ِ ما&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از هم جدا شدیم و بدین درد ساختیم&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دیدار ِ ما که آن همه شوق و امید داشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اینک نگاه کن که سراسر ملال گشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;وان عشق ِ نازنین که میان ِ من و تو بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دردا که چون جوانی ِ ما پایمال گشت !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با آن همه نیاز که من داشتم به تو&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پرهیز ِ عاشقانه ی من ناگزیر بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من بارها به سوی تو باز آمدم ولی&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر بار دیر بود !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اینک من و توایم دو تنهای بی نصیب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر یک جدا گرفته ره ِ سرنوشت ِ خویش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سرگشته در کشاکش ِ طوفان ِ روزگار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گم کرده همچو آدم و حوا بهشت ِ خویش&lt;/p&gt;', 'گریز', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(202, '&lt;p&gt;ای دریغا چه گلی ریخت به خاک !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه بهاری پژمرد !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه دلی رفت به باد !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه چراغی افسرد !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر شب این دلهره ی طاقت سوز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;خوابم از دیده ربود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر سحر چشم گشودم نگران :&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;چه خبر خواهد بود ؟&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سرنوشت ِ دل من بود درین بیم و امید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آه ای چشمه ی نوشین حیات !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای امید ِ دلبند !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گرچه صدبار دلم از تو شکست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هیچ گه از لب ِ نوشت نبریدم پیوند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آخر ای صبحدم ِ خون آلود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آمد آن خنجر ِ بیداد فرود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شش ستاره به زمین درغلتید&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شش دل ِ شیر فروماند از کار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شش صدا شد خاموش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بانگ ِ خون در دل ِ ریشم برخاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پر شدم از فریاد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هفتمین اختر ِ این صبح ِ سیاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دل ِ من بود که بر خاک افتاد&lt;/p&gt;', 'هفتمین اختر این صبح سیاه', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(203, '&lt;p&gt;زین پیش &amp;nbsp;شاعران ِ ثنا خوان که چشم شان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;در سعد و نحس ِ طالع و سیر ِ ستاره بود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بس نکته های نغز و سخن های پر نگار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گفتند در ستایش ِ این گنبد ِ کبود&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اما زمین که بیش تر از هر چه در جهان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;شایسته ی ستایش و تکریم آدمی ست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;گمنام و ناشناخته و بی سپاس ماند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای مادر ای زمین !&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;امروز این منم که ستایشگر ِ توام&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از توست ریشه و رگ و خون و خروش ِ من&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;فرزند ِ حقگزار ِ تو و شاکر ِ توام&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بس روزگار گشت و بهار و خزان گشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;تو ماندی و گشادگی ِ بی کرانه ات&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;طوفان ِ نوح هم نتوانست شعله کشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از آتش ِ گداخته ی جاودانه ات&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر پهلوان به خاک رسیده ست گرده اش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;غیر از تو ای زمین که در این صحنه ی ستیز&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ماندی به جای خویش&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیوسته زورمند و گران سنگ و استوار&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;فرزند ِ بدسگالی اگر چون حرامیان&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر حرمت ِ تو تاخت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هرگز تهی نشد دلت از مهر ِ مادری&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;با جمله ناسپاسی ِ فرزند ِ بی شناخت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آری زمین ستایش و تکریم را سزاست&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از اوست هر چه هست در این پهن بارگاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پروردگان ِ دامن و گهواره ی وی اند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سهراب ِ پهلوان و سلیمان ِ پادشاه&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای بس که تازیانه ی خونین ِ برق و باد&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیچیده دردناک&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بر گرده ی زمین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;ای بس که سیل ِ کف به لب آورده ی عبوس&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;جوشیده سهمناک بر این خاک ِ سهمگین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زان گونه مرگ بار که پنداشتی ، دریغ&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دیگر زمین همیشه تهی مانده از حیات&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;اما زمین همیشه همان گونه سخت پشت&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بیرون کشیده تن&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;از زیر ِ هر بلا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;و آغوش باز کرده به لبخند ِ آفتاب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زرین و پر سخاوت و سرسبز و دلگشا&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;بگذار چون زمین&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;من بگذرانم این شب ِ طوفان گرفته را&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;آنگه به نوش خند ِ گهربار ِ آفتاب&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;پیش ِ تو گسترم همه گنج ِ نهفته را&lt;/p&gt;', 'زمین', 'هوشنگ ابتهاج', 'تاسیان', 'کلاسیک و نیمایی', 3, 4),
(205, '&lt;p&gt;بشنو این نی چون شکایت می‌کند&lt;/p&gt;&lt;p&gt;از جدایی‌ها حکایت می‌کند&lt;/p&gt;&lt;p&gt;کز نیستان تا مرا ببریده‌اند&lt;/p&gt;&lt;p&gt;در نفیرم مرد و زن نالیده‌اند&lt;/p&gt;&lt;p&gt;سینه خواهم شرحه شرحه از فراق&lt;/p&gt;&lt;p&gt;تا بگویم شرح درد اشتیاق&lt;/p&gt;&lt;p&gt;هر کسی کو دور ماند از اصل خویش&lt;/p&gt;&lt;p&gt;باز جوید روزگار وصل خویش&lt;/p&gt;&lt;p&gt;من به هر جمعیتی نالان شدم&lt;/p&gt;&lt;p&gt;جفت بدحالان و خوش‌حالان شدم&lt;/p&gt;&lt;p&gt;هر کسی از ظن خود شد یار من&lt;/p&gt;&lt;p&gt;از درون من نجست اسرار من&lt;/p&gt;&lt;p&gt;سر من از نالهٔ من دور نیست&lt;/p&gt;&lt;p&gt;لیک چشم و گوش را آن نور نیست&lt;/p&gt;&lt;p&gt;تن ز جان و جان ز تن مستور نیست&lt;/p&gt;&lt;p&gt;لیک کس را دید جان دستور نیست&lt;/p&gt;&lt;p&gt;آتش است این بانگ نای و نیست باد&lt;/p&gt;&lt;p&gt;هر که این آتش ندارد نیست باد&lt;/p&gt;&lt;p&gt;آتش عشق است کاندر نی فتاد&lt;/p&gt;&lt;p&gt;جوشش عشق است کاندر می فتاد&lt;/p&gt;&lt;p&gt;نی حریف هر که از یاری برید&lt;/p&gt;&lt;p&gt;پرده‌هایش پرده‌های ما درید&lt;/p&gt;&lt;p&gt;همچو نی زهری و تریاقی که دید&lt;/p&gt;&lt;p&gt;همچو نی دمساز و مشتاقی که دید&lt;/p&gt;&lt;p&gt;نی حدیث راه پر خون می‌کند&lt;/p&gt;&lt;p&gt;قصه‌های عشق مجنون می‌کند&lt;/p&gt;&lt;p&gt;محرم این هوش جز بیهوش نیست&lt;/p&gt;&lt;p&gt;مر زبان را مشتری جز گوش نیست&lt;/p&gt;&lt;p&gt;در غم ما روزها بیگاه شد&lt;/p&gt;&lt;p&gt;روزها با سوزها همراه شد&lt;/p&gt;&lt;p&gt;روزها گر رفت گو رو باک نیست&lt;/p&gt;&lt;p&gt;تو بمان ای آن که چون تو پاک نیست&lt;/p&gt;&lt;p&gt;هر که جز ماهی ز آبش سیر شد&lt;/p&gt;&lt;p&gt;هرکه بی روزیست روزش دیر شد&lt;/p&gt;&lt;p&gt;در نیابد حال پخته هیچ خام&lt;/p&gt;&lt;p&gt;پس سخن کوتاه باید والسلام&lt;/p&gt;&lt;p&gt;بند بگسل باش آزاد ای پسر&lt;/p&gt;&lt;p&gt;چند باشی بند سیم و بند زر&lt;/p&gt;&lt;p&gt;گر بریزی بحر را در کوزه‌ای&lt;/p&gt;&lt;p&gt;چند گنجد قسمت یک روزه‌ای&lt;/p&gt;&lt;p&gt;کوزه‌ی چشم حریصان پر نشد&lt;/p&gt;&lt;p&gt;تا صدف قانع نشد پر در نشد&lt;/p&gt;&lt;p&gt;هر که را جامه ز عشقی چاک شد&lt;/p&gt;&lt;p&gt;او ز حرص و عیب کلی پاک شد&lt;/p&gt;&lt;p&gt;شاد باش ای عشق خوش سودای ما&lt;/p&gt;&lt;p&gt;ای طبیب جمله علت‌های ما&lt;/p&gt;&lt;p&gt;ای دوای نخوت و ناموس ما&lt;/p&gt;&lt;p&gt;ای تو افلاطون و جالینوس ما&lt;/p&gt;&lt;p&gt;جسم خاک از عشق بر افلاک شد&lt;/p&gt;&lt;p&gt;کوه در رقص آمد و چالاک شد&lt;/p&gt;&lt;p&gt;عشق، جانِ طور آمد عاشقا!&lt;/p&gt;&lt;p&gt;طور، مست و خرَّ موسی صَاعِقا&lt;/p&gt;&lt;p&gt;با لب دمساز خود گر جفتمی&lt;/p&gt;&lt;p&gt;همچو نی من گفتنی‌ها گفتمی&lt;/p&gt;&lt;p&gt;هر که او از هم‌زبانی شد جدا&lt;/p&gt;&lt;p&gt;بی‌زبان شد گرچه دارد صد نوا&lt;/p&gt;&lt;p&gt;چون که گل رفت و گلستان درگذشت&lt;/p&gt;&lt;p&gt;نشنوی زآن پس ز بلبل سرگذشت&lt;/p&gt;&lt;p&gt;جمله معشوق است و عاشق پرده‌ای&lt;/p&gt;&lt;p&gt;زنده معشوق است و عاشق مرده‌ای&lt;/p&gt;&lt;p&gt;چون نباشد عشق را پروای او&lt;/p&gt;&lt;p&gt;او چو مرغی ماند بی‌پر وای او&lt;/p&gt;&lt;p&gt;من چگونه هوش دارم پیش و پس&lt;/p&gt;&lt;p&gt;چون نباشد نور یارم پیش و پس&lt;/p&gt;&lt;p&gt;عشق خواهد کاین سخن بیرون بود&lt;/p&gt;&lt;p&gt;آینه غماز نبود چون بود&lt;/p&gt;&lt;p&gt;آینه‌ت دانی چرا غماز نیست&lt;/p&gt;&lt;p&gt;زآن که زنگار از رخش ممتاز نیست&lt;/p&gt;', 'مثنوی شماره ۱', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(206, '&lt;p&gt;بشنوید ای دوستان این داستان&lt;/p&gt;&lt;p&gt;خود حقیقت نقد حال ماست آن&lt;/p&gt;&lt;p&gt;بود شاهی در زمش ازین&lt;/p&gt;&lt;p&gt;ملک دنیا بودش و هم ملک دین&lt;/p&gt;&lt;p&gt;اتفاقا شاه روزی شد سوار&lt;/p&gt;&lt;p&gt;با خواص خویش از بهر شکار&lt;/p&gt;&lt;p&gt;یک کنیزک دید شه بر شاه‌راه&lt;/p&gt;&lt;p&gt;شد غلام آن کنیزک پادشاه&lt;/p&gt;&lt;p&gt;مرغ جانش در قفس چون می‌تپید&lt;/p&gt;&lt;p&gt;داد مال و آن کنیزک را خرید&lt;/p&gt;&lt;p&gt;چون خرید او را و برخوردار شد&lt;/p&gt;&lt;p&gt;آن کنیزک از قضا بیمار شد&lt;/p&gt;&lt;p&gt;آن یکی خر داشت و پالانش نبود&lt;/p&gt;&lt;p&gt;یافت پالان گرگ خر را در ربود&lt;/p&gt;&lt;p&gt;کوزه بودش آب می‌نامد بدست&lt;/p&gt;&lt;p&gt;آب را چون یافت خود کوزه شکست&lt;/p&gt;&lt;p&gt;شه طبیبان جمع کرد از چپ و راست&lt;/p&gt;&lt;p&gt;گفت جان هر دو در دست شماست&lt;/p&gt;&lt;p&gt;جان من سهلست جان جانم اوست&lt;/p&gt;&lt;p&gt;دردمند و خسته‌ام درمانم اوست&lt;/p&gt;&lt;p&gt;هر که درمان کرد مر جان مرا&lt;/p&gt;&lt;p&gt;برد گنج و در و مرجان مرا&lt;/p&gt;&lt;p&gt;جمله گفتندش که جانبازی کنیم&lt;/p&gt;&lt;p&gt;فهم گرد آریم و انبازی کنیم&lt;/p&gt;&lt;p&gt;هر یکی از ما مسیح عالمیست&lt;/p&gt;&lt;p&gt;هر الم را در کف ما مرهمیست&lt;/p&gt;&lt;p&gt;گر خدا خواهد نگفتند از بطر&lt;/p&gt;&lt;p&gt;پس خدا بنمودشان عجز بشر&lt;/p&gt;&lt;p&gt;ترک استثنا مرادم قسوتیست&lt;/p&gt;&lt;p&gt;نه همین گفتن که عارض حالتیست&lt;/p&gt;&lt;p&gt;ای بسا ناورده استثنا بگفت&lt;/p&gt;&lt;p&gt;جان او با جان استثناست جفت&lt;/p&gt;&lt;p&gt;هرچه کردند از علاج و از دوا&lt;/p&gt;&lt;p&gt;گشت رنج افزون و حاجت ناروا&lt;/p&gt;&lt;p&gt;آن کنیزک از مرض چون موی شد&lt;/p&gt;&lt;p&gt;چشم شه از اشک خون چون جوی شد&lt;/p&gt;&lt;p&gt;از قضا سرکنگبین صفرا فزود&lt;/p&gt;&lt;p&gt;روغن بادام خشکی می‌نمود&lt;/p&gt;&lt;p&gt;از هلیله قبض شد اطلاق رفت&lt;/p&gt;&lt;p&gt;آب آتش را مدد شد همچو نفت&lt;/p&gt;', 'مثنوی شماره ۲', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(207, '&lt;p&gt;شه چو عجز آن حکیمان را بدید&lt;/p&gt;&lt;p&gt;پابرهنه جانب مسجد دوید&lt;/p&gt;&lt;p&gt;رفت در مسجد سوی محراب شد&lt;/p&gt;&lt;p&gt;سجده‌گاه از اشک شه پر آب شد&lt;/p&gt;&lt;p&gt;چون به خویش آمد ز غرقاب فنا&lt;/p&gt;&lt;p&gt;خوش زبان بگشاد در مدح و دعا&lt;/p&gt;&lt;p&gt;کای کمینه بخششت مُلک جهان&lt;/p&gt;&lt;p&gt;من چه گویم چون تو می‌دانی نهان&lt;/p&gt;&lt;p&gt;ای همیشه حاجت ما را پناه&lt;/p&gt;&lt;p&gt;بار دیگر ما غلط کردیم راه&lt;/p&gt;&lt;p&gt;لیک گفتی گرچه می‌دانم سرت&lt;/p&gt;&lt;p&gt;زود هم پیدا کنش بر ظاهرت&lt;/p&gt;&lt;p&gt;چون برآورد از میان جان خروش&lt;/p&gt;&lt;p&gt;اندر آمد بحر بخشایش به جوش&lt;/p&gt;&lt;p&gt;درمیان گریه خوابش در ربود&lt;/p&gt;&lt;p&gt;دید در خواب او که پیری رو نمود&lt;/p&gt;&lt;p&gt;گفت ای شه مژده حاجاتت رواست&lt;/p&gt;&lt;p&gt;گر غریبی آیدت فردا ز ماست&lt;/p&gt;&lt;p&gt;چونکه آید او حکیمی حاذقست&lt;/p&gt;&lt;p&gt;صادقش دان کو امین و صادقست&lt;/p&gt;&lt;p&gt;در علاجش سِحر مطلق را ببین&lt;/p&gt;&lt;p&gt;در مزاجش قدرت حق را ببین&lt;/p&gt;&lt;p&gt;چون رسید آن وعده‌گاه و روز شد&lt;/p&gt;&lt;p&gt;آفتاب از شرق اخترسوز شد&lt;/p&gt;&lt;p&gt;بود اندر منظره شه منتظر&lt;/p&gt;&lt;p&gt;تا ببیند آنچه بنمودند سر&lt;/p&gt;&lt;p&gt;دید شخصی فاضلی پرمایه‌ای&lt;/p&gt;&lt;p&gt;آفتابی درمیان سایه‌ای&lt;/p&gt;&lt;p&gt;می‌رسید از دور مانند هلال&lt;/p&gt;&lt;p&gt;نیست بود و هست بر شکل خیال&lt;/p&gt;&lt;p&gt;نیست‌وش باشد خیال اندر روان&lt;/p&gt;&lt;p&gt;تو جهانی بر خیالی بین روان&lt;/p&gt;&lt;p&gt;بر خیالی صلحشان و جنگشان&lt;/p&gt;&lt;p&gt;وز خیالی فخرشان و ننگشان&lt;/p&gt;&lt;p&gt;آن خیالاتی که دام اولیاست&lt;/p&gt;&lt;p&gt;عکس مه‌رویان بستان خداست&lt;/p&gt;&lt;p&gt;آن خیالی که شه اندر خواب دید&lt;/p&gt;&lt;p&gt;در رخ مهمان همی آمد پدید&lt;/p&gt;&lt;p&gt;شه به جای حاجبان فا پیش رفت&lt;/p&gt;&lt;p&gt;پیش آن مهمان غیب خویش رفت&lt;/p&gt;&lt;p&gt;هر دو بحری آشنا آموخته&lt;/p&gt;&lt;p&gt;هر دو جان بی دوختن بر دوخته&lt;/p&gt;&lt;p&gt;گفت معشوقم تو بودستی نه آن&lt;/p&gt;&lt;p&gt;لیک کار از کار خیزد در جهان&lt;/p&gt;&lt;p&gt;ای مرا تو مصطفی من چو عمر&lt;/p&gt;&lt;p&gt;از برای خدمتت بندم کمر&lt;/p&gt;', 'مثنوی شماره ۳', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(208, '&lt;p&gt;از خدا جوییم توفیق ادب&lt;/p&gt;&lt;p&gt;بی‌ادب محروم گشت از لطف رب&lt;/p&gt;&lt;p&gt;بی‌ادب تنها نه خود را داشت بد&lt;/p&gt;&lt;p&gt;بلک آتش در همه آفاق زد&lt;/p&gt;&lt;p&gt;مایده از آسمان در می‌رسید&lt;/p&gt;&lt;p&gt;بی‌شری و بیع و بی‌گفت و شنید&lt;/p&gt;&lt;p&gt;درمیان قوم موسی چند کس&lt;/p&gt;&lt;p&gt;بی‌ادب گفتند کو سیر و عدس&lt;/p&gt;&lt;p&gt;منقطع شد خوان و نان از آسمان&lt;/p&gt;&lt;p&gt;ماند رنج زرع و بیل و داس‌مان&lt;/p&gt;&lt;p&gt;باز عیسی چون شفاعت کرد حق&lt;/p&gt;&lt;p&gt;خوان فرستاد و غنیمت بر طبق&lt;/p&gt;&lt;p&gt;مائده از آسمان شد عائده&lt;/p&gt;&lt;p&gt;چون که گفت انزل علینا مائده&lt;/p&gt;&lt;p&gt;باز گستاخان ادب بگذاشتند&lt;/p&gt;&lt;p&gt;چون گدایان زله‌ها برداشتند&lt;/p&gt;&lt;p&gt;لابه کرده عیسی ایشان را که این&lt;/p&gt;&lt;p&gt;دایمست و کم نگردد از زمین&lt;/p&gt;&lt;p&gt;بدگمانی کردن و حرص‌آوری&lt;/p&gt;&lt;p&gt;کفر باشد پیش خوان مهتری&lt;/p&gt;&lt;p&gt;زان گدارویان نادیده ز آز&lt;/p&gt;&lt;p&gt;آن در رحمت بریشان شد فراز&lt;/p&gt;&lt;p&gt;ابر بر ناید پی منع زکات&lt;/p&gt;&lt;p&gt;وز زنا افتد وبا اندر جهات&lt;/p&gt;&lt;p&gt;هر چه بر تو آید از ظلمات و غم&lt;/p&gt;&lt;p&gt;آن ز بی‌باکی و گستاخیست هم&lt;/p&gt;&lt;p&gt;هر که بی‌باکی کند در راه دوست&lt;/p&gt;&lt;p&gt;ره‌زن مردان شد و نامرد اوست&lt;/p&gt;&lt;p&gt;از ادب پرنور گشته‌ست این فلک&lt;/p&gt;&lt;p&gt;وز ادب معصوم و پاک آمد ملک&lt;/p&gt;&lt;p&gt;بد ز گستاخی کسوف آفتاب&lt;/p&gt;&lt;p&gt;شد عزازیلی ز جرات رد باب&lt;/p&gt;', 'مثنوی شماره ۴', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(209, '&lt;p&gt;دست بگشاد و کنارانش گرفت&lt;/p&gt;&lt;p&gt;همچو عشق اندر دل و جانش گرفت&lt;/p&gt;&lt;p&gt;دست و پیشانیش بوسیدن گرفت&lt;/p&gt;&lt;p&gt;وز مقام و راه پرسیدن گرفت&lt;/p&gt;&lt;p&gt;پرس‌پرسان می‌کشیدش تا به صدر&lt;/p&gt;&lt;p&gt;گفت گنجی یافتم آخر به صبر&lt;/p&gt;&lt;p&gt;گفت ای نور حَقُ و دفعِ حَرَج&lt;/p&gt;&lt;p&gt;معنی‌ِ الصبر مفتاح الفرج&lt;/p&gt;&lt;p&gt;ای لقای تو جواب هر سؤال&lt;/p&gt;&lt;p&gt;مشکل از تو حل شود بی‌قیل‌وقال&lt;/p&gt;&lt;p&gt;ترجمانی هرچه ما را در دل است&lt;/p&gt;&lt;p&gt;دستگیری هر که پایش در گل است&lt;/p&gt;&lt;p&gt;مرحبا یا مجتبی یا مرتضی&lt;/p&gt;&lt;p&gt;إن تغب، جاء القضا، ضاق الفضا&lt;/p&gt;&lt;p&gt;انت مولی‌القوم من لا یشتهی&lt;/p&gt;&lt;p&gt;قد ردی کلا لئن لم ینتهی&lt;/p&gt;&lt;p&gt;چون گذشت آن مجلس و خوان کرم&lt;/p&gt;&lt;p&gt;دست او بگرفت و برد اندر حرم&lt;/p&gt;', 'مثنوی شماره ۵', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(210, '&lt;p&gt;قصهٔ رنجور و رنجوری بخواند&lt;/p&gt;&lt;p&gt;بعد از آن در پیش رنجورش نشاند&lt;/p&gt;&lt;p&gt;رنگ روی و نبض و قاروره بدید&lt;/p&gt;&lt;p&gt;هم علاماتش هم اسبابش شنید&lt;/p&gt;&lt;p&gt;گفت هر دارو که‌ایشان کرده‌اند&lt;/p&gt;&lt;p&gt;آن عمارت نیست ویران کرده‌اند&lt;/p&gt;&lt;p&gt;بی‌خبر بودند از حال درون&lt;/p&gt;&lt;p&gt;استعیذ الله مما یفترون&lt;/p&gt;&lt;p&gt;دید رنج و کشف شد بَر وی نهفت&lt;/p&gt;&lt;p&gt;لیک پنهان کرد و با سلطان نگفت&lt;/p&gt;&lt;p&gt;رنجش از صفرا و از سودا نبود&lt;/p&gt;&lt;p&gt;بوی هر هیزم پدید آید ز دود&lt;/p&gt;&lt;p&gt;دید از زاریش کاو زار دل است&lt;/p&gt;&lt;p&gt;تن خوش است و او گرفتار دل است&lt;/p&gt;&lt;p&gt;عاشقی پیداست از زاری دل&lt;/p&gt;&lt;p&gt;نیست بیماری چو بیماری دل&lt;/p&gt;&lt;p&gt;علت عاشق ز علت‌ها جداست&lt;/p&gt;&lt;p&gt;عشق اصطرلاب اسرار خداست&lt;/p&gt;&lt;p&gt;عاشقی گر زین سر و گر زان سرست&lt;/p&gt;&lt;p&gt;عاقبت ما را بدان سر رهبرست&lt;/p&gt;&lt;p&gt;هرچه گویم عشق را شرح و بیان&lt;/p&gt;&lt;p&gt;چون به عشق آیم خجل باشم از آن&lt;/p&gt;&lt;p&gt;گرچه تفسیر زبان روشنگرست&lt;/p&gt;&lt;p&gt;لیک عشق بی‌زبان روشنترست&lt;/p&gt;&lt;p&gt;چون قلم اندر نوشتن می‌شتافت&lt;/p&gt;&lt;p&gt;چون به عشق آمد قلم بر خود شکافت&lt;/p&gt;&lt;p&gt;عقل در شرحش چو خر در گل بخفت&lt;/p&gt;&lt;p&gt;شرح عشق و عاشقی هم عشق گفت&lt;/p&gt;&lt;p&gt;آفتاب آمد دلیل آفتاب&lt;/p&gt;&lt;p&gt;گر دلیلت باید از وی رو متاب&lt;/p&gt;&lt;p&gt;از وی ار سایه نشانی می‌دهد&lt;/p&gt;&lt;p&gt;شمس هر دم نور جانی می‌دهد&lt;/p&gt;&lt;p&gt;سایه، خواب آرد تو را همچون سمر&lt;/p&gt;&lt;p&gt;چون برآید شمس انشق القمر&lt;/p&gt;&lt;p&gt;خود غریبی در جهان چون شمس نیست&lt;/p&gt;&lt;p&gt;شمس جان باقیست کاو را امس نیست&lt;/p&gt;&lt;p&gt;شمس در خارج اگر چه هست فرد&lt;/p&gt;&lt;p&gt;می‌توان هم مثل او تصویر کرد&lt;/p&gt;&lt;p&gt;شمس جان کو خارج آمد از اثیر&lt;/p&gt;&lt;p&gt;نبودش در ذهن و در خارج نظیر&lt;/p&gt;&lt;p&gt;در تصور ذات او را گُنج کو؟&lt;/p&gt;&lt;p&gt;تا درآید در تصور مثل او&lt;/p&gt;&lt;p&gt;چون حدیث روی شمس‌الدین رسید&lt;/p&gt;&lt;p&gt;شمس چارم‌آسمان سر در کشید&lt;/p&gt;&lt;p&gt;واجب آید چونکه آمد نام او&lt;/p&gt;&lt;p&gt;شرح کردن رمزی از انعام او&lt;/p&gt;&lt;p&gt;این نفس جان دامنم برتافته‌ست&lt;/p&gt;&lt;p&gt;بوی پیراهان یوسف یافته‌ست&lt;/p&gt;&lt;p&gt;کز برای حقِ صحبت سال‌ها&lt;/p&gt;&lt;p&gt;بازگو حالی از آن خوش حال‌ها&lt;/p&gt;&lt;p&gt;تا زمین و آسمان خندان شود&lt;/p&gt;&lt;p&gt;عقل و روح و دیده صدچندان شود&lt;/p&gt;&lt;p&gt;لاتکلفنی فانی فی الفنا&lt;/p&gt;&lt;p&gt;کلت افهامی فلا احصی ثنا&lt;/p&gt;&lt;p&gt;کل شیء قاله غیرالمفیق&lt;/p&gt;&lt;p&gt;ان تکلف او تصلف لا یلیق&lt;/p&gt;&lt;p&gt;من چه گویم یک رگم هشیار نیست&lt;/p&gt;&lt;p&gt;شرح آن یاری که او را یار نیست&lt;/p&gt;&lt;p&gt;شرح این هجران و این خون جگر&lt;/p&gt;&lt;p&gt;این زمان بگذار تا وقت دگر&lt;/p&gt;&lt;p&gt;قال اطعمنی فانی جائع&lt;/p&gt;&lt;p&gt;واعتجل فالوقت سیف قاطع&lt;/p&gt;&lt;p&gt;صوفی ابن‌الوقت باشد ای رفیق&lt;/p&gt;&lt;p&gt;نیست فردا گفتن از شرط طریق&lt;/p&gt;&lt;p&gt;تو مگر خود مرد صوفی نیستی؟&lt;/p&gt;&lt;p&gt;هست را از نسیه خیزد نیستی&lt;/p&gt;&lt;p&gt;گفتمش پوشیده خوش‌تر سِرِّ یار&lt;/p&gt;&lt;p&gt;خود تو در ضمن حکایت گوش‌ دار&lt;/p&gt;&lt;p&gt;خوش‌تر آن باشد که سرّ دلبران&lt;/p&gt;&lt;p&gt;گفته آید در حدیث دیگران&lt;/p&gt;&lt;p&gt;گفت مکشوف و برهنه بی‌غلول&lt;/p&gt;&lt;p&gt;بازگو، دفعم مِدِه ای بوالفضول&lt;/p&gt;&lt;p&gt;پرده بردار و برهنه گو که من&lt;/p&gt;&lt;p&gt;می‌نخسپم با صنم با پیرهن&lt;/p&gt;&lt;p&gt;گفتم ار عریان شود او در عیان&lt;/p&gt;&lt;p&gt;نه تو مانی نه کنارت نه میان&lt;/p&gt;&lt;p&gt;آرزو می‌خواه، لیک اندازه خواه&lt;/p&gt;&lt;p&gt;برنتابد کوه را یک برگ کاه&lt;/p&gt;&lt;p&gt;آفتابی کز وی این عالم فروخت&lt;/p&gt;&lt;p&gt;اندکی گر پیش آید جمله سوخت&lt;/p&gt;&lt;p&gt;فتنه و آشوب و خون‌ریزی مجوی&lt;/p&gt;&lt;p&gt;بیش ازین از شمس تبریزی مگوی&lt;/p&gt;&lt;p&gt;این ندارد آخر، از آغاز گوی&lt;/p&gt;&lt;p&gt;رو تمام این حکایت بازگوی&lt;/p&gt;', 'مثنوی شماره ۶', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(211, '&lt;p&gt;گفت ای شه خلوتی کن خانه را&lt;/p&gt;&lt;p&gt;دور کن هم خویش و هم بیگانه را&lt;/p&gt;&lt;p&gt;کس ندارد گوش در دهلیزها&lt;/p&gt;&lt;p&gt;تا بپرسم زین کنیزک چیزها&lt;/p&gt;&lt;p&gt;خانه خالی مانْد و یک دَیّار نَه&lt;/p&gt;&lt;p&gt;جز طبیب و جز همان بیمار نه&lt;/p&gt;&lt;p&gt;نرم‌نرمک گفت شهر تو کجاست؟&lt;/p&gt;&lt;p&gt;که علاج اهل هر شهری جداست&lt;/p&gt;&lt;p&gt;واندر آن شهر از قرابت کیستت؟&lt;/p&gt;&lt;p&gt;خویشی و پیوستگی با چیستت؟&lt;/p&gt;&lt;p&gt;دست بر نبضش نهاد و یک‌به‌یک&lt;/p&gt;&lt;p&gt;باز می‌پرسید از جور فلک&lt;/p&gt;&lt;p&gt;چون کسی را خار در پایش جهد&lt;/p&gt;&lt;p&gt;پای خود را بر سر زانو نهد&lt;/p&gt;&lt;p&gt;وز سر سوزن همی جوید سرش&lt;/p&gt;&lt;p&gt;ور نیابد، می‌کند با لب ترش&lt;/p&gt;&lt;p&gt;خار در پا شد چنین دشواریاب&lt;/p&gt;&lt;p&gt;خار در دل چون بود؟ وادِه جواب&lt;/p&gt;&lt;p&gt;خار در دل گر بدیدی هر خسی&lt;/p&gt;&lt;p&gt;دستْ کِی بودی غمان را بر کسی؟&lt;/p&gt;&lt;p&gt;کس به زیر دمِّ خر خاری نهد&lt;/p&gt;&lt;p&gt;خر نداند دفع آن، برمی‌جهد&lt;/p&gt;&lt;p&gt;برجهد، وان خار محکم‌تر زند&lt;/p&gt;&lt;p&gt;عاقلی باید که خاری برکند&lt;/p&gt;&lt;p&gt;خر ز بهر دفع خار از سوز و درد&lt;/p&gt;&lt;p&gt;جفته می‌انداخت، صد جا زخم کرد&lt;/p&gt;&lt;p&gt;آن حکیمِ خارچینْ استاد بود&lt;/p&gt;&lt;p&gt;دست می‌زد جابجا می‌آزمود&lt;/p&gt;&lt;p&gt;زان کنیزک بر طریق داستان&lt;/p&gt;&lt;p&gt;باز می‌پرسید حال دوستان&lt;/p&gt;&lt;p&gt;با حکیمْ او قصه‌ها می‌گفت فاش&lt;/p&gt;&lt;p&gt;از مُقام و خواجگان و شهر و باش&lt;/p&gt;&lt;p&gt;سوی قصه گفتنش می‌داشت گوش&lt;/p&gt;&lt;p&gt;سوی نبض و جَستنش می‌داشت هوش&lt;/p&gt;&lt;p&gt;تا که نبض از نام کی گردد جَهان&lt;/p&gt;&lt;p&gt;او بود مقصود جانش در جهان&lt;/p&gt;&lt;p&gt;دوستان و شهر او را برشمرد&lt;/p&gt;&lt;p&gt;بعد از آن شهری دگر را نام برد&lt;/p&gt;&lt;p&gt;گفت چون بیرون شدی از شهر خویش&lt;/p&gt;&lt;p&gt;در کدامین شهر بودستی تو بیش؟&lt;/p&gt;&lt;p&gt;نام شهری گفت و زان هم درگذشت&lt;/p&gt;&lt;p&gt;رنگ روی و نبض او دیگر نگشت&lt;/p&gt;&lt;p&gt;خواجگان و شهرها را یَک‌به‌یَک&lt;/p&gt;&lt;p&gt;باز گفت از جای و از نان و نمک&lt;/p&gt;&lt;p&gt;شهر شهر و خانه خانه قصه کرد&lt;/p&gt;&lt;p&gt;نه رگش جنبید و نه رخ گشت زرد&lt;/p&gt;&lt;p&gt;نبض او بر حال خود بُد بی‌گزند&lt;/p&gt;&lt;p&gt;تا بپرسید از سمرقند چو قند&lt;/p&gt;&lt;p&gt;نبضْ جست و رویْ سرخ و زرد شد&lt;/p&gt;&lt;p&gt;کز سمرقندیِّ زرگر فرد شد&lt;/p&gt;&lt;p&gt;چون ز رنجور آن حکیم این راز یافت&lt;/p&gt;&lt;p&gt;اصل آن درد و بلا را باز یافت&lt;/p&gt;&lt;p&gt;گفت کوی او کدام است در گُذَر&lt;/p&gt;&lt;p&gt;او سرِ پل گفت و کوی غاتِفَر&lt;/p&gt;&lt;p&gt;گفت دانستم که رنجت چیست، زود&lt;/p&gt;&lt;p&gt;در خلاصت سِحرها خواهم نمود&lt;/p&gt;&lt;p&gt;شاد باش و فارغ و آمن که من&lt;/p&gt;&lt;p&gt;آن کنم با تو که باران با چمن&lt;/p&gt;&lt;p&gt;من غم تو می‌خَورم، تو غم مخَور&lt;/p&gt;&lt;p&gt;بر تو من مشفق‌ترم از صد پدر&lt;/p&gt;&lt;p&gt;هان و هان این راز را با کس مگو&lt;/p&gt;&lt;p&gt;گرچه از تو شه کند بس جست‌وجو&lt;/p&gt;&lt;p&gt;خانهٔ اسرار تو چون دل شود&lt;/p&gt;&lt;p&gt;آن مرادت زودتر حاصل شود&lt;/p&gt;&lt;p&gt;گفت پیغامبر که هر که سِرّ نهفت&lt;/p&gt;&lt;p&gt;زود گردد با مراد خویش جفت&lt;/p&gt;&lt;p&gt;دانه چون اندر زمین پنهان شود&lt;/p&gt;&lt;p&gt;سِرِّ او سرسبزی بستان شود&lt;/p&gt;&lt;p&gt;زَرّ و نقره گر نبودندی نهان&lt;/p&gt;&lt;p&gt;پرورش کی یافتندی زیر کان&lt;/p&gt;&lt;p&gt;وعده‌ها و لطف‌های آن حکیم&lt;/p&gt;&lt;p&gt;کرد آن رنجور را آمن ز بیم&lt;/p&gt;&lt;p&gt;وعده‌ها باشد حقیقی دل‌پذیر&lt;/p&gt;&lt;p&gt;وعده‌ها باشد مجازی تاسه‌گیر&lt;/p&gt;&lt;p&gt;وعدهٔ اهل کرم گنج روان&lt;/p&gt;&lt;p&gt;وعدهٔ نااهل شد رنج روان&lt;/p&gt;', 'مثنوی شماره ۷', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(212, '&lt;p&gt;بعد از آن برخاست و عزم شاه کرد&lt;/p&gt;&lt;p&gt;شاه را زان شمّه‌ای آگاه کرد&lt;/p&gt;&lt;p&gt;گفت تدبیر آن بوَد کان مرد را&lt;/p&gt;&lt;p&gt;حاضر آریم از پی این درد را&lt;/p&gt;&lt;p&gt;مرد زرگر را بخوان زان شهر دور&lt;/p&gt;&lt;p&gt;با زر و خلعت بده او را غرور&lt;/p&gt;', 'مثنوی شماره ۸', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(213, '&lt;p&gt;شه فرستاد آن طرف یک دو رسول&lt;/p&gt;&lt;p&gt;حاذقان و کافیان بس عَدول&lt;/p&gt;&lt;p&gt;تا سمرقند آمدند آن دو امیر&lt;/p&gt;&lt;p&gt;پیش آن زرگر ز شاهنشه بَشیر&lt;/p&gt;&lt;p&gt;«کای لطیف استاد کامل معرفت&lt;/p&gt;&lt;p&gt;فاش اندر شهرها از تو صفت&lt;/p&gt;&lt;p&gt;نک فلان شه از برای زرگری&lt;/p&gt;&lt;p&gt;اختیارت کرد زیرا مهتری&lt;/p&gt;&lt;p&gt;اینک این خلعت بگیر و زرّ و سیم&lt;/p&gt;&lt;p&gt;چون بیایی خاص باشی و ندیم»&lt;/p&gt;&lt;p&gt;مرد مال و خلعت بسیار دید&lt;/p&gt;&lt;p&gt;غرّه شد از شهر و فرزندان برید.&lt;/p&gt;&lt;p&gt;اندر آمد شادمان در راه مرد&lt;/p&gt;&lt;p&gt;بی‌خبر کان شاه قصد جانْش کرد&lt;/p&gt;&lt;p&gt;اسپ تازی برنشست و شاد تاخت&lt;/p&gt;&lt;p&gt;خونبهای خویش را خلعت شناخت.&lt;/p&gt;&lt;p&gt;ای شده اندر سفر با صد رضا&lt;/p&gt;&lt;p&gt;خود به پای خویش تا سوء القضا&lt;/p&gt;&lt;p&gt;در خیالش مُلک و عِزّ و مهتری&lt;/p&gt;&lt;p&gt;گفت عزرائیل رو، آری بری&lt;/p&gt;&lt;p&gt;چون رسید از راه آن مرد غریب&lt;/p&gt;&lt;p&gt;اندر آوردش به پیش شه طبیب&lt;/p&gt;&lt;p&gt;سوی شاهنشاه بردندش بناز&lt;/p&gt;&lt;p&gt;تا بسوزد بر سر شمع طراز&lt;/p&gt;&lt;p&gt;شاه دید او را بسی تعظیم کرد&lt;/p&gt;&lt;p&gt;مخزن زر را بدو تسلیم کرد&lt;/p&gt;&lt;p&gt;پس حکیمش گفت کای سلطان مِه&lt;/p&gt;&lt;p&gt;آن کنیزک را بدین خواجه بدِه&lt;/p&gt;&lt;p&gt;تا کنیزک در وصالش خوش شود&lt;/p&gt;&lt;p&gt;آب وصلش دفع آن آتش شود&lt;/p&gt;&lt;p&gt;شه بدو بخشید آن مه روی را&lt;/p&gt;&lt;p&gt;جفت کرد آن هر دو صحبت‌جوی را&lt;/p&gt;&lt;p&gt;مدت شش ماه می‌راندند کام&lt;/p&gt;&lt;p&gt;تا به صحتْ آمد آن دختر تمام&lt;/p&gt;&lt;p&gt;بعد از آن از بهر او شربت بساخت&lt;/p&gt;&lt;p&gt;تا بخورد و پیش دختر می‌گداخت&lt;/p&gt;&lt;p&gt;چون ز رنجوری جمال او نماند&lt;/p&gt;&lt;p&gt;جان دختر در وبال او نماند&lt;/p&gt;&lt;p&gt;چونک زشت و ناخوش و رخ زرد شد&lt;/p&gt;&lt;p&gt;اندک‌اندک در دل او سرد شد.&lt;/p&gt;&lt;p&gt;عشقهایی کز پی رنگی بود&lt;/p&gt;&lt;p&gt;عشق نبود عاقبت ننگی بود&lt;/p&gt;&lt;p&gt;کاش کان هم ننگ بودی یکسری&lt;/p&gt;&lt;p&gt;تا نرفتی بر وی آن بد داوری&lt;/p&gt;&lt;p&gt;خون دوید از چشم همچون جوی او&lt;/p&gt;&lt;p&gt;دشمن جان وی آمد روی او&lt;/p&gt;&lt;p&gt;دشمن طاووس آمد پرّ او&lt;/p&gt;&lt;p&gt;ای بسی شه را بکشته فرّ او&lt;/p&gt;&lt;p&gt;گفت: «من آن آهوم کز ناف من&lt;/p&gt;&lt;p&gt;ریخت این صیاد خون صاف من،&lt;/p&gt;&lt;p&gt;ای من آن روباهِ صحرا، کز کمین&lt;/p&gt;&lt;p&gt;سر بریدندش برای پوستین،&lt;/p&gt;&lt;p&gt;ای من آن پیلی که زخم پیلبان&lt;/p&gt;&lt;p&gt;ریخت خونم از برای استخوان،&lt;/p&gt;&lt;p&gt;آنک کشتستم پی مادون من&lt;/p&gt;&lt;p&gt;می‌نداند که نخسپد خون من&lt;/p&gt;&lt;p&gt;بر منست امروز و فردا بر وی‌است&lt;/p&gt;&lt;p&gt;خون چون من کسْ، چنین ضایع کی‌است؟&lt;/p&gt;&lt;p&gt;گر چه دیوار افکند سایهٔ دراز&lt;/p&gt;&lt;p&gt;باز گردد سوی او آن سایه باز؛&lt;/p&gt;&lt;p&gt;این جهان کوهست و فعل ما ندا&lt;/p&gt;&lt;p&gt;سوی ما آید نداها را صدا»&lt;/p&gt;&lt;p&gt;این بگفت و رفت در دَم زیر خاک&lt;/p&gt;&lt;p&gt;آن کنیزک شد ز عشق‌ و رنج پاک&lt;/p&gt;&lt;p&gt;زانک عشق مردگان پاینده نیست&lt;/p&gt;&lt;p&gt;زانک مرده سوی ما آینده نیست&lt;/p&gt;&lt;p&gt;عشق زنده در روان و در بصر&lt;/p&gt;&lt;p&gt;هر دمی باشد ز غنچه تازه‌تر&lt;/p&gt;&lt;p&gt;عشق آن زنده گزین کو باقیست&lt;/p&gt;&lt;p&gt;کز شراب جان‌فزایت ساقیست&lt;/p&gt;&lt;p&gt;عشق آن بگزین که جمله انبیا&lt;/p&gt;&lt;p&gt;یافتند از عشق او کار و کیا&lt;/p&gt;&lt;p&gt;تو مگو ما را بدان شه بار نیست&lt;/p&gt;&lt;p&gt;با کریمان کارها دشوار نیست&lt;/p&gt;', 'مثنوی شماره ۹', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(214, '&lt;p&gt;کشتن آن مرد بر دست حکیم&lt;/p&gt;&lt;p&gt;نه پی اومید بود و نه ز بیم&lt;/p&gt;&lt;p&gt;او نکشتش از برای طبع شاه&lt;/p&gt;&lt;p&gt;تا نیامد امر و الهام اله&lt;/p&gt;&lt;p&gt;آن پسر را کش خضر ببرید حلق&lt;/p&gt;&lt;p&gt;سر آن را در نیابد عام خلق&lt;/p&gt;&lt;p&gt;آنک از حق یابد او وحی و جواب&lt;/p&gt;&lt;p&gt;هرچه فرماید بود عین صواب&lt;/p&gt;&lt;p&gt;آنک جان بخشد اگر بکشد رواست&lt;/p&gt;&lt;p&gt;نایبست و دست او دست خداست&lt;/p&gt;&lt;p&gt;همچو اسماعیل پیشش سر بنه&lt;/p&gt;&lt;p&gt;شاد و خندان پیش تیغش جان بده&lt;/p&gt;&lt;p&gt;تا بماند جانت خندان تا ابد&lt;/p&gt;&lt;p&gt;همچو جان پاک احمد با احد&lt;/p&gt;&lt;p&gt;عاشقان آنگه شراب جان کشند&lt;/p&gt;&lt;p&gt;که به دست خویش خوبانشان کشند&lt;/p&gt;&lt;p&gt;شاه آن خون از پی شهوت نکرد&lt;/p&gt;&lt;p&gt;تو رها کن بدگمانی و نبرد&lt;/p&gt;&lt;p&gt;تو گمان بردی که کرد آلودگی&lt;/p&gt;&lt;p&gt;در صفا غش کی هلد پالودگی&lt;/p&gt;&lt;p&gt;بهر آنست این ریاضت وین جفا&lt;/p&gt;&lt;p&gt;تا بر آرد کوره از نقره جفا&lt;/p&gt;&lt;p&gt;بهر آنست امتحان نیک و بد&lt;/p&gt;&lt;p&gt;تا بجوشد بر سر آرد زر زبد&lt;/p&gt;&lt;p&gt;گر نبودی کارش الهام اله&lt;/p&gt;&lt;p&gt;او سگی بودی دراننده نه شاه&lt;/p&gt;&lt;p&gt;پاک بود از شهوت و حرص و هوا&lt;/p&gt;&lt;p&gt;نیک کرد او لیک نیک بد نما&lt;/p&gt;&lt;p&gt;گر خضر در بحر کشتی را شکست&lt;/p&gt;&lt;p&gt;صد درستی در شکست خضر هست&lt;/p&gt;&lt;p&gt;وهم موسی با همه نور و هنر&lt;/p&gt;&lt;p&gt;شد از آن محجوب تو بی پر مپر&lt;/p&gt;&lt;p&gt;آن گل سرخست تو خونش مخوان&lt;/p&gt;&lt;p&gt;مست عقلست او تو مجنونش مخوان&lt;/p&gt;&lt;p&gt;گر بدی خون مسلمان کام او&lt;/p&gt;&lt;p&gt;کافرم گر بردمی من نام او&lt;/p&gt;&lt;p&gt;می‌بلرزد عرش از مدح شقی&lt;/p&gt;&lt;p&gt;بدگمان گردد ز مدحش متقی&lt;/p&gt;&lt;p&gt;شاه بود و شاه بس آگاه بود&lt;/p&gt;&lt;p&gt;خاص بود و خاصهٔ الله بود&lt;/p&gt;&lt;p&gt;آن کسی را کش چنین شاهی کُشد&lt;/p&gt;&lt;p&gt;سوی بخت و بهترین جاهی کِشد&lt;/p&gt;&lt;p&gt;گر ندیدی سود او در قهر او&lt;/p&gt;&lt;p&gt;کی شدی آن لطف مطلق قهرجو&lt;/p&gt;&lt;p&gt;بچه می‌لرزد از آن نیش حجام&lt;/p&gt;&lt;p&gt;مادر مشفق در آن دم شادکام&lt;/p&gt;&lt;p&gt;نیم جان بستاند و صد جان دهد&lt;/p&gt;&lt;p&gt;آنچ در وهمت نیاید آن دهد&lt;/p&gt;&lt;p&gt;تو قیاس از خویش می‌گیری ولیک&lt;/p&gt;&lt;p&gt;دور دور افتاده‌ای بنگر تو نیک&lt;/p&gt;', 'مثنوی شماره ۱۰ ', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(215, '&lt;p&gt;بود بقالی و وی را طوطیی&lt;/p&gt;&lt;p&gt;خوش‌نوایی سبز و گویا طوطیی&lt;/p&gt;&lt;p&gt;بر دکان بودی نگهبان دکان&lt;/p&gt;&lt;p&gt;نکته گفتی با همه سوداگران&lt;/p&gt;&lt;p&gt;در خطاب آدمی ناطق بدی&lt;/p&gt;&lt;p&gt;در نوای طوطیان حاذق بدی&lt;/p&gt;&lt;p&gt;خواجه روزی سوی خانه رفته بود&lt;/p&gt;&lt;p&gt;بر دکان طوطی نگهبانی نمود&lt;/p&gt;&lt;p&gt;گربه‌ای برجست ناگه بر دکان&lt;/p&gt;&lt;p&gt;بهر موشی طوطیک از بیم جان&lt;/p&gt;&lt;p&gt;جست از سوی دکان سویی گریخت&lt;/p&gt;&lt;p&gt;شیشه‌های روغن گل را بریخت&lt;/p&gt;&lt;p&gt;از سوی خانه بیامد خواجه‌اش&lt;/p&gt;&lt;p&gt;بر دکان بنشست فارغ خواجه‌وش&lt;/p&gt;&lt;p&gt;دید پر روغن دکان و جامه چرب&lt;/p&gt;&lt;p&gt;بر سرش زد گشت طوطی کل ز ضرب&lt;/p&gt;&lt;p&gt;روزکی چندی سخن کوتاه کرد&lt;/p&gt;&lt;p&gt;مرد بقال از ندامت آه کرد&lt;/p&gt;&lt;p&gt;ریش بر می‌کند و می‌گفت ای دریغ&lt;/p&gt;&lt;p&gt;کافتاب نعمتم شد زیر میغ&lt;/p&gt;&lt;p&gt;دست من بشکسته بودی آن زمان&lt;/p&gt;&lt;p&gt;که زدم من بر سر آن خوش زبان&lt;/p&gt;&lt;p&gt;هدیه‌ها می‌داد هر درویش را&lt;/p&gt;&lt;p&gt;تا بیابد نطق مرغ خویش را&lt;/p&gt;&lt;p&gt;بعد سه روز و سه شب حیران و زار&lt;/p&gt;&lt;p&gt;بر دکان بنشسته بد نومیدوار&lt;/p&gt;&lt;p&gt;می‌نمود آن مرغ را هر گون نهفت&lt;/p&gt;&lt;p&gt;تا که باشد اندر آید او بگفت&lt;/p&gt;&lt;p&gt;جولقیی سر برهنه می‌گذشت&lt;/p&gt;&lt;p&gt;با سر بی مو چو پشت طاس و طشت&lt;/p&gt;&lt;p&gt;آمد اندر گفت طوطی آن زمان&lt;/p&gt;&lt;p&gt;بانگ بر درویش زد چون عاقلان&lt;/p&gt;&lt;p&gt;کز چه ای کل با کلان آمیختی&lt;/p&gt;&lt;p&gt;تو مگر از شیشه روغن ریختی&lt;/p&gt;&lt;p&gt;از قیاسش خنده آمد خلق را&lt;/p&gt;&lt;p&gt;کو چو خود پنداشت صاحب دلق را&lt;/p&gt;&lt;p&gt;کار پاکان را قیاس از خود مگیر&lt;/p&gt;&lt;p&gt;گر چه ماند در نبشتن شیر و شیر&lt;/p&gt;&lt;p&gt;جمله عالم زین سبب گمراه شد&lt;/p&gt;&lt;p&gt;کم کسی ز ابدال حق آگاه شد&lt;/p&gt;&lt;p&gt;همسری با انبیا برداشتند&lt;/p&gt;&lt;p&gt;اولیا را همچو خود پنداشتند&lt;/p&gt;&lt;p&gt;گفته اینک ما بشر ایشان بشر&lt;/p&gt;&lt;p&gt;ما و ایشان بستهٔ خوابیم و خور&lt;/p&gt;&lt;p&gt;این ندانستند ایشان از عمی&lt;/p&gt;&lt;p&gt;هست فرقی درمیان بی‌منتهی&lt;/p&gt;&lt;p&gt;هر دو گون زنبور خوردند از محل&lt;/p&gt;&lt;p&gt;لیک شد زان نیش و زین دیگر عسل&lt;/p&gt;&lt;p&gt;هر دو گون آهو گیا خوردند و آب&lt;/p&gt;&lt;p&gt;زین یکی سرگین شد و زان مشک ناب&lt;/p&gt;&lt;p&gt;هر دو نی خوردند از یک آب‌خور&lt;/p&gt;&lt;p&gt;این یکی خالی و آن پر از شکر&lt;/p&gt;&lt;p&gt;صد هزاران این چنین اشباه بین&lt;/p&gt;&lt;p&gt;فرقشان هفتاد ساله راه بین&lt;/p&gt;&lt;p&gt;این خورد گردد پلیدی زو جدا&lt;/p&gt;&lt;p&gt;آن خورد گردد همه نور خدا&lt;/p&gt;&lt;p&gt;این خورد زاید همه بخل و حسد&lt;/p&gt;&lt;p&gt;وآن خورد زاید همه نور احد&lt;/p&gt;&lt;p&gt;این زمین پاک و آن شوره‌ست و بد&lt;/p&gt;&lt;p&gt;این فرشتهٔ پاک و آن دیوست و دد&lt;/p&gt;&lt;p&gt;هر دو صورت گر به هم ماند رواست&lt;/p&gt;&lt;p&gt;آب تلخ و آب شیرین را صفاست&lt;/p&gt;&lt;p&gt;جز که صاحب ذوق کی شناسد بیاب&lt;/p&gt;&lt;p&gt;او شناسد آب خوش از شوره آب&lt;/p&gt;&lt;p&gt;سحر را با معجزه کرده قیاس&lt;/p&gt;&lt;p&gt;هر دو را بر مکر پندارد اساس&lt;/p&gt;&lt;p&gt;ساحران موسی از استیزه را&lt;/p&gt;&lt;p&gt;برگرفته چون عصای او عصا&lt;/p&gt;&lt;p&gt;زین عصا تا آن عصا فرقیست ژرف&lt;/p&gt;&lt;p&gt;زین عمل تا آن عمل راهی شگرف&lt;/p&gt;&lt;p&gt;لعنة الله این عمل را در قفا&lt;/p&gt;&lt;p&gt;رحمة الله آن عمل را در وفا&lt;/p&gt;&lt;p&gt;کافران اندر مری بوزینه طبع&lt;/p&gt;&lt;p&gt;آفتی آمد درون سینه طبع&lt;/p&gt;&lt;p&gt;هرچه مردم می‌کند بوزینه هم&lt;/p&gt;&lt;p&gt;آن کند کز مرد بیند دم بدم&lt;/p&gt;&lt;p&gt;او گمان برده که من کردم چو او&lt;/p&gt;&lt;p&gt;فرق را کی داند آن استیزه‌رو&lt;/p&gt;&lt;p&gt;این کند از امر و او بهر ستیز&lt;/p&gt;&lt;p&gt;بر سر استیزه‌رویان خاک ریز&lt;/p&gt;&lt;p&gt;آن منافق با موافق در نماز&lt;/p&gt;&lt;p&gt;از پی استیزه آید نه نیاز&lt;/p&gt;&lt;p&gt;در نماز و روزه و حج و زکات&lt;/p&gt;&lt;p&gt;با منافق مؤمنان در برد و مات&lt;/p&gt;&lt;p&gt;مؤمنان را برد باشد عاقبت&lt;/p&gt;&lt;p&gt;بر منافق مات اندر آخرت&lt;/p&gt;&lt;p&gt;گرچه هر دو بر سر یک بازی‌اند&lt;/p&gt;&lt;p&gt;هر دو با هم مروزی و رازی‌اند&lt;/p&gt;&lt;p&gt;هر یکی سوی مقام خود رود&lt;/p&gt;&lt;p&gt;هر یکی بر وفق نام خود رود&lt;/p&gt;&lt;p&gt;مؤمنش خوانند جانش خوش شود&lt;/p&gt;&lt;p&gt;ور منافق تیز و پر آتش شود&lt;/p&gt;&lt;p&gt;نام او محبوب از ذات وی است&lt;/p&gt;&lt;p&gt;نام این مبغوض از آفات وی است&lt;/p&gt;&lt;p&gt;میم و واو و میم و نون تشریف نیست&lt;/p&gt;&lt;p&gt;لفظ مؤمن جز پی تعریف نیست&lt;/p&gt;&lt;p&gt;گر منافق خوانیش این نام دون&lt;/p&gt;&lt;p&gt;همچو کزدم می‌خلد در اندرون&lt;/p&gt;&lt;p&gt;گرنه این نام اشتقاق دوزخست&lt;/p&gt;&lt;p&gt;پس چرا در وی مذاق دوزخست&lt;/p&gt;&lt;p&gt;زشتی آن نام بد از حرف نیست&lt;/p&gt;&lt;p&gt;تلخی آن آب بحر از ظرف نیست&lt;/p&gt;&lt;p&gt;حرف ظرف آمد درو معنی چو آب&lt;/p&gt;&lt;p&gt;بحر معنی عنده ام الکتاب&lt;/p&gt;&lt;p&gt;بحر تلخ و بحر شیرین در جهان&lt;/p&gt;&lt;p&gt;در میانشان برزخ لا یبغیان&lt;/p&gt;&lt;p&gt;وانگه این هر دو ز یک اصلی روان&lt;/p&gt;&lt;p&gt;بر گذر زین هر دو رو تا اصل آن&lt;/p&gt;&lt;p&gt;زر قلب و زر نیکو در عیار&lt;/p&gt;&lt;p&gt;بی محک هرگز ندانی ز اعتبار&lt;/p&gt;&lt;p&gt;هر که را در جان خدا بنهد محک&lt;/p&gt;&lt;p&gt;هر یقین را باز داند او ز شک&lt;/p&gt;&lt;p&gt;در دهان زنده خاشاکی جهد&lt;/p&gt;&lt;p&gt;آنگه آرامد که بیرونش نهد&lt;/p&gt;&lt;p&gt;در هزاران لقمه یک خاشاک خرد&lt;/p&gt;&lt;p&gt;چون در آمد حس زنده پی ببرد&lt;/p&gt;&lt;p&gt;حس دنیا نردبان این جهان&lt;/p&gt;&lt;p&gt;حس دینی نردبان آسمان&lt;/p&gt;&lt;p&gt;صحت این حس بجویید از طبیب&lt;/p&gt;&lt;p&gt;صحت آن حس بجویید از حبیب&lt;/p&gt;&lt;p&gt;صحت این حس ز معموری تن&lt;/p&gt;&lt;p&gt;صحت آن حس ز تخریب بدن&lt;/p&gt;&lt;p&gt;راه جان مر جسم را ویران کند&lt;/p&gt;&lt;p&gt;بعد از آن ویرانی آبادان کند&lt;/p&gt;&lt;p&gt;کرد ویران خانه بهر گنج زر&lt;/p&gt;&lt;p&gt;وز همان گنجش کند معمورتر&lt;/p&gt;&lt;p&gt;آب را ببرید و جو را پاک کرد&lt;/p&gt;&lt;p&gt;بعد از آن در جو روان کرد آب خورد&lt;/p&gt;&lt;p&gt;پوست را بشکافت و پیکان را کشید&lt;/p&gt;&lt;p&gt;پوست تازه بعد از آنش بر دمید&lt;/p&gt;&lt;p&gt;قلعه ویران کرد و از کافر ستد&lt;/p&gt;&lt;p&gt;بعد از آن بر ساختش صد برج و سد&lt;/p&gt;&lt;p&gt;کار بی‌چون را که کیفیت نهد&lt;/p&gt;&lt;p&gt;اینک گفتم این ضرورت می‌دهد&lt;/p&gt;&lt;p&gt;گه چنین بنماید و گه ضد این&lt;/p&gt;&lt;p&gt;جز که حیرانی نباشد کار دین&lt;/p&gt;&lt;p&gt;نه چنان حیران که پشتش سوی اوست&lt;/p&gt;&lt;p&gt;بل چنان حیران و غرق و مست دوست&lt;/p&gt;&lt;p&gt;آن یکی را روی او شد سوی دوست&lt;/p&gt;&lt;p&gt;وان یکی را روی او خود روی اوست&lt;/p&gt;&lt;p&gt;روی هر یک می‌نگر می‌دار پاس&lt;/p&gt;&lt;p&gt;بوک گردی تو ز خدمت روشناس&lt;/p&gt;&lt;p&gt;چون بسی ابلیس آدم‌روی هست&lt;/p&gt;&lt;p&gt;پس به هر دستی نشاید داد دست&lt;/p&gt;&lt;p&gt;زانک صیاد آورد بانگ صفیر&lt;/p&gt;&lt;p&gt;تا فریبد مرغ را آن مرغ‌گیر&lt;/p&gt;&lt;p&gt;بشنود آن مرغ بانگ جنس خویش&lt;/p&gt;&lt;p&gt;از هوا آید بیابد دام و نیش&lt;/p&gt;&lt;p&gt;حرف درویشان بدزدد مرد دون&lt;/p&gt;&lt;p&gt;تا بخواند بر سلیمی زان فسون&lt;/p&gt;&lt;p&gt;کار مردان روشنی و گرمیست&lt;/p&gt;&lt;p&gt;کار دونان حیله و بی‌شرمیست&lt;/p&gt;&lt;p&gt;شیر پشمین از برای کد کنند&lt;/p&gt;&lt;p&gt;بومسیلم را لقب احمد کنند&lt;/p&gt;&lt;p&gt;بومسیلم را لقب کذاب ماند&lt;/p&gt;&lt;p&gt;مر محمد را اولو الالباب ماند&lt;/p&gt;&lt;p&gt;آن شراب حق ختامش مشک ناب&lt;/p&gt;&lt;p&gt;باده را ختمش بود گند و عذاب&lt;/p&gt;', 'مثنوی شماره ۱۱ ', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(216, '&lt;p&gt;بود شاهی در جُهودان ظلم‌ساز&lt;/p&gt;&lt;p&gt;دشمنِ عیسی و نصرانی گُداز&lt;/p&gt;&lt;p&gt;عهدِ عیسی بود و نوبتْ آنِ او&lt;/p&gt;&lt;p&gt;جانِ موسی او و موسی جانِ او&lt;/p&gt;&lt;p&gt;شاهِ اَحْوَل کرد در راهِ خدا&lt;/p&gt;&lt;p&gt;آن دو دَمسازِ خدایی را جدا&lt;/p&gt;&lt;p&gt;گفت استادْ اَحْوَلی را کَاندَر آ&lt;/p&gt;&lt;p&gt;زو بُرون آر از وِثاق آن شیشه را&lt;/p&gt;&lt;p&gt;گفت اَحوَل: زان دو شیشه من کدام&lt;/p&gt;&lt;p&gt;پیشِ تو آرَم؟ بکُن شرحِ تمام&lt;/p&gt;&lt;p&gt;گفت استاد: آن، دو شیشه نیست، رو&lt;/p&gt;&lt;p&gt;اَحوَلی بگذار و افزون‌ْبین مشو&lt;/p&gt;&lt;p&gt;گفت: ای اُستا، مرا طعنه مزن&lt;/p&gt;&lt;p&gt;گفت اُستا: زان دو، یک را دَر شکن&lt;/p&gt;&lt;p&gt;چون یکی بشکست، هر دو شد ز چشم&lt;/p&gt;&lt;p&gt;مَرد، اَحوَل گردد از مَیلان و خشم&lt;/p&gt;&lt;p&gt;شیشه یک بود و به چشمش دو نمود&lt;/p&gt;&lt;p&gt;چون شکست او شیشه را، دیگر نبود&lt;/p&gt;&lt;p&gt;خشم و شَهوَت مرد را احوَل کند&lt;/p&gt;&lt;p&gt;ز استقامت روح را مُبْدَل کند&lt;/p&gt;&lt;p&gt;چون غَرَض آمد، هنر پوشیده شد&lt;/p&gt;&lt;p&gt;صد حجاب از دل به سوی دیده شد&lt;/p&gt;&lt;p&gt;چون دهد قاضی به دل رُشْوَت قرار&lt;/p&gt;&lt;p&gt;کی شناسد ظالم از مظلومِ زار&lt;/p&gt;&lt;p&gt;شاه، از حِقْدِ جُهودانه چنان&lt;/p&gt;&lt;p&gt;گشت اَحوَل، کَالْاَمانْ یا رَب اَمان&lt;/p&gt;&lt;p&gt;صد هزاران مؤمنِ مظلوم کُشت&lt;/p&gt;&lt;p&gt;که پناهم دینِ موسی را و پُشت&lt;/p&gt;', 'مثنوی شماره ۱۲', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(217, '&lt;p&gt;او وزیری داشت گَبْر و عِشوِه دِه&lt;/p&gt;&lt;p&gt;کو بَر آبْ از مَکر بَر بَستی گِرِه&lt;/p&gt;&lt;p&gt;گفت: تَرسایان پناهِ جان کُنند&lt;/p&gt;&lt;p&gt;دینِ خْوَد را از مَلِک پنهان کنند&lt;/p&gt;&lt;p&gt;کم کُش ایشان را، که کشتنْ سود نیست&lt;/p&gt;&lt;p&gt;دینْ ندارد بویْ، مُشک و عود نیست&lt;/p&gt;&lt;p&gt;سِرّ پنهانست اندر صد غِلاف&lt;/p&gt;&lt;p&gt;ظاهرش با تُست و باطن بَر خِلاف&lt;/p&gt;&lt;p&gt;شاه گفتش: پس بگو تَدبیر چیست؟&lt;/p&gt;&lt;p&gt;چارهٔ آن مَکر و آن تَزْویر چیست؟&lt;/p&gt;&lt;p&gt;تا نمانَد در جهان نصرانیی&lt;/p&gt;&lt;p&gt;نی هُوَیدا دین و نه پنهانیی&lt;/p&gt;&lt;p&gt;گفت: ای شَه، گوش و دستم را ببُر&lt;/p&gt;&lt;p&gt;بینی‌ام بشکاف و لب در حُکمِ مُر&lt;/p&gt;&lt;p&gt;بعد از آن در زیرِ دار آور مرا&lt;/p&gt;&lt;p&gt;تا بخواهد یک شفاعت‌گر مرا&lt;/p&gt;&lt;p&gt;بر مُنادی‌گاه کُن این کارْ تو&lt;/p&gt;&lt;p&gt;بر سرِ راهی که باشد چارسو&lt;/p&gt;&lt;p&gt;آنگَهَم از خْوَد بِران تا شهرِ دور&lt;/p&gt;&lt;p&gt;تا دَر اندازَم دَریشان شَرّ و شور&lt;/p&gt;', 'مثنوی شماره ۱۳', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(218, '&lt;p&gt;پس بگویم من به سِر نصرانیم&lt;/p&gt;&lt;p&gt;ای خدای رازدان می‌دانیم&lt;/p&gt;&lt;p&gt;شاه واقف گشت از ایمان من&lt;/p&gt;&lt;p&gt;وز تعصب کرد قصد جان من&lt;/p&gt;&lt;p&gt;خواستم تا دین ز شه پنهان کنم&lt;/p&gt;&lt;p&gt;آنک دین اوست ظاهر آن کنم&lt;/p&gt;&lt;p&gt;شاه بویی برد از اسرار من&lt;/p&gt;&lt;p&gt;متهم شد پیش شه گفتار من&lt;/p&gt;&lt;p&gt;گفتْ گفتِ تو چو در نان سوزنست&lt;/p&gt;&lt;p&gt;از دل من تا دل تو روزنست&lt;/p&gt;&lt;p&gt;من از آن روزن بدیدم حال تو&lt;/p&gt;&lt;p&gt;حال تو دیدم ننوشم قال تو&lt;/p&gt;&lt;p&gt;گر نبودی جان عیسی چاره‌ام&lt;/p&gt;&lt;p&gt;او جهودانه بکردی پاره‌ام&lt;/p&gt;&lt;p&gt;بهر عیسی جان سپارم سر دهم&lt;/p&gt;&lt;p&gt;صد هزاران منتش بر خود نهم&lt;/p&gt;&lt;p&gt;جان دریغم نیست از عیسی ولیک&lt;/p&gt;&lt;p&gt;واقفم بر علم دینش نیک‌نیک&lt;/p&gt;&lt;p&gt;حیف می‌آمد مرا کان دین پاک&lt;/p&gt;&lt;p&gt;درمیان جاهلان گردد هلاک&lt;/p&gt;&lt;p&gt;شکر ایزد را و عیسی را که ما&lt;/p&gt;&lt;p&gt;گشته‌ایم آن کیش حق را ره‌نما&lt;/p&gt;&lt;p&gt;از جهود و از جهودی رسته‌ایم&lt;/p&gt;&lt;p&gt;تا به زناری میان را بسته‌ایم&lt;/p&gt;&lt;p&gt;دور دور عیسیست ای مردمان&lt;/p&gt;&lt;p&gt;بشنوید اسرار کیش او بجان&lt;/p&gt;&lt;p&gt;کرد با وی شاه آن کاری که گفت&lt;/p&gt;&lt;p&gt;خلق حیران مانده زان مکر نهفت&lt;/p&gt;&lt;p&gt;راند او را جانب نصرانیان&lt;/p&gt;&lt;p&gt;کرد در دعوت شروع او بعد از آن&lt;/p&gt;', 'مثنوی شماره ۱۴ ', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(219, '&lt;p&gt;صد هزاران مرد ترسا سوی او&lt;/p&gt;&lt;p&gt;اندک‌اندک جمع شد در کوی او&lt;/p&gt;&lt;p&gt;او بیان می‌کرد با ایشان براز&lt;/p&gt;&lt;p&gt;سر انگلیون و زنار و نماز&lt;/p&gt;&lt;p&gt;او به ظاهر واعظ احکام بود&lt;/p&gt;&lt;p&gt;لیک در باطن صفیر و دام بود&lt;/p&gt;&lt;p&gt;بهر این بعضی صحابه از رسول&lt;/p&gt;&lt;p&gt;ملتمس بودند مکر نفس غول&lt;/p&gt;&lt;p&gt;کو چه آمیزد ز اغراض نهان&lt;/p&gt;&lt;p&gt;در عبادتها و در اخلاص جان&lt;/p&gt;&lt;p&gt;فضل طاعت را نجستندی ازو&lt;/p&gt;&lt;p&gt;عیب ظاهر را بجستندی که کو&lt;/p&gt;&lt;p&gt;مو به مو و ذره ذره مکر نفس&lt;/p&gt;&lt;p&gt;می‌شناسیدند چون گل از کرفس&lt;/p&gt;', 'مثنوی شماره ۱۵', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(220, '&lt;p&gt;دل بدو دادند ترسایان تمام&lt;/p&gt;&lt;p&gt;خود چه باشد قوت تقلید عام&lt;/p&gt;&lt;p&gt;در درون سینه مهرش کاشتند&lt;/p&gt;&lt;p&gt;نایب عیسیش می‌پنداشتند&lt;/p&gt;&lt;p&gt;او به سِر دجال یک چشم لعین&lt;/p&gt;&lt;p&gt;ای خدا فریاد رس نعم المعین&lt;/p&gt;&lt;p&gt;صد هزاران دام و دانه‌ست ای خدا&lt;/p&gt;&lt;p&gt;ما چو مرغان حریص بی‌نوا&lt;/p&gt;&lt;p&gt;دم بدم ما بستهٔ دام نویم&lt;/p&gt;&lt;p&gt;هر یکی گر باز و سیمرغی شویم&lt;/p&gt;&lt;p&gt;می‌رهانی هر دمی ما را و باز&lt;/p&gt;&lt;p&gt;سوی دامی می‌رویم ای بی‌نیاز&lt;/p&gt;&lt;p&gt;ما درین انبار گندم می‌کنیم&lt;/p&gt;&lt;p&gt;گندم جمع آمده گم می‌کنیم&lt;/p&gt;&lt;p&gt;می‌نیندیشیم آخر ما بهوش&lt;/p&gt;&lt;p&gt;کین خلل در گندمست از مکر موش&lt;/p&gt;&lt;p&gt;موش تا انبار ما حفره زدست&lt;/p&gt;&lt;p&gt;و از فنش انبار ما ویران شدست&lt;/p&gt;&lt;p&gt;اول ای جان دفع شر موش کن&lt;/p&gt;&lt;p&gt;وانگهان در جمع گندم جوش کن&lt;/p&gt;&lt;p&gt;بشنو از اخبار آن صدر الصدور&lt;/p&gt;&lt;p&gt;لا صلوة تم الا بالحضور&lt;/p&gt;&lt;p&gt;گر نه موشی دزد در انبار ماست&lt;/p&gt;&lt;p&gt;گندم اعمال چل ساله کجاست&lt;/p&gt;&lt;p&gt;ریزه‌ریزه صدق هر روزه چرا&lt;/p&gt;&lt;p&gt;جمع می‌ناید درین انبار ما&lt;/p&gt;&lt;p&gt;بس ستارهٔ آتش از آهن جهید&lt;/p&gt;&lt;p&gt;وان دل سوزیده پذرفت و کشید&lt;/p&gt;&lt;p&gt;لیک در ظلمت یکی دزدی نهان&lt;/p&gt;&lt;p&gt;می‌نهد انگشت بر استارگان&lt;/p&gt;&lt;p&gt;می‌کشد استارگان را یک به یک&lt;/p&gt;&lt;p&gt;تا که نفروزد چراغی از فلک&lt;/p&gt;&lt;p&gt;گر هزاران دام باشد در قدم&lt;/p&gt;&lt;p&gt;چون تو با مایی نباشد هیچ غم&lt;/p&gt;&lt;p&gt;چون عنایاتت بود با ما مقیم&lt;/p&gt;&lt;p&gt;کی بود بیمی از آن دزد لئیم&lt;/p&gt;&lt;p&gt;هر شبی از دام تن ارواح را&lt;/p&gt;&lt;p&gt;می‌رهانی می‌کنی الواح را&lt;/p&gt;&lt;p&gt;می‌رهند ارواح هر شب زین قفس&lt;/p&gt;&lt;p&gt;فارغان نه حاکم و محکوم کس&lt;/p&gt;&lt;p&gt;شب ز زندان بی‌خبر زندانیان&lt;/p&gt;&lt;p&gt;شب ز دولت بی‌خبر سلطانیان&lt;/p&gt;&lt;p&gt;نه غم و اندیشهٔ سود و زیان&lt;/p&gt;&lt;p&gt;نه خیال این فلان و آن فلان&lt;/p&gt;&lt;p&gt;حال عارف این بود بی‌خواب هم&lt;/p&gt;&lt;p&gt;گفت ایزد هم رقود زین مرم&lt;/p&gt;&lt;p&gt;خفته از احوال دنیا روز و شب&lt;/p&gt;&lt;p&gt;چون قلم در پنجهٔ تقلیب رب&lt;/p&gt;&lt;p&gt;آنک او پنجه نبیند در رقم&lt;/p&gt;&lt;p&gt;فعل پندارد بجنبش از قلم&lt;/p&gt;&lt;p&gt;شمه‌ای زین حال عارف وا نمود&lt;/p&gt;&lt;p&gt;عقل را هم خواب حسی در ربود&lt;/p&gt;&lt;p&gt;رفته در صحرای بی‌چون جانشان&lt;/p&gt;&lt;p&gt;روحشان آسوده و ابدانشان&lt;/p&gt;&lt;p&gt;وز صفیری باز دام اندر کشی&lt;/p&gt;&lt;p&gt;جمله را در داد و در داور کشی&lt;/p&gt;&lt;p&gt;چونک نور صبحدم سر بر زند&lt;/p&gt;&lt;p&gt;کرکس زرین گردون پر زند&lt;/p&gt;&lt;p&gt;فالق الاصباح اسرافیل‌وار&lt;/p&gt;&lt;p&gt;جمله را در صورت آرد زان دیار&lt;/p&gt;&lt;p&gt;روحهای منبسط را تن کند&lt;/p&gt;&lt;p&gt;هر تنی را باز آبستن کند&lt;/p&gt;&lt;p&gt;اسپ جانها را کند عاری ز زین&lt;/p&gt;&lt;p&gt;سر النوم اخ الموتست این&lt;/p&gt;&lt;p&gt;لیک بهر آنک روز آیند باز&lt;/p&gt;&lt;p&gt;بر نهد بر پایشان بند دراز&lt;/p&gt;&lt;p&gt;تا که روزش واکشد زان مرغزار&lt;/p&gt;&lt;p&gt;وز چراگاه آردش در زیر بار&lt;/p&gt;&lt;p&gt;کاش چون اصحاب کهف این روح را&lt;/p&gt;&lt;p&gt;حفظ کردی یا چو کشتی نوح را&lt;/p&gt;&lt;p&gt;تا ازین طوفان بیداری و هوش&lt;/p&gt;&lt;p&gt;وا رهیدی این ضمیر و چشم و گوش&lt;/p&gt;&lt;p&gt;ای بسی اصحاب کهف اندر جهان&lt;/p&gt;&lt;p&gt;پهلوی تو پیش تو هست این زمان&lt;/p&gt;&lt;p&gt;یار با او غار با او در سرود&lt;/p&gt;&lt;p&gt;مهر بر چشمست و بر گوشت چه سود&lt;/p&gt;', 'مثنوی شماره ۱۶', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(221, '&lt;p&gt;گفت لیلی را خلیفه، کان توئی؟&lt;/p&gt;&lt;p&gt;کز تو مجنون شد پریشان و غوی؟&lt;/p&gt;&lt;p&gt;از دگر خوبان تو افزون نیستی!&lt;/p&gt;&lt;p&gt;گفت خامش، چون تو مجنون نیستی&lt;/p&gt;&lt;p&gt;هر که بیدارست او در خواب‌تر&lt;/p&gt;&lt;p&gt;هست بیداریش از خوابش بتر&lt;/p&gt;&lt;p&gt;چون بحق بیدار نبود جان ما&lt;/p&gt;&lt;p&gt;هست بیداری چو در بندان ما&lt;/p&gt;&lt;p&gt;جان همه روز از لگدکوب خیال&lt;/p&gt;&lt;p&gt;وز زیان و سود وز خوف زوال&lt;/p&gt;&lt;p&gt;نی صفا می‌ماندش نی لطف و فر&lt;/p&gt;&lt;p&gt;نی بسوی آسمان راه سفر&lt;/p&gt;&lt;p&gt;خفته آن باشد که او از هر خیال&lt;/p&gt;&lt;p&gt;دارد اومید و کند با او مقال&lt;/p&gt;&lt;p&gt;دیو را چون حور بیند او به خواب&lt;/p&gt;&lt;p&gt;پس ز شهوت ریزد او با دیو آب&lt;/p&gt;&lt;p&gt;چونک تخم نسل را در شوره ریخت&lt;/p&gt;&lt;p&gt;او به خویش آمد خیال از وی گریخت&lt;/p&gt;&lt;p&gt;ضعف سر بیند از آن و تن پلید&lt;/p&gt;&lt;p&gt;آه از آن نقش پدید ناپدید&lt;/p&gt;&lt;p&gt;مرغ بر بالا و زیر آن سایه‌اش&lt;/p&gt;&lt;p&gt;می‌دود بر خاک پران مرغ‌وش&lt;/p&gt;&lt;p&gt;ابلهی صیاد آن سایه شود&lt;/p&gt;&lt;p&gt;می‌دود چندانکه بی‌مایه شود&lt;/p&gt;&lt;p&gt;بی‌خبر کان عکس آن مرغ هواست&lt;/p&gt;&lt;p&gt;بی‌خبر که اصل آن سایه کجاست&lt;/p&gt;&lt;p&gt;تیر اندازد به سوی سایه او&lt;/p&gt;&lt;p&gt;ترکشش خالی شود از جست و جو&lt;/p&gt;&lt;p&gt;ترکش عمرش تهی شد عمر رفت&lt;/p&gt;&lt;p&gt;از دویدن در شکار سایه تفت&lt;/p&gt;&lt;p&gt;سایهٔ یزدان چو باشد دایه‌اش&lt;/p&gt;&lt;p&gt;وا رهاند از خیال و سایه‌اش&lt;/p&gt;&lt;p&gt;سایهٔ یزدان بود بندهٔ خدا&lt;/p&gt;&lt;p&gt;مرده او زین عالم و زندهٔ خدا&lt;/p&gt;&lt;p&gt;دامن او گیر زوتر بی‌گمان&lt;/p&gt;&lt;p&gt;تا رهی در دامن آخر زمان&lt;/p&gt;&lt;p&gt;کیف مد الظل نقش اولیاست&lt;/p&gt;&lt;p&gt;کو دلیل نور خورشید خداست&lt;/p&gt;&lt;p&gt;اندرین وادی مرو بی این دلیل&lt;/p&gt;&lt;p&gt;لا احب افلین گو چون خلیل&lt;/p&gt;&lt;p&gt;رو ز سایه آفتابی را بیاب&lt;/p&gt;&lt;p&gt;دامن شه شمس تبریزی بتاب&lt;/p&gt;&lt;p&gt;ره ندانی جانب این سور و عرس&lt;/p&gt;&lt;p&gt;از ضیاء الحق حسام الدین بپرس&lt;/p&gt;&lt;p&gt;ور حسد گیرد ترا در ره گلو&lt;/p&gt;&lt;p&gt;در حسد ابلیس را باشد غلو&lt;/p&gt;&lt;p&gt;کو ز آدم ننگ دارد از حسد&lt;/p&gt;&lt;p&gt;با سعادت جنگ دارد از حسد&lt;/p&gt;&lt;p&gt;عقبه‌ای زین صعب‌تر در راه نیست&lt;/p&gt;&lt;p&gt;ای خنک آنکش حسد همراه نیست&lt;/p&gt;&lt;p&gt;این جسد خانهٔ حسد آمد بدان&lt;/p&gt;&lt;p&gt;از حسد آلوده باشد خاندان&lt;/p&gt;&lt;p&gt;گر جسد خانهٔ حسد باشد ولیک&lt;/p&gt;&lt;p&gt;آن جسد را پاک کرد الله نیک&lt;/p&gt;&lt;p&gt;طهرا بیتی بیان پاکیست&lt;/p&gt;&lt;p&gt;گنج نورست ار طلسمش خاکیست&lt;/p&gt;&lt;p&gt;چون کنی بر بی‌حسد مکر و حسد&lt;/p&gt;&lt;p&gt;زان حسد دل را سیاهیها رسد&lt;/p&gt;&lt;p&gt;خاک شو مردان حق را زیر پا&lt;/p&gt;&lt;p&gt;خاک بر سر کن حسد را همچو ما&lt;/p&gt;', 'مثنوی شماره ۱۷', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(222, '&lt;p&gt;آن وزیرک از حسد بودش نژاد&lt;/p&gt;&lt;p&gt;تا به باطل گوش و بینی باد داد&lt;/p&gt;&lt;p&gt;بر امید آنک از نیش حسد&lt;/p&gt;&lt;p&gt;زهر او در جان مسکینان رسد&lt;/p&gt;&lt;p&gt;هر کسی کو از حسد بینی کند&lt;/p&gt;&lt;p&gt;خویش را بی‌گوش و بی بینی کند&lt;/p&gt;&lt;p&gt;بینی آن باشد که او بویی برد&lt;/p&gt;&lt;p&gt;بوی او را جانب کویی برد&lt;/p&gt;&lt;p&gt;هر که بویش نیست بی بینی بود&lt;/p&gt;&lt;p&gt;بوی آن بویست کان دینی بود&lt;/p&gt;&lt;p&gt;چونک بویی برد و شکر آن نکرد&lt;/p&gt;&lt;p&gt;کفر نعمت آمد و بینیش خورد&lt;/p&gt;&lt;p&gt;شکر کن مر شاکران را بنده باش&lt;/p&gt;&lt;p&gt;پیش ایشان مرده شو پاینده باش&lt;/p&gt;&lt;p&gt;چون وزیر از ره‌زنی مایه مساز&lt;/p&gt;&lt;p&gt;خلق را تو بر میاور از نماز&lt;/p&gt;&lt;p&gt;ناصح دین گشته آن کافر وزیر&lt;/p&gt;&lt;p&gt;کرده او از مکر در گوزینه سیر&lt;/p&gt;', 'مثنوی شماره ۱۸ ', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(223, '&lt;p&gt;هر که صاحب ذوق بود از گفت او&lt;/p&gt;&lt;p&gt;لذتی می‌دید و تلخی جفت او&lt;/p&gt;&lt;p&gt;نکته‌ها می‌گفت او آمیخته&lt;/p&gt;&lt;p&gt;در جلاب قند زهری ریخته&lt;/p&gt;&lt;p&gt;ظاهرش می‌گفت در ره چست شو&lt;/p&gt;&lt;p&gt;وز اثر می‌گفت جان را سست شو&lt;/p&gt;&lt;p&gt;ظاهر نقره گر اسپیدست و نو&lt;/p&gt;&lt;p&gt;دست و جامه می سیه گردد ازو&lt;/p&gt;&lt;p&gt;آتش ار چه سرخ رویست از شرر&lt;/p&gt;&lt;p&gt;تو ز فعل او سیه کاری نگر&lt;/p&gt;&lt;p&gt;برق اگر نوری نماید در نظر&lt;/p&gt;&lt;p&gt;لیک هست از خاصیت دزد بصر&lt;/p&gt;&lt;p&gt;هر که جز آگاه و صاحب ذوق بود&lt;/p&gt;&lt;p&gt;گفت او در گردن او طوق بود&lt;/p&gt;&lt;p&gt;مدتی شش سال در هجران شاه&lt;/p&gt;&lt;p&gt;شد وزیر اتباع عیسی را پناه&lt;/p&gt;&lt;p&gt;دین و دل را کل بدو بسپرد خلق&lt;/p&gt;&lt;p&gt;پیش امر و حکم او می‌مرد خلق&lt;/p&gt;', 'مثنوی شماره ۱۹ ', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(224, '&lt;p&gt;در میان شاه و او پیغامها&lt;/p&gt;&lt;p&gt;شاه را پنهان بدو آرامها&lt;/p&gt;&lt;p&gt;آخر الامر از برای آن مراد&lt;/p&gt;&lt;p&gt;تا دهد چون خاک ایشان را به باد&lt;/p&gt;&lt;p&gt;پیش او بنوشت شه کای مقبلم&lt;/p&gt;&lt;p&gt;وقت آمد زود فارغ کن دلم&lt;/p&gt;&lt;p&gt;گفت اینک اندر آن کارم شها&lt;/p&gt;&lt;p&gt;کافکنم در دین عیسی فتنه‌ها&lt;/p&gt;', 'مثنوی شماره ۲۰', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(225, '&lt;p&gt;قوم عیسی را بد اندر دار و گیر&lt;/p&gt;&lt;p&gt;حاکمانشان ده امیر و دو امیر&lt;/p&gt;&lt;p&gt;هر فریقی مر امیری را تبع&lt;/p&gt;&lt;p&gt;بنده گشته میر خود را از طمع&lt;/p&gt;&lt;p&gt;این ده و این دو امیر و قومشان&lt;/p&gt;&lt;p&gt;گشته بند آن وزیر بد نشان&lt;/p&gt;&lt;p&gt;اعتماد جمله بر گفتار او&lt;/p&gt;&lt;p&gt;اقتدای جمله بر رفتار او&lt;/p&gt;&lt;p&gt;پیش او در وقت و ساعت هر امیر&lt;/p&gt;&lt;p&gt;جان بدادی گر بدو گفتی بمیر&lt;/p&gt;', 'مثنوی شماره ۲۱', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(226, '&lt;p&gt;ساخت طوماری به نام هر یکی&lt;/p&gt;&lt;p&gt;نقش هر طومار دیگر مسلکی&lt;/p&gt;&lt;p&gt;حکمهای هر یکی نوعی دگر&lt;/p&gt;&lt;p&gt;این خلاف آن ز پایان تا به سر&lt;/p&gt;&lt;p&gt;در یکی راه ریاضت را و جوع&lt;/p&gt;&lt;p&gt;رکن توبه کرده و شرط رجوع&lt;/p&gt;&lt;p&gt;در یکی گفته ریاضت سود نیست&lt;/p&gt;&lt;p&gt;اندرین ره مَخلَصی جز جود نیست&lt;/p&gt;&lt;p&gt;در یکی گفته که جوع و جود تو&lt;/p&gt;&lt;p&gt;شرک باشد از تو با معبود تو&lt;/p&gt;&lt;p&gt;جز توکل جز که تسلیم تمام&lt;/p&gt;&lt;p&gt;در غم و راحت همه مکرست و دام&lt;/p&gt;&lt;p&gt;در یکی گفته که واجب خدمتست&lt;/p&gt;&lt;p&gt;ور نه اندیشهٔ توکل تهمتست&lt;/p&gt;&lt;p&gt;در یکی گفته که امر و نهیهاست&lt;/p&gt;&lt;p&gt;بهر کردن نیست شرح عجز ماست&lt;/p&gt;&lt;p&gt;تا که عجز خود بینیم اندر آن&lt;/p&gt;&lt;p&gt;قدرت او را بدانیم آن زمان&lt;/p&gt;&lt;p&gt;در یکی گفته که عجز خود مبین&lt;/p&gt;&lt;p&gt;کفر نعمت کردنست آن عجز هین&lt;/p&gt;&lt;p&gt;قدرت خود بین که این قدرت ازوست&lt;/p&gt;&lt;p&gt;قدرت تو نعمت او دان که هوست&lt;/p&gt;&lt;p&gt;در یکی گفته کزین دو بر گذر&lt;/p&gt;&lt;p&gt;بت بود هر چه بگنجد در نظر&lt;/p&gt;&lt;p&gt;در یکی گفته مکش این شمع را&lt;/p&gt;&lt;p&gt;کین نظر چون شمع آمد جمع را&lt;/p&gt;&lt;p&gt;از نظر چون بگذری و از خیال&lt;/p&gt;&lt;p&gt;کشته باشی نیم شب شمع وصال&lt;/p&gt;&lt;p&gt;در یکی گفته بکش باکی مدار&lt;/p&gt;&lt;p&gt;تا عوض بینی نظر را صد هزار&lt;/p&gt;&lt;p&gt;که ز کشتن شمع جان افزون شود&lt;/p&gt;&lt;p&gt;لیلی‌ات از صبر تو مجنون شود&lt;/p&gt;&lt;p&gt;ترک دنیا هر که کرد از زهد خویش&lt;/p&gt;&lt;p&gt;بیش آید پیش او دنیا و بیش&lt;/p&gt;&lt;p&gt;در یکی گفته که آنچت داد حق&lt;/p&gt;&lt;p&gt;بر تو شیرین کرد در ایجاد حق&lt;/p&gt;&lt;p&gt;بر تو آسان کرد و خوش آن را بگیر&lt;/p&gt;&lt;p&gt;خویشتن را در میفکن در زحیر&lt;/p&gt;&lt;p&gt;در یکی گفته که بگذار آن خود&lt;/p&gt;&lt;p&gt;کان قبول طبع تو ردست و بد&lt;/p&gt;&lt;p&gt;راههای مختلف آسان شدست&lt;/p&gt;&lt;p&gt;هر یکی را ملتی چون جان شدست&lt;/p&gt;&lt;p&gt;گر میسر کردن حق ره بدی&lt;/p&gt;&lt;p&gt;هر جهود و گبر ازو آگه بدی&lt;/p&gt;&lt;p&gt;در یکی گفته میسر آن بود&lt;/p&gt;&lt;p&gt;که حیات دل غذای جان بود&lt;/p&gt;&lt;p&gt;هر چه ذوق طبع باشد چون گذشت&lt;/p&gt;&lt;p&gt;بر نه آرد همچو شوره ریع و کشت&lt;/p&gt;&lt;p&gt;جز پشیمانی نباشد ریع او&lt;/p&gt;&lt;p&gt;جز خسارت پیش نارد بیع او&lt;/p&gt;&lt;p&gt;آن میسر نبود اندر عاقبت&lt;/p&gt;&lt;p&gt;نام او باشد معسر عاقبت&lt;/p&gt;&lt;p&gt;تو معسر از میسر بازدان&lt;/p&gt;&lt;p&gt;عاقبت بنگر جمال این و آن&lt;/p&gt;&lt;p&gt;در یکی گفته که استادی طلب&lt;/p&gt;&lt;p&gt;عاقبت‌بینی نیابی در حسب&lt;/p&gt;&lt;p&gt;عاقبت دیدند هر گون ملتی&lt;/p&gt;&lt;p&gt;لاجرم گشتند اسیر زلتی&lt;/p&gt;&lt;p&gt;عاقبت دیدن نباشد دست‌باف&lt;/p&gt;&lt;p&gt;ورنه کی بودی ز دینها اختلاف&lt;/p&gt;&lt;p&gt;در یکی گفته که استا هم توی&lt;/p&gt;&lt;p&gt;زانک استا را شناسا هم توی&lt;/p&gt;&lt;p&gt;مرد باش و سخرهٔ مردان مشو&lt;/p&gt;&lt;p&gt;رو سر خود گیر و سرگردان مشو&lt;/p&gt;&lt;p&gt;در یکی گفته که این جمله یکیست&lt;/p&gt;&lt;p&gt;هر که او دو بیند احول مردکیست&lt;/p&gt;&lt;p&gt;در یکی گفته که صد یک چون بود&lt;/p&gt;&lt;p&gt;این کی اندیشد مگر مجنون بود&lt;/p&gt;&lt;p&gt;هر یکی قولیست ضد هم‌دگر&lt;/p&gt;&lt;p&gt;چون یکی باشد یکی زهر و شکر&lt;/p&gt;&lt;p&gt;تا ز زهر و از شکر در نگذری&lt;/p&gt;&lt;p&gt;کی تو از گلزار وحدت بو بری&lt;/p&gt;&lt;p&gt;این نمط وین نوع ده طومار و دو&lt;/p&gt;&lt;p&gt;بر نوشت آن دین عیسی را عدو&lt;/p&gt;', 'مثنوی شماره ۲۲', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(227, '&lt;p&gt;او ز یک رنگی عیسی بو نداشت&lt;/p&gt;&lt;p&gt;وز مزاج خم عیسی خو نداشت&lt;/p&gt;&lt;p&gt;جامهٔ صد رنگ از آن خم صفا&lt;/p&gt;&lt;p&gt;ساده و یک‌رنگ گشتی چون صبا&lt;/p&gt;&lt;p&gt;نیست یک‌رنگی کزو خیزد ملال&lt;/p&gt;&lt;p&gt;بل مثال ماهی و آب زلال&lt;/p&gt;&lt;p&gt;گرچه در خشکی هزاران رنگهاست&lt;/p&gt;&lt;p&gt;ماهیان را با یبوست جنگهاست&lt;/p&gt;&lt;p&gt;کیست ماهی چیست دریا در مثل&lt;/p&gt;&lt;p&gt;تا بدان ماند ملک عز و جل&lt;/p&gt;&lt;p&gt;صد هزاران بحر و ماهی در وجود&lt;/p&gt;&lt;p&gt;سجده آرد پیش آن اکرام و جود&lt;/p&gt;&lt;p&gt;چند باران عطا باران شده&lt;/p&gt;&lt;p&gt;تا بدان آن بحر در افشان شده&lt;/p&gt;&lt;p&gt;چند خورشید کرم افروخته&lt;/p&gt;&lt;p&gt;تا که ابر و بحر جود آموخته&lt;/p&gt;&lt;p&gt;پرتو دانش زده بر خاک و طین&lt;/p&gt;&lt;p&gt;تا که شد دانه پذیرنده زمین&lt;/p&gt;&lt;p&gt;خاک امین و هر چه در وی کاشتی&lt;/p&gt;&lt;p&gt;بی‌خیانت جنس آن برداشتی&lt;/p&gt;&lt;p&gt;این امانت زان امانت یافتست&lt;/p&gt;&lt;p&gt;کآفتاب عدل بر وی تافتست&lt;/p&gt;&lt;p&gt;تا نشان حق نیارد نوبهار&lt;/p&gt;&lt;p&gt;خاک سرها را نکرده آشکار&lt;/p&gt;&lt;p&gt;آن جوادی که جمادی را بداد&lt;/p&gt;&lt;p&gt;این خبرها وین امانت وین سداد&lt;/p&gt;&lt;p&gt;مر جمادی را کند فضلش خبیر&lt;/p&gt;&lt;p&gt;عاقلان را کرده قهر او ضریر&lt;/p&gt;&lt;p&gt;جان و دل را طاقت آن جوش نیست&lt;/p&gt;&lt;p&gt;با که گویم در جهان یک گوش نیست&lt;/p&gt;&lt;p&gt;هر کجا گوشی بد از وی چشم گشت&lt;/p&gt;&lt;p&gt;هر کجا سنگی بد از وی یشم گشت&lt;/p&gt;&lt;p&gt;کیمیاسازست چه بود کیمیا&lt;/p&gt;&lt;p&gt;معجزه بخش است چه بود سیمیا&lt;/p&gt;&lt;p&gt;این ثنا گفتن ز من ترک ثناست&lt;/p&gt;&lt;p&gt;کین دلیل هستی و هستی خطاست&lt;/p&gt;&lt;p&gt;پیش هست او بباید نیست بود&lt;/p&gt;&lt;p&gt;چیست هستی پیش او کور و کبود&lt;/p&gt;&lt;p&gt;گر نبودی کور زو بگداختی&lt;/p&gt;&lt;p&gt;گرمی خورشید را بشناختی&lt;/p&gt;&lt;p&gt;ور نبودی او کبود از تعزیت&lt;/p&gt;&lt;p&gt;کی فسردی همچو یخ این ناحیت&lt;/p&gt;', 'مثنوی شماره ۲۳', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(228, '&lt;p&gt;همچو شه نادان و غافل بد وزیر&lt;/p&gt;&lt;p&gt;پنجه می‌زد با قدیم ناگزیر&lt;/p&gt;&lt;p&gt;با چنان قادر خدایی کز عدم&lt;/p&gt;&lt;p&gt;صد چو عالم هست گرداند بدم&lt;/p&gt;&lt;p&gt;صد چو عالم در نظر پیدا کند&lt;/p&gt;&lt;p&gt;چونک چشمت را به خود بینا کند&lt;/p&gt;&lt;p&gt;گر جهان پیشت بزرگ و بی‌بنیست&lt;/p&gt;&lt;p&gt;پیش قدرت ذره‌ای می‌دان که نیست&lt;/p&gt;&lt;p&gt;این جهان خود حبس جانهای شماست&lt;/p&gt;&lt;p&gt;هین روید آن سو که صحرای شماست&lt;/p&gt;&lt;p&gt;این جهان محدود و آن خود بی‌حدست&lt;/p&gt;&lt;p&gt;نقش و صورت پیش آن معنی سدست&lt;/p&gt;&lt;p&gt;صد هزاران نیزهٔ فرعون را&lt;/p&gt;&lt;p&gt;در شکست از&amp;nbsp;موسیئ&amp;nbsp;با یک عصا&lt;/p&gt;&lt;p&gt;صد هزاران طب جالینوس بود&lt;/p&gt;&lt;p&gt;پیش عیسی و دمش افسوس بود&lt;/p&gt;&lt;p&gt;صد هزاران دفتر اشعار بود&lt;/p&gt;&lt;p&gt;پیش حرف امیی‌اش عار بود&lt;/p&gt;&lt;p&gt;با چنین غالب خداوندی کسی&lt;/p&gt;&lt;p&gt;چون نمیرد گر نباشد او خسی&lt;/p&gt;&lt;p&gt;بس دل چون کوه را انگیخت او&lt;/p&gt;&lt;p&gt;مرغ زیرک با دو پا آویخت او&lt;/p&gt;&lt;p&gt;فهم و خاطر تیز کردن نیست راه&lt;/p&gt;&lt;p&gt;جز شکسته می‌نگیرد فضل شاه&lt;/p&gt;&lt;p&gt;ای بسا گنج آگنان کنج‌کاو&lt;/p&gt;&lt;p&gt;کان خیال‌اندیش را شد ریش گاو&lt;/p&gt;&lt;p&gt;گاو که بود تا تو ریش او شوی&lt;/p&gt;&lt;p&gt;خاک چه بود تا حشیش او شوی&lt;/p&gt;&lt;p&gt;چون زنی از کار بد شد روی زرد&lt;/p&gt;&lt;p&gt;مسخ کرد او را خدا و زهره کرد&lt;/p&gt;&lt;p&gt;عورتی را زهره کردن مسخ بود&lt;/p&gt;&lt;p&gt;خاک و گل گشتن نه مسخست ای عنود&lt;/p&gt;&lt;p&gt;روح می‌بردت سوی چرخ برین&lt;/p&gt;&lt;p&gt;سوی آب و گل شدی در اسفلین&lt;/p&gt;&lt;p&gt;خویشتن را مسخ کردی زین سفول&lt;/p&gt;&lt;p&gt;زان وجودی که بد آن رشک عقول&lt;/p&gt;&lt;p&gt;پس ببین کین مسخ کردن چون بود&lt;/p&gt;&lt;p&gt;پیش آن مسخ این به غایت دون بود&lt;/p&gt;&lt;p&gt;اسپ همت سوی اختر تاختی&lt;/p&gt;&lt;p&gt;آدم مسجود را نشناختی&lt;/p&gt;&lt;p&gt;آخر آدم‌زاده‌ای ای ناخلف&lt;/p&gt;&lt;p&gt;چند پنداری تو پستی را شرف&lt;/p&gt;&lt;p&gt;چند گویی من بگیرم عالمی&lt;/p&gt;&lt;p&gt;این جهان را پر کنم از خود همی&lt;/p&gt;&lt;p&gt;گر جهان پر برف گردد سربسر&lt;/p&gt;&lt;p&gt;تاب خور بگدازدش با یک نظر&lt;/p&gt;&lt;p&gt;وزر او و صد وزیر و صدهزار&lt;/p&gt;&lt;p&gt;نیست گرداند خدا از یک شرار&lt;/p&gt;&lt;p&gt;عین آن تخییل را حکمت کند&lt;/p&gt;&lt;p&gt;عین آن زهراب را شربت کند&lt;/p&gt;&lt;p&gt;آن گمان‌انگیز را سازد یقین&lt;/p&gt;&lt;p&gt;مهرها رویاند از اسباب کین&lt;/p&gt;&lt;p&gt;پرورد در آتش ابراهیم را&lt;/p&gt;&lt;p&gt;ایمنی روح سازد بیم را&lt;/p&gt;&lt;p&gt;از سبب سوزیش من سوداییم&lt;/p&gt;&lt;p&gt;در خیالاتش چو سوفسطاییم&lt;/p&gt;', 'مثنوی شماره۲۴', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(229, '&lt;p&gt;مکر دیگر آن وزیر از خود ببست&lt;/p&gt;&lt;p&gt;وعظ را بگذاشت و در خلوت نشست&lt;/p&gt;&lt;p&gt;در مریدان در فکند از شوق سوز&lt;/p&gt;&lt;p&gt;بود در خلوت چهل پنجاه روز&lt;/p&gt;&lt;p&gt;خلق دیوانه شدند از شوق او&lt;/p&gt;&lt;p&gt;از فراق حال و قال و ذوق او&lt;/p&gt;&lt;p&gt;لابه و زاری همی کردند و او&lt;/p&gt;&lt;p&gt;از ریاضت گشته در خلوت دوتو&lt;/p&gt;&lt;p&gt;گفته ایشان نیست ما را بی تو نور&lt;/p&gt;&lt;p&gt;بی عصاکش چون بود احوال کور&lt;/p&gt;&lt;p&gt;از سر اکرام و از بهر خدا&lt;/p&gt;&lt;p&gt;بیش ازین ما را مدار از خود جدا&lt;/p&gt;&lt;p&gt;ما چو طفلانیم و ما را دایه تو&lt;/p&gt;&lt;p&gt;بر سر ما گستران آن سایه تو&lt;/p&gt;&lt;p&gt;گفت جانم از محبان دور نیست&lt;/p&gt;&lt;p&gt;لیک بیرون آمدن دستور نیست&lt;/p&gt;&lt;p&gt;آن امیران در شفاعت آمدند&lt;/p&gt;&lt;p&gt;وان مریدان در شناعت آمدند&lt;/p&gt;&lt;p&gt;کین چه بدبختیست ما را ای کریم&lt;/p&gt;&lt;p&gt;از دل و دین مانده ما بی تو یتیم&lt;/p&gt;&lt;p&gt;تو بهانه می‌کنی و ما ز درد&lt;/p&gt;&lt;p&gt;می‌زنیم از سوز دل دمهای سرد&lt;/p&gt;&lt;p&gt;ما به گفتار خوشت خو کرده‌ایم&lt;/p&gt;&lt;p&gt;ما ز شیر حکمت تو خورده‌ایم&lt;/p&gt;&lt;p&gt;الله الله این جفا با ما مکن&lt;/p&gt;&lt;p&gt;خیر کن امروز را فردا مکن&lt;/p&gt;&lt;p&gt;می‌دهد دل مر ترا کین بی‌دلان&lt;/p&gt;&lt;p&gt;بی تو گردند آخر از بی‌حاصلان&lt;/p&gt;&lt;p&gt;جمله در خشکی چو ماهی می‌طپند&lt;/p&gt;&lt;p&gt;آب را بگشا ز جو بر دار بند&lt;/p&gt;&lt;p&gt;ای که چون تو در زمانه نیست کس&lt;/p&gt;&lt;p&gt;الله الله خلق را فریاد رس&lt;/p&gt;', 'مثنوی شماره  ۲۵', 'مولانا', 'مثنوی معنوی', 'مثنوی', 4, 7),
(230, '&lt;p&gt;ای رستخیز ناگهان وی رحمت بی‌منتها&lt;/p&gt;&lt;p&gt;ای آتشی افروخته، در بیشه اندیشه‌ها&lt;/p&gt;&lt;p&gt;امروز خندان آمدی، مفتاح زندان آمدی&lt;/p&gt;&lt;p&gt;بر مستمندان آمدی، چون بخشش و فضل خدا&lt;/p&gt;&lt;p&gt;خورشید را حاجب تویی، اومید را واجب تویی&lt;/p&gt;&lt;p&gt;مطلب تویی طالب تویی، هم منتها هم مبتدا&lt;/p&gt;&lt;p&gt;در سینه‌ها برخاسته، اندیشه را آراسته&lt;/p&gt;&lt;p&gt;هم خویش حاجت خواسته، هم خویشتن کرده روا&lt;/p&gt;&lt;p&gt;ای روح بخش بی‌بدل، وی لذّت علم و عمل&lt;/p&gt;&lt;p&gt;باقی بهانه‌ست و دغل، کاین علّت آمد وان دوا&lt;/p&gt;&lt;p&gt;ما زان دغل کژبین شده، با بی‌گنه در کین شده&lt;/p&gt;&lt;p&gt;گه مست حورالعین شده، گه مست نان و شوربا&lt;/p&gt;&lt;p&gt;این سُکر بین هِل عقل را، وین نُقل بین هِل نَقل را&lt;/p&gt;&lt;p&gt;کز بهر نان و بقل را، چندین نشاید ماجرا&lt;/p&gt;&lt;p&gt;تدبیر صدرنگ افکنی، بر روم و بر زنگ افکنی&lt;/p&gt;&lt;p&gt;و اندر میان جنگ افکنی، «فی اِصطِناعٍ لا یُری»&lt;/p&gt;&lt;p&gt;می‌مال پنهان گوش جان، می‌نه بهانه بر کسان&lt;/p&gt;&lt;p&gt;جان «رَبِّ خَلِّصنی» زنان، والله که لاغ است ای کیا&lt;/p&gt;&lt;p&gt;خامش که بس مُستَعجِلَم، رفتم سوی پای علم&lt;/p&gt;&lt;p&gt;کاغذ بنه بشکن قلم، ساقی درآمد الصلا&lt;/p&gt;', 'غزل شماره ۱', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(231, '&lt;p&gt;ای طایران قدس را عشقت فزوده بال‌ها&lt;/p&gt;&lt;p&gt;در حلقه سودای تو روحانیان را حال‌ها&lt;/p&gt;&lt;p&gt;در لا احب الآفلین پاکی ز صورت‌ها یقین&lt;/p&gt;&lt;p&gt;در دیده‌های غیب بین هر دم ز تو تمثال‌ها&lt;/p&gt;&lt;p&gt;افلاک از تو سرنگون خاک از تو چون دریای خون&lt;/p&gt;&lt;p&gt;ماهت نخوانم ای فزون از ماه‌ها و سال‌ها&lt;/p&gt;&lt;p&gt;کوه از غمت بشکافته وان غم به دل درتافته&lt;/p&gt;&lt;p&gt;یک قطره خونی یافته از فضلت این افضال‌ها&lt;/p&gt;&lt;p&gt;ای سروران را تو سند بشمار ما را زان عدد&lt;/p&gt;&lt;p&gt;دانی سران را هم بود اندر تبع دنبال‌ها&lt;/p&gt;&lt;p&gt;سازی ز خاکی سیدی بر وی فرشته حاسدی&lt;/p&gt;&lt;p&gt;با نقد تو جان کاسدی پامال گشته مال‌ها&lt;/p&gt;&lt;p&gt;آن کو تو باشی بال او ای رفعت و اجلال او&lt;/p&gt;&lt;p&gt;آن کو چنین شد حال او بر روی دارد خال‌ها&lt;/p&gt;&lt;p&gt;گیرم که خارم خار بد خار از پی گل می‌زهد&lt;/p&gt;&lt;p&gt;صراف زر هم می‌نهد جو بر سر مثقال‌ها&lt;/p&gt;&lt;p&gt;فکری بدست افعال‌ها خاکی بدست این مال‌ها&lt;/p&gt;&lt;p&gt;قالی بدست این حال‌ها حالی بدست این قال‌ها&lt;/p&gt;&lt;p&gt;آغاز عالم غلغله پایان عالم زلزله&lt;/p&gt;&lt;p&gt;عشقی و شکری با گله آرام با زلزال‌ها&lt;/p&gt;&lt;p&gt;توقیع شمس آمد شفق طغرای دولت عشق حق&lt;/p&gt;&lt;p&gt;فال وصال آرد سبق کان عشق زد این فال‌ها&lt;/p&gt;&lt;p&gt;از رحمة للعالمین اقبال درویشان ببین&lt;/p&gt;&lt;p&gt;چون مه منور خرقه‌ها چون گل معطر شال‌ها&lt;/p&gt;&lt;p&gt;عشق امر کل ما رقعه‌ای او قلزم و ما جرعه‌ای&lt;/p&gt;&lt;p&gt;او صد دلیل آورده و ما کرده استدلال‌ها&lt;/p&gt;&lt;p&gt;از عشق گردون مؤتلف بی‌عشق اختر منخسف&lt;/p&gt;&lt;p&gt;از عشق گشته دال الف بی‌عشق الف چون دال‌ها&lt;/p&gt;&lt;p&gt;آب حیات آمد سخن کاید ز علم من لدن&lt;/p&gt;&lt;p&gt;جان را از او خالی مکن تا بردهد اعمال‌ها&lt;/p&gt;&lt;p&gt;بر اهل معنی شد سخن اجمال‌ها تفصیل‌ها&lt;/p&gt;&lt;p&gt;بر اهل صورت شد سخن تفصیل‌ها اجمال‌ها&lt;/p&gt;&lt;p&gt;گر شعرها گفتند پر پر به بود دریا ز در&lt;/p&gt;&lt;p&gt;کز ذوق شعر آخر شتر خوش می‌کشد ترحال‌ها&lt;/p&gt;', 'غزل شماره ۲', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(232, '&lt;p&gt;ای دل چه اندیشیده‌ای در عذر آن تقصیرها&lt;/p&gt;&lt;p&gt;زان سوی او چندان وفا زین سوی تو چندین جفا&lt;/p&gt;&lt;p&gt;زان سوی او چندان کرم زین سو خلاف و بیش و کم&lt;/p&gt;&lt;p&gt;زان سوی او چندان نعم زین سوی تو چندین خطا&lt;/p&gt;&lt;p&gt;زین سوی تو چندین حسد چندین خیال و ظن بد&lt;/p&gt;&lt;p&gt;زان سوی او چندان کشش چندان چشش چندان عطا&lt;/p&gt;&lt;p&gt;چندین چشش از بهر چه تا جان تلخت خوش شود&lt;/p&gt;&lt;p&gt;چندین کشش از بهر چه تا دررسی در اولیا&lt;/p&gt;&lt;p&gt;از بد پشیمان می‌شوی الله گویان می‌شوی&lt;/p&gt;&lt;p&gt;آن دم تو را او می‌کشد تا وارهاند مر تو را&lt;/p&gt;&lt;p&gt;از جرم ترسان می‌شوی وز چاره پرسان می‌شوی&lt;/p&gt;&lt;p&gt;آن لحظه ترساننده را با خود نمی‌بینی چرا&lt;/p&gt;&lt;p&gt;گر چشم تو بربست او چون مهره‌ای در دست او&lt;/p&gt;&lt;p&gt;گاهی بغلطاند چنین گاهی ببازد در هوا&lt;/p&gt;&lt;p&gt;گاهی نهد در طبع تو سودای سیم و زر و زن&lt;/p&gt;&lt;p&gt;گاهی نهد در جان تو نور خیال مصطفی&lt;/p&gt;&lt;p&gt;این سو کشان سوی خوشان وان سو کشان با ناخوشان&lt;/p&gt;&lt;p&gt;یا بگذرد یا بشکند کشتی در این گرداب‌ها&lt;/p&gt;&lt;p&gt;چندان دعا کن در نهان چندان بنال اندر شبان&lt;/p&gt;&lt;p&gt;کز گنبد هفت آسمان در گوش تو آید صدا&lt;/p&gt;&lt;p&gt;بانگ شعیب و ناله‌اش وان اشک همچون ژاله‌اش&lt;/p&gt;&lt;p&gt;چون شد ز حد از آسمان آمد سحرگاهش ندا&lt;/p&gt;&lt;p&gt;گر مجرمی بخشیدمت وز جرم آمرزیدمت&lt;/p&gt;&lt;p&gt;فردوس خواهی دادمت خامش رها کن این دعا&lt;/p&gt;&lt;p&gt;گفتا نه این خواهم نه آن دیدار حق خواهم عیان&lt;/p&gt;&lt;p&gt;گر هفت بحر آتش شود من درروم بهر لقا&lt;/p&gt;&lt;p&gt;گر رانده آن منظرم بستست از او چشم ترم&lt;/p&gt;&lt;p&gt;من در جحیم اولیترم جنت نشاید مر مرا&lt;/p&gt;&lt;p&gt;جنت مرا بی‌روی او هم دوزخست و هم عدو&lt;/p&gt;&lt;p&gt;من سوختم زین رنگ و بو کو فر انوار بقا&lt;/p&gt;&lt;p&gt;گفتند باری کم گری تا کم نگردد مبصری&lt;/p&gt;&lt;p&gt;که چشم نابینا شود چون بگذرد از حد بکا&lt;/p&gt;&lt;p&gt;گفت ار دو چشمم عاقبت خواهند دیدن آن صفت&lt;/p&gt;&lt;p&gt;هر جزو من چشمی شود کی غم خورم من از عمی&lt;/p&gt;&lt;p&gt;ور عاقبت این چشم من محروم خواهد ماندن&lt;/p&gt;&lt;p&gt;تا کور گردد آن بصر کو نیست لایق دوست را&lt;/p&gt;&lt;p&gt;اندر جهان هر آدمی باشد فدای یار خود&lt;/p&gt;&lt;p&gt;یار یکی انبان خون یار یکی شمس ضیا&lt;/p&gt;&lt;p&gt;چون هر کسی درخورد خود یاری گزید از نیک و بد&lt;/p&gt;&lt;p&gt;ما را دریغ آید که خود فانی کنیم از بهر لا&lt;/p&gt;&lt;p&gt;روزی یکی همراه شد با بایزید اندر رهی&lt;/p&gt;&lt;p&gt;پس بایزیدش گفت چه پیشه گزیدی ای دغا&lt;/p&gt;&lt;p&gt;گفتا که من خربنده‌ام پس بایزیدش گفت رو&lt;/p&gt;&lt;p&gt;یا رب خرش را مرگ ده تا او شود بنده خدا&lt;/p&gt;', 'غزل شماره ۳', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(233, '&lt;p&gt;ای یوسف خوش نام ما خوش می‌روی بر بام ما&lt;/p&gt;&lt;p&gt;ای درشکسته جام ما ای بردریده دام ما&lt;/p&gt;&lt;p&gt;ای نور ما ای سور ما ای دولت منصور ما&lt;/p&gt;&lt;p&gt;جوشی بنه در شور ما تا می شود انگور ما&lt;/p&gt;&lt;p&gt;ای دلبر و مقصود ما ای قبله و معبود ما&lt;/p&gt;&lt;p&gt;آتش زدی در عود ما نظاره کن در دود ما&lt;/p&gt;&lt;p&gt;ای یار ما عیار ما دام دل خمار ما&lt;/p&gt;&lt;p&gt;پا وامکش از کار ما بستان گرو دستار ما&lt;/p&gt;&lt;p&gt;در گل بمانده پای دل جان می‌دهم چه جای دل&lt;/p&gt;&lt;p&gt;وز آتش سودای دل ای وای دل ای وای ما&lt;/p&gt;', 'غزل شماره ۴', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(234, '&lt;p&gt;&lt;br&gt;آن شکل بین وان شیوه بین وان قد و خد و دست و پا&lt;/p&gt;&lt;p&gt;آن رنگ بین وان هنگ بین وان ماه بدر اندر قبا&lt;/p&gt;&lt;p&gt;از سرو گویم یا چمن از لاله گویم یا سمن&lt;/p&gt;&lt;p&gt;از شمع گویم یا لگن یا رقص گل پیش صبا&lt;/p&gt;&lt;p&gt;ای عشق چون آتشکده در نقش و صورت آمده&lt;/p&gt;&lt;p&gt;بر کاروان دل زده یک دم امان ده یا فتی&lt;/p&gt;&lt;p&gt;در آتش و در سوز من شب می‌برم تا روز من&lt;/p&gt;&lt;p&gt;ای فرخ پیروز من از روی آن شمس الضحی&lt;/p&gt;&lt;p&gt;بر گرد ماهش می‌تنم بی‌لب سلامش می‌کنم&lt;/p&gt;&lt;p&gt;خود را زمین برمی‌زنم زان پیش کو گوید صلا&lt;/p&gt;&lt;p&gt;گلزار و باغ عالمی چشم و چراغ عالمی&lt;/p&gt;&lt;p&gt;هم درد و داغ عالمی چون پا نهی اندر جفا&lt;/p&gt;&lt;p&gt;آیم کنم جان را گرو گویی مده زحمت برو&lt;/p&gt;&lt;p&gt;خدمت کنم تا واروم گویی که ای ابله بیا&lt;/p&gt;&lt;p&gt;گشته خیال همنشین با عاشقان آتشین&lt;/p&gt;&lt;p&gt;غایب مبادا صورتت یک دم ز پیش چشم ما&lt;/p&gt;&lt;p&gt;ای دل قرار تو چه شد وان کار و بار تو چه شد&lt;/p&gt;&lt;p&gt;خوابت که می‌بندد چنین اندر صباح و در مسا&lt;/p&gt;&lt;p&gt;دل گفت حسن روی او وان نرگس جادوی او&lt;/p&gt;&lt;p&gt;وان سنبل ابروی او وان لعل شیرین ماجرا&lt;/p&gt;&lt;p&gt;ای عشق پیش هر کسی نام و لقب داری بسی&lt;/p&gt;&lt;p&gt;من دوش نام دیگرت کردم که درد بی‌دوا&lt;/p&gt;&lt;p&gt;ای رونق جانم ز تو چون چرخ گردانم ز تو&lt;/p&gt;&lt;p&gt;گندم فرست ای جان که تا خیره نگردد آسیا&lt;/p&gt;&lt;p&gt;دیگر نخواهم زد نفس این بیت را می‌گوی و بس&lt;/p&gt;&lt;p&gt;بگداخت جانم زین هوس ارفق بنا یا ربنا&lt;/p&gt;', 'غزل شماره ۵', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(235, '&lt;p&gt;بگریز ای میر اجل از ننگ ما از ننگ ما&lt;/p&gt;&lt;p&gt;زیرا نمی‌دانی شدن همرنگ ما همرنگ ما&lt;/p&gt;&lt;p&gt;از حمله‌های جند او وز زخم‌های تند او&lt;/p&gt;&lt;p&gt;سالم نماند یک رگت بر چنگ ما بر چنگ ما&lt;/p&gt;&lt;p&gt;اول شرابی درکشی سرمست گردی از خوشی&lt;/p&gt;&lt;p&gt;بیخود شوی آنگه کنی آهنگ ما آهنگ ما&lt;/p&gt;&lt;p&gt;زین باده می‌خواهی برو اول تنک چون شیشه شو&lt;/p&gt;&lt;p&gt;چون شیشه گشتی برشکن بر سنگ ما بر سنگ ما&lt;/p&gt;&lt;p&gt;هر کان می احمر خورد بابرگ گردد برخورد&lt;/p&gt;&lt;p&gt;از دل فراخی‌ها برد دلتنگ ما دلتنگ ما&lt;/p&gt;&lt;p&gt;بس جره‌ها در جو زند بس بربط شش تو زند&lt;/p&gt;&lt;p&gt;بس با شهان پهلو زند سرهنگ ما سرهنگ ما&lt;/p&gt;&lt;p&gt;ماده است مریخ زمن این جا در این خنجر زدن&lt;/p&gt;&lt;p&gt;با مقنعه کی تان شدن در جنگ ما در جنگ ما&lt;/p&gt;&lt;p&gt;گر تیغ خواهی تو ز خور از بدر برسازی سپر&lt;/p&gt;&lt;p&gt;گر قیصری اندرگذر از زنگ ما از زنگ ما&lt;/p&gt;&lt;p&gt;اسحاق شو در نحر ما خاموش شو در بحر ما&lt;/p&gt;&lt;p&gt;تا نشکند کشتی تو در گنگ ما در گنگ ما&lt;/p&gt;', 'غزل شماره ۶', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(236, '&lt;p&gt;بنشسته‌ام من بر درت تا بوک برجوشد وفا&lt;/p&gt;&lt;p&gt;باشد که بگشایی دری گویی که برخیز اندرآ&lt;/p&gt;&lt;p&gt;غرقست جانم بر درت در بوی مشک و عنبرت&lt;/p&gt;&lt;p&gt;ای صد هزاران مرحمت بر روی خوبت دایما&lt;/p&gt;&lt;p&gt;ماییم مست و سرگران فارغ ز کار دیگران&lt;/p&gt;&lt;p&gt;عالم اگر برهم رود عشق تو را بادا بقا&lt;/p&gt;&lt;p&gt;عشق تو کف برهم زند صد عالم دیگر کند&lt;/p&gt;&lt;p&gt;صد قرن نو پیدا شود بیرون ز افلاک و خلا&lt;/p&gt;&lt;p&gt;ای عشق خندان همچو گل وی خوش نظر چون عقل کل&lt;/p&gt;&lt;p&gt;خورشید را درکش به جل ای شهسوار هل اتی&lt;/p&gt;&lt;p&gt;امروز ما مهمان تو مست رخ خندان تو&lt;/p&gt;&lt;p&gt;چون نام رویت می‌برم دل می‌رود والله ز جا&lt;/p&gt;&lt;p&gt;کو بام غیر بام تو کو نام غیر نام تو&lt;/p&gt;&lt;p&gt;کو جام غیر جام تو ای ساقی شیرین ادا&lt;/p&gt;&lt;p&gt;گر زنده جانی یابمی من دامنش برتابمی&lt;/p&gt;&lt;p&gt;ای کاشکی درخوابمی در خواب بنمودی لقا&lt;/p&gt;&lt;p&gt;ای بر درت خیل و حشم بیرون خرام ای محتشم&lt;/p&gt;&lt;p&gt;زیرا که سرمست و خوشم زان چشم مست دلربا&lt;/p&gt;&lt;p&gt;افغان و خون دیده بین صد پیرهن بدریده بین&lt;/p&gt;&lt;p&gt;خون جگر پیچیده بین بر گردن و روی و قفا&lt;/p&gt;&lt;p&gt;آن کس که بیند روی تو مجنون نگردد کو بگو&lt;/p&gt;&lt;p&gt;سنگ و کلوخی باشد او او را چرا خواهم بلا&lt;/p&gt;&lt;p&gt;رنج و بلایی زین بتر کز تو بود جان بی‌خبر&lt;/p&gt;&lt;p&gt;ای شاه و سلطان بشر لا تبل نفسا بالعمی&lt;/p&gt;&lt;p&gt;جان‌ها چو سیلابی روان تا ساحل دریای جان&lt;/p&gt;&lt;p&gt;از آشنایان منقطع با بحر گشته آشنا&lt;/p&gt;&lt;p&gt;سیلی روان اندر وله سیلی دگر گم کرده ره&lt;/p&gt;&lt;p&gt;الحمدلله گوید آن وین آه و لا حول و لا&lt;/p&gt;&lt;p&gt;ای آفتابی آمده بر مفلسان ساقی شده&lt;/p&gt;&lt;p&gt;بر بندگان خود را زده باری کرم باری عطا&lt;/p&gt;&lt;p&gt;گل دیده ناگه مر تو را بدریده جان و جامه را&lt;/p&gt;&lt;p&gt;وان چنگ زار از چنگ تو افکنده سر پیش از حیا&lt;/p&gt;&lt;p&gt;مقبلترین و نیک پی در برج زهره کیست نی&lt;/p&gt;&lt;p&gt;زیرا نهد لب بر لبت تا از تو آموزد نوا&lt;/p&gt;&lt;p&gt;نی‌ها و خاصه نیشکر بر طمع این بسته کمر&lt;/p&gt;&lt;p&gt;رقصان شده در نیستان یعنی تعز من تشا&lt;/p&gt;&lt;p&gt;بد بی‌تو چنگ و نی حزین برد آن کنار و بوسه این&lt;/p&gt;&lt;p&gt;دف گفت می‌زن بر رخم تا روی من یابد بها&lt;/p&gt;&lt;p&gt;این جان پاره پاره را خوش پاره پاره مست کن&lt;/p&gt;&lt;p&gt;تا آن چه دوشش فوت شد آن را کند این دم قضا&lt;/p&gt;&lt;p&gt;حیفست ای شاه مهین هشیار کردن این چنین&lt;/p&gt;&lt;p&gt;والله نگویم بعد از این هشیار شرحت ای خدا&lt;/p&gt;&lt;p&gt;یا باده ده حجت مجو یا خود تو برخیز و برو&lt;/p&gt;&lt;p&gt;یا بنده را با لطف تو شد صوفیانه ماجرا&lt;/p&gt;', 'غزل شماره ۷', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(237, '&lt;p&gt;جز وی چه باشد کز اجل اندررباید کل ما&lt;/p&gt;&lt;p&gt;صد جان برافشانم بر او گویم هنییا مرحبا&lt;/p&gt;&lt;p&gt;رقصان سوی گردون شوم زان جا سوی بی‌چون شوم&lt;/p&gt;&lt;p&gt;صبر و قرارم برده‌ای ای میزبان زوتر بیا&lt;/p&gt;&lt;p&gt;از مه ستاره می‌بری تو پاره پاره می‌بری&lt;/p&gt;&lt;p&gt;گه شیرخواره می‌بری گه می‌کشانی دایه را&lt;/p&gt;&lt;p&gt;دارم دلی همچون جهان تا می‌کشد کوه گران&lt;/p&gt;&lt;p&gt;من که کشم که کی کشم زین کاهدان واخر مرا&lt;/p&gt;&lt;p&gt;گر موی من چون شیر شد از شوق مردن پیر شد&lt;/p&gt;&lt;p&gt;من آردم گندم نیم چون آمدم در آسیا&lt;/p&gt;&lt;p&gt;در آسیا گندم رود کز سنبله زادست او&lt;/p&gt;&lt;p&gt;زاده مهم نی سنبله در آسیا باشم چرا&lt;/p&gt;&lt;p&gt;نی نی فتد در آسیا هم نور مه از روزنی&lt;/p&gt;&lt;p&gt;زان جا به سوی مه رود نی در دکان نانبا&lt;/p&gt;&lt;p&gt;با عقل خود گر جفتمی من گفتنی‌ها گفتمی&lt;/p&gt;&lt;p&gt;خاموش کن تا نشنود این قصه را باد هوا&lt;/p&gt;', 'غزل شماره ۸', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(238, '&lt;p&gt;من از کجا پند از کجا، باده بگردان ساقیا&lt;/p&gt;&lt;p&gt;آن جام جان افزای را، برریز بر جان ساقیا&lt;/p&gt;&lt;p&gt;بر دست من نِه جام جان، ای دستگیر عاشقان&lt;/p&gt;&lt;p&gt;دور از لب بیگانگان، پیش آر پنهان ساقیا&lt;/p&gt;&lt;p&gt;نانی بده نان خواره را، آن طامع بیچاره را&lt;/p&gt;&lt;p&gt;آن عاشق نانباره را، کنجی بخسبان ساقیا&lt;/p&gt;&lt;p&gt;ای جانِ جانِ جانِ جان، ما نامدیم از بهر نان&lt;/p&gt;&lt;p&gt;برجَه گدارویی مکن، در بزم سلطان ساقیا&lt;/p&gt;&lt;p&gt;اول بگیر آن جامِ مِه، بر کفهٔ آن پیر نِه&lt;/p&gt;&lt;p&gt;چون مست گردد پیر ده، رو سوی مستان ساقیا&lt;/p&gt;&lt;p&gt;رو سخت کن ای مرتجا، مست از کجا شرم از کجا&lt;/p&gt;&lt;p&gt;ور شرم داری، یک قدح بر شرم افشان ساقیا&lt;/p&gt;&lt;p&gt;برخیز ای ساقی بیا، ای دشمن شرم و حیا&lt;/p&gt;&lt;p&gt;تا بخت ما خندان شود، پیش آی خندان ساقیا&lt;/p&gt;', 'غزل شماره ۹', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(239, '&lt;p&gt;مهمان شاهم هر شبی بر خوان احسان و وفا&lt;/p&gt;&lt;p&gt;مهمان صاحب دولتم که دولتش پاینده با&lt;/p&gt;&lt;p&gt;بر خوان شیران یک شبی بوزینه‌ای همراه شد&lt;/p&gt;&lt;p&gt;استیزه رو گر نیستی او از کجا شیر از کجا&lt;/p&gt;&lt;p&gt;بنگر که از شمشیر شه در قهرمان خون می‌چکد&lt;/p&gt;&lt;p&gt;آخر چه گستاخی است این والله خطا والله خطا&lt;/p&gt;&lt;p&gt;گر طفل شیری پنجه زد بر روی مادر ناگهان&lt;/p&gt;&lt;p&gt;تو دشمن خود نیستی بر وی منه تو پنجه را&lt;/p&gt;&lt;p&gt;آن کو ز شیران شیر خورد او شیر باشد نیست مرد&lt;/p&gt;&lt;p&gt;بسیار نقش آدمی دیدم که بود آن اژدها&lt;/p&gt;&lt;p&gt;نوح ار چه مردم وار بد طوفان مردم خوار بد&lt;/p&gt;&lt;p&gt;گر هست آتش ذره‌ای آن ذره دارد شعله‌ها&lt;/p&gt;&lt;p&gt;شمشیرم و خون ریز من هم نرمم و هم تیز من&lt;/p&gt;&lt;p&gt;همچون جهان فانیم ظاهر خوش و باطن بلا&lt;/p&gt;', 'غزل شماره ۱۰', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(240, '&lt;p&gt;ای طوطی عیسی نفس وی بلبل شیرین نوا&lt;/p&gt;&lt;p&gt;هین زهره را کالیوه کن زان نغمه‌های جان فزا&lt;/p&gt;&lt;p&gt;دعوی خوبی کن بیا تا صد عدو و آشنا&lt;/p&gt;&lt;p&gt;با چهره‌ای چون زعفران با چشم تر آید گوا&lt;/p&gt;&lt;p&gt;غم جمله را نالان کند تا مرد و زن افغان کند&lt;/p&gt;&lt;p&gt;که داد ده ما را ز غم کو گشت در ظلم اژدها&lt;/p&gt;&lt;p&gt;غم را بدرانی شکم با دورباش زیر و بم&lt;/p&gt;&lt;p&gt;تا غلغل افتد در عدم از عدل تو ای خوش صدا&lt;/p&gt;&lt;p&gt;ساقی تو ما را یاد کن صد خیک را پرباد کن&lt;/p&gt;&lt;p&gt;ارواح را فرهاد کن در عشق آن شیرین لقا&lt;/p&gt;&lt;p&gt;چون تو سرافیل دلی زنده کن آب و گلی&lt;/p&gt;&lt;p&gt;دردم ز راه مقبلی در گوش ما نفخه خدا&lt;/p&gt;&lt;p&gt;ما همچو خرمن ریخته گندم به کاه آمیخته&lt;/p&gt;&lt;p&gt;هین از نسیم باد جان که را ز گندم کن جدا&lt;/p&gt;&lt;p&gt;تا غم به سوی غم رود خرم سوی خرم رود&lt;/p&gt;&lt;p&gt;تا گل به سوی گل رود تا دل برآید بر سما&lt;/p&gt;&lt;p&gt;این دانه‌های نازنین محبوس مانده در زمین&lt;/p&gt;&lt;p&gt;در گوش یک باران خوش موقوف یک باد صبا&lt;/p&gt;&lt;p&gt;تا کار جان چون زر شود با دلبران هم‌بر شود&lt;/p&gt;&lt;p&gt;پا بود اکنون سر شود که بود اکنون کهربا&lt;/p&gt;&lt;p&gt;خاموش کن آخر دمی دستور بودی گفتمی&lt;/p&gt;&lt;p&gt;سری که نفکندست کس در گوش اخوان صفا&lt;/p&gt;', 'غزل شماره ۱۱', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(241, '&lt;p&gt;ای نوبهار عاشقان داری خبر از یار ما&lt;/p&gt;&lt;p&gt;ای از تو آبستن چمن و ای از تو خندان باغ‌ها&lt;/p&gt;&lt;p&gt;ای بادهای خوش نفس عشاق را فریادرس&lt;/p&gt;&lt;p&gt;ای پاکتر از جان و جا آخر کجا بودی کجا&lt;/p&gt;&lt;p&gt;ای فتنه روم و حبش حیران شدم کاین بوی خوش&lt;/p&gt;&lt;p&gt;پیراهن یوسف بود یا خود روان مصطفی&lt;/p&gt;&lt;p&gt;ای جویبار راستی از جوی یار ماستی&lt;/p&gt;&lt;p&gt;بر سینه‌ها سیناستی بر جان‌هایی جان فزا&lt;/p&gt;&lt;p&gt;ای قیل و ای قال تو خوش و ای جمله اشکال تو خوش&lt;/p&gt;&lt;p&gt;ماه تو خوش سال تو خوش ای سال و مه چاکر تو را&lt;/p&gt;', 'غزل شماره ۱۲', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(242, '&lt;p&gt;ای باد بی‌آرام ما با گل بگو پیغام ما&lt;/p&gt;&lt;p&gt;کای گل گریز اندر شکر چون گشتی از گلشن جدا&lt;/p&gt;&lt;p&gt;ای گل ز اصل شکری تو با شکر لایقتری&lt;/p&gt;&lt;p&gt;شکر خوش و گل هم خوش و از هر دو شیرینتر وفا&lt;/p&gt;&lt;p&gt;رخ بر رخ شکر بنه لذت بگیر و بو بده&lt;/p&gt;&lt;p&gt;در دولت شکر بجه از تلخی جور فنا&lt;/p&gt;&lt;p&gt;اکنون که گشتی گلشکر قوت دلی نور نظر&lt;/p&gt;&lt;p&gt;از گل برآ بر دل گذر آن از کجا این از کجا&lt;/p&gt;&lt;p&gt;با خار بودی همنشین چون عقل با جانی قرین&lt;/p&gt;&lt;p&gt;بر آسمان رو از زمین منزل به منزل تا لقا&lt;/p&gt;&lt;p&gt;در سر خلقان می‌روی در راه پنهان می‌روی&lt;/p&gt;&lt;p&gt;بستان به بستان می‌روی آن جا که خیزد نقش‌ها&lt;/p&gt;&lt;p&gt;ای گل تو مرغ نادری برعکس مرغان می‌پری&lt;/p&gt;&lt;p&gt;کامد پیامت زان سری پرها بنه بی‌پر بیا&lt;/p&gt;&lt;p&gt;ای گل تو این‌ها دیده‌ای زان بر جهان خندیده‌ای&lt;/p&gt;&lt;p&gt;زان جامه‌ها بدریده‌ای ای کربز لعلین قبا&lt;/p&gt;&lt;p&gt;گل‌های پار از آسمان نعره زنان در گلستان&lt;/p&gt;&lt;p&gt;کای هر که خواهد نردبان تا جان سپارد در بلا&lt;/p&gt;&lt;p&gt;هین از ترشح زین طبق بگذر تو بی‌ره چون عرق&lt;/p&gt;&lt;p&gt;از شیشه گلابگر چون روح از آن جام سما&lt;/p&gt;&lt;p&gt;ای مقبل و میمون شما با چهره گلگون شما&lt;/p&gt;&lt;p&gt;بودیم ما همچون شما ما روح گشتیم الصلا&lt;/p&gt;&lt;p&gt;از گلشکر مقصود ما لطف حقست و بود ما&lt;/p&gt;&lt;p&gt;ای بود ما آهن صفت وی لطف حق آهن ربا&lt;/p&gt;&lt;p&gt;آهن خرد آیینه گر بر وی نهد زخم شرر&lt;/p&gt;&lt;p&gt;ما را نمی‌خواهد مگر خواهم شما را بی‌شما&lt;/p&gt;&lt;p&gt;هان ای دل مشکین سخن پایان ندارد این سخن&lt;/p&gt;&lt;p&gt;با کس نیارم گفت من آن‌ها که می‌گویی مرا&lt;/p&gt;&lt;p&gt;ای شمس تبریزی بگو سر شهان شاه خو&lt;/p&gt;&lt;p&gt;بی حرف و صوت و رنگ و بو بی‌شمس کی تابد ضیا&lt;/p&gt;', 'غزل شماره ۱۳', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(243, '&lt;p&gt;ای عاشقان ای عاشقان امروز ماییم و شما&lt;/p&gt;&lt;p&gt;افتاده در غرقابه‌ای تا خود که داند آشنا&lt;/p&gt;&lt;p&gt;گر سیل عالم پر شود هر موج چون اشتر شود&lt;/p&gt;&lt;p&gt;مرغان آبی را چه غم تا غم خورد مرغ هوا&lt;/p&gt;&lt;p&gt;ما رخ ز شکر افروخته با موج و بحر آموخته&lt;/p&gt;&lt;p&gt;زان سان که ماهی را بود دریا و طوفان جان فزا&lt;/p&gt;&lt;p&gt;ای شیخ ما را فوطه ده وی آب ما را غوطه ده&lt;/p&gt;&lt;p&gt;ای موسی عمران بیا بر آب دریا زن عصا&lt;/p&gt;&lt;p&gt;این باد اندر هر سری سودای دیگر می‌پزد&lt;/p&gt;&lt;p&gt;سودای آن ساقی مرا باقی همه آن شما&lt;/p&gt;&lt;p&gt;دیروز مستان را به ره بربود آن ساقی کله&lt;/p&gt;&lt;p&gt;امروز می در می‌دهد تا برکند از ما قبا&lt;/p&gt;&lt;p&gt;ای رشک ماه و مشتری با ما و پنهان چون پری&lt;/p&gt;&lt;p&gt;خوش خوش کشانم می‌بری آخر نگویی تا کجا&lt;/p&gt;&lt;p&gt;هر جا روی تو با منی ای هر دو چشم و روشنی&lt;/p&gt;&lt;p&gt;خواهی سوی مستیم کش خواهی ببر سوی فنا&lt;/p&gt;&lt;p&gt;عالم چو کوه طور دان ما همچو موسی طالبان&lt;/p&gt;&lt;p&gt;هر دم تجلی می‌رسد برمی‌شکافد کوه را&lt;/p&gt;&lt;p&gt;یک پاره اخضر می‌شود یک پاره عبهر می‌شود&lt;/p&gt;&lt;p&gt;یک پاره گوهر می‌شود یک پاره لعل و کهربا&lt;/p&gt;&lt;p&gt;ای طالب دیدار او بنگر در این کهسار او&lt;/p&gt;&lt;p&gt;ای کُه چه باد خورده‌ای ما مست گشتیم از صدا&lt;/p&gt;&lt;p&gt;ای باغبان ای باغبان در ما چه درپیچیده‌ای&lt;/p&gt;&lt;p&gt;گر برده‌ایم انگور تو تو برده‌ای انبان ما&lt;/p&gt;', 'غزل شماره ۱۴', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(244, '&lt;p&gt;ای نوش کرده نیش را بی‌خویش کن باخویش را&lt;/p&gt;&lt;p&gt;باخویش کن بی‌خویش را چیزی بده درویش را&lt;/p&gt;&lt;p&gt;تشریف ده عشاق را پرنور کن آفاق را&lt;/p&gt;&lt;p&gt;بر زهر زن تریاق را چیزی بده درویش را&lt;/p&gt;&lt;p&gt;با روی همچون ماه خود با لطف مسکین خواه خود&lt;/p&gt;&lt;p&gt;ما را تو کن همراه خود چیزی بده درویش را&lt;/p&gt;&lt;p&gt;چون جلوه مه می‌کنی وز عشق آگه می‌کنی&lt;/p&gt;&lt;p&gt;با ما چه همره می‌کنی چیزی بده درویش را&lt;/p&gt;&lt;p&gt;درویش را چه بود نشان جان و زبان درفشان&lt;/p&gt;&lt;p&gt;نی دلق صدپاره کشان چیزی بده درویش را&lt;/p&gt;&lt;p&gt;هم آدم و آن دم تویی هم عیسی و مریم تویی&lt;/p&gt;&lt;p&gt;هم راز و هم محرم تویی چیزی بده درویش را&lt;/p&gt;&lt;p&gt;تلخ از تو شیرین می‌شود کفر از تو چون دین می‌شود&lt;/p&gt;&lt;p&gt;خار از تو نسرین می‌شود چیزی بده درویش را&lt;/p&gt;&lt;p&gt;جان من و جانان من کفر من و ایمان من&lt;/p&gt;&lt;p&gt;سلطان سلطانان من چیزی بده درویش را&lt;/p&gt;&lt;p&gt;ای تن پرست بوالحزن در تن مپیچ و جان مکن&lt;/p&gt;&lt;p&gt;منگر به تن بنگر به من چیزی بده درویش را&lt;/p&gt;&lt;p&gt;امروز ای شمع آن کنم بر نور تو جولان کنم&lt;/p&gt;&lt;p&gt;بر عشق جان افشان کنم چیزی بده درویش را&lt;/p&gt;&lt;p&gt;امروز گویم چون کنم یک باره دل را خون کنم&lt;/p&gt;&lt;p&gt;وین کار را یک سون کنم چیزی بده درویش را&lt;/p&gt;&lt;p&gt;تو عیب ما را کیستی تو مار یا ماهیستی&lt;/p&gt;&lt;p&gt;خود را بگو تو چیستی چیزی بده درویش را&lt;/p&gt;&lt;p&gt;جان را درافکن در عدم زیرا نشاید ای صنم&lt;/p&gt;&lt;p&gt;تو محتشم او محتشم چیزی بده درویش را&lt;/p&gt;', 'غزل شماره ۱۵', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(245, '&lt;p&gt;ای یوسف آخر سوی این یعقوب نابینا بیا&lt;/p&gt;&lt;p&gt;ای عیسی پنهان شده بر طارم مینا بیا&lt;/p&gt;&lt;p&gt;از هجر روزم قیر شد دل چون کمان بد تیر شد&lt;/p&gt;&lt;p&gt;یعقوب مسکین پیر شد ای یوسف برنا بیا&lt;/p&gt;&lt;p&gt;ای موسی عمران که در سینه چه سیناهاستت&lt;/p&gt;&lt;p&gt;گاوی خدایی می‌کند از سینه سینا بیا&lt;/p&gt;&lt;p&gt;رخ زعفران رنگ آمدم خم داده چون چنگ آمدم&lt;/p&gt;&lt;p&gt;در گور تن تنگ آمدم ای جان باپهنا بیا&lt;/p&gt;&lt;p&gt;چشم محمد با نمت واشوق گفته در غمت&lt;/p&gt;&lt;p&gt;زان طره‌ای اندرهمت ای سر ارسلنا بیا&lt;/p&gt;&lt;p&gt;خورشید پیشت چون شفق ای برده از شاهان سبق&lt;/p&gt;&lt;p&gt;ای دیده بینا به حق وی سینه دانا بیا&lt;/p&gt;&lt;p&gt;ای جان تو و جان‌ها چو تن بی‌جان چه ارزد خود بدن&lt;/p&gt;&lt;p&gt;دل داده‌ام دیر است من تا جان دهم جانا بیا&lt;/p&gt;&lt;p&gt;تا برده‌ای دل را گرو شد کشت جانم در درو&lt;/p&gt;&lt;p&gt;اول تو ای دردا برو و آخر تو درمانا بیا&lt;/p&gt;&lt;p&gt;ای تو دوا و چاره‌ام نور دل صدپاره‌ام&lt;/p&gt;&lt;p&gt;اندر دل بیچاره‌ام چون غیر تو شد لا بیا&lt;/p&gt;&lt;p&gt;نشناختم قدر تو من تا چرخ می‌گوید ز فن&lt;/p&gt;&lt;p&gt;دی بر دلش تیری بزن دی بر سرش خارا بیا&lt;/p&gt;&lt;p&gt;ای قاب قوس مرتبت وان دولت بامکرمت&lt;/p&gt;&lt;p&gt;کس نیست شاها محرمت در قرب او ادنی بیا&lt;/p&gt;&lt;p&gt;ای خسرو مه وش بیا ای خوشتر از صد خوش بیا&lt;/p&gt;&lt;p&gt;ای آب و ای آتش بیا ای در و ای دریا بیا&lt;/p&gt;&lt;p&gt;مخدوم جانم شمس دین از جاهت ای روح الامین&lt;/p&gt;&lt;p&gt;تبریز چون عرش مکین از مسجد اقصی بیا&lt;/p&gt;', 'غزل شماره ۱۶', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(246, '&lt;p&gt;آمد ندا از آسمان جان را که بازآ الصلا&lt;/p&gt;&lt;p&gt;جان گفت ای نادی خوش اهلا و سهلا مرحبا&lt;/p&gt;&lt;p&gt;سمعا و طاعه ای ندا هر دم دو صد جانت فدا&lt;/p&gt;&lt;p&gt;یک بار دیگر بانگ زن تا برپرم بر هل اتی&lt;/p&gt;&lt;p&gt;ای نادره مهمان ما بردی قرار از جان ما&lt;/p&gt;&lt;p&gt;آخر کجا می‌خوانیم گفتا برون از جان و جا&lt;/p&gt;&lt;p&gt;از پای این زندانیان بیرون کنم بند گران&lt;/p&gt;&lt;p&gt;بر چرخ بنهم نردبان تا جان برآید بر علا&lt;/p&gt;&lt;p&gt;تو جان جان افزاستی آخر ز شهر ماستی&lt;/p&gt;&lt;p&gt;دل بر غریبی می‌نهی این کی بود شرط وفا&lt;/p&gt;&lt;p&gt;آوارگی نوشت شده خانه فراموشت شده&lt;/p&gt;&lt;p&gt;آن گنده پیر کابلی صد سحر کردت از دغا&lt;/p&gt;&lt;p&gt;این قافله بر قافله پویان سوی آن مرحله&lt;/p&gt;&lt;p&gt;چون برنمی‌گردد سرت چون دل نمی‌جوشد تو را&lt;/p&gt;&lt;p&gt;بانگ شتربان و جرس می‌نشنود از پیش و پس&lt;/p&gt;&lt;p&gt;ای بس رفیق و همنفس آن جا نشسته گوش ما&lt;/p&gt;&lt;p&gt;خلقی نشسته گوش ما مست و خوش و بی‌هوش ما&lt;/p&gt;&lt;p&gt;نعره زنان در گوش ما که سوی شاه آ ای گدا&lt;/p&gt;', 'غزل شماره ۱۷', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(247, '&lt;p&gt;ای یوسف خوش نام ما خوش می‌روی بر بام ما&lt;/p&gt;&lt;p&gt;انا فتحنا الصلا بازآ ز بام از در درآ&lt;/p&gt;&lt;p&gt;ای بحر پرمرجان من والله سبک شد جان من&lt;/p&gt;&lt;p&gt;این جان سرگردان من از گردش این آسیا&lt;/p&gt;&lt;p&gt;ای ساربان با قافله مگذر مرو زین مرحله&lt;/p&gt;&lt;p&gt;اشتر بخوابان هین هله نه از بهر من بهر خدا&lt;/p&gt;&lt;p&gt;نی نی برو مجنون برو خوش در میان خون برو&lt;/p&gt;&lt;p&gt;از چون مگو بی‌چون برو زیرا که جان را نیست جا&lt;/p&gt;&lt;p&gt;گر قالبت در خاک شد جان تو بر افلاک شد&lt;/p&gt;&lt;p&gt;گر خرقه تو چاک شد جان تو را نبود فنا&lt;/p&gt;&lt;p&gt;از سر دل بیرون نه‌ای بنمای رو کایینه‌ای&lt;/p&gt;&lt;p&gt;چون عشق را سرفتنه‌ای پیش تو آید فتنه‌ها&lt;/p&gt;&lt;p&gt;گویی مرا چون می‌روی گستاخ و افزون می‌روی&lt;/p&gt;&lt;p&gt;بنگر که در خون می‌روی آخر نگویی تا کجا&lt;/p&gt;&lt;p&gt;گفتم کز آتش‌های دل بر روی مفرش‌های دل&lt;/p&gt;&lt;p&gt;می غلط در سودای دل تا بحر یفعل ما یشا&lt;/p&gt;&lt;p&gt;هر دم رسولی می‌رسد جان را گریبان می‌کشد&lt;/p&gt;&lt;p&gt;بر دل خیالی می‌دود یعنی به اصل خود بیا&lt;/p&gt;&lt;p&gt;دل از جهان رنگ و بو گشته گریزان سو به سو&lt;/p&gt;&lt;p&gt;نعره زنان کان اصل کو جامه دران اندر وفا&lt;/p&gt;', 'غزل شماره ۱۸', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(248, '&lt;p&gt;امروز دیدم یار را آن رونق هر کار را&lt;/p&gt;&lt;p&gt;می‌شد روان بر آسمان همچون روان مصطفا&lt;/p&gt;&lt;p&gt;خورشید از رویش خجل گردون مشبک همچو دل&lt;/p&gt;&lt;p&gt;از تابش او آب و گل افزون ز آتش در ضیا&lt;/p&gt;&lt;p&gt;گفتم که بنما نردبان تا برروم بر آسمان&lt;/p&gt;&lt;p&gt;گفتا سر تو نردبان سر را درآور زیر پا&lt;/p&gt;&lt;p&gt;چون پای خود بر سر نهی پا بر سر اختر نهی&lt;/p&gt;&lt;p&gt;چون تو هوا را بشکنی پا بر هوا نه هین بیا&lt;/p&gt;&lt;p&gt;بر آسمان و بر هوا صد رد پدید آید تو را&lt;/p&gt;&lt;p&gt;بر آسمان پران شوی هر صبحدم همچون دعا&lt;/p&gt;', 'غزل شماره ۱۹', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(249, '&lt;p&gt;چندانک خواهی جنگ کن یا گرم کن تهدید را&lt;/p&gt;&lt;p&gt;می‌دان که دود گولخن هرگز نیاید بر سما&lt;/p&gt;&lt;p&gt;ور خود برآید بر سما کی تیره گردد آسمان&lt;/p&gt;&lt;p&gt;کز دود آورد آسمان چندان لطیفی و ضیا&lt;/p&gt;&lt;p&gt;خود را مرنجان ای پدر سر را مکوب اندر حجر&lt;/p&gt;&lt;p&gt;با نقش گرمابه مکن این جمله چالیش و غزا&lt;/p&gt;&lt;p&gt;گر تو کنی بر مه تفو بر روی تو بازآید آن&lt;/p&gt;&lt;p&gt;ور دامن او را کشی هم بر تو تنگ آید قبا&lt;/p&gt;&lt;p&gt;پیش از تو خامان دگر در جوش این دیگ جهان&lt;/p&gt;&lt;p&gt;بس برطپیدند و نشد درمان نبود الا رضا&lt;/p&gt;&lt;p&gt;بگرفت دم مار را یک خارپشت اندر دهن&lt;/p&gt;&lt;p&gt;سر درکشید و گرد شد مانند گویی آن دغا&lt;/p&gt;&lt;p&gt;آن مار ابله خویش را بر خار می‌زد دم به دم&lt;/p&gt;&lt;p&gt;سوراخ سوراخ آمد او از خود زدن بر خارها&lt;/p&gt;&lt;p&gt;بی صبر بود و بی‌حیل خود را بکشت او از عجل&lt;/p&gt;&lt;p&gt;گر صبر کردی یک زمان رستی از او آن بدلقا&lt;/p&gt;&lt;p&gt;بر خارپشت هر بلا خود را مزن تو هم هلا&lt;/p&gt;&lt;p&gt;ساکن نشین وین ورد خوان جاء القضا ضاق الفضا&lt;/p&gt;&lt;p&gt;فرمود رب العالمین با صابرانم همنشین&lt;/p&gt;&lt;p&gt;ای همنشین صابران افرغ علینا صبرنا&lt;/p&gt;&lt;p&gt;رفتم به وادی دگر باقی تو فرما ای پدر&lt;/p&gt;&lt;p&gt;مر صابران را می‌رسان هر دم سلامی نو ز ما&lt;/p&gt;', 'غزل شماره ۲۰', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(250, '&lt;p&gt;جرمی ندارم بیش از این کز دل هوا دارم تو را&lt;/p&gt;&lt;p&gt;از زعفران روی من رو می‌بگردانی چرا&lt;/p&gt;&lt;p&gt;یا این دل خون خواره را لطف و مراعاتی بکن&lt;/p&gt;&lt;p&gt;یا قوت صبرش بده در یفعل الله ما یشا&lt;/p&gt;&lt;p&gt;این دو ره آمد در روش یا صبر یا شکر نعم&lt;/p&gt;&lt;p&gt;بی شمع روی تو نتان دیدن مر این دو راه را&lt;/p&gt;&lt;p&gt;هر گه بگردانی تو رو آبی ندارد هیچ جو&lt;/p&gt;&lt;p&gt;کی ذره‌ها پیدا شود بی‌شعشعه شمس الضحی&lt;/p&gt;&lt;p&gt;بی باده تو کی فتد در مغز نغزان مستی یی&lt;/p&gt;&lt;p&gt;بی عصمت تو کی رود شیطان بلا حول و لا&lt;/p&gt;&lt;p&gt;نی قرص سازد قرصی یی مطبوخ هم مطبوخیی&lt;/p&gt;&lt;p&gt;تا درنیندازی کفی ز اهلیله خود در دوا&lt;/p&gt;&lt;p&gt;امرت نغرد کی رود خورشید در برج اسد&lt;/p&gt;&lt;p&gt;بی تو کجا جنبد رگی در دست و پای پارسا&lt;/p&gt;&lt;p&gt;در مرگ هشیاری نهی در خواب بیداری نهی&lt;/p&gt;&lt;p&gt;در سنگ سقایی نهی در برق میرنده وفا&lt;/p&gt;&lt;p&gt;سیل سیاه شب برد هر جا که عقلست و خرد&lt;/p&gt;&lt;p&gt;زان سیلشان کی واخرد جز مشتری هل اتی&lt;/p&gt;&lt;p&gt;ای جان جان جزو و کل وی حله بخش باغ و گل&lt;/p&gt;&lt;p&gt;وی کوفته هر سو دهل کای جان حیران الصلا&lt;/p&gt;&lt;p&gt;هر کس فریباند مرا تا عشر بستاند مرا&lt;/p&gt;&lt;p&gt;آن کم دهد فهم بیا گوید که پیش من بیا&lt;/p&gt;&lt;p&gt;زان سو که فهمت می‌رسد باید که فهم آن سو رود&lt;/p&gt;&lt;p&gt;آن کت دهد طال بقا او را سزد طال بقا&lt;/p&gt;&lt;p&gt;هم او که دلتنگت کند سرسبز و گلرنگت کند&lt;/p&gt;&lt;p&gt;هم اوت آرد در دعا هم او دهد مزد دعا&lt;/p&gt;&lt;p&gt;هم ری و بی و نون را کردست مقرون با الف&lt;/p&gt;&lt;p&gt;در باد دم اندر دهن تا خوش بگویی ربنا&lt;/p&gt;&lt;p&gt;لبیک لبیک ای کرم سودای تست اندر سرم&lt;/p&gt;&lt;p&gt;ز آب تو چرخی می‌زنم مانند چرخ آسیا&lt;/p&gt;&lt;p&gt;هرگز نداند آسیا مقصود گردش‌های خود&lt;/p&gt;&lt;p&gt;کاستون قوت ماست او یا کسب و کار نانبا&lt;/p&gt;&lt;p&gt;آبیش گردان می‌کند او نیز چرخی می‌زند&lt;/p&gt;&lt;p&gt;حق آب را بسته کند او هم نمی‌جنبد ز جا&lt;/p&gt;&lt;p&gt;خامش که این گفتار ما می‌پرد از اسرار ما&lt;/p&gt;&lt;p&gt;تا گوید او که گفت او هرگز بننماید قفا&lt;/p&gt;', 'غزل شماره ۲۱', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(251, '&lt;p&gt;چندان بنالم ناله‌ها چندان برآرم رنگ‌ها&lt;/p&gt;&lt;p&gt;تا برکنم از آینه هر منکری من زنگ‌ها&lt;/p&gt;&lt;p&gt;بر مرکب عشق تو دل می‌راند و این مرکبش&lt;/p&gt;&lt;p&gt;در هر قدم می‌بگذرد زان سوی جان فرسنگ‌ها&lt;/p&gt;&lt;p&gt;بنما تو لعل روشنت بر کوری هر ظلمتی&lt;/p&gt;&lt;p&gt;تا بر سر سنگین‌دلان از عرش بارد سنگ‌ها&lt;/p&gt;&lt;p&gt;با این چنین تابانی‌ات دانی چرا منکر شدند ؟&lt;/p&gt;&lt;p&gt;کاین دولت و اقبال را باشد از ایشان ننگ‌ها&lt;/p&gt;&lt;p&gt;گر نی که کورندی چنین آخر بدیدندی چنان&lt;/p&gt;&lt;p&gt;آن سو هزاران جان ز مه چون اختران آونگ‌ها&lt;/p&gt;&lt;p&gt;چون از نشاط نور تو کوران همی بینا شوند&lt;/p&gt;&lt;p&gt;تا از خوشی راه تو رهوار گردد لنگ‌ها&lt;/p&gt;&lt;p&gt;اما چو اندر راه تو ناگاه بی‌خود می‌شود&lt;/p&gt;&lt;p&gt;هر عقل زیرا رسته شد در سبزه زارت بنگ‌ها&lt;/p&gt;&lt;p&gt;زین رو همی‌بینم کسان نالان چو نی وز دل تهی&lt;/p&gt;&lt;p&gt;زین رو دو صد سرو روان خم شد ز غم چون چنگ‌ها&lt;/p&gt;&lt;p&gt;زین رو هزاران کاروان بشکسته شد از ره روان&lt;/p&gt;&lt;p&gt;زین ره بسی کشتی پر بشکسته شد بر گنگ‌ها&lt;/p&gt;&lt;p&gt;اشکستگان را جان‌ها بسته‌ست بر اومید تو&lt;/p&gt;&lt;p&gt;تا دانش بی‌حد تو پیدا کند فرهنگ‌ها&lt;/p&gt;&lt;p&gt;تا قهر را برهم زند آن لطف اندر لطف تو&lt;/p&gt;&lt;p&gt;تا صلح گیرد هر طرف تا محو گردد جنگ‌ها&lt;/p&gt;&lt;p&gt;تا جَستنی نوعی دگر ره رفتنی طرزی دگر&lt;/p&gt;&lt;p&gt;پیدا شود در هر جگر در سلسله آهنگ‌ها&lt;/p&gt;&lt;p&gt;وز دعوت جذب خوشی آن شمس تبریزی شود&lt;/p&gt;&lt;p&gt;هر ذره انگیزنده‌ای هر موی چون سرهنگ‌ها&lt;/p&gt;', 'غزل شماره ۲۲', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(252, '&lt;p&gt;چون خون نخسپد خسروا چشمم کجا خسپد مها&lt;/p&gt;&lt;p&gt;کز چشم من دریای خون جوشان شد از جور و جفا&lt;/p&gt;&lt;p&gt;گر لب فروبندم کنون جانم به جوش آید درون&lt;/p&gt;&lt;p&gt;ور بر سرش آبی زنم بر سر زند او جوش را&lt;/p&gt;&lt;p&gt;معذور دارم خلق را گر منکرند از عشق ما&lt;/p&gt;&lt;p&gt;اه لیک خود معذور را کی باشد اقبال و سنا&lt;/p&gt;&lt;p&gt;از جوش خون نطقی به فم آن نطق آمد در قلم&lt;/p&gt;&lt;p&gt;شد حرف‌ها چون مور هم سوی سلیمان لابه را&lt;/p&gt;&lt;p&gt;کای شه سلیمان لطف وی لطف را از تو شرف&lt;/p&gt;&lt;p&gt;در تو را جان‌ها صدف باغ تو را جان‌ها گیا&lt;/p&gt;&lt;p&gt;ما مور بیچاره شده وز خرمن آواره شده&lt;/p&gt;&lt;p&gt;در سیر سیاره شده هم تو برس فریاد ما&lt;/p&gt;&lt;p&gt;ما بنده خاک کفت چون چاکران اندر صفت&lt;/p&gt;&lt;p&gt;ما دیدبان آن صفت با این همه عیب عما&lt;/p&gt;&lt;p&gt;تو یاد کن الطاف خود در سابق الله الصمد&lt;/p&gt;&lt;p&gt;در حق هر بدکار بد هم مجرم هر دو سرا&lt;/p&gt;&lt;p&gt;تو صدقه کن ای محتشم بر دل که دیدت ای صنم&lt;/p&gt;&lt;p&gt;در غیر تو چون بنگرم اندر زمین یا در سما&lt;/p&gt;&lt;p&gt;آن آب حیوان صفا هم در گلو گیرد ورا&lt;/p&gt;&lt;p&gt;کو خورده باشد باده‌ها زان خسرو میمون لقا&lt;/p&gt;&lt;p&gt;ای آفتاب اندر نظر تاریک و دلگیر و شرر&lt;/p&gt;&lt;p&gt;آن را که دید او آن قمر در خوبی و حسن و بها&lt;/p&gt;&lt;p&gt;ای جان شیرین تلخ وش بر عاشقان هجر کش&lt;/p&gt;&lt;p&gt;در فرقت آن شاه خوش بی‌کبر با صد کبریا&lt;/p&gt;&lt;p&gt;ای جان سخن کوتاه کن یا این سخن در راه کن&lt;/p&gt;&lt;p&gt;در راه شاهنشاه کن در سوی تبریز صفا&lt;/p&gt;&lt;p&gt;ای تن چو سگ کاهل مشو افتاده عوعو بس معو&lt;/p&gt;&lt;p&gt;تو بازگرد از خویش و رو سوی شهنشاه بقا&lt;/p&gt;&lt;p&gt;ای صد بقا خاک کفش آن صد شهنشه در صفش&lt;/p&gt;&lt;p&gt;گشته رهی صد آصفش واله سلیمان در ولا&lt;/p&gt;&lt;p&gt;وانگه سلیمان زان ولا لرزان ز مکر ابتلا&lt;/p&gt;&lt;p&gt;از ترس کو را آن علا کمتر شود از رشک‌ها&lt;/p&gt;&lt;p&gt;ناگه قضا را شیطنت از جام عز و سلطنت&lt;/p&gt;&lt;p&gt;بربوده از وی مکرمت کرده به ملکش اقتضا&lt;/p&gt;&lt;p&gt;چون یک دمی آن شاه فرد تدبیر ملک خویش کرد&lt;/p&gt;&lt;p&gt;دیو و پری را پای مرد ترتیب کرد آن پادشا&lt;/p&gt;&lt;p&gt;تا باز از آن عاقل شده دید از هوا غافل شده&lt;/p&gt;&lt;p&gt;زان باغ‌ها آفل شده بی‌بر شده هم بی‌نوا&lt;/p&gt;&lt;p&gt;زد تیغ قهر و قاهری بر گردن دیو و پری&lt;/p&gt;&lt;p&gt;کو را ز عشق آن سری مشغول کردند از قضا&lt;/p&gt;&lt;p&gt;زود اندرآمد لطف شه مخدوم شمس الدین چو مه&lt;/p&gt;&lt;p&gt;در منع او گفتا که نه عالم مسوز ای مجتبا&lt;/p&gt;&lt;p&gt;از شه چو دید او مژده‌ای آورد در حین سجده‌ای&lt;/p&gt;&lt;p&gt;تبریز را از وعده‌ای کارزد به این هر دو سرا&lt;/p&gt;', 'غزل شماره ۲۳', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(253, '&lt;p&gt;چون نالد این مسکین که تا رحم آید آن دلدار را&lt;/p&gt;&lt;p&gt;خون بارد این چشمان که تا بینم من آن گلزار را&lt;/p&gt;&lt;p&gt;خورشید چون افروزدم تا هجر کمتر سوزدم&lt;/p&gt;&lt;p&gt;دل حیلتی آموزدم کز سر بگیرم کار را&lt;/p&gt;&lt;p&gt;ای عقل کل ذوفنون تعلیم فرما یک فسون&lt;/p&gt;&lt;p&gt;کز وی بخیزد در درون رحمی نگارین یار را&lt;/p&gt;&lt;p&gt;چون نور آن شمع چگل می‌درنیابد جان و دل&lt;/p&gt;&lt;p&gt;کی داند آخر آب و گل دلخواه آن عیار را&lt;/p&gt;&lt;p&gt;جبریل با لطف و رشد عجل سمین را چون چشد&lt;/p&gt;&lt;p&gt;این دام و دانه کی کشد عنقای خوش منقار را&lt;/p&gt;&lt;p&gt;عنقا که یابد دام کس در پیش آن عنقامگس&lt;/p&gt;&lt;p&gt;ای عنکبوت عقل بس تا کی تنی این تار را&lt;/p&gt;&lt;p&gt;کو آن مسیح خوش دمی بی‌واسطه مریم یمی&lt;/p&gt;&lt;p&gt;کز وی دل ترسا همی پاره کند زنار را&lt;/p&gt;&lt;p&gt;دجال غم چون آتشی گسترد ز آتش مفرشی&lt;/p&gt;&lt;p&gt;کو عیسی خنجرکشی دجال بدکردار را&lt;/p&gt;&lt;p&gt;تن را سلامت‌ها ز تو جان را قیامت‌ها ز تو&lt;/p&gt;&lt;p&gt;عیسی علامت‌ها ز تو وصل قیامت وار را&lt;/p&gt;&lt;p&gt;ساغر ز غم در سر فتد چون سنگ در ساغر فتد&lt;/p&gt;&lt;p&gt;آتش به خار اندرفتد چون گل نباشد خار را&lt;/p&gt;&lt;p&gt;ماندم ز عذرا وامقی چون من نبودم لایقی&lt;/p&gt;&lt;p&gt;لیکن خمار عاشقی در سر دل خمار را&lt;/p&gt;&lt;p&gt;شطرنج دولت شاه را صد جان به خرجش راه را&lt;/p&gt;&lt;p&gt;صد که حمایل کاه را صد درد دردی خوار را&lt;/p&gt;&lt;p&gt;بینم به شه واصل شده می از خودی فاصل شده&lt;/p&gt;&lt;p&gt;وز شاه جان حاصل شده جان‌ها در او دیوار را&lt;/p&gt;&lt;p&gt;باشد که آن شاه حرون زان لطف از حدها برون&lt;/p&gt;&lt;p&gt;منسوخ گرداند کنون آن رسم استغفار را&lt;/p&gt;&lt;p&gt;جانی که رو این سو کند با بایزید او خو کند&lt;/p&gt;&lt;p&gt;یا در سنایی رو کند یا بو دهد عطار را&lt;/p&gt;&lt;p&gt;مخدوم جان کز جام او سرمست شد ایام او&lt;/p&gt;&lt;p&gt;گاهی که گویی نام او لازم شمر تکرار را&lt;/p&gt;&lt;p&gt;عالی خداوند شمس دین تبریز از او جان زمین&lt;/p&gt;&lt;p&gt;پرنور چون عرش مکین کو رشک شد انوار را&lt;/p&gt;&lt;p&gt;ای صد هزاران آفرین بر ساعت فرخترین&lt;/p&gt;&lt;p&gt;کان ناطق روح الامین بگشاید آن اسرار را&lt;/p&gt;&lt;p&gt;در پاکی بی‌مهر و کین در بزم عشق او نشین&lt;/p&gt;&lt;p&gt;در پرده منکر ببین آن پرده صدمسمار را&lt;/p&gt;', 'غزل شماره ۲۴', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(254, '&lt;p&gt;من دی نگفتم مر تو را کای بی‌نظیر خوش لقا&lt;/p&gt;&lt;p&gt;ای قد مه از رشک تو چون آسمان گشته دوتا&lt;/p&gt;&lt;p&gt;امروز صد چندان شدی حاجب بدی سلطان شدی&lt;/p&gt;&lt;p&gt;هم یوسف کنعان شدی هم فر نور مصطفی&lt;/p&gt;&lt;p&gt;امشب ستایمت ای پری فردا ز گفتن بگذری&lt;/p&gt;&lt;p&gt;فردا زمین و آسمان در شرح تو باشد فنا&lt;/p&gt;&lt;p&gt;امشب غنیمت دارمت باشم غلام و چاکرت&lt;/p&gt;&lt;p&gt;فردا ملک بی‌هش شود هم عرش بشکافد قبا&lt;/p&gt;&lt;p&gt;ناگه برآید صرصری نی بام ماند نه دری&lt;/p&gt;&lt;p&gt;زین پشگان پر کی زند چونک ندارد پیل پا&lt;/p&gt;&lt;p&gt;باز از میان صرصرش درتابد آن حسن و فرش&lt;/p&gt;&lt;p&gt;هر ذره‌ای خندان شود در فر آن شمس الضحی&lt;/p&gt;&lt;p&gt;تعلیم گیرد ذره‌ها زان آفتاب خوش لقا&lt;/p&gt;&lt;p&gt;صد ذرگی دلربا کان‌ها نبودش ز ابتدا&lt;/p&gt;', 'غزل شماره ۲۵', 'مولانا', 'دیوان شمس', 'غزل', 4, 6),
(256, '&lt;p&gt;گلرنگ شد در و دشت، از اشکباری ما&lt;/p&gt;&lt;p&gt;چون غیر خون نبارد، ابر بهاری ما&lt;/p&gt;&lt;p&gt;با صد هزار دیده، چشم چمن ندیده&lt;/p&gt;&lt;p&gt;در گلستان گیتی، مرغی به خواری ما&lt;/p&gt;&lt;p&gt;بی‌خانمان و مسکین، بدبخت و زار و غمگین&lt;/p&gt;&lt;p&gt;خوب اعتبار دارد، بی‌اعتباری ما&lt;/p&gt;&lt;p&gt;این پرده‌ها اگر شد، چون سینه پاره دانی&lt;/p&gt;&lt;p&gt;دل پرده پرده خون است، از پرده‌داری ما&lt;/p&gt;&lt;p&gt;یک دسته منفعت‌جو، با مشتی اهرمن‌خو&lt;/p&gt;&lt;p&gt;با هم قرار دادند، بر بی‌قراری ما&lt;/p&gt;&lt;p&gt;گوش سخن شنو نیست، روی زمین وگرنه&lt;/p&gt;&lt;p&gt;تا آسمان رسیده است، گلبانگ زاری ما&lt;/p&gt;&lt;p&gt;بی‌مهر روی آن مه، شب تا سحر نشد کم&lt;/p&gt;&lt;p&gt;اخترشماری دل، شب‌زنده‌داری ما&lt;/p&gt;&lt;p&gt;بس در مقام جانان، چون بنده جان فشاندیم&lt;/p&gt;&lt;p&gt;در عشق شد مسلم، پروردگاری ما&lt;/p&gt;&lt;p&gt;از فر فقر دادیم، فرمان به باد و آتش&lt;/p&gt;&lt;p&gt;اسباب آبرو شد، این خاکساری ما&lt;/p&gt;&lt;p&gt;در این دیار باری، ای کاش بود یاری&lt;/p&gt;&lt;p&gt;کز روی غمگساری، آید به یاری ما&lt;/p&gt;', 'غزل شماره ۱', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(257, '&lt;p&gt;ز بس ای دیده سر کردی شبِ غم اشکباری را&lt;/p&gt;&lt;p&gt;به روزِ خویش بنشاندی من و ابرِ بهاری را&lt;/p&gt;&lt;p&gt;گدا و بی‌نوا و پاکباز و مُفلِس و مِسکین&lt;/p&gt;&lt;p&gt;ندارد کس چو من سرمایه بی‌اعتباری را&lt;/p&gt;&lt;p&gt;چرا چون نافه آهو نگردد خونِ دل دانا&lt;/p&gt;&lt;p&gt;در آن کشور که پُشک ارزان کند مُشکِ تتاری را&lt;/p&gt;&lt;p&gt;غِنا با پافشاری کرد ایجادِ تهی‌دستی&lt;/p&gt;&lt;p&gt;خدا ویران نماید خانه سرمایه‌داری را&lt;/p&gt;&lt;p&gt;وکالت چون وزارت شد ردیفِ نامِ اشرافی&lt;/p&gt;&lt;p&gt;چه خوب آموختند این قوم علمِ خرسواری را&lt;/p&gt;&lt;p&gt;ز جورِ کارفرما کارگر آن سان به خود لرزد&lt;/p&gt;&lt;p&gt;که گردد روبه‌رو کبکِ دری بازِ شکاری را&lt;/p&gt;&lt;p&gt;ز بس بی‌آفتابِ عارِضت شب را سحر کردم&lt;/p&gt;&lt;p&gt;ز من آموخت اختر، شیوه شب‌زنده‌داری را&lt;/p&gt;', 'غزل شماره ۲', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(258, '&lt;p&gt;به هنگامِ سیه‌روزی عَلَم کن قَدِّ مردی را&lt;/p&gt;&lt;p&gt;ز خونِ سرخ‌فامِ خود بشوی این رنگِ زردی را&lt;/p&gt;&lt;p&gt;نصیبِ مردمِ دانا به جز خونِ جگر نَبوَد&lt;/p&gt;&lt;p&gt;در آن کشور که خلقش کرده عادت هرزه‌گردی را&lt;/p&gt;&lt;p&gt;ز لیدرهای جمعیت ندیدم غیر خودخواهی&lt;/p&gt;&lt;p&gt;از آن با جبر کردم اختیار اقدامِ فردی را&lt;/p&gt;&lt;p&gt;کنون تازم چنان بر این مبارزهای نالایق&lt;/p&gt;&lt;p&gt;که تا بیرون کنند از سر هوای هم‌نبردی را&lt;/p&gt;&lt;p&gt;شبی کز سوزِ دل شد برقِ آهم آسمان‌پیما&lt;/p&gt;&lt;p&gt;چو بختِ خود سیه کردم، سپهرِ لاجوردی را&lt;/p&gt;', 'غزل شماره ۳', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(259, '&lt;p&gt;گر که تأمین شود از دستِ غم آزادیِ ما&lt;/p&gt;&lt;p&gt;می‌رود تا به فلک هلهله شادیِ ما&lt;/p&gt;&lt;p&gt;ما از آن خانه‌خرابیم که معمارِ دو دل&lt;/p&gt;&lt;p&gt;نیست یک لحظه در اندیشه&amp;nbsp;آبادیِ ما&lt;/p&gt;&lt;p&gt;بس که جان را به رَهِ عشق تو شیرین دادیم&lt;/p&gt;&lt;p&gt;تیشه خون می‌خورد از حسرتِ فرهادیِ ما&lt;/p&gt;&lt;p&gt;داد از دستِ جفای تو که با خیره‌سری&lt;/p&gt;&lt;p&gt;کرد پامالِ ستم مدفنِ اجدادیِ ما&lt;/p&gt;&lt;p&gt;آنچنان شُهره به شاگردیِ عشقِ تو شدیم&lt;/p&gt;&lt;p&gt;که جنون سرخطِ زر داد به استادیِ ما&lt;/p&gt;&lt;p&gt;فرخی دادِ سخندانی از آن داد که کرد&lt;/p&gt;&lt;p&gt;در غزل بندگیِ طبعِ خدادادیِ ما&lt;/p&gt;', 'غزل شماره ۴', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(260, '&lt;p&gt;ای که پرسی تا به کی دربند دربندیم ما&lt;/p&gt;&lt;p&gt;تا که آزادی بود دربند در بندیم ما&lt;/p&gt;&lt;p&gt;خوار و زار و بی‌کس و بی‌خانمان و دربه‌در&lt;/p&gt;&lt;p&gt;با وجود این همه غم، شاد و خرسندیم ما&lt;/p&gt;&lt;p&gt;جای ما در گوشه صحرا بُوَد مانند کوه&lt;/p&gt;&lt;p&gt;گوشه‌گیر و سربلند و سخت‌پیوندیم ما&lt;/p&gt;&lt;p&gt;در گلستان جهان چون غنچه‌های صبحدم&lt;/p&gt;&lt;p&gt;با درون پر ز خون در حال لبخندیم ما&lt;/p&gt;&lt;p&gt;مادر ایران نشد از مرد زاییدن عقیم&lt;/p&gt;&lt;p&gt;زان زن فرخنده را فرزانه فرزندیم ما&lt;/p&gt;&lt;p&gt;ارتقاء ما میسر می‌شود با سوختن&lt;/p&gt;&lt;p&gt;بر فراز مجمر گیتی چو اسفندیم ما&lt;/p&gt;&lt;p&gt;گر نمی‌آمد چنین روزی کجا دانند خلق&lt;/p&gt;&lt;p&gt;در میان همگنان بی‌مثل و مانندیم ما&lt;/p&gt;&lt;p&gt;کشتی ما را خدایا ناخدا از هم شکست&lt;/p&gt;&lt;p&gt;با وجود آنکه کشتی را خداوندیم ما&lt;/p&gt;&lt;p&gt;در جهان کهنه ماند نام ما و فرخی&lt;/p&gt;&lt;p&gt;چون ز ایجاد غزل طرح نو افکندیم ما&lt;/p&gt;', 'غزل شماره ۵', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(261, '&lt;p&gt;بی‌سر و پایی اگر در چشم خوار آید ترا&lt;/p&gt;&lt;p&gt;دل به دست آرش که یک روزی به کار آید ترا&lt;/p&gt;&lt;p&gt;با هزاران رنج بردن گنج عالم هیچ نیست&lt;/p&gt;&lt;p&gt;دولت آن باشد ز در بی‌انتظار آید ترا&lt;/p&gt;&lt;p&gt;دولت هر مملکت در اختیار ملت است&lt;/p&gt;&lt;p&gt;آخر ای ملت به کف کی اختیار آید ترا&lt;/p&gt;&lt;p&gt;پافشاری کن، حقوق زندگان آور به دست&lt;/p&gt;&lt;p&gt;ورنه همچون مرده تا محشر فشار آید ترا&lt;/p&gt;&lt;p&gt;نام جان کندن به شهر مردگان چون زندگی‌ست&lt;/p&gt;&lt;p&gt;همچو من زین زندگانی ننگ و عار آید ترا&lt;/p&gt;&lt;p&gt;تا نسازی دست و دامن را نگار از خون دل&lt;/p&gt;&lt;p&gt;کی به کف بی‌خون دل دست نگار آید ترا&lt;/p&gt;&lt;p&gt;کیستی ای نوگل خندان که در باغ بهشت&lt;/p&gt;&lt;p&gt;بلبل شوریده‌دل هر سو هزار آید ترا&lt;/p&gt;&lt;p&gt;کن روان از خون دل جو در کنار خویشتن&lt;/p&gt;&lt;p&gt;تا مگر آن سرو دلجو در کنار آید ترا&lt;/p&gt;&lt;p&gt;فرخی بسپار جان وز انتظار آسوده شو&lt;/p&gt;&lt;p&gt;گر به بالینت نیامد در مزار آید ترا&lt;/p&gt;', 'غزل شماره ۶', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(262, '&lt;p&gt;دوش یارم زد چو بر زلف پریشان شانه را&lt;/p&gt;&lt;p&gt;موبه‌مو بگذاشت زیر بار دل‌ها شانه را&lt;/p&gt;&lt;p&gt;نیست عاقل را خبر از عالم دیوانگی&lt;/p&gt;&lt;p&gt;گر ز نادانی ملامت می‌کند، دیوانه را&lt;/p&gt;&lt;p&gt;در عزای عاشق خود شمع سوزد تا به حشر&lt;/p&gt;&lt;p&gt;خوب معشوق وفاداری بود، پروانه را&lt;/p&gt;&lt;p&gt;جز دل سوراخ سوراخش نبود از دست شیخ&lt;/p&gt;&lt;p&gt;دانه‌دانه چون شمردم سبحه صد دانه را&lt;/p&gt;&lt;p&gt;این بنای داد یارب چیست کز بیداد آن&lt;/p&gt;&lt;p&gt;دادها باشد به گردون محرم و بیگانه را&lt;/p&gt;&lt;p&gt;از در و دیوار این عدلیه بارد ظلم و جور&lt;/p&gt;&lt;p&gt;محو باید کرد یکسر این عدالتخانه را&lt;/p&gt;', 'غزل شماره ۷', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(263, '&lt;p&gt;با دل آغشته در خون گر چه خاموشیم ما&lt;/p&gt;&lt;p&gt;لیک چون خم دهان کف کرده در جوشیم ما&lt;/p&gt;&lt;p&gt;ساغر تقدیر ما را مست آزادی نمود&lt;/p&gt;&lt;p&gt;زین سبب از نشئه آن باده مدهوشیم ما&lt;/p&gt;&lt;p&gt;گر توئی سرمایه دار با وقار تازه چرخ&lt;/p&gt;&lt;p&gt;کهنه رند لات و لوت خانه بر دوشیم ما&lt;/p&gt;&lt;p&gt;همچو زنبور عسل هستیم چون ما لاجرم&lt;/p&gt;&lt;p&gt;هر غنی را نیش و هر بیچاره را نوشیم ما&lt;/p&gt;&lt;p&gt;نور یزدان هر مکان، سر تا به پا هستیم چشم&lt;/p&gt;&lt;p&gt;حرف ایمان هرکجا، پا تا به سر گوشیم ما&lt;/p&gt;&lt;p&gt;دوش زیر بار آزادی چه سنگین گشت دوش&lt;/p&gt;&lt;p&gt;تا قیامت زیر بار منت دوشیم ما&lt;/p&gt;&lt;p&gt;حلقه بر گوش تهی دستان بود گر فرخی&lt;/p&gt;&lt;p&gt;جرعه نوش جام رندان خطاپوشیم ما&lt;/p&gt;', 'غزل شماره ۸', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(264, '&lt;p&gt;باور نکنی گر غم دل گفتن ما را&lt;/p&gt;&lt;p&gt;بین از اثر اشک به خون خفتن ما را&lt;/p&gt;&lt;p&gt;صد بار بهار آمد و یکبار ندیدند&lt;/p&gt;&lt;p&gt;مرغان مصیبت زده بشکفتن ما را&lt;/p&gt;&lt;p&gt;در زندگی از بسکه گرانجانی ما دید&lt;/p&gt;&lt;p&gt;حاضر نبود مرگ پذیرفتن ما را&lt;/p&gt;&lt;p&gt;رفت از بر من گرچه رهش با مژه رفتم&lt;/p&gt;&lt;p&gt;ره رفتن او بنگر و ره رفتن ما را&lt;/p&gt;&lt;p&gt;جز فرخی از طبع گهربار ندارد&lt;/p&gt;&lt;p&gt;کس طرز غزل گفتن و در سفتن ما را&lt;/p&gt;', 'غزل شماره ۹', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(265, '&lt;p&gt;با آنکه کسی نیست به وارستگی ما&lt;/p&gt;&lt;p&gt;هست از چه به گیسوی تو دلبستگی ما&lt;/p&gt;&lt;p&gt;بشکست مرا پشت اگر بار درستی&lt;/p&gt;&lt;p&gt;میزان درستی شده بشکستگی ما&lt;/p&gt;&lt;p&gt;ما خسته دلان قلب جهانیم و از اینرو&lt;/p&gt;&lt;p&gt;دل خسته جهانیست ز دلخستگی ما&lt;/p&gt;&lt;p&gt;در مملکتی کآتش آشوب بود تند&lt;/p&gt;&lt;p&gt;بی‌جا نبود کندی و آهستگی ما&lt;/p&gt;&lt;p&gt;از حسن عمل با خط برجسته از این پس&lt;/p&gt;&lt;p&gt;تاریخ گواه است به برجستگی ما&lt;/p&gt;', 'غزل شماره ۱۰', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(266, '&lt;p&gt;زاهدا چند کنی منع قدح نوشی را&lt;/p&gt;&lt;p&gt;که به عالم ندهم عالم مدهوشی را&lt;/p&gt;&lt;p&gt;بایدش سوخت به هر جمع سراپا چون شمع&lt;/p&gt;&lt;p&gt;هر که از دست دهد شیوه خاموشی را&lt;/p&gt;&lt;p&gt;زندگی بی تو مرا ساخت چنان از جان سیر&lt;/p&gt;&lt;p&gt;که طلب می کنم از مرگ هم آغوشی را&lt;/p&gt;&lt;p&gt;آنکه تا دوش جگر گوشه ناپاکی بود&lt;/p&gt;&lt;p&gt;دارد امروز به پاکان سر همدوشی را&lt;/p&gt;&lt;p&gt;وای بر حافظه ما که ز طفلی همگی&lt;/p&gt;&lt;p&gt;کرده از حفظ الفبای فراموشی را&lt;/p&gt;&lt;p&gt;فرخی گر چه گنهکار و خطاپیشه بود&lt;/p&gt;&lt;p&gt;دارد از لطف تو امید خطاپوشی را&lt;/p&gt;', 'غزل شماره ۱۱', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(267, '&lt;p&gt;سخت با دل، دل سخت تو به جنگ است اینجا&lt;/p&gt;&lt;p&gt;تا که را دل شکند شیشه و سنگ است اینجا&lt;/p&gt;&lt;p&gt;در بهاران گل این باغ ز غم وا نشود&lt;/p&gt;&lt;p&gt;غنچه تا فصل خزان با دل تنگ است اینجا&lt;/p&gt;&lt;p&gt;نکنم شکوه ز مژگان تو اما چکنم&lt;/p&gt;&lt;p&gt;که دل آماجگه نوک خدنگ است اینجا&lt;/p&gt;&lt;p&gt;از می میکده دهر مشو مست غرور&lt;/p&gt;&lt;p&gt;که به ساغر عوض شهد شرنگ است اینجا&lt;/p&gt;&lt;p&gt;بی خطر کس نبرد گوهر از این لجه ژرف&lt;/p&gt;&lt;p&gt;کام دل در گرو کام نهنگ است اینجا&lt;/p&gt;&lt;p&gt;من نه تنها به ره عشق ز پا افتادم&lt;/p&gt;&lt;p&gt;پای یک ران فلک خسته و لنگ است اینجا&lt;/p&gt;&lt;p&gt;تا به سر حد جنونم بشتاب آوردی&lt;/p&gt;&lt;p&gt;ای دل آهسته که هنگام درنگ است اینجا&lt;/p&gt;&lt;p&gt;گل یک رنگ در این باغ نگردد سر سبز&lt;/p&gt;&lt;p&gt;خرمی قسمت گلهای دو رنگ است اینجا&lt;/p&gt;&lt;p&gt;از خطا بسکه در این خطه سیه رو پر شد&lt;/p&gt;&lt;p&gt;پیش بیگانه کم از کشور زنگ است اینجا&lt;/p&gt;&lt;p&gt;فرخی با همه شیرین سخنی از دهنت&lt;/p&gt;&lt;p&gt;دم نزد هیچ ز بس قافیه تنگ است اینجا&lt;/p&gt;', 'غزل شماره ۱۲', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(268, '&lt;p&gt;با بتی تا بطی از باده ناب است مرا&lt;/p&gt;&lt;p&gt;گاه پیرانه سری عهد شباب است مرا&lt;/p&gt;&lt;p&gt;گوش تا گوش جهان گر شودم زیر نگین&lt;/p&gt;&lt;p&gt;چشم بر گوشه آن چشم خراب است مرا&lt;/p&gt;&lt;p&gt;هست از کثرت جوشیدن دریای جنون&lt;/p&gt;&lt;p&gt;داغهائی که به دل همچو حباب است مرا&lt;/p&gt;&lt;p&gt;بی مه روی تو، اختر شمرم تا به سحر&lt;/p&gt;&lt;p&gt;شب هجر تو مگر روز حساب است مرا&lt;/p&gt;&lt;p&gt;رنگ خونابه دهد بوی جگر سوختگی&lt;/p&gt;&lt;p&gt;بسکه دل ز آتش جور تو کباب است مرا&lt;/p&gt;&lt;p&gt;مایه زندگی امروزه دورنگی گر نیست&lt;/p&gt;&lt;p&gt;بیدرنگ از چه سوی مرگ شتاب است مرا&lt;/p&gt;&lt;p&gt;چشم من در پی دارائی اسکندر نیست&lt;/p&gt;&lt;p&gt;چشمه آب خضر همچو سراب است مرا&lt;/p&gt;&lt;p&gt;نقشهائی که تو در پرده گیتی نگری&lt;/p&gt;&lt;p&gt;همه چون واقعه عالم خواب است مرا&lt;/p&gt;&lt;p&gt;چکنم گر نکنم زندگی طوفانی&lt;/p&gt;&lt;p&gt;چون به یک چشم زدن خانه بر آب است مرا&lt;/p&gt;', 'غزل شماره ۱۳', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(269, '&lt;p&gt;تا دیده دلم عارِضِ آن رَشکِ پری را&lt;/p&gt;&lt;p&gt;پوشیده به تن جامه&amp;nbsp;دیوانه‌گری را&lt;/p&gt;&lt;p&gt;چون مردِ هنرپیشه به هر دوره ذلیل است&lt;/p&gt;&lt;p&gt;خوش آنکه کند پیشه خود بی‌هنری را&lt;/p&gt;&lt;p&gt;شب تا به سحر در طلبِ صبحِ وصالت&lt;/p&gt;&lt;p&gt;بگرفته دلم دامنِ آهِ سحری را&lt;/p&gt;&lt;p&gt;در عصرِ تَمَدُّن چون تَوَحُّش شده افزون&lt;/p&gt;&lt;p&gt;بر دیده کشم سرمه عهدِ حجری را&lt;/p&gt;&lt;p&gt;یاقوت مگر پیشِ لبِ لعلِ تو دم زد&lt;/p&gt;&lt;p&gt;کز رَشک چو من جلوه دهد خون‌جگری را&lt;/p&gt;&lt;p&gt;از روزِ ازل دستِ قضا قسمتِ ما کرد&lt;/p&gt;&lt;p&gt;رسوایی و آوارگی و دربه‌دری را&lt;/p&gt;&lt;p&gt;تا فرخی از سِرِّ غمِ عشق خبر شد&lt;/p&gt;&lt;p&gt;رجحان دهد از هر خبری بی‌خبری را&lt;/p&gt;', 'غزل شماره ۱۴', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(270, '&lt;p&gt;ی‌پرده برآمد مهر زین پرده مینایی&lt;/p&gt;&lt;p&gt;از پرده تو ای مه‌روی، بیرون ز چه می‌آیی&lt;/p&gt;&lt;p&gt;بر یاد شهید عشق، جامی زن و کامی جو&lt;/p&gt;&lt;p&gt;گر ساده در آغوشی، ور باده به مینایی&lt;/p&gt;&lt;p&gt;ای دل به سر زلفش، دستی زده‌ای زین روی&lt;/p&gt;&lt;p&gt;هم رشته به بازویی، هم سلسله در پایی&lt;/p&gt;&lt;p&gt;پیش نظر عاقل، چیزی نبود خوش‌تر&lt;/p&gt;&lt;p&gt;از مسلک مجنونی، وز شیوه شیدایی&lt;/p&gt;&lt;p&gt;فردای قیامت را، در چشم نمی‌آرد&lt;/p&gt;&lt;p&gt;دیده است چو من مجنون، هرکس شب تنهایی&lt;/p&gt;&lt;p&gt;با فقر و فنا خو کن، زین عالم دون بگذر&lt;/p&gt;&lt;p&gt;بنگر چه شد اسکندر، با آن همه دارایی&lt;/p&gt;&lt;p&gt;چون فرخی بیدل، کی شد به سخن مشهور&lt;/p&gt;&lt;p&gt;بلبل به نواخوانی، طوطی به شکرخایی&lt;/p&gt;', 'غزل شماره ۱۵', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(271, '&lt;p&gt;غم نیست که با اهل جفا مهر و وفا داشت&lt;/p&gt;&lt;p&gt;با اهل وفا از چه دگر جور و جفا داشت&lt;/p&gt;&lt;p&gt;از کوی تو آن روز که دل بار سفر بست&lt;/p&gt;&lt;p&gt;در هر قدمی دیده حسرت بقفا داشت&lt;/p&gt;&lt;p&gt;همچشمی چشمان سیاه تو نمی کرد&lt;/p&gt;&lt;p&gt;در چشم اگر نرگس بیشرم، حیا داشت&lt;/p&gt;&lt;p&gt;هر روز یکی خواجه فرمانده ما گشت&lt;/p&gt;&lt;p&gt;یک بنده در این خانه دو صد خانه خدا داشت&lt;/p&gt;&lt;p&gt;بی برگ و نوائی نفشارد جگر مرد&lt;/p&gt;&lt;p&gt;نی با دل سوراخ، دو صد شور و نوا داشت&lt;/p&gt;&lt;p&gt;بشکست دلم را و ندانست ز طفلی&lt;/p&gt;&lt;p&gt;کاین گوهر یکدانه چه مقدار بها داشت&lt;/p&gt;&lt;p&gt;با دست تهی پا بسر چرخ برین زد&lt;/p&gt;&lt;p&gt;چون فرخی آن رند که با فقر غنا داشت&lt;/p&gt;', 'غزل شماره ۱۶', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(272, '&lt;p&gt;گرچه مجنونم و صحرای جنون جای من است&lt;/p&gt;&lt;p&gt;لیک دیوانه‌تر از من دلِ شیدای من است&lt;/p&gt;&lt;p&gt;آخر از راه دل و دیده سر آرد بیرون&lt;/p&gt;&lt;p&gt;نیش آن خار که از دست تو در پای من است&lt;/p&gt;&lt;p&gt;رخت بربست ز دل شادی و هنگام وداع&lt;/p&gt;&lt;p&gt;با غمت گفت که یا جای تو یا جای من است&lt;/p&gt;&lt;p&gt;جامه‌ای را که به خون رنگ نمودم امروز&lt;/p&gt;&lt;p&gt;بر جفاکاری تو شاهد فردای من است&lt;/p&gt;&lt;p&gt;چیزهایی که نبایست ببیند، بس دید&lt;/p&gt;&lt;p&gt;به خدا قاتلِ من دیده بینای من است&lt;/p&gt;&lt;p&gt;سرِ تسلیم به چرخ آنکه نیاورد فرود&lt;/p&gt;&lt;p&gt;با همه جور و ستم همّتِ والای من است&lt;/p&gt;&lt;p&gt;دل تماشایی تو، دیده تماشایی دل&lt;/p&gt;&lt;p&gt;من به فکر دل و خلقی به تماشای من است&lt;/p&gt;&lt;p&gt;آنکه در راهِ طلب خسته نگردد هرگز&lt;/p&gt;&lt;p&gt;پای پُرآبله بادیه‌پیمای من است&lt;/p&gt;', 'غزل شماره ۱۷', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(273, '&lt;p&gt;در غمت کاری که آه آتشینم کرده است&lt;/p&gt;&lt;p&gt;آنقدر دانم که خاکسترنشینم کرده است&lt;/p&gt;&lt;p&gt;دولت وصل تو شیرین‌لب به رغم آسمان&lt;/p&gt;&lt;p&gt;با گدایی خسرو روی زمینم کرده است&lt;/p&gt;&lt;p&gt;تا برون آرم دمار از آن گروه ماردوش&lt;/p&gt;&lt;p&gt;تربیت همدوش پور آبتینم کرده است&lt;/p&gt;&lt;p&gt;خاک کوی آن بهشتی‌طلعت غلمان‌سرشت&lt;/p&gt;&lt;p&gt;بی‌نیاز از کوثر و خلد برینم کرده است&lt;/p&gt;&lt;p&gt;سوختم از دست غم پا تا به سر در راه عشق&lt;/p&gt;&lt;p&gt;چند گویم آنچنان یا این چنینم کرده است&lt;/p&gt;', 'غزل شماره ۱۸', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(274, '&lt;p&gt;عشقبازی را چه خوش فرهاد مسکین کرد و رفت&lt;/p&gt;&lt;p&gt;جان شیرین را فدای جان شیرین کرد و رفت&lt;/p&gt;&lt;p&gt;یادگاری در جهان از تیشه بهر خود گذاشت&lt;/p&gt;&lt;p&gt;بیستون را گر ز خون خویش رنگین کرد و رفت&lt;/p&gt;&lt;p&gt;دیشب آن نامهربان مه آمد و از اشک شوق&lt;/p&gt;&lt;p&gt;آسمان دامنم را پر ز پروین کرد و رفت&lt;/p&gt;&lt;p&gt;پیش از این‌ها ای مسلمان داشتم دین و دلی&lt;/p&gt;&lt;p&gt;آن بت کافر چنینم بی‌دل و دین کرد و رفت&lt;/p&gt;&lt;p&gt;تا شود آگه ز حال زار دل، باد صبا&lt;/p&gt;&lt;p&gt;موبه‌مو گردش در آن گیسوی پرچین کرد و رفت&lt;/p&gt;&lt;p&gt;وای بر آن مردم‌آزاری که در ده روز عمر&lt;/p&gt;&lt;p&gt;آمد و خود را میان خلق ننگین کرد و رفت&lt;/p&gt;&lt;p&gt;این غزل را تا غزال مشک موی من شنید&lt;/p&gt;&lt;p&gt;آمد و بر فرخی صد گونه تحسین کرد و رفت&lt;/p&gt;', 'غزل شماره ۱۹', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(275, '&lt;p&gt;کیست در شهر که از دست غمت داد نداشت&lt;/p&gt;&lt;p&gt;هیچکس همچو تو بیدادگری یاد نداشت&lt;/p&gt;&lt;p&gt;گوش فریاد شنو نیست خدایا در شهر&lt;/p&gt;&lt;p&gt;ورنه از دست تو کس نیست که فریاد نداشت&lt;/p&gt;&lt;p&gt;خوش به گل درد دل خویش به افغان می گفت&lt;/p&gt;&lt;p&gt;مرغ بیدل خبر از حیله صیاد نداشت&lt;/p&gt;&lt;p&gt;عشق در کوه کنی داد نشان قدرت خویش&lt;/p&gt;&lt;p&gt;ورنه این مایه هنر تیشه فرهاد نداشت&lt;/p&gt;&lt;p&gt;جز به آزادی ملت نبود آبادی&lt;/p&gt;&lt;p&gt;آه اگر مملکتی ملت آزاد نداشت&lt;/p&gt;&lt;p&gt;فقر و بدبختی و بیچارگی و خون جگر&lt;/p&gt;&lt;p&gt;چه غمی بود که این خاطر ناشاد نداشت&lt;/p&gt;&lt;p&gt;هر بنائی ننهادند بر افکار عموم&lt;/p&gt;&lt;p&gt;بود اگر ز آهن، او پایه و بنیاد نداشت&lt;/p&gt;&lt;p&gt;کی توانست بدین پایه دهد داد سخن&lt;/p&gt;&lt;p&gt;فرخی گر به غزل طبع خداداد نداشت&lt;/p&gt;', 'غزل شماره ۲۰', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(276, '&lt;p&gt;جهان نمای درستی، دل شکسته ماست&lt;/p&gt;&lt;p&gt;کلید قفل حقیقت زبان بسته ماست&lt;/p&gt;&lt;p&gt;مگو چه دانه تسبیح از چه پامالیم&lt;/p&gt;&lt;p&gt;که عیب ما همه از رشته گسسته ماست&lt;/p&gt;&lt;p&gt;دو دسته یکسره در جنگ و توده بدبخت&lt;/p&gt;&lt;p&gt;در این مبارزه پامال هر دو دسته ماست&lt;/p&gt;&lt;p&gt;نوید صلح امید آنکه می دهد به بشر&lt;/p&gt;&lt;p&gt;سفیر خوش خبر و پیک پی خجسته ماست&lt;/p&gt;&lt;p&gt;نه غنچه باز نه گل بو دهد در این گلشن&lt;/p&gt;&lt;p&gt;گواه آن دل تنگ و دماغ خسته ماست&lt;/p&gt;&lt;p&gt;ز قید و بند جهان فرخی بود آزاد&lt;/p&gt;&lt;p&gt;که رند دربه‌ در و از علاقه رسته ماست&lt;/p&gt;', 'غزل شماره ۲۱', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(277, '&lt;p&gt;هر لحظه مزن در، که در این خانه کسی نیست&lt;/p&gt;&lt;p&gt;بیهوده مکن ناله، که فریادرسی نیست&lt;/p&gt;&lt;p&gt;شهری که شه و شحنه و شیخش همه مستند&lt;/p&gt;&lt;p&gt;شاهد شکند شیشه که بیم عسسی نیست&lt;/p&gt;&lt;p&gt;آزادی اگر می‌طلبی غرقه به خون باش&lt;/p&gt;&lt;p&gt;کاین گلبن نوخاسته بی‌خار و خسی نیست&lt;/p&gt;&lt;p&gt;دهقان رهد از زحمت ما یک نفس اما&lt;/p&gt;&lt;p&gt;آن روز که دیگر ز حیاتش نفسی نیست&lt;/p&gt;&lt;p&gt;با بودن مجلس بود آزادی ما محو&lt;/p&gt;&lt;p&gt;چون مرغ که پابسته ولی در قفسی نیست&lt;/p&gt;&lt;p&gt;گر موجد گندم بود از چیست که زارع&lt;/p&gt;&lt;p&gt;از نان جوین سیر به قدر عدسی نیست&lt;/p&gt;&lt;p&gt;هر سر به هوای سر و سامانی و ما را&lt;/p&gt;&lt;p&gt;در دل به جز آزادی ایران هوسی نیست&lt;/p&gt;&lt;p&gt;تازند و برند اهل جهان گوی تمدن&lt;/p&gt;&lt;p&gt;ای فارْس مگر فارِسِ ما را فرسی نیست&lt;/p&gt;&lt;p&gt;در راه طلب فرخی ار خسته نگردید&lt;/p&gt;&lt;p&gt;دانست که تا منزل مقصود بسی نیست&lt;/p&gt;', 'غزل شماره ۲۲', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(278, '&lt;p&gt;خرم آن روزی که ما را جای در میخانه بود&lt;/p&gt;&lt;p&gt;تا دل شب بوسه گاه ما لب پیمانه بود&lt;/p&gt;&lt;p&gt;عقده های اهل دل را مو به مو می کرد باز&lt;/p&gt;&lt;p&gt;در کف مشاطه باد صبا گر شانه بود&lt;/p&gt;&lt;p&gt;با من و مرغ بهشتی کی شود هم آشیان&lt;/p&gt;&lt;p&gt;آن نظر تنگی که چشمش سوی آب و دانه بود&lt;/p&gt;&lt;p&gt;سوخت از یک شعله آخر شمع را پا تا به سر&lt;/p&gt;&lt;p&gt;برق آن آتش که در بال و پر پروانه بود&lt;/p&gt;&lt;p&gt;فرق شهر و دشت از نقص جنون کی می گذاشت&lt;/p&gt;&lt;p&gt;راستی مجنون اگر مانند من دیوانه بود&lt;/p&gt;&lt;p&gt;خانه آباد ما را کرد در یک دم خراب&lt;/p&gt;&lt;p&gt;جور و بیدادی که در این کشور ویرانه بود&lt;/p&gt;&lt;p&gt;هر کرا از جنس این مردم گرفتم یار خویش&lt;/p&gt;&lt;p&gt;دیدم از ناآشنایی محرم بیگانه بود&lt;/p&gt;&lt;p&gt;روزگار او را نسازد پست همچون فرخی&lt;/p&gt;&lt;p&gt;هر که با طبع بلند و همت مردانه بود&lt;/p&gt;', 'غزل شماره ۲۳', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(279, '&lt;p&gt;چون ز شهر آن شاهد شیرین‌شمایل می‌رود&lt;/p&gt;&lt;p&gt;در قفایش، کاروان در کاروان، دل می‌رود&lt;/p&gt;&lt;p&gt;همچو کز دنبال او وادی به وادی چشم رفت&lt;/p&gt;&lt;p&gt;پیش‌پیشش اشک هم منزل به منزل می‌رود&lt;/p&gt;&lt;p&gt;دل اگر دیوانه نبود الفتش با زلف چیست&lt;/p&gt;&lt;p&gt;کی به پای خویش عاقل در سلاسل می‌رود&lt;/p&gt;&lt;p&gt;چون به باطن در جهان نبود وجودی غیر حق&lt;/p&gt;&lt;p&gt;حق بود آن هم که در ظاهر به باطل می‌رود&lt;/p&gt;&lt;p&gt;یارب این مقتول عشق از چیست کز راه وفا&lt;/p&gt;&lt;p&gt;سر به کف بگرفته استقبال قاتل می‌رود&lt;/p&gt;&lt;p&gt;کوی لیلی بس خطرناک است ز آنجا تا به حشر&lt;/p&gt;&lt;p&gt;همچو مجنون بازگردد هرچه عاقل می‌رود&lt;/p&gt;', 'غزل شماره ۲۴', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(280, '&lt;p&gt;کام دلم ز وصل تو حاصل نمی‌شود&lt;/p&gt;&lt;p&gt;گیرم که شد، دگر دل من دل نمی‌شود&lt;/p&gt;&lt;p&gt;دیوانه‌ای که مزه دیوانگی چشید&lt;/p&gt;&lt;p&gt;با صد هزار سلسله عاقل نمی‌شود&lt;/p&gt;&lt;p&gt;اجرا نشد میان بشر گر مرام ما&lt;/p&gt;&lt;p&gt;آجل شود اگر چه به عاجل نمی‌شود&lt;/p&gt;&lt;p&gt;حق گر خورد شکست ز یک دسته بی‌شرف&lt;/p&gt;&lt;p&gt;حق است و حق به مغلطه باطل نمی‌شود&lt;/p&gt;&lt;p&gt;زور و فشار و سختی و تهدید و گیر و دار&lt;/p&gt;&lt;p&gt;با این رویه حل مسائل نمی‌شود&lt;/p&gt;&lt;p&gt;تکفیر و ارتجاع و خرافات و های هوی&lt;/p&gt;&lt;p&gt;از این طریق طی مراحل نمی‌شود&lt;/p&gt;&lt;p&gt;مجلس مقام مردم ناپاک دل مخواه&lt;/p&gt;&lt;p&gt;کاین جای پاک جای اراذل نمی‌شود&lt;/p&gt;&lt;p&gt;یک ملک بی‌عقیده و یک شهر چاپلوس&lt;/p&gt;&lt;p&gt;یارب بلا برای چه نازل نمی‌شود&lt;/p&gt;&lt;p&gt;نازم به عزم ثابت چون کوه فرخی&lt;/p&gt;&lt;p&gt;کز باد سهمگین متزلزل نمی‌شود&lt;/p&gt;', 'غزل شماره ۲۵', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(281, '&lt;p&gt;اگر مرد خردمندی تو را فرزانگی باید&lt;/p&gt;&lt;p&gt;وگر همدرد مجنونی غم دیوانگی باید&lt;/p&gt;&lt;p&gt;رفیقی بایدم همدم، به شادی یار و در غم هم&lt;/p&gt;&lt;p&gt;وزین خویشان نامحرم مرا بیگانگی باید&lt;/p&gt;&lt;p&gt;من و گنج سخن‌سنجی که کنجی خواهد و رنجی&lt;/p&gt;&lt;p&gt;چو من گر اهل این گنجی تو را ویرانگی باید&lt;/p&gt;&lt;p&gt;چو زد دهقان زحمتکش به کشت عمر خود آتش&lt;/p&gt;&lt;p&gt;تو را ای مالک سرکش جوی مردانگی باید&lt;/p&gt;&lt;p&gt;قناعت داده دنیا را گروه بی‌سر و پا را&lt;/p&gt;&lt;p&gt;چرا با این غنا ما را، غم بی‌خانگی باید&lt;/p&gt;&lt;p&gt;در این بی‌انتها وادی، چو پا از عشق بنهادی&lt;/p&gt;&lt;p&gt;به گرد شمع آزادی، تو را پروانگی باید&lt;/p&gt;', 'غزل شماره ۲۶', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(282, '&lt;p&gt;پیش خود تا فکر نفع بینهایت می‌کند&lt;/p&gt;&lt;p&gt;کارفرما کارگر را کی رعایت می‌کند&lt;/p&gt;&lt;p&gt;ماه نو با روی پر خون شفق را کن نگاه&lt;/p&gt;&lt;p&gt;کان ز داس و دست دهقانان حکایت می‌کند&lt;/p&gt;&lt;p&gt;فوری از نای وزیر آید نوای راضیم&lt;/p&gt;&lt;p&gt;از فلان مأمور اگر ملت شکایت می‌کند&lt;/p&gt;&lt;p&gt;آخر ای مظلوم از مظلوم چون خود یاد کن&lt;/p&gt;&lt;p&gt;چون ببینی ظالم از ظالم حمایت می‌کند&lt;/p&gt;&lt;p&gt;آه مظلومان چو آتش در میان پنبه است&lt;/p&gt;&lt;p&gt;چون فتد اینجا به آنجا هم سرایت می‌کند&lt;/p&gt;&lt;p&gt;بگذرند از کبریایی گر خداوندان آز&lt;/p&gt;&lt;p&gt;ثروت دنیا خلایق را کفایت می‌کند&lt;/p&gt;&lt;p&gt;از طریق نامه طوفانی خود فرخی&lt;/p&gt;&lt;p&gt;اهل ثروت را به سوی حق هدایت می‌کند&lt;/p&gt;', 'غزل شماره ۲۷', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(283, '&lt;p&gt;با تو در پرده دلم راز و نیازی دارد&lt;/p&gt;&lt;p&gt;کس ندانست که در پرده چه رازی دارد&lt;/p&gt;&lt;p&gt;بر سر زلف تو دارد هوس چنگ زدن&lt;/p&gt;&lt;p&gt;دست کوتاه من امید درازی دارد&lt;/p&gt;&lt;p&gt;گرو آخر ببرد درگه بازی ز حریف&lt;/p&gt;&lt;p&gt;پاکبازی که دل و دیده بازی دارد&lt;/p&gt;&lt;p&gt;خواجه گاهی به نگاهی دل ما را ننواخت&lt;/p&gt;&lt;p&gt;تا بگویم نظر بنده نوازی دارد&lt;/p&gt;&lt;p&gt;شمع در ماتم پروانه اگر غمزده نیست&lt;/p&gt;&lt;p&gt;از چه شب تا به سحر سوز و گدازی دارد&lt;/p&gt;&lt;p&gt;خسرو محتشم روی زمین دانی کیست؟&lt;/p&gt;&lt;p&gt;آن گدایی که چو محمود ایازی دارد&lt;/p&gt;', 'غزل شماره ۲۸', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(284, '&lt;p&gt;جز شور و شر از چشم سیاه تو نریزد&lt;/p&gt;&lt;p&gt;اِلّا خطر از تیر نگاه تو نریزد&lt;/p&gt;&lt;p&gt;آهسته بزن شانه بر آن زلف پریشان&lt;/p&gt;&lt;p&gt;تا جمع دل از طرف کلاه تو نریزد&lt;/p&gt;&lt;p&gt;کانون شدی ای سینه مگر کز شرر دل&lt;/p&gt;&lt;p&gt;جز اخگر غم ز آتش آه تو نریزد&lt;/p&gt;&lt;p&gt;تا در خم می از پی توبه نکنی غسل&lt;/p&gt;&lt;p&gt;ای شیخ گنهکار گناه تو نریزد&lt;/p&gt;&lt;p&gt;ای خاکِ مقدّس که بُوَد نام تو ایران&lt;/p&gt;&lt;p&gt;فاسد بُوَد آن خون که به راه تو نریزد&lt;/p&gt;', 'غزل شماره ۲۹', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(285, '&lt;p&gt;نیمه شب زلف را در سایه مه تاب دادی&lt;/p&gt;&lt;p&gt;وز رخ چون آفتابت زینت مهتاب دادی&lt;/p&gt;&lt;p&gt;چشم می آلوده را پیوستگی دادی به ابرو&lt;/p&gt;&lt;p&gt;جای ترک مست را در گوشه محراب دادی&lt;/p&gt;&lt;p&gt;ابرویت را پر عرق کردی دگر از آتش می&lt;/p&gt;&lt;p&gt;یا برای قتل ما شمشیر خود را آب دادی؟&lt;/p&gt;&lt;p&gt;چون پرستاران نشاندی کنج لب خال سیه را&lt;/p&gt;&lt;p&gt;هندوی پر تاب و تب را شیره عناب دادی&lt;/p&gt;&lt;p&gt;دیده ام را تا قیامت روز و شب بیدار دارد&lt;/p&gt;&lt;p&gt;وعده وصلی که از شوخی توام در خواب دادی&lt;/p&gt;&lt;p&gt;تا زدی ای لعبت چین شانه زلف عنبرین را&lt;/p&gt;&lt;p&gt;در کف باد صبا صد نافه مشک ناب دادی&lt;/p&gt;', 'غزل شماره ۳۰', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(286, '&lt;p&gt;بس تنگ شد از سختی جان حوصله دل&lt;/p&gt;&lt;p&gt;دل شِکوه ز جان می‌کند و جان گله دل&lt;/p&gt;&lt;p&gt;دل شیفته سلسله مویی است کز افسون&lt;/p&gt;&lt;p&gt;با یک سر مو بسته دو صد سلسله دل&lt;/p&gt;&lt;p&gt;از بادیه عشق حذر کن که در آن دشت&lt;/p&gt;&lt;p&gt;در هر قدمی گمشده صد قافله دل&lt;/p&gt;&lt;p&gt;سر منزل دلدار کجا هست که واماند&lt;/p&gt;&lt;p&gt;از دست غمش پای پر از آبله دل&lt;/p&gt;&lt;p&gt;تا خلوت دل جایگه مهر تو گردید&lt;/p&gt;&lt;p&gt;نبود بخدا یکسر مو فاصله دل&lt;/p&gt;&lt;p&gt;با غیر تو مشغولی و غافل که ز حسرت&lt;/p&gt;&lt;p&gt;نبود به جز از خوردن خون مشغله دل&lt;/p&gt;', 'غزل شماره ۳۱', 'فرخی یزدی', 'دیوان اشعار', 'غزل', 5, 8),
(287, '&lt;p&gt;تن یافت برهنگی ز بی رختی ما&lt;/p&gt;&lt;p&gt;دل تن بقضا داد ز جان سختی ما&lt;/p&gt;&lt;p&gt;چون دید غم و محنت ما را شب عید&lt;/p&gt;&lt;p&gt;بگرفت عزای روز بدبختی ما&lt;/p&gt;', 'رباعی شماره ۳۱', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(288, '&lt;p&gt;بی چیزی من اگر چه پابست مرا&lt;/p&gt;&lt;p&gt;غم نیست که تاب نیستی هست مرا&lt;/p&gt;&lt;p&gt;با بی سر و پائی ز قناعت دایم&lt;/p&gt;&lt;p&gt;سرمایه روزگار در دست مرا&lt;/p&gt;', 'رباعی شماره ۳۲', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(289, '&lt;p&gt;دردا که ز جهل درد نادانی ما&lt;/p&gt;&lt;p&gt;چون سلسله شد جمع پریشانی ما&lt;/p&gt;&lt;p&gt;با حق قضاوت اجانب امروز&lt;/p&gt;&lt;p&gt;یک داغ سیاهیست به پیشانی ما&lt;/p&gt;', 'رباعی شماره ۳۳', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(290, '&lt;p&gt;ای آنکه ترا به دل نه شک است و نه ریب&lt;/p&gt;&lt;p&gt;آگاه ز حال خضر و چوپان شعیب&lt;/p&gt;&lt;p&gt;خوش باش که گر خبر به طوفان ندهند&lt;/p&gt;&lt;p&gt;هر روز بگیرد خبر از مخبر غیب&lt;/p&gt;', 'رباعی شماره ۳۴', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(291, '&lt;p&gt;در این ره سخت گر شود پای تو سست&lt;/p&gt;&lt;p&gt;از دست شکستگان شوی رنجه درست&lt;/p&gt;&lt;p&gt;هر چیز که خواستی مهیا کردند&lt;/p&gt;&lt;p&gt;گر مرد هنروری کنون نوبت تست&lt;/p&gt;', 'رباعی شماره ۳۵', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(292, '&lt;p&gt;هر کس که چو گل در این چمن یکرنگ است&lt;/p&gt;&lt;p&gt;با خار به پیش باغبان هم سنگ است&lt;/p&gt;&lt;p&gt;دل تنگی غنچه در چمن تنها نیست&lt;/p&gt;&lt;p&gt;بر هر که نظر کنی چو من دلتنگ است&lt;/p&gt;', 'رباعی شماره ۳۶', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(293, '&lt;p&gt;در ملک وجود خودنمائی غلط است&lt;/p&gt;&lt;p&gt;در بندگی اظهار خدائی غلط است&lt;/p&gt;&lt;p&gt;بیگانگی آموز که با مسلک راست&lt;/p&gt;&lt;p&gt;با خلق زمانه آشنائی غلط است&lt;/p&gt;', 'رباعی شماره ۳۷', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(294, '&lt;p&gt;در مسلک ما طریق مطلوب خوش است&lt;/p&gt;&lt;p&gt;دلجوئی مردمان مغلوب خوش است&lt;/p&gt;&lt;p&gt;کافی نبود برای ما نیت خوب&lt;/p&gt;&lt;p&gt;با نیت خوب کرده خوب خوش است&lt;/p&gt;', 'رباعی شماره ۳۸', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(295, '&lt;p&gt;دنیا که حیاتش همه جنگ و جدل است&lt;/p&gt;&lt;p&gt;وصلش همگی فراغ و اصلش بدل است&lt;/p&gt;&lt;p&gt;امروز چو دیروز مکن تکیه به حرف&lt;/p&gt;&lt;p&gt;کامروز جهان، جهان سعی و عمل است&lt;/p&gt;', 'رباعی شماره ۳۹', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(296, '&lt;p&gt;در کشور ما که مهد اندوه و غم است&lt;/p&gt;&lt;p&gt;در آن دل و جان شاد بسیار کم است&lt;/p&gt;&lt;p&gt;از همقدمان خود عقب خواهد ماند&lt;/p&gt;&lt;p&gt;هر کس که درین زمانه ثابت قدم است&lt;/p&gt;', 'رباعی شماره ۴۰', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(297, '&lt;p&gt;آئینه حق نما دل خسته ماست&lt;/p&gt;&lt;p&gt;برهان حقیقت دهن بسته ماست&lt;/p&gt;&lt;p&gt;آنکس که درست حق و باطل بنوشت&lt;/p&gt;&lt;p&gt;نوک قلم و خامه بشکسته ماست&lt;/p&gt;', 'رباعی شماره ۴۱', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(298, '&lt;p&gt;درد و غم خوبان جوان پیرم کرد&lt;/p&gt;&lt;p&gt;بد عهدی آسمان زمینگیرم کرد&lt;/p&gt;&lt;p&gt;من ماندم و من با همه بدبختیها&lt;/p&gt;&lt;p&gt;ای مرگ بیا که زندگی سیرم کرد&lt;/p&gt;', 'رباعی شماره ۴۲', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(299, '&lt;p&gt;آن شیخ که دم ز علم اخفش می‌زد&lt;/p&gt;&lt;p&gt;با ساده‌رخان باده بی‌غش می‌زد&lt;/p&gt;&lt;p&gt;دیدم که برای دستمالی موهوم&lt;/p&gt;&lt;p&gt;بی‌واسطه قیصریه آتش می‌زد&lt;/p&gt;', 'رباعی شماره ۴۳', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(300, '&lt;p&gt;گر بر دل ما گرد ملالت باشد&lt;/p&gt;&lt;p&gt;آن گرد ملال از جهالت باشد&lt;/p&gt;&lt;p&gt;قانون مهاجرت بود لازم لیک&lt;/p&gt;&lt;p&gt;لازمتر از آن بسط عدالت باشد&lt;/p&gt;', 'رباعی شماره ۴۴', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(301, '&lt;p&gt;گفتی دل خون کرده عوض خواهد شد&lt;/p&gt;&lt;p&gt;از دیده سر آورده عوض خواهد شد&lt;/p&gt;&lt;p&gt;با رنگ سیاستی که من می بینم&lt;/p&gt;&lt;p&gt;یکبار دگر پرده عوض خواهد شد&lt;/p&gt;', 'رباعی شماره ۴۵', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(302, '&lt;p&gt;از درد و غم زمانه افسرده مباش&lt;/p&gt;&lt;p&gt;وز کجروی سپهر آزرده مباش&lt;/p&gt;&lt;p&gt;ور گردش آسمان زمینت بزند&lt;/p&gt;&lt;p&gt;چون مردم سرگشته کله خورده مباش&lt;/p&gt;', 'رباعی شماره ۴۶', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(303, '&lt;p&gt;در بیشه دهر، شیر با دندان باش&lt;/p&gt;&lt;p&gt;هم پیشه پنجه هنرمندان باش&lt;/p&gt;&lt;p&gt;گر شام کند خار چمن خون به دلت&lt;/p&gt;&lt;p&gt;چون غنچه صبحدم دمی خندان باش&lt;/p&gt;', 'رباعی شماره ۴۷', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(304, '&lt;p&gt;آن خم که بود مدام در جوش، منم&lt;/p&gt;&lt;p&gt;آن مرغ که شد به شام خاموش، منم&lt;/p&gt;&lt;p&gt;در حلقه رندان خراباتی خویش&lt;/p&gt;&lt;p&gt;آن پاک نشین خانه بر دوش، منم&lt;/p&gt;', 'رباعی شماره ۴۸', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(306, '&lt;p&gt;ای غافل نشناخته زنگی از حور&lt;/p&gt;&lt;p&gt;وز جهل نداده فرق ظلمت از نور&lt;/p&gt;&lt;p&gt;عالم همه پر صدا ولی گوش تو کر&lt;/p&gt;&lt;p&gt;دنیا همه با ضیا ولی چشم تو کور&lt;/p&gt;', 'رباعی شماره ۴۹', 'فرخی یزدی', 'دیوان اشعار', 'رباعی', 5, 8),
(308, '&lt;p&gt;اول دفتر به نام ایزد دانا&lt;/p&gt;&lt;p&gt;صانع پروردگار حی توانا&lt;/p&gt;&lt;p&gt;اکبر و اعظم، خدای عالم و آدم&lt;/p&gt;&lt;p&gt;صورت خوب آفرید و سیرت زیبا&lt;/p&gt;&lt;p&gt;از در بخشندگی و بنده‌نوازی&lt;/p&gt;&lt;p&gt;مرغ هوا را نصیب و ماهی دریا&lt;/p&gt;&lt;p&gt;قسمت خود می‌خورند مُنعِم و درویش&lt;/p&gt;&lt;p&gt;روزی خود می‌برند پشّه و عَنقا&lt;/p&gt;&lt;p&gt;حاجت موری به علم غیب بداند&lt;/p&gt;&lt;p&gt;در بن چاهی به زیر صخرهٔ صَمّا&lt;/p&gt;&lt;p&gt;جانور از نطفه می‌کند، شکر از نی&lt;/p&gt;&lt;p&gt;برگِ تر از چوب خشک و چشمه ز خارا&lt;/p&gt;&lt;p&gt;شربت نوش آفرید از مگس نحل&lt;/p&gt;&lt;p&gt;نخل تناور کند ز دانهٔ خرما&lt;/p&gt;&lt;p&gt;از همگان بی‌نیاز و بر همه مشفق&lt;/p&gt;&lt;p&gt;از همه عالم نهان و بر همه پیدا&lt;/p&gt;&lt;p&gt;پرتو نور سرادقات جلالش&lt;/p&gt;&lt;p&gt;از عظمت، ماورای فکرت دانا&lt;/p&gt;&lt;p&gt;خود نه زبان در دهان عارف مدهوش&lt;/p&gt;&lt;p&gt;حمد و ثنا می‌کند که موی بر اعضا&lt;/p&gt;&lt;p&gt;هر که نداند سپاس نعمت امروز&lt;/p&gt;&lt;p&gt;حیف خورد بر نصیب رحمت فردا&lt;/p&gt;&lt;p&gt;بار خدایا مهیمنی و مدبر&lt;/p&gt;&lt;p&gt;وز همه عیبی مقدسی و مبرا&lt;/p&gt;&lt;p&gt;ما نتوانیم حق حمد تو گفتن&lt;/p&gt;&lt;p&gt;با همه کروبیان عالم بالا&lt;/p&gt;&lt;p&gt;سعدی از آنجا که فهم اوست سخن گفت&lt;/p&gt;&lt;p&gt;ور نه کمال تو، وهم کی رسد آنجا؟&lt;/p&gt;', 'غزل شمارهٔ ۱', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(309, '&lt;p&gt;ای نفسِ خرّمِ بادِ صبا&lt;/p&gt;&lt;p&gt;!از برِ یار آمده‌ای، مرحبا&lt;/p&gt;&lt;p&gt;قافلهٔ شب چه شنیدی ز صبح؟&lt;/p&gt;&lt;p&gt;مرغِ سلیمان چه خبر از سبا؟&lt;/p&gt;&lt;p&gt;بر سرِ خشم است هنوز آن حریف؟&lt;/p&gt;&lt;p&gt;یا سخنی می‌رود اندر رضا؟&lt;/p&gt;&lt;p&gt;از درِ صلح آمده‌ای یا خِلاف؟&lt;/p&gt;&lt;p&gt;با قدمِ خوف رَوم یا رَجا؟&lt;/p&gt;&lt;p&gt;بارِ دگر گر به سرِ کویِ دوست&lt;/p&gt;&lt;p&gt;بگذری ای پیکِ نسیمِ صبا&lt;/p&gt;&lt;p&gt;گو رمقی بیش نمانْد از ضعیف&lt;/p&gt;&lt;p&gt;چند کُنَد صورتِ بی‌جان بقا؟&lt;/p&gt;&lt;p&gt;آن‌همه دل‌داری و پیمان و عهد&lt;/p&gt;&lt;p&gt;نیک نکردی که نکردی وفا&lt;/p&gt;&lt;p&gt;لیکن اگر دورِ وصالی بُوَد&lt;/p&gt;&lt;p&gt;صلح فراموش کند ماجرا&lt;/p&gt;&lt;p&gt;تا به گریبان نرسد دستِ مرگ&lt;/p&gt;&lt;p&gt;دست ز دامن نکنیمت رها&lt;/p&gt;&lt;p&gt;دوست نباشد به حقیقت که او&lt;/p&gt;&lt;p&gt;دوست فراموش کند در بلا&lt;/p&gt;&lt;p&gt;خستگی اندر طلبت راحت است&lt;/p&gt;&lt;p&gt;درد کشیدن به امیدِ دوا&lt;/p&gt;&lt;p&gt;سر نتوانم که برآرم چو چنگ&lt;/p&gt;&lt;p&gt;ور چو دفم پوست بدرّد قَفا&lt;/p&gt;&lt;p&gt;هر سَحر از عشق دمی می‌زنم&lt;/p&gt;&lt;p&gt;روزِ دگر می‌شنوم بر ملا&lt;/p&gt;&lt;p&gt;قصهٔ دردم همه عالَم گرفت&lt;/p&gt;&lt;p&gt;در که نگیرد نفسِ آشنا؟&lt;/p&gt;&lt;p&gt;گر برسد نالهٔ سعدی به کوه&lt;/p&gt;&lt;p&gt;کوه بنالد به زبانِ صدا&lt;/p&gt;', 'غزل شماره ۲', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(310, '&lt;p&gt;روی تو خوش می‌نماید آینهٔ ما&lt;/p&gt;&lt;p&gt;کآینه پاکیزه است و روی تو زیبا&lt;/p&gt;&lt;p&gt;چون می روشن در آبگینهٔ صافی&lt;/p&gt;&lt;p&gt;خویِ جمیل از جمالِ روی تو پیدا&lt;/p&gt;&lt;p&gt;هر که دمی با تو بود یا قدمی رفت&lt;/p&gt;&lt;p&gt;از تو نباشد به هیچ روی شکیبا&lt;/p&gt;&lt;p&gt;صیدِ بیابان سر از کمند بپیچد&lt;/p&gt;&lt;p&gt;ما همه پیچیده در کمند تو عمدا&lt;/p&gt;&lt;p&gt;طایرِ مسکین که مهر بست به جایی&lt;/p&gt;&lt;p&gt;گر بکشندش نمی‌رود به دگر جا&lt;/p&gt;&lt;p&gt;غیرتم آید شکایت از تو به هر کس&lt;/p&gt;&lt;p&gt;درد اَحِبّا نمی‌برم به اطبا&lt;/p&gt;&lt;p&gt;برخیِ جانت شوم که شمع افق را&lt;/p&gt;&lt;p&gt;پیش بمیرد چراغدانِ ثریا&lt;/p&gt;&lt;p&gt;گر تو شکرخنده آستین نفشانی&lt;/p&gt;&lt;p&gt;هر مگسی طوطیی شوند شکرخا&lt;/p&gt;&lt;p&gt;لعبت شیرین اگر تُرُش ننشیند&lt;/p&gt;&lt;p&gt;مدعیانش طمع کنند به حلوا&lt;/p&gt;&lt;p&gt;مردِ تماشای باغِ حسن تو سعدیست&lt;/p&gt;&lt;p&gt;دستْ فرومایگان برند به یغما&lt;/p&gt;', 'غزل شماره ۳', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(311, '&lt;p&gt;اگر تو فارغی از حال دوستان یارا&lt;/p&gt;&lt;p&gt;فراغت از تو میسر نمی‌شود ما را&lt;/p&gt;&lt;p&gt;تو را در آینه دیدن جمال طلعت خویش&lt;/p&gt;&lt;p&gt;بیان کند که چه بودست ناشکیبا را&lt;/p&gt;&lt;p&gt;بیا که وقت بهارست تا من و تو به هم&lt;/p&gt;&lt;p&gt;به دیگران بگذاریم باغ و صحرا را&lt;/p&gt;&lt;p&gt;به جای سرو بلند ایستاده بر لب جوی&lt;/p&gt;&lt;p&gt;چرا نظر نکنی یار سروبالا را&lt;/p&gt;&lt;p&gt;شمایلی که در اوصاف حسن ترکیبش&lt;/p&gt;&lt;p&gt;مجال نطق نماند زبان گویا را&lt;/p&gt;&lt;p&gt;که گفت در رخ زیبا نظر خطا باشد&lt;/p&gt;&lt;p&gt;خطا بود که نبینند روی زیبا را&lt;/p&gt;&lt;p&gt;به دوستی که اگر زهر باشد از دستت&lt;/p&gt;&lt;p&gt;چنان به ذوق ارادت خورم که حلوا را&lt;/p&gt;&lt;p&gt;کسی ملامت وامق کند به نادانی&lt;/p&gt;&lt;p&gt;حبیب من که ندیدست روی عذرا را&lt;/p&gt;&lt;p&gt;گرفتم آتش پنهان خبر نمی‌داری&lt;/p&gt;&lt;p&gt;نگاه می‌نکنی آب چشم پیدا را&lt;/p&gt;&lt;p&gt;نگفتمت که به یغما رود دلت سعدی&lt;/p&gt;&lt;p&gt;چو دل به عشق دهی دلبران یغما را&lt;/p&gt;&lt;p&gt;هنوز با همه دردم امید درمانست&lt;/p&gt;&lt;p&gt;که آخری بود آخر شبان یلدا را&lt;/p&gt;', 'غزل شماره ۴', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(312, '&lt;p&gt;شب فراق نخواهم دواج دیبا را&lt;/p&gt;&lt;p&gt;که شب دراز بود خوابگاه تنها را&lt;/p&gt;&lt;p&gt;ز دست رفتن دیوانه عاقلان دانند&lt;/p&gt;&lt;p&gt;که احتمال نماندست ناشکیبا را&lt;/p&gt;&lt;p&gt;گرش ببینی و دست از ترنج بشناسی&lt;/p&gt;&lt;p&gt;روا بود که ملامت کنی زلیخا را&lt;/p&gt;&lt;p&gt;چنین جوان که تویی برقعی فروآویز&lt;/p&gt;&lt;p&gt;و گر نه دل برود پیر پای برجا را&lt;/p&gt;&lt;p&gt;تو آن درخت گلی کاعتدال قامت تو&lt;/p&gt;&lt;p&gt;ببرد قیمت سرو بلندبالا را&lt;/p&gt;&lt;p&gt;دگر به هر چه تو گویی مخالفت نکنم&lt;/p&gt;&lt;p&gt;که بی تو عیش میسر نمی‌شود ما را&lt;/p&gt;&lt;p&gt;دو چشم باز نهاده نشسته‌ام همه شب&lt;/p&gt;&lt;p&gt;چو فرقدین و نگه می‌کنم ثریا را&lt;/p&gt;&lt;p&gt;شبی و شمعی و جمعی چه خوش بود تا روز&lt;/p&gt;&lt;p&gt;نظر به روی تو کوری چشم اعدا را&lt;/p&gt;&lt;p&gt;من از تو پیش که نالم که در شریعت عشق&lt;/p&gt;&lt;p&gt;معاف دوست بدارند قتل عمدا را&lt;/p&gt;&lt;p&gt;تو همچنان دل شهری به غمزه‌ای ببری&lt;/p&gt;&lt;p&gt;که بندگان بنی سعد خوان یغما را&lt;/p&gt;&lt;p&gt;در این روش که تویی بر هزار چون سعدی&lt;/p&gt;&lt;p&gt;جفا و جور توانی ولی مکن یارا&lt;/p&gt;', 'غزل شماره ۵', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(313, '&lt;p&gt;پیش ما رسم شکستن نبود عهد وفا را&lt;/p&gt;&lt;p&gt;الله الله تو فراموش مکن صحبت ما را&lt;/p&gt;&lt;p&gt;قیمت عشق نداند قدم صدق ندارد&lt;/p&gt;&lt;p&gt;سست‌عهدی که تحمل نکند بار جفا را&lt;/p&gt;&lt;p&gt;گر مخیر بکنندم به قیامت که چه خواهی&lt;/p&gt;&lt;p&gt;دوست ما را و همه نعمت فردوس شما را&lt;/p&gt;&lt;p&gt;گر سرم می‌رود از عهد تو سر بازنپیچم&lt;/p&gt;&lt;p&gt;تا بگویند پس از من که به سر برد وفا را&lt;/p&gt;&lt;p&gt;خنک آن درد که یارم به عیادت به سر آید&lt;/p&gt;&lt;p&gt;دردمندان به چنین درد نخواهند دوا را&lt;/p&gt;&lt;p&gt;باور از مات نباشد تو در آیینه نگه کن&lt;/p&gt;&lt;p&gt;تا بدانی که چه بودست گرفتار بلا را&lt;/p&gt;&lt;p&gt;از سر زلف عروسان چمن دست بدارد&lt;/p&gt;&lt;p&gt;به سر زلف تو گر دست رسد باد صبا را&lt;/p&gt;&lt;p&gt;سر انگشت تحیر بگزد عقل به دندان&lt;/p&gt;&lt;p&gt;چون تأمل کند این صورت انگشت‌نما را&lt;/p&gt;&lt;p&gt;آرزو می‌کندم شمع صفت پیش وجودت&lt;/p&gt;&lt;p&gt;که سراپای بسوزند من بی سر و پا را&lt;/p&gt;&lt;p&gt;چشم کوته‌نظران بر ورق صورت خوبان&lt;/p&gt;&lt;p&gt;خط همی‌بیند و عارف قلم صنع خدا را&lt;/p&gt;&lt;p&gt;همه را دیده به رویت نگرانست ولیکن&lt;/p&gt;&lt;p&gt;خودپرستان ز حقیقت نشناسند هوا را&lt;/p&gt;&lt;p&gt;مهربانی ز من آموز و گرم عمر نماند&lt;/p&gt;&lt;p&gt;به سر تربت سعدی بطلب مهرگیا را&lt;/p&gt;&lt;p&gt;هیچ هشیار ملامت نکند مستی ما را&lt;/p&gt;&lt;p&gt;قُل لِصاحٍ تَرَکَ النّاسَ مِن الوَجْد سُکاریٰ&lt;/p&gt;', 'غزل شماره ۶', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(314, '&lt;p&gt;مشتاقی و صبوری از حد گذشت یارا&lt;/p&gt;&lt;p&gt;گر تو شکیب داری طاقت نمانْد ما را&lt;/p&gt;&lt;p&gt;باری به چشم احسان در حال ما نظر کن&lt;/p&gt;&lt;p&gt;کز خوان پادشاهان راحت بود گدا را&lt;/p&gt;&lt;p&gt;سلطان که خشم گیرد بر بندگان حضرت&lt;/p&gt;&lt;p&gt;حکمش رسد ولیکن حدی بود جفا را&lt;/p&gt;&lt;p&gt;من بی تو زندگانی خود را نمی‌پسندم&lt;/p&gt;&lt;p&gt;کآسایشی نباشد بی دوستان بقا را&lt;/p&gt;&lt;p&gt;چون تشنه جان سپردم آن گه چه سود دارد&lt;/p&gt;&lt;p&gt;آب از دو چشم دادن بر خاک من گیا را&lt;/p&gt;&lt;p&gt;حال نیازمندی در وصف می‌نیاید&lt;/p&gt;&lt;p&gt;آن گه که بازگردی گوییم ماجرا را&lt;/p&gt;&lt;p&gt;بازآ و جان شیرین از من ستان به خدمت&lt;/p&gt;&lt;p&gt;دیگر چه برگ باشد درویش بی‌نوا را&lt;/p&gt;&lt;p&gt;یا رب تو آشنا را مهلت ده و سلامت&lt;/p&gt;&lt;p&gt;چندان که بازبیند دیدار آشنا را&lt;/p&gt;&lt;p&gt;نه ملک پادشا را در چشم خوبرویان&lt;/p&gt;&lt;p&gt;وقعیست ای برادر نه زهد پارسا را&lt;/p&gt;&lt;p&gt;ای کاش برفتادی برقع ز روی لیلی&lt;/p&gt;&lt;p&gt;تا مدعی نماندی مجنونِ مبتلا را&lt;/p&gt;&lt;p&gt;سعدی قلم به سختی رفتست و نیکبختی&lt;/p&gt;&lt;p&gt;پس هر چه پیشت آید گردن بنه قضا را&lt;/p&gt;', 'غزل شماره ۷', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(315, '&lt;p&gt;ز اندازه بیرون تشنه‌ام ساقی بیار آن آب را&lt;/p&gt;&lt;p&gt;اول مرا سیراب کن وآنگه بده اصحاب را&lt;/p&gt;&lt;p&gt;من نیز چشم از خواب خوش بر می‌نکردم پیش از این&lt;/p&gt;&lt;p&gt;روز فراق دوستان شب‌خوش بگفتم خواب را&lt;/p&gt;&lt;p&gt;هر پارسا را کآن صنم در پیش مسجد بگذرد&lt;/p&gt;&lt;p&gt;چشمش بر ابرو افکند باطل کند محراب را&lt;/p&gt;&lt;p&gt;من صید وحشی نیستم در بند جان خویشتن&lt;/p&gt;&lt;p&gt;گر وی به تیرم می‌زند اِستاده‌ام نُشّاب را&lt;/p&gt;&lt;p&gt;مقدار یار هم‌نفس چون من نداند هیچ‌کس&lt;/p&gt;&lt;p&gt;ماهی که بر خشک اوفتد قیمت بداند آب را&lt;/p&gt;&lt;p&gt;وقتی در آبی تا میان دستی و پایی می‌زدم&lt;/p&gt;&lt;p&gt;اکنون همان پنداشتم دریای بی‌پایاب را&lt;/p&gt;&lt;p&gt;امروز حالا غرقه‌ام تا با کناری اوفتم&lt;/p&gt;&lt;p&gt;آنگه حکایت گویمت درد دل غرقاب را&lt;/p&gt;&lt;p&gt;گر بی‌وفایی کردمی یَرغو به قاآن بردمی&lt;/p&gt;&lt;p&gt;کآن کافر اعدا می‌کشد وین سنگدل احباب را&lt;/p&gt;&lt;p&gt;فریاد می‌دارد رقیب از دست مشتاقان او&lt;/p&gt;&lt;p&gt;آواز مطرب در سرا زحمت بُوَد بواب را&lt;/p&gt;&lt;p&gt;«سعدی! چو جورش می‌بری نزدیک او دیگر مرو»&lt;/p&gt;&lt;p&gt;ای بی‌بصر! من می‌روم؟ او می‌کشد قلاب را&lt;/p&gt;', 'غزل شماره ۸', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(316, '&lt;p&gt;گر ماه من برافکند از رخ نقاب را&lt;/p&gt;&lt;p&gt;برقع فروهلد به جمال آفتاب را&lt;/p&gt;&lt;p&gt;گویی دو چشم جادوی عابدفریب او&lt;/p&gt;&lt;p&gt;بر چشم من به سحر ببستند خواب را&lt;/p&gt;&lt;p&gt;اول نظر ز دست برفتم عنان عقل&lt;/p&gt;&lt;p&gt;وان را که عقل رفت چه داند صواب را&lt;/p&gt;&lt;p&gt;گفتم مگر به وصل رهایی بود ز عشق&lt;/p&gt;&lt;p&gt;بی‌حاصل است خوردن مستسقی آب را&lt;/p&gt;&lt;p&gt;دعوی درست نیست گر از دست نازنین&lt;/p&gt;&lt;p&gt;چون شربت شکر نخوری زهر ناب را&lt;/p&gt;&lt;p&gt;عشق آدمیت است گر این ذوق در تو نیست&lt;/p&gt;&lt;p&gt;همشرکتی به خوردن و خفتن دواب را&lt;/p&gt;&lt;p&gt;آتش بیار و خرمن آزادگان بسوز&lt;/p&gt;&lt;p&gt;تا پادشه خراج نخواهد خراب را&lt;/p&gt;&lt;p&gt;قوم از شراب مست و ز منظور بی‌نصیب&lt;/p&gt;&lt;p&gt;من مست از او چنان که نخواهم شراب را&lt;/p&gt;&lt;p&gt;سعدی نگفتمت که مرو در کمند عشق&lt;/p&gt;&lt;p&gt;تیر نظر بیفکند افراسیاب را&lt;/p&gt;', 'غزل شماره ۹', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(317, '&lt;p&gt;&lt;br&gt;با جوانی سر خوش است این پیر بی تدبیر را&lt;/p&gt;&lt;p&gt;جهل باشد با جوانان پنجه کردن پیر را&lt;/p&gt;&lt;p&gt;من که با مویی به قوّت برنیایم ای عجب&lt;/p&gt;&lt;p&gt;با یکی افتاده‌ام کو بُگسلد زنجیر را&lt;/p&gt;&lt;p&gt;چون کمان در بازو آرد سروقدِ سیم‌تن&lt;/p&gt;&lt;p&gt;آرزویم می‌کند کآماج باشم تیر را&lt;/p&gt;&lt;p&gt;می‌رود تا در کمند افتد به پای خویشتن&lt;/p&gt;&lt;p&gt;گر بر آن دست و کمانِ چشم اوفتد نخجیر را&lt;/p&gt;&lt;p&gt;کس ندیدست آدمیزاد از تو شیرین‌تر سخن&lt;/p&gt;&lt;p&gt;شِکَّر از پستانِ مادر خورده‌ای یا شیر را&lt;/p&gt;&lt;p&gt;روزِ بازارِ جوانی پنج روزی بیش نیست&lt;/p&gt;&lt;p&gt;نقد را باش ای پسر کآفت بود تأخیر را&lt;/p&gt;&lt;p&gt;ای که گفتی دیده از دیدارِ بُت‌رویان بدوز&lt;/p&gt;&lt;p&gt;هر چه گویی چاره دانم کرد جز تقدیر را&lt;/p&gt;&lt;p&gt;زهد پیدا، کفر پنهان، بود چندین روزگار&lt;/p&gt;&lt;p&gt;پرده از سر برگرفتیم آن همه تزویر را&lt;/p&gt;&lt;p&gt;سعدیا در پای جانان گر به خدمت سر نهی&lt;/p&gt;&lt;p&gt;همچنان عذرت بباید خواستن تقصیر را&lt;/p&gt;', 'غزل شماره ۱۰', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(318, '&lt;p&gt;وقت طرب خوش یافتم آن دلبر طناز را&lt;/p&gt;&lt;p&gt;ساقی بیار آن جام می، مطرب بزن آن ساز را&lt;/p&gt;&lt;p&gt;امشب که بزم عارفان از شمع رویت روشن است&lt;/p&gt;&lt;p&gt;آهسته تا نبود خبر رندان شاهدباز را&lt;/p&gt;&lt;p&gt;دوش ای پسر می خورده‌ای چشمت گواهی می‌دهد&lt;/p&gt;&lt;p&gt;باری حریفی جو که او مستور دارد راز را&lt;/p&gt;&lt;p&gt;روی خوش و آواز خوش دارند هر یک لذتی&lt;/p&gt;&lt;p&gt;بنگر که لذت چون بود! محبوب خوش‌آواز را&lt;/p&gt;&lt;p&gt;چشمان ترک و ابروان جان را به ناوک می‌زنند&lt;/p&gt;&lt;p&gt;یا رب که دادست این کمان آن ترک تیرانداز را !؟&lt;/p&gt;&lt;p&gt;شور غم عشقش چنین حیف است پنهان داشتن&lt;/p&gt;&lt;p&gt;در گوش نی رمزی بگو تا برکشد آواز را&lt;/p&gt;&lt;p&gt;شیرازْ پرغوغا شدست از فتنه‌ی چشم خوشت&lt;/p&gt;&lt;p&gt;ترسم که آشوبِ خوشت برهم زند شیراز را&lt;/p&gt;&lt;p&gt;من مرغکی پربسته‌ام زان در قفس بنشسته‌ام&lt;/p&gt;&lt;p&gt;گر زان که بشکستی قفس بنمودمی پرواز را&lt;/p&gt;&lt;p&gt;سعدی تو مرغ زیرکی خوبت به دام آورده‌ام&lt;/p&gt;&lt;p&gt;مشکل به دست آرد کسی مانند تو شهباز را&lt;/p&gt;', 'غزل شماره ۱۱', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(319, '&lt;p&gt;دوست می‌دارم من این نالیدن دلسوز را&lt;/p&gt;&lt;p&gt;تا به هر نوعی که باشد بگذرانم روز را&lt;/p&gt;&lt;p&gt;شب همه شب انتظار صبح‌رویی می‌رود&lt;/p&gt;&lt;p&gt;کان صباحت نیست این صبح جهان‌افروز را&lt;/p&gt;&lt;p&gt;وه که گر من بازبینم چهر مهرافزای او&lt;/p&gt;&lt;p&gt;تا قیامت شکر گویم طالع پیروز را&lt;/p&gt;&lt;p&gt;گر من از سنگ ملامت روی برپیچم زنم&lt;/p&gt;&lt;p&gt;جان سپر کردند مردانْ ناوکِ دلدوز را&lt;/p&gt;&lt;p&gt;کامجویان را ز ناکامی چشیدن چاره نیست&lt;/p&gt;&lt;p&gt;بر زمستان صبر باید طالب نوروز را&lt;/p&gt;&lt;p&gt;عاقلان خوشه‌چین از سر لیلی غافلند&lt;/p&gt;&lt;p&gt;این کرامت نیست جز مجنون خرمن‌سوز را&lt;/p&gt;&lt;p&gt;عاشقان دین و دنیاباز را خاصیتیست&lt;/p&gt;&lt;p&gt;کان نباشد زاهدان مال و جاه‌اندوز را&lt;/p&gt;&lt;p&gt;دیگری را در کمند آور که ما خود بنده‌ایم&lt;/p&gt;&lt;p&gt;ریسمان در پای حاجت نیست دست‌آموز را&lt;/p&gt;&lt;p&gt;سعدیا دی رفت و فردا همچنان موجود نیست&lt;/p&gt;&lt;p&gt;در میان این و آن فرصت شمار امروز را&lt;/p&gt;', 'غزل شماره ۱۲', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(320, '&lt;p&gt;وه که گر من بازبینم روی یار خویش را&lt;/p&gt;&lt;p&gt;تا قیامت شکر گویم کردگار خویش را&lt;/p&gt;&lt;p&gt;یارِ بارافتاده را در کاروان بگذاشتند&lt;/p&gt;&lt;p&gt;بی‌وفا یاران که بربستند بار خویش را&lt;/p&gt;&lt;p&gt;مردم بیگانه را خاطر نگه دارند خلق&lt;/p&gt;&lt;p&gt;دوستان ما بیازردند یار خویش را&lt;/p&gt;&lt;p&gt;همچنان امید می‌دارم که بعد از داغ هجر&lt;/p&gt;&lt;p&gt;مرهمی بر دل نهد امیدوارِ خویش را&lt;/p&gt;&lt;p&gt;رای رای توست خواهی جنگ و خواهی آشتی&lt;/p&gt;&lt;p&gt;ما قلم در سر کشیدیم اختیار خویش را&lt;/p&gt;&lt;p&gt;هر که را در خاک غربت پای در گل مانْد مانْد&lt;/p&gt;&lt;p&gt;گو دگر در خواب خوش بینی دیار خویش را&lt;/p&gt;&lt;p&gt;عافیت خواهی نظر در منظر خوبان مکن&lt;/p&gt;&lt;p&gt;ور کنی بدرود کن خواب و قرار خویش را&lt;/p&gt;&lt;p&gt;گبر و ترسا و مسلمان هر کسی در دین خویش&lt;/p&gt;&lt;p&gt;قبله‌ای دارند و ما زیبا نگار خویش را&lt;/p&gt;&lt;p&gt;خاک پایش خواستم شد بازگفتم زینهار&lt;/p&gt;&lt;p&gt;من بر آن دامن نمی‌خواهم غبار خویش را&lt;/p&gt;&lt;p&gt;دوش حورازاده‌ای دیدم که پنهان از رقیب&lt;/p&gt;&lt;p&gt;در میان یاوران می‌گفت یار خویش را&lt;/p&gt;&lt;p&gt;گر مراد خویش خواهی ترک وصل ما بگوی&lt;/p&gt;&lt;p&gt;ور مرا خواهی رها کن اختیار خویش را&lt;/p&gt;&lt;p&gt;درد دل پوشیده مانی تا جگر پرخون شود&lt;/p&gt;&lt;p&gt;بِه که با دشمن نمایی حال زار خویش را&lt;/p&gt;&lt;p&gt;گر هزارت غم بود با کس نگویی زینهار&lt;/p&gt;&lt;p&gt;ای برادر تا نبینی غمگسار خویش را&lt;/p&gt;&lt;p&gt;ای سهی سرو روان آخر نگاهی باز کن&lt;/p&gt;&lt;p&gt;تا به خدمت عرضه دارم افتقار خویش را&lt;/p&gt;&lt;p&gt;دوستان گویند سعدی دل چرا دادی به عشق&lt;/p&gt;&lt;p&gt;تا میان خلق کم کردی وقار خویش را&lt;/p&gt;&lt;p&gt;ما صلاح خویشتن در بی‌نوایی دیده‌ایم&lt;/p&gt;&lt;p&gt;هر کسی گو مصلحت بینند کار خویش را&lt;/p&gt;', 'غزل شماره ۱۳', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(321, '&lt;p&gt;امشب سبکتر می‌زنند این طبل بی‌هنگام را&lt;/p&gt;&lt;p&gt;یا وقت بیداری غلط بودست مرغ بام را&lt;/p&gt;&lt;p&gt;یک لحظه بود این یا شبی کز عمر ما تاراج شد&lt;/p&gt;&lt;p&gt;ما همچنان لب بر لبی نابرگرفته کام را&lt;/p&gt;&lt;p&gt;هم تازه‌رویم هم خجل هم شادمان هم تنگ‌دل&lt;/p&gt;&lt;p&gt;کز عهده بیرون آمدن نتوانم این انعام را&lt;/p&gt;&lt;p&gt;گر پای بر فرقم نهی تشریف قربت می‌دهی&lt;/p&gt;&lt;p&gt;جز سر نمی‌دانم نهادن عذر این اقدام را&lt;/p&gt;&lt;p&gt;چون بخت نیک‌انجام را با ما به کلی صلح شد&lt;/p&gt;&lt;p&gt;بگذار تا جان می‌دهد بدگوی بدفرجام را&lt;/p&gt;&lt;p&gt;سعدی عَلَم شد در جهان صوفی و عامی گو بدان&lt;/p&gt;&lt;p&gt;ما بت پرستی می‌کنیم آن گه چنین اصنام را&lt;/p&gt;', 'غزل شماره ۱۴', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(322, '&lt;p&gt;برخیز تا یک سو نهیم این دلق ازرق‌فام را&lt;/p&gt;&lt;p&gt;بر باد قلاشی دهیم این شرک تقوا نام را&lt;/p&gt;&lt;p&gt;هر ساعت از نو قبله‌ای با بت‌پرستی می‌رود&lt;/p&gt;&lt;p&gt;توحید بر ما عرضه کن تا بشکنیم اصنام را&lt;/p&gt;&lt;p&gt;می با جوانان خوردنم باری تمنا می‌کند&lt;/p&gt;&lt;p&gt;تا کودکان در پی فتند این پیر دُردآشام را&lt;/p&gt;&lt;p&gt;از مایهٔ بیچارگی قطمیر مردم می‌شود&lt;/p&gt;&lt;p&gt;ماخولیای مهتری سگ می‌کند بلعام را&lt;/p&gt;&lt;p&gt;زین تنگنای خلوتم خاطر به صحرا می‌کشد&lt;/p&gt;&lt;p&gt;کز بوستان باد سحر خوش می‌دهد پیغام را&lt;/p&gt;&lt;p&gt;غافل مباش ار عاقلی، دریاب اگر صاحب‌دلی&lt;/p&gt;&lt;p&gt;باشد که نتوان یافتن دیگر چنین ایام را&lt;/p&gt;&lt;p&gt;جایی که سرو بوستان با پای چوبین می‌چمد&lt;/p&gt;&lt;p&gt;ما نیز در رقص آوریم آن سرو سیم‌اندام را&lt;/p&gt;&lt;p&gt;دلبندم آن پیمان‌گُسِل منظور چشم آرام دل&lt;/p&gt;&lt;p&gt;نی نی دلارامش مخوان کز دل ببرد آرام را&lt;/p&gt;&lt;p&gt;دنیا و دین و صبر و عقل از من برفت اندر غمش&lt;/p&gt;&lt;p&gt;جایی که سلطان خیمه زد غوغا نماند عام را&lt;/p&gt;&lt;p&gt;باران اَشکم می‌رود وز اَبرَم آتش می‌جهد&lt;/p&gt;&lt;p&gt;با پختگان گوی این سخن سوزش نباشد خام را&lt;/p&gt;&lt;p&gt;سعدی ملامت نشنود ور جان در این سر می‌رود&lt;/p&gt;&lt;p&gt;صوفی گران‌جانی بِبَر ساقی بیاور جام را&lt;/p&gt;', 'غزل شماره ۱۵', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(323, '&lt;p&gt;تا بود بار غمت بر دل بی‌هوش مرا&lt;/p&gt;&lt;p&gt;سوز عشقت ننشاند ز جگر جوش مرا&lt;/p&gt;&lt;p&gt;نگذرد یاد گل و سنبلم اندر خاطر&lt;/p&gt;&lt;p&gt;تا به خاطر بود آن زلف و بناگوش مرا&lt;/p&gt;&lt;p&gt;شربتی تلختر از زهر فراقت باید&lt;/p&gt;&lt;p&gt;تا کند لذت وصل تو فراموش مرا&lt;/p&gt;&lt;p&gt;هر شبم با غم هجران تو سر بر بالین&lt;/p&gt;&lt;p&gt;روزی ار با تو نشد دست در آغوش مرا&lt;/p&gt;&lt;p&gt;بی دهان تو اگر صد قدح نوش دهند&lt;/p&gt;&lt;p&gt;به دهان تو که زهر آید از آن نوش مرا&lt;/p&gt;&lt;p&gt;سعدی اندر کف جلاد غمت می‌گوید&lt;/p&gt;&lt;p&gt;بنده‌ام بنده به کشتن ده و مفروش مرا&lt;/p&gt;', 'غزل شماره ۱۶', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(324, '&lt;p&gt;چه کند بنده که گردن ننهد فرمان را&lt;/p&gt;&lt;p&gt;چه کند گوی که عاجز نشود چوگان را&lt;/p&gt;&lt;p&gt;سروبالایِ کمان‌ابرو اگر تیر زند&lt;/p&gt;&lt;p&gt;عاشق آنست که بر دیده نهد پیکان را&lt;/p&gt;&lt;p&gt;دست من گیر که بیچارگی از حد بگذشت&lt;/p&gt;&lt;p&gt;سر من دار که در پای تو ریزم جان را&lt;/p&gt;&lt;p&gt;کاشکی پرده برافتادی از آن منظر حسن&lt;/p&gt;&lt;p&gt;تا همه خلق ببینند نگارستان را&lt;/p&gt;&lt;p&gt;همه را دیده در اوصاف تو حیران ماندی&lt;/p&gt;&lt;p&gt;تا دگر عیب نگویند من حیران را&lt;/p&gt;&lt;p&gt;لیکن آن نقش که در روی تو من می‌بینم&lt;/p&gt;&lt;p&gt;همه را دیده نباشد که ببینند آن را&lt;/p&gt;&lt;p&gt;چشم گریان مرا حال بگفتم به طبیب&lt;/p&gt;&lt;p&gt;گفت یک بار ببوس آن دهن خندان را&lt;/p&gt;&lt;p&gt;گفتم آیا که در این درد بخواهم مردن&lt;/p&gt;&lt;p&gt;که محالست که حاصل کنم این درمان را&lt;/p&gt;&lt;p&gt;پنجه با ساعدِ سیمین نَه به عقل افکندم&lt;/p&gt;&lt;p&gt;غایت جهل بود مشت زدن سندان را&lt;/p&gt;&lt;p&gt;سعدی از سرزنش خلق نترسد هیهات&lt;/p&gt;&lt;p&gt;غرقه در نیل چه اندیشه کند باران را&lt;/p&gt;&lt;p&gt;سر بنه گر سر میدان ارادت داری&lt;/p&gt;&lt;p&gt;ناگزیرست که گویی بود این میدان را&lt;/p&gt;', 'غزل شماره ۱۷', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(325, '&lt;p&gt;ساقی بده آن کوزهٔ یاقوتِ روان را&lt;/p&gt;&lt;p&gt;یاقوت چه ارزد بده آن قوتِ روان را&lt;/p&gt;&lt;p&gt;اول پدر پیر خورد رطل دمادم&lt;/p&gt;&lt;p&gt;تا مدعیان هیچ نگویند جوان را&lt;/p&gt;&lt;p&gt;تا مست نباشی نبری بار غم یار&lt;/p&gt;&lt;p&gt;آری شتر مست کشد بار گران را&lt;/p&gt;&lt;p&gt;ای روی تو آرام دل خلق جهانی&lt;/p&gt;&lt;p&gt;بی روی تو شاید که نبینند جهان را&lt;/p&gt;&lt;p&gt;در صورت و معنی که تو داری چه توان گفت&lt;/p&gt;&lt;p&gt;حسن تو ز تحسین تو بستست زبان را&lt;/p&gt;&lt;p&gt;آنک عسل اندوخته دارد مگس نحل&lt;/p&gt;&lt;p&gt;شهد لب شیرین تو زنبور میان را&lt;/p&gt;&lt;p&gt;زین دست که دیدار تو دل می‌برد از دست&lt;/p&gt;&lt;p&gt;ترسم نبرم عاقبت از دست تو جان را&lt;/p&gt;&lt;p&gt;یا تیر هلاکم بزنی بر دل مجروح&lt;/p&gt;&lt;p&gt;یا جان بدهم تا بدهی تیر امان را&lt;/p&gt;&lt;p&gt;وان گه که به تیرم زنی اول خبرم ده&lt;/p&gt;&lt;p&gt;تا پیشترت بوسه دهم دست و کمان را&lt;/p&gt;&lt;p&gt;سعدی ز فراق تو نه آن رنج کشیدست&lt;/p&gt;&lt;p&gt;کز شادی وصل تو فرامُش کند آن را&lt;/p&gt;&lt;p&gt;ور نیز جراحت به دوا باز هم آید&lt;/p&gt;&lt;p&gt;از جای جراحت نتوان بُرد نشان را&lt;/p&gt;', 'غزل شماره ۱۸', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(326, '&lt;p&gt;کمان سختْ که داد آن لطیف بازو را؟&lt;/p&gt;&lt;p&gt;که تیر غمزه تمامست صید آهو را&lt;/p&gt;&lt;p&gt;هزار صید دلت پیش تیر باز آید&lt;/p&gt;&lt;p&gt;بدین صفت که تو داری کمان ابرو را&lt;/p&gt;&lt;p&gt;تو خود به جوشن و برگستوان نه محتاجی&lt;/p&gt;&lt;p&gt;که روز معرکه بر خود زره کنی مو را&lt;/p&gt;&lt;p&gt;دیار هند و اقالیم ترک بسپارند&lt;/p&gt;&lt;p&gt;چو چشم ترک تو بینند و زلف هندو را&lt;/p&gt;&lt;p&gt;مغان که خدمت بت می‌کنند در فرخار&lt;/p&gt;&lt;p&gt;ندیده‌اند مگر دلبران بت‌رو را&lt;/p&gt;&lt;p&gt;حصار قلعهٔ باغی به منجنیق مده&lt;/p&gt;&lt;p&gt;به بام قصر برافکن کمند گیسو را&lt;/p&gt;&lt;p&gt;مرا که عزلت عنقا گرفتمی همه عمر&lt;/p&gt;&lt;p&gt;چنان اسیر گرفتی که باز تیهو را&lt;/p&gt;&lt;p&gt;لبت بدیدم و لعلم بیوفتاد از چشم&lt;/p&gt;&lt;p&gt;سخن بگفتی و قیمت برفت لؤلؤ را&lt;/p&gt;&lt;p&gt;بهای روی تو بازار ماه و خور بشکست&lt;/p&gt;&lt;p&gt;چنان که معجز موسی طلسم جادو را&lt;/p&gt;&lt;p&gt;به رنج بردن بیهوده گنج نتوان برد&lt;/p&gt;&lt;p&gt;که بخت راست فضیلت، نه زور بازو را&lt;/p&gt;&lt;p&gt;به عشق روی نکو دل کسی دهد سعدی&lt;/p&gt;&lt;p&gt;که احتمال کند خوی زشت نیکو را&lt;/p&gt;', 'غزل شماره ۱۹', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(327, '&lt;p&gt;لاابالی چه کند دفتر دانایی را&lt;/p&gt;&lt;p&gt;طاقت وعظ نباشد سر سودایی را&lt;/p&gt;&lt;p&gt;آب را قول تو با آتش اگر جمع کند&lt;/p&gt;&lt;p&gt;نتواند که کند عشق و شکیبایی را&lt;/p&gt;&lt;p&gt;دیده را فایده آن است که دلبر بیند&lt;/p&gt;&lt;p&gt;ور نبیند چه بود فایده بینایی را&lt;/p&gt;&lt;p&gt;عاشقان را چه غم از سرزنش دشمن و دوست&lt;/p&gt;&lt;p&gt;یا غم دوست خورد یا غم رسوایی را&lt;/p&gt;&lt;p&gt;همه دانند که من سبزهٔ خط دارم دوست&lt;/p&gt;&lt;p&gt;نه چو دیگر حیوان سبزه صحرایی را&lt;/p&gt;&lt;p&gt;من همان روز دل و صبر به یغما دادم&lt;/p&gt;&lt;p&gt;که مقید شدم آن دلبر یغمایی را&lt;/p&gt;&lt;p&gt;سرو بگذار که قدی و قیامی دارد&lt;/p&gt;&lt;p&gt;گو ببین آمدن و رفتن رعنایی را&lt;/p&gt;&lt;p&gt;گر برانی نرود ور برود باز آید&lt;/p&gt;&lt;p&gt;ناگزیر است مگس دکه حلوایی را&lt;/p&gt;&lt;p&gt;بر حدیث من و حسن تو نیفزاید کس&lt;/p&gt;&lt;p&gt;حد همین است سخندانی و زیبایی را&lt;/p&gt;&lt;p&gt;سعدیا نوبتی امشب دهل صبح نکوفت&lt;/p&gt;&lt;p&gt;یا مگر روز نباشد شب تنهایی را&lt;/p&gt;', 'غزل شماره ۲۰', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(328, '&lt;p&gt;تفاوتی نکند قدر پادشایی را&lt;/p&gt;&lt;p&gt;که التفات کند کمترین گدایی را&lt;/p&gt;&lt;p&gt;به جان دوست که دشمن بدین رضا ندهد&lt;/p&gt;&lt;p&gt;که در به روی ببندند آشنایی را&lt;/p&gt;&lt;p&gt;مگر حلال نباشد که بندگان ملوک&lt;/p&gt;&lt;p&gt;ز خیل‌خانه برانند بی‌نوایی را&lt;/p&gt;&lt;p&gt;و گر تو جور کنی رای ما دگر نشود&lt;/p&gt;&lt;p&gt;هزار شکر بگوییم هر جفایی را&lt;/p&gt;&lt;p&gt;همه سلامت نفس آرزو کند مردم&lt;/p&gt;&lt;p&gt;خلاف من که به جان می‌خرم بلایی را&lt;/p&gt;&lt;p&gt;حدیث عشق نداند کسی که در همه عمر&lt;/p&gt;&lt;p&gt;به سر نکوفته باشد در سرایی را&lt;/p&gt;&lt;p&gt;خیال در همه عالم برفت و بازآمد&lt;/p&gt;&lt;p&gt;که از حضور تو خوشتر ندید جایی را&lt;/p&gt;&lt;p&gt;سری به صحبت بیچارگان فرود آور&lt;/p&gt;&lt;p&gt;همین قدر که ببوسند خاک پایی را&lt;/p&gt;&lt;p&gt;قبای خوشتر از این در بدن تواند بود&lt;/p&gt;&lt;p&gt;بدن نیفتد از این خوبتر قبایی را&lt;/p&gt;&lt;p&gt;اگر تو روی نپوشی بدین لطافت و حسن&lt;/p&gt;&lt;p&gt;دگر نبینی در پارس پارسایی را&lt;/p&gt;&lt;p&gt;منه به جان تو بار فراق بر دل ریش&lt;/p&gt;&lt;p&gt;که پشه‌ای نبرد سنگ آسیایی را&lt;/p&gt;&lt;p&gt;دگر به دست نیاید چو من وفاداری&lt;/p&gt;&lt;p&gt;که ترک می‌ندهم عهد بی‌وفایی را&lt;/p&gt;&lt;p&gt;دعای سعدی اگر بشنوی زیان نکنی&lt;/p&gt;&lt;p&gt;که یحتمل که اجابت بود دعایی را&lt;/p&gt;', 'غزل شماره ۲۱', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(329, '&lt;p&gt;من بدین خوبی و زیبایی ندیدم روی را&lt;/p&gt;&lt;p&gt;وین دلاویزی و دلبندی نباشد موی را&lt;/p&gt;&lt;p&gt;روی اگر پنهان کند سنگین‌ دل سیمین‌ بدن&lt;/p&gt;&lt;p&gt;مشک غمازست نتواند نهفتن بوی را&lt;/p&gt;&lt;p&gt;ای موافق صورت و معنی که تا چشم من است&lt;/p&gt;&lt;p&gt;از تو زیباتر ندیدم روی و خوشتر خوی را&lt;/p&gt;&lt;p&gt;گر به سر می‌گردم از بیچارگی عیبم مکن&lt;/p&gt;&lt;p&gt;چون تو چوگان می‌زنی جرمی نباشد گوی را&lt;/p&gt;&lt;p&gt;هر که را وقتی دمی بودست و دردی سوخته‌ست&lt;/p&gt;&lt;p&gt;دوست دارد ناله مستان و هایاهوی را&lt;/p&gt;&lt;p&gt;ما ملامت را به جان جوییم در بازار عشق&lt;/p&gt;&lt;p&gt;کنج خلوت پارسایان سلامت جوی را&lt;/p&gt;&lt;p&gt;بوستان را هیچ دیگر در نمی‌باید به حسن&lt;/p&gt;&lt;p&gt;بلکه سروی چون تو می‌باید کنار جوی را&lt;/p&gt;&lt;p&gt;ای گل خوشبوی اگر صد قرن باز آید بهار&lt;/p&gt;&lt;p&gt;مثل من دیگر نبینی بلبل خوشگوی را&lt;/p&gt;&lt;p&gt;سعدیا گر بوسه بر دستش نمی‌یاری نهاد&lt;/p&gt;&lt;p&gt;چاره آن دانم که در پایش بمالی روی را&lt;/p&gt;', 'غزل شماره ۲۲', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(330, '&lt;p&gt;رفتیم اگر ملول شدی از نشست ما&lt;/p&gt;&lt;p&gt;فرمای خدمتی که برآید ز دست ما&lt;/p&gt;&lt;p&gt;برخاستیم و نقش تو در نفس ما چنانک&lt;/p&gt;&lt;p&gt;هر جا که هست بی تو نباشد نشست ما&lt;/p&gt;&lt;p&gt;با چون خودی درافکن اگر پنجه می‌کنی&lt;/p&gt;&lt;p&gt;ما خود شکسته‌ایم چه باشد شکست ما&lt;/p&gt;&lt;p&gt;جرمی نکرده‌ام که عقوبت کند ولیک&lt;/p&gt;&lt;p&gt;مردم به شرع می‌نکشد ترک مست ما&lt;/p&gt;&lt;p&gt;شکر خدای بود که آن بت وفا نکرد&lt;/p&gt;&lt;p&gt;باشد که توبه‌ای بکند بت‌پرست ما&lt;/p&gt;&lt;p&gt;سعدی نگفتمت که به سرو بلند او&lt;/p&gt;&lt;p&gt;مشکل توان رسید به بالای پست ما&lt;/p&gt;', 'غزل شماره ۲۳', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(331, '&lt;p&gt;وقتی دل سودایی می‌رفت به بستان‌ها&lt;/p&gt;&lt;p&gt;بی خویشتنم کردی بوی گل و ریحان‌ها&lt;/p&gt;&lt;p&gt;گه نعره زدی بلبل گه جامه دریدی گل&lt;/p&gt;&lt;p&gt;با یاد تو افتادم از یاد برفت آن‌ها&lt;/p&gt;&lt;p&gt;ای مهر تو در دل‌ها وی مهر تو بر لب‌ها&lt;/p&gt;&lt;p&gt;وی شور تو در سرها وی سر تو در جان‌ها&lt;/p&gt;&lt;p&gt;تا عهد تو دربستم عهد همه بشکستم&lt;/p&gt;&lt;p&gt;بعد از تو روا باشد نقض همه پیمان‌ها&lt;/p&gt;&lt;p&gt;تا خار غم عشقت آویخته در دامن&lt;/p&gt;&lt;p&gt;کوته نظری باشد رفتن به گلستان‌ها&lt;/p&gt;&lt;p&gt;آن را که چنین دردی از پای دراندازد&lt;/p&gt;&lt;p&gt;باید که فروشوید دست از همه درمان‌ها&lt;/p&gt;&lt;p&gt;گر در طلبت رنجی ما را برسد شاید&lt;/p&gt;&lt;p&gt;چون عشق حرم باشد سهل است بیابان‌ها&lt;/p&gt;&lt;p&gt;هر تیر که در کیش است گر بر دل ریش آید&lt;/p&gt;&lt;p&gt;ما نیز یکی باشیم از جمله قربان‌ها&lt;/p&gt;&lt;p&gt;هر کاو نظری دارد با یار کمان ابرو&lt;/p&gt;&lt;p&gt;باید که سپر باشد پیش همه پیکان‌ها&lt;/p&gt;&lt;p&gt;گویند مگو سعدی چندین سخن از عشقش&lt;/p&gt;&lt;p&gt;می‌گویم و بعد از من گویند به دوران‌ها&lt;/p&gt;', 'غزل شماره ۲۴', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(332, '&lt;p&gt;اگر تو برفکنی در میان شهر نقاب&lt;/p&gt;&lt;p&gt;هزار مؤمن مخلص درافکنی به عقاب&lt;/p&gt;&lt;p&gt;که را مجال نظر بر جمال میمونت&lt;/p&gt;&lt;p&gt;بدین صفت که تو دل می‌بری ورای حجاب&lt;/p&gt;&lt;p&gt;درون ما ز تو یک دم نمی‌شود خالی&lt;/p&gt;&lt;p&gt;کنون که شهر گرفتی روا مدار خراب&lt;/p&gt;&lt;p&gt;به موی تافته پای دلم فروبستی&lt;/p&gt;&lt;p&gt;چو موی تافتی ای نیکبخت روی متاب&lt;/p&gt;&lt;p&gt;تو را حکایت ما مختصر به گوش آید&lt;/p&gt;&lt;p&gt;که حال تشنه نمی‌دانی ای گل سیراب&lt;/p&gt;&lt;p&gt;اگر چراغ بمیرد صبا چه غم دارد&lt;/p&gt;&lt;p&gt;و گر بریزد کتان چه غم خورد مهتاب&lt;/p&gt;&lt;p&gt;دعات گفتم و دشنام اگر دهی سهل است&lt;/p&gt;&lt;p&gt;که با شکردهنان خوش بود سؤال و جواب&lt;/p&gt;&lt;p&gt;کجایی ای که تعنت کنی و طعنه زنی&lt;/p&gt;&lt;p&gt;تو بر کناری و ما اوفتاده در غرقاب&lt;/p&gt;&lt;p&gt;اسیر بند بلا را چه جای سرزنش است&lt;/p&gt;&lt;p&gt;گرت معاونتی دست می‌دهد دریاب&lt;/p&gt;&lt;p&gt;اگر چه صبر من از روی دوست ممکن نیست&lt;/p&gt;&lt;p&gt;همی‌کنم به ضرورت چو صبر ماهی از آب&lt;/p&gt;&lt;p&gt;تو باز دعوی پرهیز می‌کنی سعدی&lt;/p&gt;&lt;p&gt;که دل به کس ندهم کُلُّ مُدِّعٍ کَذّاب&lt;/p&gt;', 'غزل شماره ۲۵', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(333, '&lt;p&gt;ما را همه شب نمی‌برد خواب&lt;/p&gt;&lt;p&gt;ای خفتهٔ روزگار دریاب&lt;/p&gt;&lt;p&gt;در بادیه تشنگان بمردند&lt;/p&gt;&lt;p&gt;وز حله به کوفه می‌رود آب&lt;/p&gt;&lt;p&gt;ای سخت کمان سست پیمان&lt;/p&gt;&lt;p&gt;این بود وفای عهد اصحاب&lt;/p&gt;&lt;p&gt;خار است به زیر پهلوانم&lt;/p&gt;&lt;p&gt;بی روی تو خوابگاه سنجاب&lt;/p&gt;&lt;p&gt;ای دیده عاشقان به رویت&lt;/p&gt;&lt;p&gt;چون روی مجاوران به محراب&lt;/p&gt;&lt;p&gt;من تن به قضای عشق دادم&lt;/p&gt;&lt;p&gt;پیرانه سر آمدم به کُتّاب&lt;/p&gt;&lt;p&gt;زهر از کف دست نازنینان&lt;/p&gt;&lt;p&gt;در حلق چنان رود که جُلّاب&lt;/p&gt;&lt;p&gt;دیوانه‌ی کوی خوبرویان&lt;/p&gt;&lt;p&gt;دردش نکند جفای بواب&lt;/p&gt;&lt;p&gt;سعدی نتوان به هیچ کشتن&lt;/p&gt;&lt;p&gt;اِلّا به فراق روی احباب&lt;/p&gt;', 'غزل شماره ۲۶', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(334, '&lt;p&gt;ماهرویا! روی خوب از من متاب&lt;/p&gt;&lt;p&gt;بی‌خطا کشتن چه می‌بینی صواب&lt;/p&gt;&lt;p&gt;دوش در خوابم در آغوش آمدی&lt;/p&gt;&lt;p&gt;وین نپندارم که بینم جز به خواب&lt;/p&gt;&lt;p&gt;از درون سوزناک و چشم تر&lt;/p&gt;&lt;p&gt;نیمه‌ای در آتشم نیمی در آب&lt;/p&gt;&lt;p&gt;هر که باز آید ز در پندارم اوست&lt;/p&gt;&lt;p&gt;تشنه مسکین آب پندارد سراب&lt;/p&gt;&lt;p&gt;ناوکش را جان درویشان هدف&lt;/p&gt;&lt;p&gt;ناخنش را خون مسکینان خضاب&lt;/p&gt;&lt;p&gt;او سخن می‌گوید و دل می‌برد&lt;/p&gt;&lt;p&gt;و او نمک می‌ریزد و مردم کباب&lt;/p&gt;&lt;p&gt;حیف باشد بر چنان تن پیرهن&lt;/p&gt;&lt;p&gt;ظلم باشد بر چنان صورت نقاب&lt;/p&gt;&lt;p&gt;خوی به دامان از بناگوشش بگیر&lt;/p&gt;&lt;p&gt;تا بگیرد جامه‌ات بوی گلاب&lt;/p&gt;&lt;p&gt;فتنه باشد شاهدی شمعی به دست&lt;/p&gt;&lt;p&gt;سرگران از خواب و سرمست از شراب&lt;/p&gt;&lt;p&gt;بامدادی تا به شب رویت مپوش&lt;/p&gt;&lt;p&gt;تا بپوشانی جمال آفتاب&lt;/p&gt;&lt;p&gt;سعدیا گر در برش خواهی چو چنگ&lt;/p&gt;&lt;p&gt;گوشمالت خورد باید چون رباب&lt;/p&gt;', 'غزل شماره ۲۷', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(335, '&lt;p&gt;سرمست درآمد از خرابات&lt;/p&gt;&lt;p&gt;با عقل خراب در مناجات&lt;/p&gt;&lt;p&gt;بر خاک فکنده خرقه زهد&lt;/p&gt;&lt;p&gt;و آتش زده در لباس طامات&lt;/p&gt;&lt;p&gt;دل برده شمع مجلس او&lt;/p&gt;&lt;p&gt;پروانه به شادی و سعادات&lt;/p&gt;&lt;p&gt;جان در ره او به عجز می‌گفت&lt;/p&gt;&lt;p&gt;کای مالک عرصه کرامات&lt;/p&gt;&lt;p&gt;از خون پیاده‌ای چه خیزد&lt;/p&gt;&lt;p&gt;ای بر رخ تو هزار شه مات&lt;/p&gt;&lt;p&gt;حقا و به جانت ار توان کرد&lt;/p&gt;&lt;p&gt;با تو به هزار جان ملاقات&lt;/p&gt;&lt;p&gt;گر چشم دلم به صبر بودی&lt;/p&gt;&lt;p&gt;جز عشق ندیدمی مهمات&lt;/p&gt;&lt;p&gt;تا باقی عمر بر چه آید&lt;/p&gt;&lt;p&gt;بر باد شد آن چه رفت هیهات&lt;/p&gt;&lt;p&gt;صافی چو بشد به دور سعدی&lt;/p&gt;&lt;p&gt;زین پس من و دردی خرابات&lt;/p&gt;', 'غزل شماره ۲۸', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(336, '&lt;p&gt;متناسبند و موزون حرکات دلفریبت&lt;/p&gt;&lt;p&gt;متوجه است با ما سخنان بی‌حسیبت&lt;/p&gt;&lt;p&gt;چو نمی‌توان صبوری ستمت کشم ضروری&lt;/p&gt;&lt;p&gt;مگر آدمی نباشد که برنجد از عتیبت&lt;/p&gt;&lt;p&gt;اگرم تو خصم باشی نروم ز پیش تیرت&lt;/p&gt;&lt;p&gt;وگرم تو سیل باشی نگریزم از نشیبت&lt;/p&gt;&lt;p&gt;به قیاس در نگنجی و به وصف در نیایی&lt;/p&gt;&lt;p&gt;متحیرم در اوصاف جمال و روی و زیبت&lt;/p&gt;&lt;p&gt;اگرم برآورد بخت به تخت پادشاهی&lt;/p&gt;&lt;p&gt;نه چنان که بنده باشم همه عمر در رکیبت&lt;/p&gt;&lt;p&gt;عجب از کسی در این شهر که پارسا بماند&lt;/p&gt;&lt;p&gt;مگر او ندیده باشد رخ پارسافریبت&lt;/p&gt;&lt;p&gt;تو برون خبر نداری که چه می‌رود ز عشقت&lt;/p&gt;&lt;p&gt;به درآی اگر نه آتش بزنیم در حجیبت&lt;/p&gt;&lt;p&gt;تو درخت خوب‌منظر همه میوه‌ای ولیکن&lt;/p&gt;&lt;p&gt;چه کنم به دست کوته که نمی‌رسد به سیبت&lt;/p&gt;&lt;p&gt;تو شبی در انتظاری ننشسته‌ای چه دانی&lt;/p&gt;&lt;p&gt;که چه شب گذشت بر منتظران ناشکیبت&lt;/p&gt;&lt;p&gt;تو خود ای شب جدایی چه شبی بدین درازی&lt;/p&gt;&lt;p&gt;بگذر که جان سعدی بگداخت از نهیبت&lt;/p&gt;', 'غزل شماره ۲۹', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(337, '&lt;p&gt;هر که خصم اندر او کمند انداخت&lt;/p&gt;&lt;p&gt;به مراد ویش بباید ساخت&lt;/p&gt;&lt;p&gt;هر که عاشق نبود مرد نشد&lt;/p&gt;&lt;p&gt;نقره فایق نگشت تا نگداخت&lt;/p&gt;&lt;p&gt;هیچ مصلح به کوی عشق نرفت&lt;/p&gt;&lt;p&gt;که نه دنیا و آخرت درباخت&lt;/p&gt;&lt;p&gt;آن چنانش به ذکر مشغولم&lt;/p&gt;&lt;p&gt;که ندانم به خویشتن پرداخت&lt;/p&gt;&lt;p&gt;همچنان شکر عشق می‌گویم&lt;/p&gt;&lt;p&gt;که گرم دل بسوخت جان بنواخت&lt;/p&gt;&lt;p&gt;سعدیا خوشتر از حدیث تو نیست&lt;/p&gt;&lt;p&gt;تحفه روزگار اهل شناخت&lt;/p&gt;&lt;p&gt;آفرین بر زبان شیرینت&lt;/p&gt;&lt;p&gt;کاین همه شور در جهان انداخت&lt;/p&gt;', 'غزل شماره ۳۰', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(338, '&lt;p&gt;چه فتنه بود که حسن تو در جهان انداخت&lt;/p&gt;&lt;p&gt;که یک دم از تو نظر بر نمی‌توان انداخت&lt;/p&gt;&lt;p&gt;بلای غمزه نامهربان خون‌خوارت&lt;/p&gt;&lt;p&gt;چه خون که در دل یاران مهربان انداخت&lt;/p&gt;&lt;p&gt;ز عقل و عافیت آن روز بر کران ماندم&lt;/p&gt;&lt;p&gt;که روزگار حدیث تو در میان انداخت&lt;/p&gt;&lt;p&gt;نه باغ ماند و نه بستان که سرو قامت تو&lt;/p&gt;&lt;p&gt;برست و ولوله در باغ و بوستان انداخت&lt;/p&gt;&lt;p&gt;تو دوستی کن و از دیده مفکنم زنهار&lt;/p&gt;&lt;p&gt;که دشمنم ز برای تو در زبان انداخت&lt;/p&gt;&lt;p&gt;به چشم‌های تو کان چشم کز تو برگیرند&lt;/p&gt;&lt;p&gt;دریغ باشد بر ماه آسمان انداخت&lt;/p&gt;&lt;p&gt;همین حکایت روزی به دوستان برسد&lt;/p&gt;&lt;p&gt;که سعدی از پی جانان برفت و جان انداخت&lt;/p&gt;', 'غزل شماره ۳۱', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(339, '&lt;p&gt;معلمت همه شوخی و دلبری آموخت&lt;/p&gt;&lt;p&gt;جفا و ناز و عتاب و ستمگری آموخت&lt;/p&gt;&lt;p&gt;غلام آن لب ضحاک و چشم فتانم&lt;/p&gt;&lt;p&gt;که کید سحر به ضحاک و سامری آموخت&lt;/p&gt;&lt;p&gt;تو بت چرا به معلم روی که بتگر چین&lt;/p&gt;&lt;p&gt;به چین زلف تو آید به بتگری آموخت&lt;/p&gt;&lt;p&gt;هزار بلبل دستان سرای عاشق را&lt;/p&gt;&lt;p&gt;بباید از تو سخن گفتن دری آموخت&lt;/p&gt;&lt;p&gt;برفت رونق بازار آفتاب و قمر&lt;/p&gt;&lt;p&gt;از آن که ره به دکان تو مشتری آموخت&lt;/p&gt;&lt;p&gt;همه قبیله من عالمان دین بودند&lt;/p&gt;&lt;p&gt;مرا معلم عشق تو شاعری آموخت&lt;/p&gt;&lt;p&gt;مرا به شاعری آموخت روزگار آن گه&lt;/p&gt;&lt;p&gt;که چشم مست تو دیدم که ساحری آموخت&lt;/p&gt;&lt;p&gt;مگر دهان تو آموخت تنگی از دل من&lt;/p&gt;&lt;p&gt;وجود من ز میان تو لاغری آموخت&lt;/p&gt;&lt;p&gt;بلای عشق تو بنیاد زهد و بیخ ورع&lt;/p&gt;&lt;p&gt;چنان بکند که صوفی قلندری آموخت&lt;/p&gt;&lt;p&gt;دگر نه عزم سیاحت کند نه یاد وطن&lt;/p&gt;&lt;p&gt;کسی که بر سر کویت مجاوری آموخت&lt;/p&gt;&lt;p&gt;من آدمی به چنین شکل و قد و خوی و روش&lt;/p&gt;&lt;p&gt;ندیده‌ام مگر این شیوه از پری آموخت&lt;/p&gt;&lt;p&gt;به خون خلق فروبرده پنجه کاین حناست&lt;/p&gt;&lt;p&gt;ندانمش که به قتل که شاطری آموخت&lt;/p&gt;&lt;p&gt;چنین بگریم از این پس که مرد بتواند&lt;/p&gt;&lt;p&gt;در آب دیده سعدی شناوری آموخت&lt;/p&gt;', 'غزل شماره ۳۲', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(340, '&lt;p&gt;&lt;br&gt;کهن شود همه کس را به روزگار ارادت&lt;/p&gt;&lt;p&gt;مگر مرا که همان عشق اولست و زیادت&lt;/p&gt;&lt;p&gt;گرم جواز نباشد به پیشگاه قبولت&lt;/p&gt;&lt;p&gt;کجا روم که نمیرم بر آستان عبادت&lt;/p&gt;&lt;p&gt;مرا به روز قیامت مگر حساب نباشد&lt;/p&gt;&lt;p&gt;که هجر و وصل تو دیدم چه جای موت و اعادت&lt;/p&gt;&lt;p&gt;شنیدمت که نظر می‌کنی به حال ضعیفان&lt;/p&gt;&lt;p&gt;تبم گرفت و دلم خوش به انتظار عیادت&lt;/p&gt;&lt;p&gt;گرم به گوشه چشمی شکسته‌وار ببینی&lt;/p&gt;&lt;p&gt;فلک شوم به بزرگی و مشتری به سعادت&lt;/p&gt;&lt;p&gt;بیایمت که ببینم کدام زهره و یارا&lt;/p&gt;&lt;p&gt;روم که بی‌تو نشینم کدام صبر و جلادت&lt;/p&gt;&lt;p&gt;مرا هر آینه روزی تمام کشته ببینی&lt;/p&gt;&lt;p&gt;گرفته دامن قاتل به هر دو دست ارادت&lt;/p&gt;&lt;p&gt;اگر جنازه سعدی به کوی دوست برآرند&lt;/p&gt;&lt;p&gt;زهی حیات نکونام و رفتنی به شهادت&lt;/p&gt;', 'غزل شماره ۳۳', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(341, '&lt;p&gt;دل هر که صید کردی نکشد سر از کمندت&lt;/p&gt;&lt;p&gt;نه دگر امید دارد که رها شود ز بندت&lt;/p&gt;&lt;p&gt;به خدا که پرده از روی چو آتشت برافکن&lt;/p&gt;&lt;p&gt;که به اتفاق بینی دل عالمی سپندت&lt;/p&gt;&lt;p&gt;نه چمن شکوفه‌ای رست چو روی دلستانت&lt;/p&gt;&lt;p&gt;نه صبا صنوبری یافت چو قامت بلندت&lt;/p&gt;&lt;p&gt;گرت آرزوی آنست که خون خلق ریزی&lt;/p&gt;&lt;p&gt;چه کند که شیر گردن ننهد چو گوسفندت&lt;/p&gt;&lt;p&gt;تو امیر ملک حسنی به حقیقت ای دریغا&lt;/p&gt;&lt;p&gt;اگر التفات بودی به فقیر مستمندت&lt;/p&gt;&lt;p&gt;نه تو را بگفتم ای دل که سر وفا ندارد&lt;/p&gt;&lt;p&gt;به طمع ز دست رفتی و به پای درفکندت&lt;/p&gt;&lt;p&gt;تو نه مرد عشق بودی خود از این حساب سعدی&lt;/p&gt;&lt;p&gt;که نه قوت گریزست و نه طاقت گزندت&lt;/p&gt;', 'غزل شماره ۳۴', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(342, '&lt;p&gt;دوست دارم که بپوشی رخ همچون قمرت&lt;/p&gt;&lt;p&gt;تا چو خورشید نبینند به هر بام و درت&lt;/p&gt;&lt;p&gt;جرم بیگانه نباشد که تو خود صورت خویش&lt;/p&gt;&lt;p&gt;گر در آیینه ببینی برود دل ز برت&lt;/p&gt;&lt;p&gt;جای خنده‌ست سخن گفتن شیرین پیشت&lt;/p&gt;&lt;p&gt;کآب شیرین چو بخندی برود از شکرت&lt;/p&gt;&lt;p&gt;راه آه سحر از شوق نمی‌یارم داد&lt;/p&gt;&lt;p&gt;تا نباید که بشوراند خواب سحرت&lt;/p&gt;&lt;p&gt;هیچ پیرایه زیادت نکند حسن تو را&lt;/p&gt;&lt;p&gt;هیچ مشاطه نیاراید از این خوبترت&lt;/p&gt;&lt;p&gt;بارها گفته‌ام این روی به هر کس منمای&lt;/p&gt;&lt;p&gt;تا تأمل نکند دیده هر بی‌بصرت&lt;/p&gt;&lt;p&gt;باز گویم نه که این صورت و معنی که تو راست&lt;/p&gt;&lt;p&gt;نتواند که ببیند مگر اهل نظرت&lt;/p&gt;&lt;p&gt;راه صد دشمنم از بهر تو می‌باید داد&lt;/p&gt;&lt;p&gt;تا یکی دوست ببینم که بگوید خبرت&lt;/p&gt;&lt;p&gt;آن چنان سخت نیاید سر من گر برود&lt;/p&gt;&lt;p&gt;نازنینا که پریشانی مویی ز سرت&lt;/p&gt;&lt;p&gt;غم آن نیست که بر خاک نشیند سعدی&lt;/p&gt;&lt;p&gt;زحمت خویش نمی‌خواهد بر رهگذرت&lt;/p&gt;', 'غزل شماره ۳۵', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(343, '&lt;p&gt;بنده‌وار آمدم به زنهارت&lt;/p&gt;&lt;p&gt;که ندارم سلاح پیکارت&lt;/p&gt;&lt;p&gt;متفق می‌شوم که دل ندهم&lt;/p&gt;&lt;p&gt;معتقد می‌شوم دگر بارت&lt;/p&gt;&lt;p&gt;مشتری را بهای روی تو نیست&lt;/p&gt;&lt;p&gt;من بدین مفلسی خریدارت&lt;/p&gt;&lt;p&gt;غیرتم هست و اقتدارم نیست&lt;/p&gt;&lt;p&gt;که بپوشم ز چشم اغیارت&lt;/p&gt;&lt;p&gt;گر چه بی طاقتم چو مور ضعیف&lt;/p&gt;&lt;p&gt;می‌کشم نفس و می‌کشم بارت&lt;/p&gt;&lt;p&gt;نه چنان در کمند پیچیدی&lt;/p&gt;&lt;p&gt;که مخلص شود گرفتارت&lt;/p&gt;&lt;p&gt;من هم اول که دیدمت گفتم&lt;/p&gt;&lt;p&gt;حذر از چشم مست خون‌خوارت&lt;/p&gt;&lt;p&gt;دیده شاید که بی تو برنکند&lt;/p&gt;&lt;p&gt;تا نبیند فراق دیدارت&lt;/p&gt;&lt;p&gt;تو ملولی و دوستان مشتاق&lt;/p&gt;&lt;p&gt;تو گریزان و ما طلبکارت&lt;/p&gt;&lt;p&gt;چشم سعدی به خواب بیند خواب&lt;/p&gt;&lt;p&gt;که ببستی به چشم سحارت&lt;/p&gt;&lt;p&gt;تو بدین هر دو چشم خواب‌آلود&lt;/p&gt;&lt;p&gt;چه غم از چشم‌های بیدارت&lt;/p&gt;', 'غزل شماره ۳۶', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(344, '&lt;p&gt;مپندار از لب شیرین عبارت&lt;/p&gt;&lt;p&gt;که کامی حاصل آید بی‌مرارت&lt;/p&gt;&lt;p&gt;فراق افتد میان دوستداران&lt;/p&gt;&lt;p&gt;زیان و سود باشد در تجارت&lt;/p&gt;&lt;p&gt;یکی را چون ببینی کشتهٔ دوست&lt;/p&gt;&lt;p&gt;به دیگر دوستانش ده بشارت&lt;/p&gt;&lt;p&gt;ندانم هیچکس در عهد حسنت&lt;/p&gt;&lt;p&gt;که بادل باشد الا بی‌بصارت&lt;/p&gt;&lt;p&gt;مرا آن گوشهٔ چشم دلاویز&lt;/p&gt;&lt;p&gt;به کشتن می‌کند گویی اشارت&lt;/p&gt;&lt;p&gt;گر آن حلوا به دست صوفی افتد&lt;/p&gt;&lt;p&gt;خداترسی نباشد روز غارت&lt;/p&gt;&lt;p&gt;عجب دارم درون عاشقان را&lt;/p&gt;&lt;p&gt;که پیراهن نمی‌سوزد حرارت&lt;/p&gt;&lt;p&gt;جمال دوست چندان سایه انداخت&lt;/p&gt;&lt;p&gt;که سعدی ناپدیده‌ست از حقارت&lt;/p&gt;', 'غزل شماره ۳۷', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(345, '&lt;p&gt;چه دل‌ها بردی ای ساقی به ساق فتنه‌انگیزت&lt;/p&gt;&lt;p&gt;دریغا بوسه چندی بر زنخدان دلاویزت&lt;/p&gt;&lt;p&gt;خدنگ غمزه از هر سو نهان انداختن تا کی&lt;/p&gt;&lt;p&gt;سپر انداخت عقل از دست ناوک‌های خونریزت&lt;/p&gt;&lt;p&gt;برآمیزی و بگریزی و بنمایی و بربایی&lt;/p&gt;&lt;p&gt;فغان از قهر لطف‌اندود و زهر شکرآمیزت&lt;/p&gt;&lt;p&gt;لب شیرینت ار شیرین بدیدی در سخن گفتن&lt;/p&gt;&lt;p&gt;بر او شکرانه بودی گر بدادی ملک پرویزت&lt;/p&gt;&lt;p&gt;جهان از فتنه و آشوب یک چندی برآسودی&lt;/p&gt;&lt;p&gt;اگر نه روی شهرآشوب و چشم فتنه‌انگیزت&lt;/p&gt;&lt;p&gt;دگر رغبت کجا ماند کسی را سوی هشیاری&lt;/p&gt;&lt;p&gt;چو بیند دست در آغوش مستان سحرخیزت&lt;/p&gt;&lt;p&gt;دمادم درکش ای سعدی شراب صرف و دم درکش&lt;/p&gt;&lt;p&gt;که با مستان مجلس درنگیرد زهد و پرهیزت&lt;/p&gt;', 'غزل شماره ۳۸', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(346, '&lt;p&gt;بی‌تو حرام است به خلوت نشست&lt;/p&gt;&lt;p&gt;حیف بود در به چنین روی بست&lt;/p&gt;&lt;p&gt;دامن دولت چو به دست اوفتاد&lt;/p&gt;&lt;p&gt;گر بهلی باز نیاید به دست&lt;/p&gt;&lt;p&gt;این چه نظر بود که خونم بریخت؟&lt;/p&gt;&lt;p&gt;وین چه نمک بود که ریشم بخست؟&lt;/p&gt;&lt;p&gt;هر که بیفتاد به تیرت نخاست&lt;/p&gt;&lt;p&gt;وان که درآمد به کمندت نجست&lt;/p&gt;&lt;p&gt;ما به تو یکباره مقید شدیم&lt;/p&gt;&lt;p&gt;مرغ به دام آمد و ماهی به شست&lt;/p&gt;&lt;p&gt;صبر قفا خورد و به راهی گریخت&lt;/p&gt;&lt;p&gt;عقل بلا دید و به کنجی نشست&lt;/p&gt;&lt;p&gt;بار مذلت بتوانم کشید&lt;/p&gt;&lt;p&gt;عهد محبت نتوانم شکست&lt;/p&gt;&lt;p&gt;وین رمقی نیز که هست از وجود&lt;/p&gt;&lt;p&gt;پیش وجودت نتوان گفت هست&lt;/p&gt;&lt;p&gt;هرگز اگر راه به معنی برد&lt;/p&gt;&lt;p&gt;سجدهٔ صورت نکند بت‌پرست&lt;/p&gt;&lt;p&gt;مستی خمرش نکند آرزو&lt;/p&gt;&lt;p&gt;هر که چو سعدی شود از عشق مست&lt;/p&gt;', 'غزل شماره ۳۹', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(347, '&lt;p&gt;چنان به موی تو آشفته‌ام به بوی تو مست&lt;/p&gt;&lt;p&gt;که نیستم خبر از هر چه در دو عالم هست&lt;/p&gt;&lt;p&gt;دگر به روی کسم دیده بر نمی‌باشد&lt;/p&gt;&lt;p&gt;خلیل من همه بت‌های آزری بشکست&lt;/p&gt;&lt;p&gt;مجال خواب نمی‌باشدم ز دست خیال&lt;/p&gt;&lt;p&gt;در سرای نشاید بر آشنایان بست&lt;/p&gt;&lt;p&gt;در قفس طلبد هر کجا گرفتاریست&lt;/p&gt;&lt;p&gt;من از کمند تو تا زنده‌ام نخواهم جست&lt;/p&gt;&lt;p&gt;غلام دولت آنم که پای‌بند یکیست&lt;/p&gt;&lt;p&gt;به جانبی متعلق شد از هزار برست&lt;/p&gt;&lt;p&gt;مطیع امر توام گر دلم بخواهی سوخت&lt;/p&gt;&lt;p&gt;اسیر حکم توام گر تنم بخواهی خست&lt;/p&gt;&lt;p&gt;نماز شام قیامت به هوش باز آید&lt;/p&gt;&lt;p&gt;کسی که خورده بود می ز بامداد الست&lt;/p&gt;&lt;p&gt;نگاه من به تو و دیگران به خود مشغول&lt;/p&gt;&lt;p&gt;معاشران ز می و عارفان ز ساقی مست&lt;/p&gt;&lt;p&gt;اگر تو سرو خرامان ز پای ننشینی&lt;/p&gt;&lt;p&gt;چه فتنه‌ها که بخیزد میان اهل نشست&lt;/p&gt;&lt;p&gt;برادران و بزرگان نصیحتم مکنید&lt;/p&gt;&lt;p&gt;که اختیار من از دست رفت و تیر از شست&lt;/p&gt;&lt;p&gt;حذر کنید ز باران دیدهٔ سعدی&lt;/p&gt;&lt;p&gt;که قطره سیل شود چون به یکدگر پیوست&lt;/p&gt;&lt;p&gt;خوش است نام تو بردن ولی دریغ بود&lt;/p&gt;&lt;p&gt;در این سخن که بخواهند برد دست به دست&lt;/p&gt;', 'غزل شماره ۴۰', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(348, '&lt;p&gt;دیر آمدی ای نگار سرمست&lt;/p&gt;&lt;p&gt;زودت ندهیم دامن از دست&lt;/p&gt;&lt;p&gt;بر آتش عشقت آب تدبیر&lt;/p&gt;&lt;p&gt;چندان که زدیم باز ننشست&lt;/p&gt;&lt;p&gt;از رای تو سر نمی‌توان تافت&lt;/p&gt;&lt;p&gt;وز روی تو در نمی‌توان بست&lt;/p&gt;&lt;p&gt;از پیش تو راه رفتنم نیست&lt;/p&gt;&lt;p&gt;چون ماهی اوفتاده در شست&lt;/p&gt;&lt;p&gt;سودای لب شکردهانان&lt;/p&gt;&lt;p&gt;بس توبهٔ صالحان که بشکست&lt;/p&gt;&lt;p&gt;ای سرو بلند بوستانی&lt;/p&gt;&lt;p&gt;در پیش درخت قامتت پست&lt;/p&gt;&lt;p&gt;بیچاره کسی که از تو ببرید&lt;/p&gt;&lt;p&gt;آسوده تنی که با تو پیوست&lt;/p&gt;&lt;p&gt;چشمت به کرشمه خون من ریخت&lt;/p&gt;&lt;p&gt;وز قتل خطا چه غم خورد مست&lt;/p&gt;&lt;p&gt;سعدی ز کمند خوبرویان&lt;/p&gt;&lt;p&gt;تا جان داری نمی‌توان جست&lt;/p&gt;&lt;p&gt;ور سر ننهی در آستانش&lt;/p&gt;&lt;p&gt;دیگر چه کنی دری دگر هست؟&lt;/p&gt;', 'غزل شماره ۴۱', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(349, '&lt;p&gt;نشاید گفتن آنکس را دلی هست&lt;/p&gt;&lt;p&gt;که ندهد بر چنین صورت دل از دست&lt;/p&gt;&lt;p&gt;نه منظوری که با او می‌توان گفت&lt;/p&gt;&lt;p&gt;نه خصمی کز کمندش می‌توان رست&lt;/p&gt;&lt;p&gt;به دل گفتم ز چشمانش بپرهیز&lt;/p&gt;&lt;p&gt;که هشیاران نیاویزند با مست&lt;/p&gt;&lt;p&gt;سرانگشتان مخضوبش نبینی&lt;/p&gt;&lt;p&gt;که دست صبر برپیچید و بشکست&lt;/p&gt;&lt;p&gt;نه آزاد از سرش بر‌ می‌توان خاست&lt;/p&gt;&lt;p&gt;نه با او می‌توان آسوده بنشست&lt;/p&gt;&lt;p&gt;اگر دودی رود بی‌آتشی نیست&lt;/p&gt;&lt;p&gt;و گر خونی بیاید کشته‌ای هست&lt;/p&gt;&lt;p&gt;خیالش در نظر، چون آیدم خواب؟&lt;/p&gt;&lt;p&gt;نشاید در به روی دوستان بست&lt;/p&gt;&lt;p&gt;نشاید خرمن بیچارگان سوخت&lt;/p&gt;&lt;p&gt;نمی‌باید دل درمندگان خست&lt;/p&gt;&lt;p&gt;به آخر دوستی نتوان بریدن&lt;/p&gt;&lt;p&gt;به اول خود نمی‌بایست پیوست&lt;/p&gt;&lt;p&gt;دلی از دست بیرون رفته سعدی&lt;/p&gt;&lt;p&gt;نیاید باز تیر رفته از شست&lt;/p&gt;', 'غزل شماره ۴۲', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(350, '&lt;p&gt;اگر مراد تو ای دوست بی‌مرادی ماست&lt;/p&gt;&lt;p&gt;مراد خویش دگرباره من نخواهم خواست&lt;/p&gt;&lt;p&gt;اگر قبول کنی ور برانی از بر خویش&lt;/p&gt;&lt;p&gt;خلاف رای تو کردن خلاف مذهب ماست&lt;/p&gt;&lt;p&gt;میان عیب و هنر پیش دوستان کریم&lt;/p&gt;&lt;p&gt;تفاوتی نکند چون نظر به عین رضاست&lt;/p&gt;&lt;p&gt;عنایتی که تو را بود اگر مبدل شد&lt;/p&gt;&lt;p&gt;خلل‌پذیر نباشد ارادتی که مراست&lt;/p&gt;&lt;p&gt;مرا به هر چه کنی دل نخواهی آزردن&lt;/p&gt;&lt;p&gt;که هر چه دوست پسندد به جای دوست رواست&lt;/p&gt;&lt;p&gt;اگر عداوت و جنگ است در میان عرب&lt;/p&gt;&lt;p&gt;میان لیلی و مجنون محبت است و صفاست&lt;/p&gt;&lt;p&gt;هزار دشمنی افتد به قول بدگویان&lt;/p&gt;&lt;p&gt;میان عاشق و معشوق دوستی برجاست&lt;/p&gt;&lt;p&gt;غلام قامت آن لعبت قباپوشم&lt;/p&gt;&lt;p&gt;که در محبت رویش هزار جامه قباست&lt;/p&gt;&lt;p&gt;نمی‌توانم بی‌ او نشست یک ساعت&lt;/p&gt;&lt;p&gt;چرا که از سر جان بر نمی‌توانم خاست&lt;/p&gt;&lt;p&gt;جمال در نظر و شوق همچنان باقی&lt;/p&gt;&lt;p&gt;گدا اگر همه عالم بدو دهند گداست&lt;/p&gt;&lt;p&gt;مرا به عشق تو اندیشه از ملامت نیست&lt;/p&gt;&lt;p&gt;و گر کنند ملامت نه بر من تنهاست&lt;/p&gt;&lt;p&gt;هر آدمی که چنین شخص دلستان بیند&lt;/p&gt;&lt;p&gt;ضرورت است که گوید به سرو ماند راست&lt;/p&gt;&lt;p&gt;به روی خوبان گفتی نظر خطا باشد&lt;/p&gt;&lt;p&gt;خطا نباشد دیگر مگو چنین که خطاست&lt;/p&gt;&lt;p&gt;خوش است با غم هجران دوست سعدی را&lt;/p&gt;&lt;p&gt;که گرچه رنج به جان می‌رسد امید دواست&lt;/p&gt;&lt;p&gt;بلا و زحمت امروز بر دل درویش&lt;/p&gt;&lt;p&gt;از آن خوش است که امید رحمت فرداست&lt;/p&gt;', 'غزل شماره ۴۳', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(351, '&lt;p&gt;بوی گل و بانگ مرغ برخاست&lt;/p&gt;&lt;p&gt;هنگام نشاط و روز صحراست&lt;/p&gt;&lt;p&gt;فراش خزان ورق بیفشاند&lt;/p&gt;&lt;p&gt;نقاش صبا چمن بیاراست&lt;/p&gt;&lt;p&gt;ما را سر باغ و بوستان نیست&lt;/p&gt;&lt;p&gt;هر جا که تویی تفرج آن جاست&lt;/p&gt;&lt;p&gt;گویند نظر به روی خوبان&lt;/p&gt;&lt;p&gt;نهیست نه این نظر که ما راست&lt;/p&gt;&lt;p&gt;در روی تو سر صنع بی چون&lt;/p&gt;&lt;p&gt;چون آب در آبگینه پیداست&lt;/p&gt;&lt;p&gt;چشم چپ خویشتن برآرم&lt;/p&gt;&lt;p&gt;تا چشم نبیندت به جز راست&lt;/p&gt;&lt;p&gt;هر آدمیی که مهر مهرت&lt;/p&gt;&lt;p&gt;در وی نگرفت سنگ خاراست&lt;/p&gt;&lt;p&gt;روزی تر و خشک من بسوزد&lt;/p&gt;&lt;p&gt;آتش که به زیر دیگ سوداست&lt;/p&gt;&lt;p&gt;نالیدن بی‌حساب سعدی&lt;/p&gt;&lt;p&gt;گویند خلاف رای داناست&lt;/p&gt;&lt;p&gt;از ورطه ما خبر ندارد&lt;/p&gt;&lt;p&gt;آسوده که بر کنار دریاست&lt;/p&gt;', 'غزل شماره ۴۴', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(352, '&lt;p&gt;خوش می‌رود این پسر که برخاست&lt;/p&gt;&lt;p&gt;سرویست چنین که می‌رود راست&lt;/p&gt;&lt;p&gt;ابروش کمان قتل عاشق&lt;/p&gt;&lt;p&gt;گیسوش کمند عقل داناست&lt;/p&gt;&lt;p&gt;بالای چنین اگر در اسلام&lt;/p&gt;&lt;p&gt;گویند که هست زیر و بالاست&lt;/p&gt;&lt;p&gt;ای آتش خرمن عزیزان&lt;/p&gt;&lt;p&gt;بنشین که هزار فتنه برخاست&lt;/p&gt;&lt;p&gt;بی جرم بکش که بنده مملوک&lt;/p&gt;&lt;p&gt;بی شرع ببر که خانه یغماست&lt;/p&gt;&lt;p&gt;دردت بکشم که درد داروست&lt;/p&gt;&lt;p&gt;خارت بخورم که خار خرماست&lt;/p&gt;&lt;p&gt;انگشت نمای خلق بودن&lt;/p&gt;&lt;p&gt;زشت است ولیک با تو زیباست&lt;/p&gt;&lt;p&gt;باید که سلامت تو باشد&lt;/p&gt;&lt;p&gt;سهل است ملامتی که بر ماست&lt;/p&gt;&lt;p&gt;جان در قدم تو ریخت سعدی&lt;/p&gt;&lt;p&gt;وین منزلت از خدای می‌خواست&lt;/p&gt;&lt;p&gt;خواهی که دگر حیات یابد&lt;/p&gt;&lt;p&gt;یک بار بگو که کشتهٔ ماست&lt;/p&gt;', 'غزل شماره ۴۵', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(353, '&lt;p&gt;دیگر نشنیدیم چنین فتنه که برخاست&lt;/p&gt;&lt;p&gt;از خانه برون آمد و بازار بیاراست&lt;/p&gt;&lt;p&gt;در وهم نگنجد که چه دلبند و چه شیرین&lt;/p&gt;&lt;p&gt;در وصف نیاید که چه مطبوع و چه زیباست&lt;/p&gt;&lt;p&gt;صبر و دل و دین می‌رود و طاقت و آرام&lt;/p&gt;&lt;p&gt;از زخم پدید است که بازوش تواناست&lt;/p&gt;&lt;p&gt;از بهر خدا روی مپوش از زن و از مرد&lt;/p&gt;&lt;p&gt;تا صنع خدا می‌نگرند از چپ و از راست&lt;/p&gt;&lt;p&gt;چشمی که تو را بیند و در قدرت بی چون&lt;/p&gt;&lt;p&gt;مدهوش نماند نتوان گفت که بیناست&lt;/p&gt;&lt;p&gt;دنیا به چه کار آید و فردوس چه باشد&lt;/p&gt;&lt;p&gt;از بارخدا به ز تو حاجت نتوان خواست&lt;/p&gt;&lt;p&gt;فریاد من از دست غمت عیب نباشد&lt;/p&gt;&lt;p&gt;کاین درد نپندارم از آن من تنهاست&lt;/p&gt;&lt;p&gt;با جور و جفای تو نسازیم چه سازیم&lt;/p&gt;&lt;p&gt;چون زهره و یارا نبود چاره مداراست&lt;/p&gt;&lt;p&gt;از روی شما صبر نه صبر است که زهر است&lt;/p&gt;&lt;p&gt;وز دست شما زهر نه زهر است که حلواست&lt;/p&gt;&lt;p&gt;آن کام و دهان و لب و دندان که تو داری&lt;/p&gt;&lt;p&gt;عیش است ولی تا ز برای که مهیاست&lt;/p&gt;&lt;p&gt;گر خون من و جمله عالم تو بریزی&lt;/p&gt;&lt;p&gt;اقرار بیاریم که جرم از طرف ماست&lt;/p&gt;&lt;p&gt;تسلیم تو سعدی نتواند که نباشد&lt;/p&gt;&lt;p&gt;گر سر بنهد ور ننهد دست تو بالاست&lt;/p&gt;', 'غزل شماره ۴۶', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(354, '&lt;p&gt;سلسلهٔ موی دوست حلقه دام بلاست&lt;/p&gt;&lt;p&gt;هر که در این حلقه نیست فارغ از این ماجراست&lt;/p&gt;&lt;p&gt;گر بزنندم به تیغ در نظرش بی‌دریغ&lt;/p&gt;&lt;p&gt;دیدن او یک نظر صد چو منش خونبهاست&lt;/p&gt;&lt;p&gt;گر برود جان ما در طلب وصل دوست&lt;/p&gt;&lt;p&gt;حیف نباشد که دوست دوست‌تر از جان ماست&lt;/p&gt;&lt;p&gt;دعوی عشاق را شرع نخواهد بیان&lt;/p&gt;&lt;p&gt;گونهٔ زردش دلیل ناله زارش گواست&lt;/p&gt;&lt;p&gt;مایهٔ پرهیزگار قوت صبر است و عقل&lt;/p&gt;&lt;p&gt;عقل گرفتار عشق صبر زبون هواست&lt;/p&gt;&lt;p&gt;دلشدهٔ پایبند گردن جان در کمند&lt;/p&gt;&lt;p&gt;زهرهٔ گفتار نه کاین چه سبب وان چراست&lt;/p&gt;&lt;p&gt;مالک ملک وجود حاکم رد و قبول&lt;/p&gt;&lt;p&gt;هر چه کند جور نیست ور تو بنالی جفاست&lt;/p&gt;&lt;p&gt;تیغ برآر از نیام زهر برافکن به جام&lt;/p&gt;&lt;p&gt;کز قبل ما قبول وز طرف ما رضاست&lt;/p&gt;&lt;p&gt;گر بنوازی به لطف ور بگدازی به قهر&lt;/p&gt;&lt;p&gt;حکم تو بر من روان زجر تو بر من رواست&lt;/p&gt;&lt;p&gt;هر که به جور رقیب یا به جفای حبیب&lt;/p&gt;&lt;p&gt;عهد فرامش کند مدعی بی‌وفاست&lt;/p&gt;&lt;p&gt;سعدی از اخلاق دوست هر چه برآید نکوست&lt;/p&gt;&lt;p&gt;گو همه دشنام گو کز لب شیرین دعاست&lt;/p&gt;', 'غزل شماره ۴۷', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(355, '&lt;p&gt;صبر کن ای دل که صبر سیرت اهل صفاست&lt;/p&gt;&lt;p&gt;چارهٔ عشق احتمال شرط محبت وفاست&lt;/p&gt;&lt;p&gt;مالک رد و قبول هر چه کند پادشاست&lt;/p&gt;&lt;p&gt;گر بزند حاکم است ور بنوازد رواست&lt;/p&gt;&lt;p&gt;گر چه بخواند هنوز دست جزع بر دعاست&lt;/p&gt;&lt;p&gt;ور چه براند هنوز روی امید از قفاست&lt;/p&gt;&lt;p&gt;برق یمانی بجست باد بهاری بخاست&lt;/p&gt;&lt;p&gt;طاقت مجنون برفت خیمهٔ لیلی کجاست&lt;/p&gt;&lt;p&gt;غفلت از ایام عشق پیش محقق خطاست&lt;/p&gt;&lt;p&gt;اول صبح است خیز کآخر دنیا فناست&lt;/p&gt;&lt;p&gt;صحبت یار عزیز حاصل دور بقاست&lt;/p&gt;&lt;p&gt;یک دمه دیدار دوست هر دو جهانش بهاست&lt;/p&gt;&lt;p&gt;درد دل دوستان گر تو پسندی رواست&lt;/p&gt;&lt;p&gt;هر چه مراد شماست غایت مقصود ماست&lt;/p&gt;&lt;p&gt;بنده چه دعوی کند حکم خداوند راست&lt;/p&gt;&lt;p&gt;گر تو قدم می‌نهی تا بنهم چشم راست&lt;/p&gt;&lt;p&gt;از در خویشم مران کاین نه طریق وفاست&lt;/p&gt;&lt;p&gt;در همه شهری غریب در همه ملکی گداست&lt;/p&gt;&lt;p&gt;با همه جرمم امید با همه خوفم رجاست&lt;/p&gt;&lt;p&gt;گر درم ما مس است لطف شما کیمیاست&lt;/p&gt;&lt;p&gt;سعدی اگر عاشقی میل وصالت چراست&lt;/p&gt;&lt;p&gt;هر که دل دوست جست مصلحت خود نخواست&lt;/p&gt;', 'غزل شماره ۴۸', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(356, '&lt;p&gt;خرم آن بقعه که آرامگه یار آنجاست&lt;/p&gt;&lt;p&gt;راحت جان و شفای دل بیمار آنجاست&lt;/p&gt;&lt;p&gt;من در این جای همین صورت بی‌جانم و بس&lt;/p&gt;&lt;p&gt;دلم آنجاست که آن دلبر عیار آنجاست&lt;/p&gt;&lt;p&gt;تنم اینجاست سقیم و دلم آنجاست مقیم&lt;/p&gt;&lt;p&gt;فلک اینجاست ولی کوکب سیار آنجاست&lt;/p&gt;&lt;p&gt;آخر ای باد صبا بویی اگر می‌آری&lt;/p&gt;&lt;p&gt;سوی شیراز گذر کن که مرا یار آنجاست&lt;/p&gt;&lt;p&gt;درد دل پیش که گویم غم دل با که خورم&lt;/p&gt;&lt;p&gt;روم آنجا که مرا محرم اسرار آنجاست&lt;/p&gt;&lt;p&gt;نکند میل دل من به تماشای چمن&lt;/p&gt;&lt;p&gt;که تماشای دل آنجاست که دلدار آنجاست&lt;/p&gt;&lt;p&gt;سعدی این منزل ویران چه کنی جای تو نیست&lt;/p&gt;&lt;p&gt;رخت بربند که منزلگه احرار آنجاست&lt;/p&gt;', 'غزل شماره ۴۹', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(357, '&lt;p&gt;&lt;br&gt;عشق ورزیدم و عقلم به ملامت برخاست&lt;/p&gt;&lt;p&gt;کان که عاشق شد از او حکم سلامت برخاست&lt;/p&gt;&lt;p&gt;هر که با شاهد گلروی به خلوت بنشست&lt;/p&gt;&lt;p&gt;نتواند ز سر راه ملامت برخاست&lt;/p&gt;&lt;p&gt;که شنیدی که برانگیخت سمند غم عشق&lt;/p&gt;&lt;p&gt;که نه اندر عقبش گرد ندامت برخاست&lt;/p&gt;&lt;p&gt;عشق غالب شد و از گوشه نشینان صلاح&lt;/p&gt;&lt;p&gt;نام مستوری و ناموس کرامت برخاست&lt;/p&gt;&lt;p&gt;در گلستانی کان گلبن خندان بنشست&lt;/p&gt;&lt;p&gt;سرو آزاد به یک پای غرامت برخاست&lt;/p&gt;&lt;p&gt;گل صدبرگ ندانم به چه رونق بشکفت&lt;/p&gt;&lt;p&gt;یا صنوبر به کدامین قد و قامت برخاست&lt;/p&gt;&lt;p&gt;دی زمانی به تکلف بر سعدی بنشست&lt;/p&gt;&lt;p&gt;فتنه بنشست چو برخاست قیامت برخاست&lt;/p&gt;', 'غزل شماره ۵۰', 'سعدی', 'دیوان اشعار', 'غزل', 6, 10),
(358, '&lt;p&gt;به نام خداوندِ جان‌آفرین&lt;/p&gt;&lt;p&gt;حکیمِ سخن‌درزبان‌آفرین&lt;/p&gt;&lt;p&gt;خداوند بخشندهٔ دستگیر&lt;/p&gt;&lt;p&gt;کریم خطابخش پوزش‌پذیر&lt;/p&gt;&lt;p&gt;عزیزی که هر کز درش سر بتافت&lt;/p&gt;&lt;p&gt;به هر در که شد هیچ عزّت نیافت&lt;/p&gt;&lt;p&gt;سر پادشاهان گردن‌فراز&lt;/p&gt;&lt;p&gt;به درگاه او بر زمینِ نیاز&lt;/p&gt;&lt;p&gt;نه گردن‌کشان را بگیرد به فور&lt;/p&gt;&lt;p&gt;نه عذرآوران را براند به جور&lt;/p&gt;&lt;p&gt;وگر خشم گیرد ز کردارِ زشت&lt;/p&gt;&lt;p&gt;چو بازآمدی ماجرا درنوشت&lt;/p&gt;&lt;p&gt;اگر با پدر جنگ جوید کسی&lt;/p&gt;&lt;p&gt;پدر بی‌گمان خشم گیرد بسی&lt;/p&gt;&lt;p&gt;وگر خویش راضی نباشد ز خویش&lt;/p&gt;&lt;p&gt;چو بیگانگانش براند ز پیش&lt;/p&gt;&lt;p&gt;وگر بنده چابک نباشد به کار&lt;/p&gt;&lt;p&gt;عزیزش ندارد خداوندگار&lt;/p&gt;&lt;p&gt;وگر بر رفیقان نباشی شفیق&lt;/p&gt;&lt;p&gt;به فرسنگ بگریزد از تو رفیق&lt;/p&gt;&lt;p&gt;وگر ترکِ خدمت کند لشکری&lt;/p&gt;&lt;p&gt;شود شاهِ لشکرکُش از وی بری&lt;/p&gt;&lt;p&gt;ولیکن خداوندِ بالا و پست&lt;/p&gt;&lt;p&gt;به عصیان، درِ رزق بر کس نبست&lt;/p&gt;&lt;p&gt;دو کونش یکی قطره از بحرِ علم&lt;/p&gt;&lt;p&gt;گنه بیند و پرده پوشد به حلم&lt;/p&gt;&lt;p&gt;ادیمِ زمین، سفرهٔ عام اوست&lt;/p&gt;&lt;p&gt;بر این خوان یغما چه دشمن چه دوست&lt;/p&gt;&lt;p&gt;اگر بر جفاپیشه بشتافتی&lt;/p&gt;&lt;p&gt;که از دست قهرش امان یافتی؟&lt;/p&gt;&lt;p&gt;بری ذاتش از تهمتِ ضد و جنس&lt;/p&gt;&lt;p&gt;غنی، ملکش از طاعتِ جن و انس&lt;/p&gt;&lt;p&gt;پرستارِ امرش همه چیز و کس&lt;/p&gt;&lt;p&gt;بنی‌آدم و مرغ و مور و مگس&lt;/p&gt;&lt;p&gt;چنان پهن خوان کرم گسترد&lt;/p&gt;&lt;p&gt;که سیمرغ در قاف قسمت خورد&lt;/p&gt;&lt;p&gt;لطیف کرم‌گستر کارساز&lt;/p&gt;&lt;p&gt;که دارای خَلق است و دانای راز&lt;/p&gt;&lt;p&gt;مر او را رسد کبریا و منی&lt;/p&gt;&lt;p&gt;که ملکش قدیم است و ذاتش غنی&lt;/p&gt;&lt;p&gt;یکی را به سر برنهد تاج بخت&lt;/p&gt;&lt;p&gt;یکی را به خاک اندر آرد ز تخت&lt;/p&gt;&lt;p&gt;کلاهِ سعادت یکی بر سرش&lt;/p&gt;&lt;p&gt;گلیمِ شقاوت یکی در برش&lt;/p&gt;&lt;p&gt;گلستان کند آتشی بر خلیل&lt;/p&gt;&lt;p&gt;گروهی بر آتش بَرَد ز آب نیل&lt;/p&gt;&lt;p&gt;گر آن است، منشورِ احسان اوست&lt;/p&gt;&lt;p&gt;ور این است، توقیعِ فرمان اوست&lt;/p&gt;&lt;p&gt;پسِ پرده بیند عمل‌های بد&lt;/p&gt;&lt;p&gt;هم او پرده پوشد به آلای خَوْد&lt;/p&gt;&lt;p&gt;به تهدید اگر برکشد تیغِ حکم&lt;/p&gt;&lt;p&gt;بمانند کروبیان صُمّ و بکم&lt;/p&gt;&lt;p&gt;وگر دردهد یک صلای کرم&lt;/p&gt;&lt;p&gt;عزازیل گوید نصیبی برم&lt;/p&gt;&lt;p&gt;به درگاهِ لطف و بزرگیش بر&lt;/p&gt;&lt;p&gt;بزرگان نهاده بزرگی ز سر&lt;/p&gt;&lt;p&gt;فروماندگان را به رحمت قریب&lt;/p&gt;&lt;p&gt;تضرع‌کنان را به دعوت مجیب&lt;/p&gt;&lt;p&gt;بر احوالِ نابوده، علمش بصیر&lt;/p&gt;&lt;p&gt;به اسرارِ ناگفته، لطفش خبیر&lt;/p&gt;&lt;p&gt;به قدرت، نگهدارِ بالا و شیب&lt;/p&gt;&lt;p&gt;خداوندِ دیوانِ روزِ حسیب&lt;/p&gt;&lt;p&gt;نه مستغنی از طاعتش پشت کس&lt;/p&gt;&lt;p&gt;نه بر حرف او جای انگشت کس&lt;/p&gt;&lt;p&gt;قدیمی نکوکارِ نیکی‌پسند&lt;/p&gt;&lt;p&gt;به کلک قضا در رحم نقش‌بند&lt;/p&gt;&lt;p&gt;ز مشرق به مغرب مه و آفتاب&lt;/p&gt;&lt;p&gt;روان کرد و بنهاد گیتی بر آب&lt;/p&gt;&lt;p&gt;زمین از تبِ لرزه آمد ستوه&lt;/p&gt;&lt;p&gt;فروکوفت بر دامنش میخِ کوه&lt;/p&gt;&lt;p&gt;دهد نطفه را صورتی چون پری&lt;/p&gt;&lt;p&gt;که کرده‌ست بر آب صورتگری؟&lt;/p&gt;&lt;p&gt;نهد لعل و پیروزه در صلبِ سنگ&lt;/p&gt;&lt;p&gt;گل و لعل در شاخ پیروزه‌رنگ&lt;/p&gt;&lt;p&gt;ز ابر افکند قطره‌ای سویِ یَم&lt;/p&gt;&lt;p&gt;ز صلب افکند نطفه‌ای در شکم&lt;/p&gt;&lt;p&gt;از آن قطره لولوی لالا کند&lt;/p&gt;&lt;p&gt;وز این، صورتی سروبالا کند&lt;/p&gt;&lt;p&gt;بر او علم یک ذره پوشیده نیست&lt;/p&gt;&lt;p&gt;که پیدا و پنهان به نزدش یکی‌ست&lt;/p&gt;&lt;p&gt;مهیاکنِ روزیِ مار و مور&lt;/p&gt;&lt;p&gt;اگر چند بی‌دست‌وپای‌اند و زور&lt;/p&gt;&lt;p&gt;به امرش وجود از عدم نقش بست&lt;/p&gt;&lt;p&gt;که داند جز او کردن از نیست، هست؟&lt;/p&gt;&lt;p&gt;دگر ره به کتمِ عدم در برد&lt;/p&gt;&lt;p&gt;وز آنجا به صحرای محشر برد&lt;/p&gt;&lt;p&gt;جهان مُتَفِّق بر الهیتش&lt;/p&gt;&lt;p&gt;فرومانده از کُنهِ ماهیتش&lt;/p&gt;&lt;p&gt;بشر ماورای جلالش نیافت&lt;/p&gt;&lt;p&gt;بصر منتهای جمالش نیافت&lt;/p&gt;&lt;p&gt;نه بر اوجِ ذاتش پرد مرغِ وهم&lt;/p&gt;&lt;p&gt;نه در ذیلِ وصفش رسد دستِ فهم&lt;/p&gt;&lt;p&gt;در این ورطه کشتی فروشد هزار&lt;/p&gt;&lt;p&gt;که پیدا نشد تخته‌ای بر کنار&lt;/p&gt;&lt;p&gt;چه شب‌ها نشستم در این سِیر، گم&lt;/p&gt;&lt;p&gt;که دِهشَت گرفت آستینم که قُم&lt;/p&gt;&lt;p&gt;محیط است علم مَلِک بر بسیط&lt;/p&gt;&lt;p&gt;قیاس تو بر وی نگردد محیط&lt;/p&gt;&lt;p&gt;نه ادراک در کُنهِ ذاتش رسید&lt;/p&gt;&lt;p&gt;نه فکرت به غورِ صفاتش رسید&lt;/p&gt;&lt;p&gt;توان در بلاغت به سَحبان رسید&lt;/p&gt;&lt;p&gt;نه در کُنهِ بی‌چون سُبحان رسید&lt;/p&gt;&lt;p&gt;که خاصان در این ره فَرَس رانده‌اند&lt;/p&gt;&lt;p&gt;به لااحصیٖ از تک فرومانده‌اند&lt;/p&gt;&lt;p&gt;نه هر جای مرکب توان تاختن&lt;/p&gt;&lt;p&gt;که جاها سپر باید انداختن&lt;/p&gt;&lt;p&gt;وگر سالکی مَحرمِ راز گشت&lt;/p&gt;&lt;p&gt;ببندند بر وی در بازگشت&lt;/p&gt;&lt;p&gt;کسی را در این بزم ساغر دهند&lt;/p&gt;&lt;p&gt;که داروی بیهوشی‌اش دَردهند&lt;/p&gt;&lt;p&gt;یکی باز را دیده بردوخته‌ست&lt;/p&gt;&lt;p&gt;یکی دیده‌ها باز و پر سوخته‌ست&lt;/p&gt;&lt;p&gt;کسی ره سوی گنج قارون نَبُرد&lt;/p&gt;&lt;p&gt;وگر بُرد، ره باز بیرون نبرد&lt;/p&gt;&lt;p&gt;بمردم در این موج دریای خون&lt;/p&gt;&lt;p&gt;کز او کس نبرده‌ست کشتی برون&lt;/p&gt;&lt;p&gt;اگر طالبی کاین زمین طی کنی&lt;/p&gt;&lt;p&gt;نخست اسبِ بازآمدن پی کنی&lt;/p&gt;&lt;p&gt;تأمل در آیینهٔ دل کنی&lt;/p&gt;&lt;p&gt;صفایی به‌تدریج حاصل کنی&lt;/p&gt;&lt;p&gt;مگر بویی از عشق مستت کند&lt;/p&gt;&lt;p&gt;طلبکارِ عهدِ الستت کند&lt;/p&gt;&lt;p&gt;به پای طلب ره بدان جا بری&lt;/p&gt;&lt;p&gt;وز آنجا به بالِ محبت پری&lt;/p&gt;&lt;p&gt;بِدَرَّد یقین پرده‌های خیال&lt;/p&gt;&lt;p&gt;نماند سراپرده إلّا جلال&lt;/p&gt;&lt;p&gt;دگر مرکبِ عقل را پویه نیست&lt;/p&gt;&lt;p&gt;عنانش بگیرد تَحَیَّر که بیست&lt;/p&gt;&lt;p&gt;در این بحر جز مردِ راعی نرفت&lt;/p&gt;&lt;p&gt;گم آن شد که دنبال داعی نرفت&lt;/p&gt;&lt;p&gt;کسانی کز این راه برگشته‌اند&lt;/p&gt;&lt;p&gt;برفتند بسیار و سرگشته‌اند&lt;/p&gt;&lt;p&gt;خلاف پیمبر کسی ره گزید&lt;/p&gt;&lt;p&gt;که هرگز به منزل نخواهد رسید&lt;/p&gt;&lt;p&gt;مپندار سعدی که راه صفا&lt;/p&gt;&lt;p&gt;توان رفت جز بر پی مصطفی&lt;/p&gt;', 'مثنوی شماره ۱', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(359, '&lt;p&gt;کریم السجایا جمیل الشیم&lt;/p&gt;&lt;p&gt;نبی البرایا شفیع الامم&lt;/p&gt;&lt;p&gt;امام رسل، پیشوای سبیل&lt;/p&gt;&lt;p&gt;امین خدا، مهبط جبرئیل&lt;/p&gt;&lt;p&gt;شفیع الوری، خواجهٔ بعث و نشر&lt;/p&gt;&lt;p&gt;امام الهدی، صدر دیوان حشر&lt;/p&gt;&lt;p&gt;کلیمی که چرخ فلک طور اوست&lt;/p&gt;&lt;p&gt;همه نورها پرتو نور اوست&lt;/p&gt;&lt;p&gt;شفیع مطاع نبی کریم&lt;/p&gt;&lt;p&gt;قسیم جسیم نسیم وسیم&lt;/p&gt;&lt;p&gt;یتیمی که ناکرده قرآن درست&lt;/p&gt;&lt;p&gt;کتب‌خانهٔ چند ملت بشست&lt;/p&gt;&lt;p&gt;چو عزمش برآهخت شمشیر بیم&lt;/p&gt;&lt;p&gt;به معجز میان قمر زد دو نیم&lt;/p&gt;&lt;p&gt;چو صیتش در افواه دنیا فتاد&lt;/p&gt;&lt;p&gt;تزلزل در ایوان کسری فتاد&lt;/p&gt;&lt;p&gt;به لا قامت لات بشکست خرد&lt;/p&gt;&lt;p&gt;به اعزاز دین آب عزی ببرد&lt;/p&gt;&lt;p&gt;نه از لات و عزی برآورد گرد&lt;/p&gt;&lt;p&gt;که تورات و انجیل منسوخ کرد&lt;/p&gt;&lt;p&gt;شبی بر نشست از فلک برگذشت&lt;/p&gt;&lt;p&gt;به تمکین و جاه از ملک درگذشت&lt;/p&gt;&lt;p&gt;چنان گرم در تیه قربت براند&lt;/p&gt;&lt;p&gt;که بر سدره جبریل از او بازماند&lt;/p&gt;&lt;p&gt;بدو گفت سالار بیت‌الحرام&lt;/p&gt;&lt;p&gt;که ای حامل وحی برتر خرام&lt;/p&gt;&lt;p&gt;چو در دوستی مخلصم یافتی&lt;/p&gt;&lt;p&gt;عنانم ز صحبت چرا تافتی؟&lt;/p&gt;&lt;p&gt;بگفتا فراتر مجالم نماند&lt;/p&gt;&lt;p&gt;بماندم که نیروی بالم نماند&lt;/p&gt;&lt;p&gt;اگر یک سر موی برتر پرم&lt;/p&gt;&lt;p&gt;فروغ تجلی بسوزد پرم&lt;/p&gt;&lt;p&gt;نماند به عصیان کسی در گرو&lt;/p&gt;&lt;p&gt;که دارد چنین سیدی پیشرو&lt;/p&gt;&lt;p&gt;چه نعت پسندیده گویم تو را؟&lt;/p&gt;&lt;p&gt;علیک السلام ای نبی الوری&lt;/p&gt;&lt;p&gt;درود ملک بر روان تو باد&lt;/p&gt;&lt;p&gt;بر اصحاب و بر پیروان تو باد&lt;/p&gt;&lt;p&gt;نخستین ابوبکر پیر مرید&lt;/p&gt;&lt;p&gt;عمر، پنجه بر پیچ دیو مرید&lt;/p&gt;&lt;p&gt;خردمند عثمان شب زنده‌دار&lt;/p&gt;&lt;p&gt;چهارم علی، شاه دلدل سوار&lt;/p&gt;&lt;p&gt;خدایا به حق بنی فاطمه&lt;/p&gt;&lt;p&gt;که بر قولم ایمان کنم خاتمه&lt;/p&gt;&lt;p&gt;اگر دعوتم رد کنی ور قبول&lt;/p&gt;&lt;p&gt;من و دست و دامان آل رسول&lt;/p&gt;&lt;p&gt;چه کم گردد ای صدر فرخنده پی&lt;/p&gt;&lt;p&gt;ز قدر رفیعت به درگاه حی&lt;/p&gt;&lt;p&gt;که باشند مشتی گدایان خیل&lt;/p&gt;&lt;p&gt;به مهمان دارالسلامت طفیل&lt;/p&gt;&lt;p&gt;خدایت ثنا گفت و تبجیل کرد&lt;/p&gt;&lt;p&gt;زمین بوس قدر تو جبریل کرد&lt;/p&gt;&lt;p&gt;بلند آسمان پیش قَدْرَت خجل&lt;/p&gt;&lt;p&gt;تو مخلوق و آدم هنوز آب و گل&lt;/p&gt;&lt;p&gt;تو اصل وجود آمدی از نخست&lt;/p&gt;&lt;p&gt;دگر هرچه موجود شد فرع توست&lt;/p&gt;&lt;p&gt;ندانم کدامین سخن گویمت&lt;/p&gt;&lt;p&gt;که والاتری زانچه من گویمت&lt;/p&gt;&lt;p&gt;تو را عز لولاک تمکین بس است&lt;/p&gt;&lt;p&gt;ثنای تو طه و یس بس است&lt;/p&gt;&lt;p&gt;چه وصفت کند سعدی ناتمام؟&lt;/p&gt;&lt;p&gt;علیک الصلوة ای نبی السلام&lt;/p&gt;', 'مثنوی شماره ۲', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(360, '&lt;p&gt;در اقصای عالم بگشتم بسی&lt;/p&gt;&lt;p&gt;به سر بردم ایام با هر کسی&lt;/p&gt;&lt;p&gt;تمتع به هر گوشه‌ای یافتم&lt;/p&gt;&lt;p&gt;ز هر خرمنی خوشه‌ای یافتم&lt;/p&gt;&lt;p&gt;چو پاکان شیراز، خاکی نهاد&lt;/p&gt;&lt;p&gt;ندیدم که رحمت بر این خاک باد&lt;/p&gt;&lt;p&gt;تولای مردان این پاک بوم&lt;/p&gt;&lt;p&gt;برانگیختم خاطر از شام و روم&lt;/p&gt;&lt;p&gt;دریغ آمدم زآن همه بوستان&lt;/p&gt;&lt;p&gt;تهیدست رفتن سوی دوستان&lt;/p&gt;&lt;p&gt;به دل گفتم از مصر قند آورند&lt;/p&gt;&lt;p&gt;بر دوستان ارمغانی برند&lt;/p&gt;&lt;p&gt;مرا گر تهی بود از آن قند دست&lt;/p&gt;&lt;p&gt;سخنهای شیرین‌تر از قند هست&lt;/p&gt;&lt;p&gt;نه قندی که مردم به صورت خورند&lt;/p&gt;&lt;p&gt;که ارباب معنی به کاغذ برند&lt;/p&gt;&lt;p&gt;چو این کاخ دولت بپرداختم&lt;/p&gt;&lt;p&gt;بر او ده در از تربیت ساختم&lt;/p&gt;&lt;p&gt;یکی باب عدل است و تدبیر و رای&lt;/p&gt;&lt;p&gt;نگهبانی خلق و ترس خدای&lt;/p&gt;&lt;p&gt;دوم باب احسان نهادم اساس&lt;/p&gt;&lt;p&gt;که منعم کند فضل حق را سپاس&lt;/p&gt;&lt;p&gt;سوم باب عشق است و مستی و شور&lt;/p&gt;&lt;p&gt;نه عشقی که بندند بر خود بزور&lt;/p&gt;&lt;p&gt;چهارم تواضع، رضا پنجمین&lt;/p&gt;&lt;p&gt;ششم ذکر مرد قناعت گزین&lt;/p&gt;&lt;p&gt;به هفتم در از عالم تربیت&lt;/p&gt;&lt;p&gt;به هشتم در از شکر بر عافیت&lt;/p&gt;&lt;p&gt;نهم باب توبه است و راه صواب&lt;/p&gt;&lt;p&gt;دهم در مناجات و ختم کتاب&lt;/p&gt;&lt;p&gt;به روز همایون و سال سعید&lt;/p&gt;&lt;p&gt;به تاریخ فرخ میان دو عید&lt;/p&gt;&lt;p&gt;ز ششصد فزون بود پنجاه و پنج&lt;/p&gt;&lt;p&gt;که پر در شد این نامبردار گنج&lt;/p&gt;&lt;p&gt;بمانده‌ست با دامنی گوهرم&lt;/p&gt;&lt;p&gt;هنوز از خجالت به زانو سرم&lt;/p&gt;&lt;p&gt;که در بحر لؤلؤ صدف نیز هست&lt;/p&gt;&lt;p&gt;درخت بلند است در باغ و پست&lt;/p&gt;&lt;p&gt;الا ای خردمند پاکیزه خوی&lt;/p&gt;&lt;p&gt;خردمند نشنیده‌ام عیب جوی&lt;/p&gt;&lt;p&gt;قبا گر حریر است و گر پرنیان&lt;/p&gt;&lt;p&gt;به ناچار حَشوَش بود در میان&lt;/p&gt;&lt;p&gt;تو گر پرنیانی نیابی مجوش&lt;/p&gt;&lt;p&gt;کرم کار فرما و حشوش بپوش&lt;/p&gt;&lt;p&gt;ننازم به سرمایهٔ فضل خویش&lt;/p&gt;&lt;p&gt;به دریوزه آورده‌ام دستْ پیش&lt;/p&gt;&lt;p&gt;شنیدم که در روز امید و بیم&lt;/p&gt;&lt;p&gt;بَدان را به نیکان ببخشد کریم&lt;/p&gt;&lt;p&gt;تو نیز ار بدی بینیم در سخن&lt;/p&gt;&lt;p&gt;به خلق جهان آفرین کار کن&lt;/p&gt;&lt;p&gt;چو بیتی پسند آیدت از هزار&lt;/p&gt;&lt;p&gt;به مردی که دست از تعنت بدار&lt;/p&gt;&lt;p&gt;همانا که در فارس انشای من&lt;/p&gt;&lt;p&gt;چو مشک است بی قیمت اندر ختن&lt;/p&gt;&lt;p&gt;چو بانگ دهل هولم از دور بود&lt;/p&gt;&lt;p&gt;به غیبت درم عیب مستور بود&lt;/p&gt;&lt;p&gt;گل آورد سعدی سوی بوستان&lt;/p&gt;&lt;p&gt;به شوخی و فلفل به هندوستان&lt;/p&gt;&lt;p&gt;چو خرما به شیرینی اندوده پوست&lt;/p&gt;&lt;p&gt;چو بازش کنی استخوانی در اوست&lt;/p&gt;', 'مثنوی شماره ۳', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(361, '&lt;p&gt;مرا طبع از این نوع خواهان نبود&lt;/p&gt;&lt;p&gt;سر مدحت پادشاهان نبود&lt;/p&gt;&lt;p&gt;ولی نظم کردم به نام فلان&lt;/p&gt;&lt;p&gt;مگر باز گویند صاحبدلان&lt;/p&gt;&lt;p&gt;که سعدی که گوی بلاغت ربود&lt;/p&gt;&lt;p&gt;در ایام بوبکر بن سعد بود&lt;/p&gt;&lt;p&gt;سزد گر به دورش بنازم چنان&lt;/p&gt;&lt;p&gt;که سید به دوران نوشیروان&lt;/p&gt;&lt;p&gt;جهانبان دین پرور دادگر&lt;/p&gt;&lt;p&gt;نیامد چو بوبکر بعد از عمر&lt;/p&gt;&lt;p&gt;سر سرفرازان و تاج مهان&lt;/p&gt;&lt;p&gt;به دوران عدلش بناز، ای جهان&lt;/p&gt;&lt;p&gt;گر از فتنه آید کسی در پناه&lt;/p&gt;&lt;p&gt;ندارد جز این کشور آرامگاه&lt;/p&gt;&lt;p&gt;فطوبی لباب کبیت العتیق&lt;/p&gt;&lt;p&gt;حوالیه من کل فج عمیق&lt;/p&gt;&lt;p&gt;ندیدم چنین گنج و ملک و سریر&lt;/p&gt;&lt;p&gt;که وقف است بر طفل و درویش و پیر&lt;/p&gt;&lt;p&gt;نیامد برش دردناک غمی&lt;/p&gt;&lt;p&gt;که ننهاد بر خاطرش مرهمی&lt;/p&gt;&lt;p&gt;طلبکار خیر است امیدوار&lt;/p&gt;&lt;p&gt;خدایا امیدی که دارد برآر&lt;/p&gt;&lt;p&gt;کله گوشه بر آسمان برین&lt;/p&gt;&lt;p&gt;هنوز از تواضع سرش بر زمین&lt;/p&gt;&lt;p&gt;گدا گر تواضع کند خوی اوست&lt;/p&gt;&lt;p&gt;ز گردن فرازان تواضع نکوست&lt;/p&gt;&lt;p&gt;اگر زیردستی بیفتد چه خاست؟&lt;/p&gt;&lt;p&gt;زبردست افتاده مرد خداست&lt;/p&gt;&lt;p&gt;نه ذکر جمیلش نهان می‌رود&lt;/p&gt;&lt;p&gt;که صیت کرم در جهان می‌رود&lt;/p&gt;&lt;p&gt;چنویی خردمند فرخ نژاد&lt;/p&gt;&lt;p&gt;ندارد جهان تا جهان است، یاد&lt;/p&gt;&lt;p&gt;نبینی در ایام او رنجه‌ای&lt;/p&gt;&lt;p&gt;که نالد ز بیداد سرپنجه‌ای&lt;/p&gt;&lt;p&gt;کس این رسم و ترتیب و آیین ندید&lt;/p&gt;&lt;p&gt;فریدون با آن شکوه، این ندید&lt;/p&gt;&lt;p&gt;از آن پیش حق پایگاهش قوی است&lt;/p&gt;&lt;p&gt;که دست ضعیفان به جاهش قوی است&lt;/p&gt;&lt;p&gt;چنان سایه گسترده بر عالمی&lt;/p&gt;&lt;p&gt;که زالی نیندیشد از رستمی&lt;/p&gt;&lt;p&gt;همه وقت مردم ز جور زمان&lt;/p&gt;&lt;p&gt;بنالند و از گردش آسمان&lt;/p&gt;&lt;p&gt;در ایام عدل تو ای شهریار&lt;/p&gt;&lt;p&gt;ندارد شکایت کس از روزگار&lt;/p&gt;&lt;p&gt;به عهد تو می‌بینم آرام خلق&lt;/p&gt;&lt;p&gt;پس از تو ندانم سرانجام خلق&lt;/p&gt;&lt;p&gt;هم از بخت فرخنده فرجام توست&lt;/p&gt;&lt;p&gt;که تاریخ سعدی در ایام توست&lt;/p&gt;&lt;p&gt;که تا بر فلک ماه و خورشید هست&lt;/p&gt;&lt;p&gt;در این دفترت ذکر جاوید هست&lt;/p&gt;&lt;p&gt;ملوک ار نکو نامی اندوختند&lt;/p&gt;&lt;p&gt;ز پیشینگان سیرت آموختند&lt;/p&gt;&lt;p&gt;تو در سیرت پادشاهی خویش&lt;/p&gt;&lt;p&gt;سبق بردی از پادشاهان پیش&lt;/p&gt;&lt;p&gt;سکندر به دیوار رویین و سنگ&lt;/p&gt;&lt;p&gt;بکرد از جهان راه یأجوج تنگ&lt;/p&gt;&lt;p&gt;تو را سد یأجوج کفر از زر است&lt;/p&gt;&lt;p&gt;نه رویین چو دیوار اسکندر است&lt;/p&gt;&lt;p&gt;زبان آوری کاندر این امن و داد&lt;/p&gt;&lt;p&gt;سپاست نگوید زبانش مباد&lt;/p&gt;&lt;p&gt;زهی بحر بخشایش و کان جود&lt;/p&gt;&lt;p&gt;که مستظهرند از وجودت وجود&lt;/p&gt;&lt;p&gt;برون بینم اوصاف شاه از حساب&lt;/p&gt;&lt;p&gt;نگنجد در این تنگ میدان کتاب&lt;/p&gt;&lt;p&gt;گر آن جمله را سعدی انشا کند&lt;/p&gt;&lt;p&gt;مگر دفتری دیگر املا کند&lt;/p&gt;&lt;p&gt;فروماندم از شکر چندین کرم&lt;/p&gt;&lt;p&gt;همان به که دست دعا گسترم&lt;/p&gt;&lt;p&gt;جهانت به کام و فلک یار باد&lt;/p&gt;&lt;p&gt;جهان آفرینت نگهدار باد&lt;/p&gt;&lt;p&gt;بلند اخترت عالم افروخته&lt;/p&gt;&lt;p&gt;زوال اختر دشمنت سوخته&lt;/p&gt;&lt;p&gt;غم از گردش روزگارت مباد&lt;/p&gt;&lt;p&gt;وز اندیشه بر دل غبارت مباد&lt;/p&gt;&lt;p&gt;که بر خاطر پادشاهان غمی&lt;/p&gt;&lt;p&gt;پریشان کند خاطر عالمی&lt;/p&gt;&lt;p&gt;دل و کشورت جمع و معمور باد&lt;/p&gt;&lt;p&gt;ز ملکت پراکندگی دور باد&lt;/p&gt;&lt;p&gt;تنت باد پیوسته چون دین، درست&lt;/p&gt;&lt;p&gt;بداندیش را دل چو تدبیر، سست&lt;/p&gt;&lt;p&gt;درونت به تأیید حق شاد باد&lt;/p&gt;&lt;p&gt;دل و دین و اقلیمت آباد باد&lt;/p&gt;&lt;p&gt;جهان آفرین بر تو رحمت کناد&lt;/p&gt;&lt;p&gt;دگر هرچه گویم فسانه‌ست و باد&lt;/p&gt;&lt;p&gt;همینت بس از کردگار مجید&lt;/p&gt;&lt;p&gt;که توفیق خیرت بود بر مزید&lt;/p&gt;&lt;p&gt;نرفت از جهان سعد زنگی به درد&lt;/p&gt;&lt;p&gt;که چون تو خلف نامبردار کرد&lt;/p&gt;&lt;p&gt;عجب نیست این فرع از آن اصل پاک&lt;/p&gt;&lt;p&gt;که جانش بر اوج است و جسمش به خاک&lt;/p&gt;&lt;p&gt;خدایا بر آن تربت نامدار&lt;/p&gt;&lt;p&gt;به فضلت که باران رحمت ببار&lt;/p&gt;&lt;p&gt;گر از سعد زنگی مثل ماند یاد&lt;/p&gt;&lt;p&gt;فلک یاور سعد بوبکر باد&lt;/p&gt;', 'مثنوی شماره ۴', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(362, '&lt;p&gt;اتابک محمد شه نیکبخت&lt;/p&gt;&lt;p&gt;خداوند تاج و خداوند تخت&lt;/p&gt;&lt;p&gt;جوان جوان‌بخت روشن‌ضمیر&lt;/p&gt;&lt;p&gt;به دولت جوان و به تدبیر پیر&lt;/p&gt;&lt;p&gt;به دانش بزرگ و به همت بلند&lt;/p&gt;&lt;p&gt;به بازو دلیر و به دل هوشمند&lt;/p&gt;&lt;p&gt;زهی دولت مادر روزگار&lt;/p&gt;&lt;p&gt;که رودی چنین پرورد در کنار&lt;/p&gt;&lt;p&gt;به دست کرم آب دریا ببرد&lt;/p&gt;&lt;p&gt;به رفعت محل ثریا ببرد&lt;/p&gt;&lt;p&gt;زهی چشم دولت به روی تو باز&lt;/p&gt;&lt;p&gt;سر شهریاران گردن فراز&lt;/p&gt;&lt;p&gt;صدف را که بینی ز دردانه پر&lt;/p&gt;&lt;p&gt;نه آن قدر دارد که یکدانه در&lt;/p&gt;&lt;p&gt;تو آن در مکنون یکدانه‌ای&lt;/p&gt;&lt;p&gt;که پیرایهٔ سلطنت خانه‌ای&lt;/p&gt;&lt;p&gt;نگه دار یارب به چشم خودش&lt;/p&gt;&lt;p&gt;بپرهیز از آسیب چشم بدش&lt;/p&gt;&lt;p&gt;خدایا در آفاق نامی کنش&lt;/p&gt;&lt;p&gt;به توفیق طاعت گرامی کنش&lt;/p&gt;&lt;p&gt;مقیمش در انصاف و تقوی بدار&lt;/p&gt;&lt;p&gt;مرادش به دنیا و عقبی برآر&lt;/p&gt;&lt;p&gt;غم از دشمن ناپسندش مباد&lt;/p&gt;&lt;p&gt;وز اندیشه بر دل گزندش مباد&lt;/p&gt;&lt;p&gt;بهشتی درخت آورد چون تو بار&lt;/p&gt;&lt;p&gt;پسر نامجوی و پدر نامدار&lt;/p&gt;&lt;p&gt;از آن خاندان خیر بیگانه دان&lt;/p&gt;&lt;p&gt;که باشند بدخواه این خاندان&lt;/p&gt;&lt;p&gt;زهی دین و دانش، زهی عدل و داد&lt;/p&gt;&lt;p&gt;زهی ملک و دولت که پاینده باد&lt;/p&gt;&lt;p&gt;نگنجد کرمهای حق در قیاس&lt;/p&gt;&lt;p&gt;چه خدمت گزارد زبان سپاس؟&lt;/p&gt;&lt;p&gt;خدایا تو این شاه درویش دوست&lt;/p&gt;&lt;p&gt;که آسایش خلق در ظل اوست&lt;/p&gt;&lt;p&gt;بسی بر سر خلق پاینده دار&lt;/p&gt;&lt;p&gt;به توفیق طاعت دلش زنده دار&lt;/p&gt;&lt;p&gt;برومند دارش درخت امید&lt;/p&gt;&lt;p&gt;سرش سبز و رویش به رحمت سفید&lt;/p&gt;&lt;p&gt;به راه تکلف مرو سعدیا&lt;/p&gt;&lt;p&gt;اگر صدق داری بیار و بیا&lt;/p&gt;&lt;p&gt;تو منزل شناسی و شه راهرو&lt;/p&gt;&lt;p&gt;تو حقگوی و خسرو حقایق شنو&lt;/p&gt;&lt;p&gt;چه حاجت که نه کرسی آسمان&lt;/p&gt;&lt;p&gt;نهی زیر پای قزل ارسلان&lt;/p&gt;&lt;p&gt;مگو پای عزت بر افلاک نه&lt;/p&gt;&lt;p&gt;بگو روی اخلاص بر خاک نه&lt;/p&gt;&lt;p&gt;بطاعت بنه چهره بر آستان&lt;/p&gt;&lt;p&gt;که این است سر جاده راستان&lt;/p&gt;&lt;p&gt;اگر بنده‌ای سر بر این در بنه&lt;/p&gt;&lt;p&gt;کلاه خداوندی از سر بنه&lt;/p&gt;&lt;p&gt;به درگاه فرمانده ذوالجلال&lt;/p&gt;&lt;p&gt;چو درویش پیش توانگر بنال&lt;/p&gt;&lt;p&gt;چو طاعت کنی لبس شاهی مپوش&lt;/p&gt;&lt;p&gt;چو درویش مخلص برآور خروش&lt;/p&gt;&lt;p&gt;که پروردگارا توانگر تویی&lt;/p&gt;&lt;p&gt;توانا و درویش پرور تویی&lt;/p&gt;&lt;p&gt;نه کشور خدایم نه فرماندهم&lt;/p&gt;&lt;p&gt;یکی از گدایان این درگهم&lt;/p&gt;&lt;p&gt;تو بر خیر و نیکی دهم دسترس&lt;/p&gt;&lt;p&gt;وگر نه چه خیر آید از من به کس؟&lt;/p&gt;&lt;p&gt;دعا کن به شب چون گدایان به سوز&lt;/p&gt;&lt;p&gt;اگر می‌کنی پادشاهی به روز&lt;/p&gt;&lt;p&gt;کمر بسته گردن کشان بر درت&lt;/p&gt;&lt;p&gt;تو بر آستان عبادت سرت&lt;/p&gt;&lt;p&gt;زهی بندگان را خداوندگار&lt;/p&gt;&lt;p&gt;خداوند را بندهٔ حق گزار&lt;/p&gt;', 'مثنوی شماره ۵', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(363, '&lt;p&gt;حکایت کنند از بزرگان دین&lt;/p&gt;&lt;p&gt;حقیقت شناسان عین الیقین&lt;/p&gt;&lt;p&gt;که صاحبدلی بر پلنگی نشست&lt;/p&gt;&lt;p&gt;همی راند رهوار و ماری به دست&lt;/p&gt;&lt;p&gt;یکی گفتش: ای مرد راه خدای&lt;/p&gt;&lt;p&gt;بدین ره که رفتی مرا ره نمای&lt;/p&gt;&lt;p&gt;چه کردی که درنده رام تو شد&lt;/p&gt;&lt;p&gt;نگین سعادت به نام تو شد؟&lt;/p&gt;&lt;p&gt;بگفت ار پلنگم زبون است و مار&lt;/p&gt;&lt;p&gt;وگر پیل و کرکس، شگفتی مدار&lt;/p&gt;&lt;p&gt;تو هم گردن از حکم داور مپیچ&lt;/p&gt;&lt;p&gt;که گردن نپیچد ز حکم تو هیچ&lt;/p&gt;&lt;p&gt;چو حاکم به فرمان داور بود&lt;/p&gt;&lt;p&gt;خدایش نگهبان و یاور بود&lt;/p&gt;&lt;p&gt;محال است چون دوست دارد تو را&lt;/p&gt;&lt;p&gt;که در دست دشمن گذارد تو را&lt;/p&gt;&lt;p&gt;ره این است، روی از طریقت متاب&lt;/p&gt;&lt;p&gt;بنه گام و کامی که داری بیاب&lt;/p&gt;&lt;p&gt;نصیحت کسی سودمند آیدش&lt;/p&gt;&lt;p&gt;که گفتار سعدی پسند آیدش&lt;/p&gt;', 'مثنوی شماره ۶ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(364, '&lt;p&gt;خبر یافت گردن‌کشی در عراق&lt;/p&gt;&lt;p&gt;که می‌گفت مسکینی از زیر طاق&lt;/p&gt;&lt;p&gt;تو هم بر دری هستی امیدوار&lt;/p&gt;&lt;p&gt;پس امید بر در نشینان برآر&lt;/p&gt;&lt;p&gt;نخواهی که باشد دلت دردمند&lt;/p&gt;&lt;p&gt;دل دردمندان برآور ز بند&lt;/p&gt;&lt;p&gt;پریشانی خاطر دادخواه&lt;/p&gt;&lt;p&gt;براندازد از مملکت پادشاه&lt;/p&gt;&lt;p&gt;تو خفته خنک در حرم نیمروز&lt;/p&gt;&lt;p&gt;غریب از برون گو به گرما بسوز&lt;/p&gt;&lt;p&gt;ستاننده داد آن کس خداست&lt;/p&gt;&lt;p&gt;که نتواند از پادشه دادخواست&lt;/p&gt;', 'مثنوی شماره ۷ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(365, '&lt;p&gt;یکی از بزرگان اهل تمیز&lt;/p&gt;&lt;p&gt;حکایت کند ز ابن عبدالعزیز&lt;/p&gt;&lt;p&gt;که بودش نگینی در انگشتری&lt;/p&gt;&lt;p&gt;فرو مانده در قیمتش جوهری&lt;/p&gt;&lt;p&gt;به شب گفتی از جرم گیتی فروز&lt;/p&gt;&lt;p&gt;دری بود از روشنایی چو روز&lt;/p&gt;&lt;p&gt;قضا را درآمد یکی خشک سال&lt;/p&gt;&lt;p&gt;که شد بدر سیمای مردم هلال&lt;/p&gt;&lt;p&gt;چو در مردم آرام و قوت ندید&lt;/p&gt;&lt;p&gt;خود آسوده بودن مروت ندید&lt;/p&gt;&lt;p&gt;چو بیند کسی زهر در کام خلق&lt;/p&gt;&lt;p&gt;کیش بگذرد آب نوشین به حلق&lt;/p&gt;&lt;p&gt;بفرمود و بفروختندش به سیم&lt;/p&gt;&lt;p&gt;که رحم آمدش بر غریب و یتیم&lt;/p&gt;&lt;p&gt;به یک هفته نقدش به تاراج داد&lt;/p&gt;&lt;p&gt;به درویش و مسکین و محتاج داد&lt;/p&gt;&lt;p&gt;فتادند در وی ملامت کنان&lt;/p&gt;&lt;p&gt;که دیگر به دستت نیاید چنان&lt;/p&gt;&lt;p&gt;شنیدم که می‌گفت و باران دمع&lt;/p&gt;&lt;p&gt;فرو می‌دویدش به عارض چو شمع&lt;/p&gt;&lt;p&gt;که زشت است پیرایه بر شهریار&lt;/p&gt;&lt;p&gt;دل شهری از ناتوانی فگار&lt;/p&gt;&lt;p&gt;مرا شاید انگشتری بی‌نگین&lt;/p&gt;&lt;p&gt;نشاید دل خلقی اندوهگین&lt;/p&gt;&lt;p&gt;خنک آن که آسایش مرد و زن&lt;/p&gt;&lt;p&gt;گزیند بر آرایش خویشتن&lt;/p&gt;&lt;p&gt;نکردند رغبت هنرپروران&lt;/p&gt;&lt;p&gt;به شادی خویش از غم دیگران&lt;/p&gt;&lt;p&gt;اگر خوش بخسبد ملک بر سریر&lt;/p&gt;&lt;p&gt;نپندارم آسوده خسبد فقیر&lt;/p&gt;&lt;p&gt;وگر زنده دارد شب دیر باز&lt;/p&gt;&lt;p&gt;بخسبند مردم به آرام و ناز&lt;/p&gt;&lt;p&gt;بحمدالله این سیرت و راه راست&lt;/p&gt;&lt;p&gt;اتابک ابوبکر بن سعد راست&lt;/p&gt;&lt;p&gt;کس از فتنه در پارس دیگر نشان&lt;/p&gt;&lt;p&gt;نبیند مگر قامت مهوشان&lt;/p&gt;&lt;p&gt;یکی پنج بیتم خوش آمد به گوش&lt;/p&gt;&lt;p&gt;که در مجلسی می‌سرودند دوش&lt;/p&gt;&lt;p&gt;مرا راحت از زندگی دوش بود&lt;/p&gt;&lt;p&gt;که آن ماهرویم در آغوش بود&lt;/p&gt;&lt;p&gt;مر او را چو دیدم سر از خواب مست&lt;/p&gt;&lt;p&gt;بدو گفتم ای سرو پیش تو پست&lt;/p&gt;&lt;p&gt;دمی نرگس از خواب نوشین بشوی&lt;/p&gt;&lt;p&gt;چو گلبن بخند و چو بلبل بگوی&lt;/p&gt;&lt;p&gt;چه می‌خسبی ای فتنه روزگار؟&lt;/p&gt;&lt;p&gt;بیا و می لعل نوشین بیار&lt;/p&gt;&lt;p&gt;نگه کرد شوریده از خواب و گفت&lt;/p&gt;&lt;p&gt;مرا فتنه خوانی و گویی مخفت&lt;/p&gt;&lt;p&gt;در ایام سلطان روشن نفس&lt;/p&gt;&lt;p&gt;نبیند دگر فتنه بیدار کس&lt;/p&gt;', 'مثنوی شماره ۸ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(366, '&lt;p&gt;در اخبار شاهان پیشینه هست&lt;/p&gt;&lt;p&gt;که چون تکله بر تخت زنگی نشست&lt;/p&gt;&lt;p&gt;به دورانش از کس نیازرد کس&lt;/p&gt;&lt;p&gt;سبق برد اگر خود همین بود و بس&lt;/p&gt;&lt;p&gt;چنین گفت یک ره به صاحبدلی&lt;/p&gt;&lt;p&gt;که عمرم به سر رفت بی حاصلی&lt;/p&gt;&lt;p&gt;بخواهم به کنج عبادت نشست&lt;/p&gt;&lt;p&gt;که دریابم این پنج روزی که هست&lt;/p&gt;&lt;p&gt;چو می‌بگذرد جاه و ملک و سریر&lt;/p&gt;&lt;p&gt;نبرد از جهان دولت الا فقیر&lt;/p&gt;&lt;p&gt;چو بشنید دانای روشن نفس&lt;/p&gt;&lt;p&gt;به تندی برآشفت کای تکله بس!&lt;/p&gt;&lt;p&gt;طریقت به جز خدمت خلق نیست&lt;/p&gt;&lt;p&gt;به تسبیح و سجاده و دلق نیست&lt;/p&gt;&lt;p&gt;تو بر تخت سلطانی خویش باش&lt;/p&gt;&lt;p&gt;به اخلاق پاکیزه درویش باش&lt;/p&gt;&lt;p&gt;به صدق و ارادت میان بسته‌دار&lt;/p&gt;&lt;p&gt;ز طامات و دعوی زبان بسته دار&lt;/p&gt;&lt;p&gt;قدم باید اندر طریقت نه دم&lt;/p&gt;&lt;p&gt;که اصلی ندارد دم بی‌قدم&lt;/p&gt;', 'مثنوی شماره ۹ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(367, '&lt;p&gt;شنیدم که بگریست سلطان روم&lt;/p&gt;&lt;p&gt;بر نیکمردی ز اهل علوم&lt;/p&gt;&lt;p&gt;که پایابم از دست دشمن نماند&lt;/p&gt;&lt;p&gt;جز این قلعه و شهر با من نماند&lt;/p&gt;&lt;p&gt;بسی جهد کردم که فرزند من&lt;/p&gt;&lt;p&gt;پس از من بود سرور انجمن&lt;/p&gt;&lt;p&gt;کنون دشمن بدگهر دست یافت&lt;/p&gt;&lt;p&gt;سر دست مردی و جهدم بتافت&lt;/p&gt;&lt;p&gt;چه تدبیر سازم، چه درمان کنم؟&lt;/p&gt;&lt;p&gt;که از غم بفرسود جان در تنم&lt;/p&gt;&lt;p&gt;بگفت ای برادر غم خویش خور&lt;/p&gt;&lt;p&gt;که از عمر بهتر شد و بیشتر&lt;/p&gt;&lt;p&gt;تو را این قدر تا بمانی بس است&lt;/p&gt;&lt;p&gt;چو رفتی جهان جای دیگر کس است&lt;/p&gt;&lt;p&gt;اگر هوشمند است و گر بی‌خرد&lt;/p&gt;&lt;p&gt;غم او مخور کاو غم خود خورد&lt;/p&gt;&lt;p&gt;مشقت نیرزد جهان داشتن&lt;/p&gt;&lt;p&gt;گرفتن به شمشیر و بگذاشتن&lt;/p&gt;&lt;p&gt;بدین پنج روزه اقامت مناز&lt;/p&gt;&lt;p&gt;به اندیشه تدبیر رفتن بساز&lt;/p&gt;&lt;p&gt;که را دانی از خسروان عجم&lt;/p&gt;&lt;p&gt;ز عهد فریدون و ضحاک و جم&lt;/p&gt;&lt;p&gt;که بر تخت و ملکش نیامد زوال؟&lt;/p&gt;&lt;p&gt;نماند به جز ملک ایزد تعال&lt;/p&gt;&lt;p&gt;که را جاودان ماندن امید ماند&lt;/p&gt;&lt;p&gt;چو کس را نبینی که جاوید ماند؟&lt;/p&gt;&lt;p&gt;که را سیم و زر ماند و گنج و مال&lt;/p&gt;&lt;p&gt;پس از وی به چندی شود پایمال&lt;/p&gt;&lt;p&gt;وز آن کس که خیری بماند روان&lt;/p&gt;&lt;p&gt;دمادم رسد رحمتش بر روان&lt;/p&gt;&lt;p&gt;بزرگی کز او نام نیکو نماند&lt;/p&gt;&lt;p&gt;توان گفت با اهل دل کاو نماند&lt;/p&gt;&lt;p&gt;الا تا درخت کرم پروری&lt;/p&gt;&lt;p&gt;گر امیدواری کز او بر خوری&lt;/p&gt;&lt;p&gt;کرم کن که فردا که دیوان نهند&lt;/p&gt;&lt;p&gt;منازل به مقدار احسان دهند&lt;/p&gt;&lt;p&gt;یکی را که سعی قدم پیشتر&lt;/p&gt;&lt;p&gt;به درگاه حق، منزلت بیشتر&lt;/p&gt;&lt;p&gt;یکی باز پس خائن و شرمسار&lt;/p&gt;&lt;p&gt;بترسد همی مرد ناکرده کار&lt;/p&gt;&lt;p&gt;بهل تا به دندان گزد پشت دست&lt;/p&gt;&lt;p&gt;تنوری چنین گرم و نانی نبست&lt;/p&gt;&lt;p&gt;بدانی گه غله برداشتن&lt;/p&gt;&lt;p&gt;که سستی بود تخم ناکاشتن&lt;/p&gt;', 'مثنوی شماره ۱۰ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(368, '&lt;p&gt;خردمند مردی در اقصای شام&lt;/p&gt;&lt;p&gt;گرفت از جهان کنج غاری مقام&lt;/p&gt;&lt;p&gt;به صبرش در آن کنج تاریک جای&lt;/p&gt;&lt;p&gt;به گنج قناعت فرو رفته پای&lt;/p&gt;&lt;p&gt;شنیدم که نامش خدادوست بود&lt;/p&gt;&lt;p&gt;ملک سیرتی، آدمی پوست بود&lt;/p&gt;&lt;p&gt;بزرگان نهادند سر بر درش&lt;/p&gt;&lt;p&gt;که در می‌نیامد به درها سرش&lt;/p&gt;&lt;p&gt;تمنا کند عارف پاکباز&lt;/p&gt;&lt;p&gt;به دریوزه از خویشتن ترک آز&lt;/p&gt;&lt;p&gt;چو هر ساعتش نفس گوید بده&lt;/p&gt;&lt;p&gt;به خواری بگرداندش ده به ده&lt;/p&gt;&lt;p&gt;در آن مرز کاین پیر هشیار بود&lt;/p&gt;&lt;p&gt;یکی مرزبان ستمکار بود&lt;/p&gt;&lt;p&gt;که هر ناتوان را که دریافتی&lt;/p&gt;&lt;p&gt;به سرپنجگی پنجه برتافتی&lt;/p&gt;&lt;p&gt;جهان سوز و بی‌رحمت و خیره‌کش&lt;/p&gt;&lt;p&gt;ز تلخیش روی جهانی ترش&lt;/p&gt;&lt;p&gt;گروهی برفتند از آن ظلم و عار&lt;/p&gt;&lt;p&gt;ببردند نام بدش در دیار&lt;/p&gt;&lt;p&gt;گروهی بماندند مسکین و ریش&lt;/p&gt;&lt;p&gt;پس چرخه نفرین گرفتند پیش&lt;/p&gt;&lt;p&gt;ید ظلم جایی که گردد دراز&lt;/p&gt;&lt;p&gt;نبینی لب مردم از خنده باز&lt;/p&gt;&lt;p&gt;به دیدار شیخ آمدی گاه گاه&lt;/p&gt;&lt;p&gt;خدادوست در وی نکردی نگاه&lt;/p&gt;&lt;p&gt;ملک نوبتی گفتش: ای نیکبخت&lt;/p&gt;&lt;p&gt;به نفرت ز من در مکش روی سخت&lt;/p&gt;&lt;p&gt;مرا با تو دانی سر دوستی است&lt;/p&gt;&lt;p&gt;تو را دشمنی با من از بهر چیست؟&lt;/p&gt;&lt;p&gt;گرفتم که سالار کشور نیم&lt;/p&gt;&lt;p&gt;به عزت ز درویش کمتر نیم&lt;/p&gt;&lt;p&gt;نگویم فضیلت نهم بر کسی&lt;/p&gt;&lt;p&gt;چنان باش با من که با هر کسی&lt;/p&gt;&lt;p&gt;شنید این سخن عابد هوشیار&lt;/p&gt;&lt;p&gt;بر آشفت و گفت: ای ملک، هوش دار&lt;/p&gt;&lt;p&gt;وجودت پریشانی خلق از اوست&lt;/p&gt;&lt;p&gt;ندارم پریشانی خلق دوست&lt;/p&gt;&lt;p&gt;تو با آن که من دوستم، دشمنی&lt;/p&gt;&lt;p&gt;نپندارمت دوستدار منی&lt;/p&gt;&lt;p&gt;چرا دوست دارم به باطل منت&lt;/p&gt;&lt;p&gt;چو دانم که دارد خدا دشمنت؟&lt;/p&gt;&lt;p&gt;مده بوسه بر دست من دوستوار&lt;/p&gt;&lt;p&gt;برو دوستداران من دوست دار&lt;/p&gt;&lt;p&gt;خدادوست را گر بدرند پوست&lt;/p&gt;&lt;p&gt;نخواهد شدن دشمن دوست، دوست&lt;/p&gt;&lt;p&gt;عجب دارم از خواب آن سنگدل&lt;/p&gt;&lt;p&gt;که خلقی بخسبند از او تنگدل&lt;/p&gt;', 'مثنوی شماره ۱۱ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(369, '&lt;p&gt;مها زورمندی مکن با کهان&lt;/p&gt;&lt;p&gt;که بر یک نمط می‌نماند جهان&lt;/p&gt;&lt;p&gt;سر پنجهٔ ناتوان بر مپیچ&lt;/p&gt;&lt;p&gt;که گر دست یابد برآیی به هیچ&lt;/p&gt;&lt;p&gt;عدو را به کوچک نباید شمرد&lt;/p&gt;&lt;p&gt;که کوه کلان دیدم از سنگ خرد&lt;/p&gt;&lt;p&gt;نبینی که چون با هم آیند مور&lt;/p&gt;&lt;p&gt;ز شیران جنگی برآرند شور&lt;/p&gt;&lt;p&gt;نه موری که مویی کز آن کمتر است&lt;/p&gt;&lt;p&gt;چو پر شد ز زنجیر محکمتر است&lt;/p&gt;&lt;p&gt;مبر گفتمت پای مردم ز جای&lt;/p&gt;&lt;p&gt;که عاجز شوی گر درآیی ز پای&lt;/p&gt;&lt;p&gt;دل دوستان جمع بهتر که گنج&lt;/p&gt;&lt;p&gt;خزینه تهی به که مردم به رنج&lt;/p&gt;&lt;p&gt;مینداز در پای کار کسی&lt;/p&gt;&lt;p&gt;که افتد که در پایش افتی بسی&lt;/p&gt;&lt;p&gt;تحمل کن ای ناتوان از قوی&lt;/p&gt;&lt;p&gt;که روزی تواناتر از وی شوی&lt;/p&gt;&lt;p&gt;به همت برآر از ستیهنده شور&lt;/p&gt;&lt;p&gt;که بازوی همت به از دست زور&lt;/p&gt;&lt;p&gt;لب خشک مظلوم را گو بخند&lt;/p&gt;&lt;p&gt;که دندان ظالم بخواهند کند&lt;/p&gt;&lt;p&gt;به بانگ دهل خواجه بیدار گشت&lt;/p&gt;&lt;p&gt;چه داند شب پاسبان چون گذشت؟&lt;/p&gt;&lt;p&gt;خورد کاروانی غم بار خویش&lt;/p&gt;&lt;p&gt;نسوزد دلش بر خر پشت ریش&lt;/p&gt;&lt;p&gt;گرفتم کز افتادگان نیستی&lt;/p&gt;&lt;p&gt;چو افتاده بینی چرا نیستی؟&lt;/p&gt;&lt;p&gt;بر اینت بگویم یکی سرگذشت&lt;/p&gt;&lt;p&gt;که سستی بود زین سخن درگذشت&lt;/p&gt;', 'مثنوی شماره۱۲  ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(370, '&lt;p&gt;چنان قحط سالی شد اندر دمشق&lt;/p&gt;&lt;p&gt;که یاران فراموش کردند عشق&lt;/p&gt;&lt;p&gt;چنان آسمان بر زمین شد بخیل&lt;/p&gt;&lt;p&gt;که لب تر نکردند زرع و نخیل&lt;/p&gt;&lt;p&gt;بخوشید سرچشمه‌های قدیم&lt;/p&gt;&lt;p&gt;نماند آب، جز آب چشم یتیم&lt;/p&gt;&lt;p&gt;نبودی به‌جز آه بیوه زنی&lt;/p&gt;&lt;p&gt;اگر بر شدی دودی از روزنی&lt;/p&gt;&lt;p&gt;چو درویش بی‌رنگ دیدم درخت&lt;/p&gt;&lt;p&gt;قوی بازوان سست و درمانده سخت&lt;/p&gt;&lt;p&gt;نه در کوه سبزی نه در باغ شخ&lt;/p&gt;&lt;p&gt;ملخ بوستان خورده مردم ملخ&lt;/p&gt;&lt;p&gt;در آن حال پیش آمدم دوستی&lt;/p&gt;&lt;p&gt;از او مانده بر استخوان پوستی&lt;/p&gt;&lt;p&gt;وگرچه به مکنت قوی حال بود&lt;/p&gt;&lt;p&gt;خداوند جاه و زر و مال بود&lt;/p&gt;&lt;p&gt;بدو گفتم: ای یار پاکیزه‌خوی&lt;/p&gt;&lt;p&gt;چه درماندگی پیشت آمد؟ بگوی&lt;/p&gt;&lt;p&gt;بغرید بر من که عقلت کجاست؟&lt;/p&gt;&lt;p&gt;چو دانی و پرسی سؤالت خطاست&lt;/p&gt;&lt;p&gt;نبینی که سختی به غایت رسید&lt;/p&gt;&lt;p&gt;مشقت به حد نهایت رسید؟&lt;/p&gt;&lt;p&gt;نه باران همی آید از آسمان&lt;/p&gt;&lt;p&gt;نه برمی‌رود دود فریادخوان&lt;/p&gt;&lt;p&gt;بدو گفتم: آخر تو را باک نیست&lt;/p&gt;&lt;p&gt;کشد زهر جایی که تریاک نیست&lt;/p&gt;&lt;p&gt;گر از نیستی دیگری شد هلاک&lt;/p&gt;&lt;p&gt;تو را هست، بط را ز طوفان چه باک؟&lt;/p&gt;&lt;p&gt;نگه کرد رنجیده در من فقیه&lt;/p&gt;&lt;p&gt;نگه کردن عالم اندر سفیه&lt;/p&gt;&lt;p&gt;که مرد ارچه بر ساحل است، ای رفیق&lt;/p&gt;&lt;p&gt;نیاساید و دوستانش غریق&lt;/p&gt;&lt;p&gt;من از بینوایی نیم روی زرد&lt;/p&gt;&lt;p&gt;غم بینوایان رخم زرد کرد&lt;/p&gt;&lt;p&gt;نخواهد که بیند خردمند، ریش&lt;/p&gt;&lt;p&gt;نه بر عضو مردم، نه بر عضو خویش&lt;/p&gt;&lt;p&gt;یکی اول از تندرستان منم&lt;/p&gt;&lt;p&gt;که ریشی ببینم بلرزد تنم&lt;/p&gt;&lt;p&gt;منغص بود عیش آن تندرست&lt;/p&gt;&lt;p&gt;که باشد به پهلوی بیمار سست&lt;/p&gt;&lt;p&gt;چو بینم که درویش مسکین نخورد&lt;/p&gt;&lt;p&gt;به کام اندرم لقمه زهر است و درد&lt;/p&gt;&lt;p&gt;یکی را به زندان درش دوستان&lt;/p&gt;&lt;p&gt;کجا ماندش عیش در بوستان؟&lt;/p&gt;', 'مثنوی شماره ۱۳ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(371, '&lt;p&gt;&lt;br&gt;شبی دود خلق آتشی برفروخت&lt;/p&gt;&lt;p&gt;شنیدم که بغداد نیمی بسوخت&lt;/p&gt;&lt;p&gt;یکی شکر گفت اندران خاک و دود&lt;/p&gt;&lt;p&gt;که دکان ما را گزندی نبود&lt;/p&gt;&lt;p&gt;جهاندیده‌ای گفتش ای بوالهوس&lt;/p&gt;&lt;p&gt;تو را خود غم خویشتن بود و بس؟&lt;/p&gt;&lt;p&gt;پسندی که شهری بسوزد به نار&lt;/p&gt;&lt;p&gt;اگر چه سرایت بود بر کنار؟&lt;/p&gt;&lt;p&gt;به جز سنگدل ناکند معده تنگ&lt;/p&gt;&lt;p&gt;چو بیند کسان بر شکم بسته سنگ&lt;/p&gt;&lt;p&gt;توانگر خود آن لقمه چون می‌خورد&lt;/p&gt;&lt;p&gt;چو بیند که درویش خون می‌خورد؟&lt;/p&gt;&lt;p&gt;مگو تندرست است رنجوردار&lt;/p&gt;&lt;p&gt;که می‌پیچد از غصه رنجوروار&lt;/p&gt;&lt;p&gt;تنکدل چو یاران به منزل رسند&lt;/p&gt;&lt;p&gt;نخسبد که واماندگان از پسند&lt;/p&gt;&lt;p&gt;دل پادشاهان شود بارکش&lt;/p&gt;&lt;p&gt;چو بینند در گل خر خارکش&lt;/p&gt;&lt;p&gt;اگر در سرای سعادت کس است&lt;/p&gt;&lt;p&gt;ز گفتار سعدیش حرفی بس است&lt;/p&gt;&lt;p&gt;همینت بسنده‌ست اگر بشنوی&lt;/p&gt;&lt;p&gt;که گر خار کاری سمن ندروی&lt;/p&gt;', 'مثنوی شماره ۱۴', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(372, '&lt;p&gt;خبرداری از خسروان عجم&lt;/p&gt;&lt;p&gt;که کردند بر زیردستان ستم؟&lt;/p&gt;&lt;p&gt;نه آن شوکت و پادشایی بماند&lt;/p&gt;&lt;p&gt;نه آن ظلم بر روستایی بماند&lt;/p&gt;&lt;p&gt;خطا بین که بر دست ظالم برفت&lt;/p&gt;&lt;p&gt;جهان ماند و با او مظالم برفت&lt;/p&gt;&lt;p&gt;خنک روز محشر تن دادگر&lt;/p&gt;&lt;p&gt;که در سایهٔ عرش دارد مقر&lt;/p&gt;&lt;p&gt;به قومی که نیکی پسندد خدای&lt;/p&gt;&lt;p&gt;دهد خسروی عادل و نیک رای&lt;/p&gt;&lt;p&gt;چو خواهد که ویران شود عالمی&lt;/p&gt;&lt;p&gt;کند ملک در پنجهٔ ظالمی&lt;/p&gt;&lt;p&gt;سگالند از او نیکمردان حذر&lt;/p&gt;&lt;p&gt;که خشم خدای است بیدادگر&lt;/p&gt;&lt;p&gt;بزرگی از او دان و منت شناس&lt;/p&gt;&lt;p&gt;که زایل شود نعمت ناسپاس&lt;/p&gt;&lt;p&gt;اگر شکر کردی بر این ملک و مال&lt;/p&gt;&lt;p&gt;به مالی و ملکی رسی بی زوال&lt;/p&gt;&lt;p&gt;وگر جور در پادشایی کنی&lt;/p&gt;&lt;p&gt;پس از پادشایی گدایی کنی&lt;/p&gt;&lt;p&gt;حرام است بر پادشه خواب خوش&lt;/p&gt;&lt;p&gt;چو باشد ضعیف از قوی بارکش&lt;/p&gt;&lt;p&gt;میازار عامی به یک خردله&lt;/p&gt;&lt;p&gt;که سلطان شبان است و عامی گله&lt;/p&gt;&lt;p&gt;چو پرخاش بینند و بیداد از او&lt;/p&gt;&lt;p&gt;شبان نیست، گرگ است، فریاد از او&lt;/p&gt;&lt;p&gt;بد انجام رفت و بد اندیشه کرد&lt;/p&gt;&lt;p&gt;که با زیردستان جفا، پیشه کرد&lt;/p&gt;&lt;p&gt;به سختی و سستی بر این بگذرد&lt;/p&gt;&lt;p&gt;بماند بر او سالها نام بد&lt;/p&gt;&lt;p&gt;نخواهی که نفرین کنند از پست&lt;/p&gt;&lt;p&gt;نکو باش تا بد نگوید کست&lt;/p&gt;', 'مثنوی شماره ۱۵', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(373, '&lt;p&gt;شنیدم که در مرزی از باختر&lt;/p&gt;&lt;p&gt;برادر دو بودند از یک پدر&lt;/p&gt;&lt;p&gt;سپهدار و گردن کش و پیلتن&lt;/p&gt;&lt;p&gt;نکو روی و دانا و شمشیر زن&lt;/p&gt;&lt;p&gt;پدر هر دو را سهمگین مرد یافت&lt;/p&gt;&lt;p&gt;طلبکار جولان و ناورد یافت&lt;/p&gt;&lt;p&gt;برفت آن زمین را دو قسمت نهاد&lt;/p&gt;&lt;p&gt;به هر یک پسر، زآن نصیبی بداد&lt;/p&gt;&lt;p&gt;مبادا که بر یکدگر سر کشند&lt;/p&gt;&lt;p&gt;به پیکار شمشیر کین برکشند&lt;/p&gt;&lt;p&gt;پدر بعد از آن، روزگاری شمرد&lt;/p&gt;&lt;p&gt;به جان آفرین جان شیرین سپرد&lt;/p&gt;&lt;p&gt;اجل بگسلاندش طناب امل&lt;/p&gt;&lt;p&gt;وفاتش فرو بست دست عمل&lt;/p&gt;&lt;p&gt;مقرر شد آن مملکت بر دو شاه&lt;/p&gt;&lt;p&gt;که بی حد و مر بود گنج و سپاه&lt;/p&gt;&lt;p&gt;به حکم نظر در به‌افتاد خویش&lt;/p&gt;&lt;p&gt;گرفتند هر یک، یکی راه پیش&lt;/p&gt;&lt;p&gt;یکی عدل تا نام نیکو برد&lt;/p&gt;&lt;p&gt;یکی ظلم تا مال گرد آورد&lt;/p&gt;&lt;p&gt;یکی عاطفت سیرت خویش کرد&lt;/p&gt;&lt;p&gt;درم داد و تیمار درویش خورد&lt;/p&gt;&lt;p&gt;بنا کرد و نان داد و لشکر نواخت&lt;/p&gt;&lt;p&gt;شب از بهر درویش، شبخانه ساخت&lt;/p&gt;&lt;p&gt;خزاین تهی کرد و پر کرد جیش&lt;/p&gt;&lt;p&gt;چنان کز خلایق به هنگام عیش&lt;/p&gt;&lt;p&gt;برآمد همی بانگ شادی چو رعد&lt;/p&gt;&lt;p&gt;چو شیراز در عهد بوبکر سعد&lt;/p&gt;&lt;p&gt;خدیو خردمند فرخ نهاد&lt;/p&gt;&lt;p&gt;که شاخ امیدش برومند باد&lt;/p&gt;&lt;p&gt;حکایت شنو کان گو نامجوی&lt;/p&gt;&lt;p&gt;پسندیده پی بود و فرخنده خوی&lt;/p&gt;&lt;p&gt;ملازم به دلداری خاص و عام&lt;/p&gt;&lt;p&gt;ثناگوی حق بامدادان و شام&lt;/p&gt;&lt;p&gt;در آن ملک قارون برفتی دلیر&lt;/p&gt;&lt;p&gt;که شه دادگر بود و درویش سیر&lt;/p&gt;&lt;p&gt;نیامد در ایام او بر دلی&lt;/p&gt;&lt;p&gt;نگویم که خاری که برگ گلی&lt;/p&gt;&lt;p&gt;سرآمد به تأیید ملک از سران&lt;/p&gt;&lt;p&gt;نهادند سر بر خطش سروران&lt;/p&gt;&lt;p&gt;دگر خواست کافزون کند تخت و تاج&lt;/p&gt;&lt;p&gt;بیافزود بر مرد دهقان خراج&lt;/p&gt;&lt;p&gt;طمع کرد در مال بازارگان&lt;/p&gt;&lt;p&gt;بلا ریخت بر جان بیچارگان&lt;/p&gt;&lt;p&gt;به امید بیشی نداد و نخورد&lt;/p&gt;&lt;p&gt;خردمند داند که ناخوب کرد&lt;/p&gt;&lt;p&gt;که تا جمع کرد آن زر از گربزی&lt;/p&gt;&lt;p&gt;پراکنده شد لشکر از عاجزی&lt;/p&gt;&lt;p&gt;شنیدند بازارگانان خبر&lt;/p&gt;&lt;p&gt;که ظلم است در بوم آن بی‌هنر&lt;/p&gt;&lt;p&gt;بریدند از آنجا خرید و فروخت&lt;/p&gt;&lt;p&gt;زراعت نیامد، رعیت بسوخت&lt;/p&gt;&lt;p&gt;چو اقبالش از دوستی سر بتافت&lt;/p&gt;&lt;p&gt;به ناکام دشمن بر او دست یافت&lt;/p&gt;&lt;p&gt;ستیز فلک بیخ و بارش بکند&lt;/p&gt;&lt;p&gt;سم اسب دشمن دیارش بکند&lt;/p&gt;&lt;p&gt;وفا در که جوید چو پیمان گسیخت؟&lt;/p&gt;&lt;p&gt;خراج از که خواهد چو دهقان گریخت؟&lt;/p&gt;&lt;p&gt;چه نیکی طمع دارد آن بی‌صفا&lt;/p&gt;&lt;p&gt;که باشد دعای بدش در قفا؟&lt;/p&gt;&lt;p&gt;چو بختش نگون بود در کاف کن&lt;/p&gt;&lt;p&gt;نکرد آنچه نیکانش گفتند کن&lt;/p&gt;&lt;p&gt;چه گفتند نیکان بدان نیکمرد؟&lt;/p&gt;&lt;p&gt;تو برخور که بیدادگر بر نخورد&lt;/p&gt;&lt;p&gt;گمانش خطا بود و تدبیر سست&lt;/p&gt;&lt;p&gt;که در عدل بود آنچه در ظلم جست&lt;/p&gt;&lt;p&gt;یکی بر سر شاخ، بن می‌برید&lt;/p&gt;&lt;p&gt;خداوند بستان نگه کرد و دید&lt;/p&gt;&lt;p&gt;بگفتا گر این مرد بد می‌کند&lt;/p&gt;&lt;p&gt;نه با من که با نفس خود می‌کند&lt;/p&gt;&lt;p&gt;نصیحت بجای است اگر بشنوی&lt;/p&gt;&lt;p&gt;ضعیفان میفکن به کتف قوی&lt;/p&gt;&lt;p&gt;که فردا به داور برد خسروی&lt;/p&gt;&lt;p&gt;گدایی که پیشت نیرزد جوی&lt;/p&gt;&lt;p&gt;چو خواهی که فردا به وی مهتری&lt;/p&gt;&lt;p&gt;مکن دشمن خویشتن، کهتری&lt;/p&gt;&lt;p&gt;که چون بگذرد بر تو این سلطنت&lt;/p&gt;&lt;p&gt;بگیرد به قهر آن گدا دامنت&lt;/p&gt;&lt;p&gt;مکن، پنجه از ناتوانان بدار&lt;/p&gt;&lt;p&gt;که گر بفکنندت شوی شرمسار&lt;/p&gt;&lt;p&gt;که زشت است در چشم آزادگان&lt;/p&gt;&lt;p&gt;بیافتادن از دست افتادگان&lt;/p&gt;&lt;p&gt;بزرگان روشندل نیکبخت&lt;/p&gt;&lt;p&gt;به فرزانگی تاج بردند و تخت&lt;/p&gt;&lt;p&gt;به دنباله راستان کج مرو&lt;/p&gt;&lt;p&gt;وگر راست خواهی ز سعدی شنو&lt;/p&gt;', 'مثنوی شماره ۱۶', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(374, '&lt;p&gt;مگو جاهی از سلطنت بیش نیست&lt;/p&gt;&lt;p&gt;که ایمن‌تر از ملک درویش نیست&lt;/p&gt;&lt;p&gt;سبکبار مردم سبک‌تر روند&lt;/p&gt;&lt;p&gt;حق این است و صاحبدلان بشنوند&lt;/p&gt;&lt;p&gt;تهیدست تشویش نانی خورد&lt;/p&gt;&lt;p&gt;جهانبان به قدر جهانی خورد&lt;/p&gt;&lt;p&gt;گدا را چو حاصل شود نان شام&lt;/p&gt;&lt;p&gt;چنان خوش بخسبد که سلطان شام&lt;/p&gt;&lt;p&gt;غم و شادمانی به سر می‌رود&lt;/p&gt;&lt;p&gt;به مرگ این دو از سر به در می‌رود&lt;/p&gt;&lt;p&gt;چه آن را که بر سر نهادند تاج&lt;/p&gt;&lt;p&gt;چه آن را که بر گردن آمد خراج&lt;/p&gt;&lt;p&gt;اگر سرفرازی به کیوان بر است&lt;/p&gt;&lt;p&gt;وگر تنگدستی به زندان در است&lt;/p&gt;&lt;p&gt;چو خیل اجل بر سر هر دو تاخت&lt;/p&gt;&lt;p&gt;نمی شاید از یکدگرشان شناخت&lt;/p&gt;', 'مثنوی شماره ۱۷ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(375, '&lt;p&gt;شنیدم که یک بار در حله‌ای&lt;/p&gt;&lt;p&gt;سخن گفت با عابدی کله‌ای&lt;/p&gt;&lt;p&gt;که من فر فرماندهی داشتم&lt;/p&gt;&lt;p&gt;به سر بر کلاه مهی داشتم&lt;/p&gt;&lt;p&gt;سپهرم مدد کرد و نصرت وفاق&lt;/p&gt;&lt;p&gt;گرفتم به بازوی دولت عراق&lt;/p&gt;&lt;p&gt;طمع کرده بودم که کرمان خورم&lt;/p&gt;&lt;p&gt;که ناگه بخوردند کرمان سرم&lt;/p&gt;&lt;p&gt;بکن پنبهٔ غفلت از گوش هوش&lt;/p&gt;&lt;p&gt;که از مردگان پندت آید به گوش&lt;/p&gt;', 'مثنوی شماره ۱۸ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(376, '&lt;p&gt;نکوکار مردم نباشد بدش&lt;/p&gt;&lt;p&gt;نورزد کسی بد که نیک افتدش&lt;/p&gt;&lt;p&gt;شر انگیز هم بر سر شر شود&lt;/p&gt;&lt;p&gt;چو کژدم که با خانه کمتر شود&lt;/p&gt;&lt;p&gt;اگر نفع کس در نهاد تو نیست&lt;/p&gt;&lt;p&gt;چنین گوهر و سنگ خارا یکی است&lt;/p&gt;&lt;p&gt;غلط گفتم ای یار شایسته خوی&lt;/p&gt;&lt;p&gt;که نفع است در آهن و سنگ و روی&lt;/p&gt;&lt;p&gt;چنین آدمی مرده به ننگ را&lt;/p&gt;&lt;p&gt;که بر وی فضیلت بود سنگ را&lt;/p&gt;&lt;p&gt;نه هر آدمی زاده از دد به است&lt;/p&gt;&lt;p&gt;که دد ز آدمی زادهٔ بد به است&lt;/p&gt;&lt;p&gt;به است از دد انسان صاحب خرد&lt;/p&gt;&lt;p&gt;نه انسان که در مردم افتد چو دد&lt;/p&gt;&lt;p&gt;چو انسان نداند به جز خورد و خواب&lt;/p&gt;&lt;p&gt;کدامش فضیلت بود بر دواب؟&lt;/p&gt;&lt;p&gt;سوار نگون بخت بی راهرو&lt;/p&gt;&lt;p&gt;پیاده برد ز او به رفتن گرو&lt;/p&gt;&lt;p&gt;کسی دانهٔ نیکمردی نکاشت&lt;/p&gt;&lt;p&gt;کز او خرمن کام دل برنداشت&lt;/p&gt;&lt;p&gt;نه هرگز شنیدیم در عمر خویش&lt;/p&gt;&lt;p&gt;که بدمرد را نیکی آمد به پیش&lt;/p&gt;', 'مثنوی شماره ۱۹ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(377, '&lt;p&gt;گزیری به چاهی در افتاده بود&lt;/p&gt;&lt;p&gt;که از هول او شیر نر ماده بود&lt;/p&gt;&lt;p&gt;بداندیش مردم به جز بد ندید&lt;/p&gt;&lt;p&gt;بیافتاد و عاجزتر از خود ندید&lt;/p&gt;&lt;p&gt;همه شب ز فریاد و زاری نخفت&lt;/p&gt;&lt;p&gt;یکی بر سرش کوفت سنگی و گفت:&lt;/p&gt;&lt;p&gt;تو هرگز رسیدی به فریاد کس&lt;/p&gt;&lt;p&gt;که می‌خواهی امروز فریادرس؟&lt;/p&gt;&lt;p&gt;همه تخم نامردمی کاشتی&lt;/p&gt;&lt;p&gt;ببین لاجرم بر که برداشتی&lt;/p&gt;&lt;p&gt;که بر جان ریشت نهد مرهمی&lt;/p&gt;&lt;p&gt;که دلها ز ریشت بنالد همی؟&lt;/p&gt;&lt;p&gt;تو ما را همی چاه کندی به راه&lt;/p&gt;&lt;p&gt;به سر لاجرم در فتادی به چاه&lt;/p&gt;&lt;p&gt;دو کس چه کنند از پی خاص و عام&lt;/p&gt;&lt;p&gt;یکی نیک‌محضر، دگر زشت‌نام&lt;/p&gt;&lt;p&gt;یکی تشنه را تا کند تازه حلق&lt;/p&gt;&lt;p&gt;دگر تا به گردن در افتند خلق&lt;/p&gt;&lt;p&gt;اگر بد کنی چشم نیکی مدار&lt;/p&gt;&lt;p&gt;که هرگز نیارد گز انگور بار&lt;/p&gt;&lt;p&gt;نپندارم ای در خزان کشته جو&lt;/p&gt;&lt;p&gt;که گندم ستانی به وقت درو&lt;/p&gt;&lt;p&gt;درخت زقوم ار به جان پروری&lt;/p&gt;&lt;p&gt;مپندار هرگز کز او بر خوری&lt;/p&gt;&lt;p&gt;رطب ناورد چوب خرزهره بار&lt;/p&gt;&lt;p&gt;چو تخم افکنی، بر همان چشم دار&lt;/p&gt;', 'مثنوی شماره ۲۰  ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(378, '&lt;p&gt;حکایت کنند از یکی نیکمرد&lt;/p&gt;&lt;p&gt;که اکرام حجاج یوسف نکرد&lt;/p&gt;&lt;p&gt;به سرهنگ دیوان نگه کرد تیز&lt;/p&gt;&lt;p&gt;که نطعش بیانداز و خونش بریز&lt;/p&gt;&lt;p&gt;چو حجت نماند جفا جوی را&lt;/p&gt;&lt;p&gt;به پرخاش در هم کشد روی را&lt;/p&gt;&lt;p&gt;بخندید و بگریست مرد خدای&lt;/p&gt;&lt;p&gt;عجب داشت سنگین‌دلِ تیره‌رای&lt;/p&gt;&lt;p&gt;چو دیدش که خندید و دیگر گریست&lt;/p&gt;&lt;p&gt;بپرسید کاین خنده و گریه چیست؟&lt;/p&gt;&lt;p&gt;بگفتا همی گریم از روزگار&lt;/p&gt;&lt;p&gt;که طفلان بیچاره دارم چهار&lt;/p&gt;&lt;p&gt;همی خندم از لطف یزدان پاک&lt;/p&gt;&lt;p&gt;که مظلوم رفتم نه ظالم به خاک&lt;/p&gt;&lt;p&gt;پسر گفتش: ای نامور شهریار&lt;/p&gt;&lt;p&gt;یکی دست از این مرد صوفی بدار&lt;/p&gt;&lt;p&gt;که خلقی بر او روی دارند و پشت&lt;/p&gt;&lt;p&gt;نه رای است خلقی به یک بار کشت&lt;/p&gt;&lt;p&gt;بزرگی و عفو و کرم پیشه کن&lt;/p&gt;&lt;p&gt;ز خردان اطفالش اندیشه کن&lt;/p&gt;&lt;p&gt;شنیدم که نشنید و خونش بریخت&lt;/p&gt;&lt;p&gt;ز فرمان داور که داند گریخت؟&lt;/p&gt;&lt;p&gt;بزرگی در آن فکرت آن شب بخفت&lt;/p&gt;&lt;p&gt;به خواب اندرش دید و پرسید و گفت:&lt;/p&gt;&lt;p&gt;دمی بیش بر من سیاست نراند&lt;/p&gt;&lt;p&gt;عقوبت بر او تا قیامت بماند&lt;/p&gt;&lt;p&gt;نخفته‌ست مظلوم از آهش بترس&lt;/p&gt;&lt;p&gt;ز دود دل صبحگاهش بترس&lt;/p&gt;&lt;p&gt;نترسی که پاک اندرونی شبی&lt;/p&gt;&lt;p&gt;بر آرد ز سوز جگر یا ربی؟&lt;/p&gt;&lt;p&gt;نه ابلیس بد کرد و نیکی ندید؟&lt;/p&gt;&lt;p&gt;بر پاک ناید ز تخم پلید&lt;/p&gt;', 'مثنوی شماره ۲۱  ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(379, '&lt;p&gt;مدر پرده کس به هنگام جنگ&lt;/p&gt;&lt;p&gt;که باشد تو را نیز در پرده ننگ&lt;/p&gt;&lt;p&gt;مزن بانگ بر شیرمردان درشت&lt;/p&gt;&lt;p&gt;چو با کودکان برنیایی به مشت&lt;/p&gt;&lt;p&gt;یکی پند می‌داد فرزند را&lt;/p&gt;&lt;p&gt;نگه دار پند خردمند را&lt;/p&gt;&lt;p&gt;مکن جور بر خردکان ای پسر&lt;/p&gt;&lt;p&gt;که یک روزت افتد بزرگی به سر&lt;/p&gt;&lt;p&gt;نمی‌ترسی ای گرگک کم خرد&lt;/p&gt;&lt;p&gt;که روزی پلنگیت بر هم درد؟&lt;/p&gt;&lt;p&gt;به خردی درم زور سرپنجه بود&lt;/p&gt;&lt;p&gt;دل زیردستان ز من رنجه بود&lt;/p&gt;&lt;p&gt;بخوردم یکی مشت زورآوران&lt;/p&gt;&lt;p&gt;نکردم دگر زور بر لاغران&lt;/p&gt;&lt;p&gt;الا تا به غفلت نخفتی که نوم&lt;/p&gt;&lt;p&gt;حرام است بر چشم سالار قوم&lt;/p&gt;&lt;p&gt;غم زیردستان بخور زینهار&lt;/p&gt;&lt;p&gt;بترس از زبردستی روزگار&lt;/p&gt;&lt;p&gt;نصیحت که خالی بود از غرض&lt;/p&gt;&lt;p&gt;چو داروی تلخ است، دفع مرض&lt;/p&gt;', 'مثنوی شماره ۲۲  ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(380, '&lt;p&gt;یکی را حکایت کنند از ملوک&lt;/p&gt;&lt;p&gt;که بیماری رشته کردش چو دوک&lt;/p&gt;&lt;p&gt;چنانش در انداخت ضعف جسد&lt;/p&gt;&lt;p&gt;که می‌برد بر زیردستان حسد&lt;/p&gt;&lt;p&gt;که شاه ار چه بر عرصه نام‌آور است&lt;/p&gt;&lt;p&gt;چو ضعف آمد از بیدقی کمتر است&lt;/p&gt;&lt;p&gt;ندیمی زمین ملک بوسه داد&lt;/p&gt;&lt;p&gt;که ملک خداوند جاوید باد&lt;/p&gt;&lt;p&gt;در این شهر مردی مبارک دم است&lt;/p&gt;&lt;p&gt;که در پارسایی چنویی کم است&lt;/p&gt;&lt;p&gt;نرفته‌ست هرگز ره ناصواب&lt;/p&gt;&lt;p&gt;دلی روشن و دعوتی مستجاب&lt;/p&gt;&lt;p&gt;نبردند پیشش مهمات کس&lt;/p&gt;&lt;p&gt;که مقصود حاصل نشد در نفس&lt;/p&gt;&lt;p&gt;بخوان تا بخواند دعایی بر این&lt;/p&gt;&lt;p&gt;که رحمت رسد ز آسمان برین&lt;/p&gt;&lt;p&gt;بفرمود تا مهتران خدم&lt;/p&gt;&lt;p&gt;بخواندند پیر مبارک قدم&lt;/p&gt;&lt;p&gt;برفتند و گفتند و آمد فقیر&lt;/p&gt;&lt;p&gt;تنی محتشم در لباسی حقیر&lt;/p&gt;&lt;p&gt;بگفتا دعایی کن ای هوشمند&lt;/p&gt;&lt;p&gt;که در رشته چون سوزنم پای‌بند&lt;/p&gt;&lt;p&gt;شنید این سخن پیر خم بوده پشت&lt;/p&gt;&lt;p&gt;به تندی برآورد بانگی درشت&lt;/p&gt;&lt;p&gt;که حق مهربان است بر دادگر&lt;/p&gt;&lt;p&gt;ببخشای و بخشایش حق نگر&lt;/p&gt;&lt;p&gt;دعای منت کی شود سودمند&lt;/p&gt;&lt;p&gt;اسیران محتاج در چاه و بند؟&lt;/p&gt;&lt;p&gt;تو ناکرده بر خلق بخشایشی&lt;/p&gt;&lt;p&gt;کجا بینی از دولت آسایشی؟&lt;/p&gt;&lt;p&gt;ببایدت عذر خطا خواستن&lt;/p&gt;&lt;p&gt;پس از شیخ صالح دعا خواستن&lt;/p&gt;&lt;p&gt;کجا دست گیرد دعای ویت&lt;/p&gt;&lt;p&gt;دعای ستمدیدگان در پیت؟&lt;/p&gt;&lt;p&gt;شنید این سخن شهریار عجم&lt;/p&gt;&lt;p&gt;ز خشم و خجالت بر آمد بهم&lt;/p&gt;&lt;p&gt;برنجید و پس با دل خویش گفت&lt;/p&gt;&lt;p&gt;چه رنجم؟ حق است این که درویش گفت&lt;/p&gt;&lt;p&gt;بفرمود تا هر که در بند بود&lt;/p&gt;&lt;p&gt;به فرمانش آزاد کردند زود&lt;/p&gt;&lt;p&gt;جهاندیده بعد از دو رکعت نماز&lt;/p&gt;&lt;p&gt;به داور برآورد دست نیاز&lt;/p&gt;&lt;p&gt;که ای برفرازندهٔ آسمان&lt;/p&gt;&lt;p&gt;به جنگش گرفتی به صلحش بمان&lt;/p&gt;&lt;p&gt;ولی همچنان بر دعا داشت دست&lt;/p&gt;&lt;p&gt;که شه سر برآورد و بر پای جست&lt;/p&gt;&lt;p&gt;تو گفتی ز شادی بخواهد پرید&lt;/p&gt;&lt;p&gt;چو طاووس، چون رشته در پا ندید&lt;/p&gt;&lt;p&gt;بفرمود گنجینهٔ گوهرش&lt;/p&gt;&lt;p&gt;فشاندند در پای و زر بر سرش&lt;/p&gt;&lt;p&gt;حق از بهر باطل نشاید نهفت&lt;/p&gt;&lt;p&gt;از آن جمله دامن بیفشاند و گفت&lt;/p&gt;&lt;p&gt;مرو با سر رشته بار دگر&lt;/p&gt;&lt;p&gt;مبادا که دیگر کند رشته سر&lt;/p&gt;&lt;p&gt;چو باری فتادی نگه‌دار پای&lt;/p&gt;&lt;p&gt;که یک بار دیگر بلغزد ز جای&lt;/p&gt;&lt;p&gt;ز سعدی شنو کاین سخن راست است&lt;/p&gt;&lt;p&gt;نه هر باری افتاده برخاسته‌ست&lt;/p&gt;', 'مثنوی شماره ۲۳', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(381, '&lt;p&gt;&lt;br&gt;جهان ای پسر ملک جاوید نیست&lt;/p&gt;&lt;p&gt;ز دنیا وفاداری امید نیست&lt;/p&gt;&lt;p&gt;نه بر باد رفتی سحرگاه و شام&lt;/p&gt;&lt;p&gt;سریر سلیمان علیه‌السلام؟&lt;/p&gt;&lt;p&gt;به آخر ندیدی که بر باد رفت؟&lt;/p&gt;&lt;p&gt;خنک آن که با دانش و داد رفت&lt;/p&gt;&lt;p&gt;کسی زین میان گوی دولت ربود&lt;/p&gt;&lt;p&gt;که در بند آسایش خلق بود&lt;/p&gt;&lt;p&gt;به کار آمد آنها که برداشتند&lt;/p&gt;&lt;p&gt;نه گرد آوریدند و بگذاشتند&lt;/p&gt;', 'مثنوی شماره ۲۴', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(382, '&lt;p&gt;شنیدم که در مصر میری اجل&lt;/p&gt;&lt;p&gt;سپه تاخت بر روزگارش اجل&lt;/p&gt;&lt;p&gt;جمالش برفت از رخ دل فروز&lt;/p&gt;&lt;p&gt;چو خور زرد شد بس نماند ز روز&lt;/p&gt;&lt;p&gt;گزیدند فرزانگان دست فوت&lt;/p&gt;&lt;p&gt;که در طب ندیدند داروی موت&lt;/p&gt;&lt;p&gt;همه تخت و ملکی پذیرد زوال&lt;/p&gt;&lt;p&gt;به جز ملک فرمانده لایزال&lt;/p&gt;&lt;p&gt;چو نزدیک شد روز عمرش به شب&lt;/p&gt;&lt;p&gt;شنیدند می‌گفت در زیر لب&lt;/p&gt;&lt;p&gt;که در مصر چون من عزیزی نبود&lt;/p&gt;&lt;p&gt;چو حاصل همین بود چیزی نبود&lt;/p&gt;&lt;p&gt;جهان گرد کردم نخوردم برش&lt;/p&gt;&lt;p&gt;برفتم چو بیچارگان از سرش&lt;/p&gt;&lt;p&gt;پسندیده رایی که بخشید و خورد&lt;/p&gt;&lt;p&gt;جهان از پی خویشتن گرد کرد&lt;/p&gt;&lt;p&gt;در این کوش تا با تو ماند مقیم&lt;/p&gt;&lt;p&gt;که هرچ از تو ماند دریغ است و بیم&lt;/p&gt;&lt;p&gt;کند خواجه بر بستر جان‌گداز&lt;/p&gt;&lt;p&gt;یکی دست کوتاه و دیگر دراز&lt;/p&gt;&lt;p&gt;در آن دم تو را می‌نماید به دست&lt;/p&gt;&lt;p&gt;که دهشت زبانش ز گفتن ببست&lt;/p&gt;&lt;p&gt;که دستی به جود و کرم کن دراز&lt;/p&gt;&lt;p&gt;دگر دست کوته کن از ظلم و آز&lt;/p&gt;&lt;p&gt;کنونت که دست است خاری بکن&lt;/p&gt;&lt;p&gt;دگر کی بر آری تو دست از کفن؟&lt;/p&gt;&lt;p&gt;بتابد بسی ماه و پروین و هور&lt;/p&gt;&lt;p&gt;که سر بر نداری ز بالین گور&lt;/p&gt;', 'مثنوی شماره ۲۵', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(383, '&lt;p&gt;قزل ارسلان قلعه‌ای سخت داشت&lt;/p&gt;&lt;p&gt;که گردن به الوند بر می‌فراشت&lt;/p&gt;&lt;p&gt;نه اندیشه از کس نه حاجت به هیچ&lt;/p&gt;&lt;p&gt;چو زلف عروسان رهش پیچ پیچ&lt;/p&gt;&lt;p&gt;چنان نادر افتاده در روضه‌ای&lt;/p&gt;&lt;p&gt;که بر لاجوردی طبق بیضه‌ای&lt;/p&gt;&lt;p&gt;شنیدم که مردی مبارک حضور&lt;/p&gt;&lt;p&gt;به نزدیک شاه آمد از راه دور&lt;/p&gt;&lt;p&gt;حقایق شناسی، جهاندیده‌ای&lt;/p&gt;&lt;p&gt;هنرمندی، آفاق گردیده‌ای&lt;/p&gt;&lt;p&gt;بزرگی، زبان آوری کاردان&lt;/p&gt;&lt;p&gt;حکیمی، سخنگوی بسیاردان&lt;/p&gt;&lt;p&gt;قزل گفت چندین که گردیده‌ای&lt;/p&gt;&lt;p&gt;چنین جای محکم دگر دیده‌ای؟&lt;/p&gt;&lt;p&gt;بخندید کاین قلعه‌ای خرم است&lt;/p&gt;&lt;p&gt;ولیکن نپندارمش محکم است&lt;/p&gt;&lt;p&gt;نه پیش از تو گردن کشان داشتند&lt;/p&gt;&lt;p&gt;دمی چند بودند و بگذاشتند؟&lt;/p&gt;&lt;p&gt;نه بعد از تو شاهان دیگر برند&lt;/p&gt;&lt;p&gt;درخت امید تو را بر خورند؟&lt;/p&gt;&lt;p&gt;ز دوران ملک پدر یاد کن&lt;/p&gt;&lt;p&gt;دل از بند اندیشه آزاد کن&lt;/p&gt;&lt;p&gt;چنان روزگارش به کنجی نشاند&lt;/p&gt;&lt;p&gt;که بر یک پشیزش تصرف نماند&lt;/p&gt;&lt;p&gt;چو نومید ماند از همه چیز و کس&lt;/p&gt;&lt;p&gt;امیدش به فضل خدا ماند و بس&lt;/p&gt;&lt;p&gt;بر مرد هشیار دنیا خس است&lt;/p&gt;&lt;p&gt;که هر مدتی جای دیگر کس است&lt;/p&gt;&lt;p&gt;چنین گفت شوریده‌ای در عجم&lt;/p&gt;&lt;p&gt;به کسری که ای وارث ملک جم&lt;/p&gt;&lt;p&gt;اگر ملک بر جم بماندی و بخت&lt;/p&gt;&lt;p&gt;تو را کی میسر شدی تاج و تخت؟&lt;/p&gt;&lt;p&gt;اگر گنج قارون به دست آوری&lt;/p&gt;&lt;p&gt;نماند مگر آنچه بخشی، بری&lt;/p&gt;', 'مثنوی شماره ۲۶', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(384, '&lt;p&gt;چو الب ارسلان جان به جان‌بخش داد&lt;/p&gt;&lt;p&gt;پسر تاج شاهی به سر برنهاد&lt;/p&gt;&lt;p&gt;به تربت سپردندش از تاجگاه&lt;/p&gt;&lt;p&gt;نه جای نشستن بد آماجگاه&lt;/p&gt;&lt;p&gt;چنین گفت دیوانه‌ای هوشیار&lt;/p&gt;&lt;p&gt;چو دیدش پسر روز دیگر سوار&lt;/p&gt;&lt;p&gt;زهی ملک و دوران سر در نشیب&lt;/p&gt;&lt;p&gt;پدر رفت و پای پسر در رکیب&lt;/p&gt;&lt;p&gt;چنین است گردیدن روزگار&lt;/p&gt;&lt;p&gt;سبک سیر و بدعهد و ناپایدار&lt;/p&gt;&lt;p&gt;چو دیرینه روزی سرآورد عهد&lt;/p&gt;&lt;p&gt;جوان دولتی سر برآرد ز مهد&lt;/p&gt;&lt;p&gt;منه بر جهان دل که بیگانه‌ای است&lt;/p&gt;&lt;p&gt;چو مطرب که هر روز در خانه‌ای است&lt;/p&gt;&lt;p&gt;نه لایق بود عیش با دلبری&lt;/p&gt;&lt;p&gt;که هر بامدادش بود شوهری&lt;/p&gt;&lt;p&gt;نکویی کن امسال چون ده تو راست&lt;/p&gt;&lt;p&gt;که سال دگر دیگری دهخداست&lt;/p&gt;', 'مثنوی شماره ۲۷', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(385, '&lt;p&gt;شنیدم که از پادشاهان غور&lt;/p&gt;&lt;p&gt;یکی پادشه خر گرفتی به زور&lt;/p&gt;&lt;p&gt;خران زیر بار گران بی علف&lt;/p&gt;&lt;p&gt;به روزی دو مسکین شدندی تلف&lt;/p&gt;&lt;p&gt;چو منعم کند سفله را، روزگار&lt;/p&gt;&lt;p&gt;نهد بر دل تنگ درویش، بار&lt;/p&gt;&lt;p&gt;چو بام بلندش بود خودپرست&lt;/p&gt;&lt;p&gt;کند بول و خاشاک بر بام پست&lt;/p&gt;&lt;p&gt;شنیدم که باری به عزم شکار&lt;/p&gt;&lt;p&gt;برون رفت بیدادگر شهریار&lt;/p&gt;&lt;p&gt;تکاور به دنبال صیدی براند&lt;/p&gt;&lt;p&gt;شبش در گرفت از حشم باز ماند&lt;/p&gt;&lt;p&gt;به تنها ندانست روی و رهی&lt;/p&gt;&lt;p&gt;بینداخت ناکام شب در دهی&lt;/p&gt;&lt;p&gt;یکی پیرمرد اندر آن ده مقیم&lt;/p&gt;&lt;p&gt;ز پیران مردم شناس قدیم&lt;/p&gt;&lt;p&gt;پسر را همی‌گفت کای شادبهر&lt;/p&gt;&lt;p&gt;خرت را مبر بامدادان به شهر&lt;/p&gt;&lt;p&gt;که این ناجوانمرد برگشته بخت&lt;/p&gt;&lt;p&gt;که تابوت بینمش بر جای تخت&lt;/p&gt;&lt;p&gt;کمر بسته دارد به فرمان دیو&lt;/p&gt;&lt;p&gt;به گردون بر از دست جورش غریو&lt;/p&gt;&lt;p&gt;در این کشور آسایش و خرمی&lt;/p&gt;&lt;p&gt;ندید و نبیند به چشم آدمی&lt;/p&gt;&lt;p&gt;مگر کاین سیه نامهٔ بی‌صفا&lt;/p&gt;&lt;p&gt;به دوزخ برد لعنت اندر قفا&lt;/p&gt;&lt;p&gt;پسر گفت: راه دراز است و سخت&lt;/p&gt;&lt;p&gt;پیاده نیارم شد ای نیکبخت&lt;/p&gt;&lt;p&gt;طریقی بیندیش و رایی بزن&lt;/p&gt;&lt;p&gt;که رای تو روشن‌تر از رای من&lt;/p&gt;&lt;p&gt;پدر گفت: اگر پند من بشنوی&lt;/p&gt;&lt;p&gt;یکی سنگ برداشت باید قوی&lt;/p&gt;&lt;p&gt;زدن بر خر نامور چند بار&lt;/p&gt;&lt;p&gt;سر و دست و پهلوش کردن فگار&lt;/p&gt;&lt;p&gt;مگر کان فرومایهٔ زشت کیش&lt;/p&gt;&lt;p&gt;به کارش نیاید خر پشت ریش&lt;/p&gt;&lt;p&gt;چو خضر پیمبر که کشتی شکست&lt;/p&gt;&lt;p&gt;وز او دست جبار ظالم ببست&lt;/p&gt;&lt;p&gt;به سالی که در بحر کشتی گرفت&lt;/p&gt;&lt;p&gt;بسی سالها نام زشتی گرفت&lt;/p&gt;&lt;p&gt;تفو بر چنان ملک و دولت که راند&lt;/p&gt;&lt;p&gt;که شنعت بر او تا قیامت بماند&lt;/p&gt;&lt;p&gt;پسر چون شنید این حدیث از پدر&lt;/p&gt;&lt;p&gt;سر از خط فرمان نبردش به در&lt;/p&gt;&lt;p&gt;فرو کوفت بیچاره خر را به سنگ&lt;/p&gt;&lt;p&gt;خر از دست عاجز شد از پای لنگ&lt;/p&gt;&lt;p&gt;پدر گفتش اکنون سر خویش گیر&lt;/p&gt;&lt;p&gt;هر آن ره که می‌بایدت پیش گیر&lt;/p&gt;&lt;p&gt;پسر در پی کاروان اوفتاد&lt;/p&gt;&lt;p&gt;ز دشنام چندان که دانست داد&lt;/p&gt;&lt;p&gt;وز آن سو پدر روی در آستان&lt;/p&gt;&lt;p&gt;که یارب به سجادهٔ راستان&lt;/p&gt;&lt;p&gt;که چندان امانم ده از روزگار&lt;/p&gt;&lt;p&gt;کز این نحس ظالم بر آید دمار&lt;/p&gt;&lt;p&gt;اگر من نبینم مر او را هلاک&lt;/p&gt;&lt;p&gt;شب گور چشمم نخسبد به خاک&lt;/p&gt;&lt;p&gt;اگر مار زاید زن باردار&lt;/p&gt;&lt;p&gt;به از آدمی زادهٔ دیوسار&lt;/p&gt;&lt;p&gt;زن از مرد موذی به بسیار به&lt;/p&gt;&lt;p&gt;سگ از مردم مردم‌آزار به&lt;/p&gt;&lt;p&gt;مخنث که بیداد بر خود کند&lt;/p&gt;&lt;p&gt;از آن به که با دیگری بد کند&lt;/p&gt;&lt;p&gt;شه این جمله بشنید و چیزی نگفت&lt;/p&gt;&lt;p&gt;ببست اسب و سر بر نمد زین بخفت&lt;/p&gt;&lt;p&gt;همه شب به بیداری اختر شمرد&lt;/p&gt;&lt;p&gt;ز سودا و اندیشه خوابش نبرد&lt;/p&gt;&lt;p&gt;چو آواز مرغ سحر گوش کرد&lt;/p&gt;&lt;p&gt;پریشانی شب فراموش کرد&lt;/p&gt;&lt;p&gt;سواران همه شب همی تاختند&lt;/p&gt;&lt;p&gt;سحرگه پی اسب بشناختند&lt;/p&gt;&lt;p&gt;بر آن عرصه بر اسب دیدند شاه&lt;/p&gt;&lt;p&gt;پیاده دویدند یکسر سپاه&lt;/p&gt;&lt;p&gt;به خدمت نهادند سر بر زمین&lt;/p&gt;&lt;p&gt;چو دریا شد از موج لشکر، زمین&lt;/p&gt;&lt;p&gt;یکی گفتش از دوستان قدیم&lt;/p&gt;&lt;p&gt;که شب حاجبش بود و روزش ندیم&lt;/p&gt;&lt;p&gt;رعیت چه نزلت نهادند دوش؟&lt;/p&gt;&lt;p&gt;که ما را نه چشم آرمید و نه گوش&lt;/p&gt;&lt;p&gt;شهنشه نیارست کردن حدیث&lt;/p&gt;&lt;p&gt;که بر وی چه آمد ز خبث خبیث&lt;/p&gt;&lt;p&gt;هم آهسته سر برد پیش سرش&lt;/p&gt;&lt;p&gt;فرو گفت پنهان به گوش اندرش&lt;/p&gt;&lt;p&gt;کسم پای مرغی نیاورد پیش&lt;/p&gt;&lt;p&gt;ولی دست خر رفت از اندازه بیش&lt;/p&gt;&lt;p&gt;بزرگان نشستند و خوان خواستند&lt;/p&gt;&lt;p&gt;بخوردند و مجلس بیاراستند&lt;/p&gt;&lt;p&gt;چو شور و طرب در نهاد آمدش&lt;/p&gt;&lt;p&gt;ز دهقان دوشینه یاد آمدش&lt;/p&gt;&lt;p&gt;بفرمود و جستند و بستند سخت&lt;/p&gt;&lt;p&gt;به خواری فکندند در پای تخت&lt;/p&gt;&lt;p&gt;سیه دل برآهخت شمشیر تیز&lt;/p&gt;&lt;p&gt;ندانست بیچاره راه گریز&lt;/p&gt;&lt;p&gt;سر ناامیدی برآورد و گفت&lt;/p&gt;&lt;p&gt;نشاید شب گور در خانه خفت&lt;/p&gt;&lt;p&gt;نه تنها منت گفتم ای شهریار&lt;/p&gt;&lt;p&gt;که برگشته بختی و بد روزگار&lt;/p&gt;&lt;p&gt;چرا خشم بر من گرفتی و بس؟&lt;/p&gt;&lt;p&gt;منت پیش گفتم، همه خلق پس&lt;/p&gt;&lt;p&gt;چو بیداد کردی توقع مدار&lt;/p&gt;&lt;p&gt;که نامت به نیکی رود در دیار&lt;/p&gt;&lt;p&gt;ور ایدون که دشوارت آمد سخن&lt;/p&gt;&lt;p&gt;دگر هر چه دشوارت آید مکن&lt;/p&gt;&lt;p&gt;تو را چاره از ظلم برگشتن است&lt;/p&gt;&lt;p&gt;نه بیچاره بی‌گنه کشتن است&lt;/p&gt;&lt;p&gt;مرا پنج روز دگر مانده گیر&lt;/p&gt;&lt;p&gt;دو روز دگر عیش خوش رانده گیر&lt;/p&gt;&lt;p&gt;نماند ستمکار بد روزگار&lt;/p&gt;&lt;p&gt;بماند بر او لعنت پایدار&lt;/p&gt;&lt;p&gt;تو را نیک پند است اگر بشنوی&lt;/p&gt;&lt;p&gt;وگر نشنوی خود پشیمان شوی&lt;/p&gt;&lt;p&gt;بدان کی ستوده شود پادشاه&lt;/p&gt;&lt;p&gt;که خلقش ستایند در بارگاه؟&lt;/p&gt;&lt;p&gt;چه سود آفرین بر سر انجمن&lt;/p&gt;&lt;p&gt;پس چرخه نفرین کنان پیرزن؟&lt;/p&gt;&lt;p&gt;همی گفت و شمشیر بالای سر&lt;/p&gt;&lt;p&gt;سپر کرده جان پیش تیر قدر&lt;/p&gt;&lt;p&gt;نبینی که چون کارد بر سر بود&lt;/p&gt;&lt;p&gt;قلم را زبانش روان تر بود&lt;/p&gt;&lt;p&gt;شه از مستی غفلت آمد به هوش&lt;/p&gt;&lt;p&gt;به گوشش فرو گفت فرخ سروش&lt;/p&gt;&lt;p&gt;کز این پیر دست عقوبت بدار&lt;/p&gt;&lt;p&gt;یکی کشته گیر از هزاران هزار&lt;/p&gt;&lt;p&gt;زمانی سر اندر گریبان بماند&lt;/p&gt;&lt;p&gt;پس آن گه به عفو آستین برفشاند&lt;/p&gt;&lt;p&gt;به دستان خود بند از او برگرفت&lt;/p&gt;&lt;p&gt;سرش را ببوسید و در بر گرفت&lt;/p&gt;&lt;p&gt;بزرگیش بخشید و فرماندهی&lt;/p&gt;&lt;p&gt;ز شاخ امیدش برآمد بهی&lt;/p&gt;&lt;p&gt;به گیتی حکایت شد این داستان&lt;/p&gt;&lt;p&gt;رود نیکبخت از پی راستان&lt;/p&gt;&lt;p&gt;بیاموزی از عاقلان حسن خوی&lt;/p&gt;&lt;p&gt;نه چندان که از غافل عیب جوی&lt;/p&gt;&lt;p&gt;ز دشمن شنو سیرت خود که دوست&lt;/p&gt;&lt;p&gt;هرآنچ از تو آید به چشمش نکوست&lt;/p&gt;&lt;p&gt;وبال است دادن به رنجور قند&lt;/p&gt;&lt;p&gt;که داروی تلخش بود سودمند&lt;/p&gt;&lt;p&gt;ترش روی بهتر کند سرزنش&lt;/p&gt;&lt;p&gt;که یاران خوش طبع شیرین منش&lt;/p&gt;&lt;p&gt;از این به نصیحت نگوید کست&lt;/p&gt;&lt;p&gt;اگر عاقلی یک اشارت بست&lt;/p&gt;', 'مثنوی شماره ۲۸', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(386, '&lt;p&gt;چو دور خلافت به مأمون رسید&lt;/p&gt;&lt;p&gt;یکی ماه پیکر کنیزک خرید&lt;/p&gt;&lt;p&gt;به چهر آفتابی، به تن گلبنی&lt;/p&gt;&lt;p&gt;به عقل خردمند بازی کنی&lt;/p&gt;&lt;p&gt;به خون عزیزان فرو برده چنگ&lt;/p&gt;&lt;p&gt;سر انگشتها کرده عناب رنگ&lt;/p&gt;&lt;p&gt;بر ابروی عابد فریبش خضاب&lt;/p&gt;&lt;p&gt;چو قوس قزح بود بر آفتاب&lt;/p&gt;&lt;p&gt;شب خلوت آن لعبت حور زاد&lt;/p&gt;&lt;p&gt;مگر تن در آغوش مأمون نداد&lt;/p&gt;&lt;p&gt;گرفت آتش خشم در وی عظیم&lt;/p&gt;&lt;p&gt;سرش خواست کردن چو جوزا دو نیم&lt;/p&gt;&lt;p&gt;بگفتا سر اینک به شمشیر تیز&lt;/p&gt;&lt;p&gt;بینداز و با من مکن خفت و خیز&lt;/p&gt;&lt;p&gt;بگفت از چه بر دل گزند آمدت؟&lt;/p&gt;&lt;p&gt;چه خصلت ز من ناپسند آمدت؟&lt;/p&gt;&lt;p&gt;بگفت ار کشی ور شکافی سرم&lt;/p&gt;&lt;p&gt;ز بوی دهانت به رنج اندرم&lt;/p&gt;&lt;p&gt;کشد تیر پیکار و تیغ ستم&lt;/p&gt;&lt;p&gt;به یک بار و بوی دهن دم به دم&lt;/p&gt;&lt;p&gt;شنید این سخن سرور نیکبخت&lt;/p&gt;&lt;p&gt;برآشفت تند و برنجید سخت&lt;/p&gt;&lt;p&gt;همه شب در این فکر بود و نخفت&lt;/p&gt;&lt;p&gt;دگر روز با هوشمندان بگفت&lt;/p&gt;&lt;p&gt;طبیعت شناسان هر کشوری&lt;/p&gt;&lt;p&gt;سخن گفت با هر یک از هر دری&lt;/p&gt;&lt;p&gt;دلش گر چه در حال از او رنجه شد&lt;/p&gt;&lt;p&gt;دوا کرد و خوشبوی چون غنچه شد&lt;/p&gt;&lt;p&gt;پری چهره را همنشین کرد و دوست&lt;/p&gt;&lt;p&gt;که این عیب من گفت، یار من اوست&lt;/p&gt;&lt;p&gt;به نزد من آن کس نکوخواه توست&lt;/p&gt;&lt;p&gt;که گوید فلان خار در راه توست&lt;/p&gt;&lt;p&gt;به گمراه گفتن نکو می‌روی&lt;/p&gt;&lt;p&gt;جفایی تمام است و جوری قوی&lt;/p&gt;&lt;p&gt;هر آن گه که عیبت نگویند پیش&lt;/p&gt;&lt;p&gt;هنر دانی از جاهلی عیب خویش&lt;/p&gt;&lt;p&gt;مگو شهد شیرین شکر فایق است&lt;/p&gt;&lt;p&gt;کسی را که سقمونیا لایق است&lt;/p&gt;&lt;p&gt;چه خوش گفت یک روز دارو فروش:&lt;/p&gt;&lt;p&gt;شفا بایدت داروی تلخ نوش&lt;/p&gt;&lt;p&gt;اگر شربتی بایدت سودمند&lt;/p&gt;&lt;p&gt;ز سعدی ستان تلخ داروی پند&lt;/p&gt;&lt;p&gt;به پرویزن معرفت بیخته&lt;/p&gt;&lt;p&gt;به شهد ظرافت برآمیخته&lt;/p&gt;', 'مثنوی شماره ۲۹', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(387, '&lt;p&gt;شنیدم که از نیکمردی فقیر&lt;/p&gt;&lt;p&gt;دل آزرده شد پادشاهی کبیر&lt;/p&gt;&lt;p&gt;مگر بر زبانش حقی رفته بود&lt;/p&gt;&lt;p&gt;ز گردن‌کشی بر وی آشفته بود&lt;/p&gt;&lt;p&gt;به زندان فرستادش از بارگاه&lt;/p&gt;&lt;p&gt;که زورآزمای است بازوی جاه&lt;/p&gt;&lt;p&gt;ز یاران کسی گفتش اندر نهفت&lt;/p&gt;&lt;p&gt;مصالح نبود این سخن گفت، گفت&lt;/p&gt;&lt;p&gt;رسانیدن امر حق طاعت است&lt;/p&gt;&lt;p&gt;ز زندان نترسم که یک ساعت است&lt;/p&gt;&lt;p&gt;همان دم که در خفیه این راز رفت&lt;/p&gt;&lt;p&gt;حکایت به گوش ملک باز رفت&lt;/p&gt;&lt;p&gt;بخندید کاو ظن بیهوده برد&lt;/p&gt;&lt;p&gt;نداند که خواهد در این حبس مرد&lt;/p&gt;&lt;p&gt;غلامی به درویش برد این پیام&lt;/p&gt;&lt;p&gt;بگفتا به خسرو بگو ای غلام&lt;/p&gt;&lt;p&gt;مرا بار غم بر دل ریش نیست&lt;/p&gt;&lt;p&gt;که دنیا همین ساعتی بیش نیست&lt;/p&gt;&lt;p&gt;نه گر دستگیری کنی خرمم&lt;/p&gt;&lt;p&gt;نه گر سر بری بر دل آید غمم&lt;/p&gt;&lt;p&gt;تو گر کامرانی به فرمان و گنج&lt;/p&gt;&lt;p&gt;دگر کس فرومانده در ضعف و رنج&lt;/p&gt;&lt;p&gt;به دروازهٔ مرگ چون در شویم&lt;/p&gt;&lt;p&gt;به یک هفته با هم برابر شویم&lt;/p&gt;&lt;p&gt;منه دل بر این دولت پنج روز&lt;/p&gt;&lt;p&gt;به دود دل خلق، خود را مسوز&lt;/p&gt;&lt;p&gt;نه پیش از تو بیش از تو اندوختند&lt;/p&gt;&lt;p&gt;به بیداد کردن جهان سوختند؟&lt;/p&gt;&lt;p&gt;چنان زی که ذکرت به تحسین کنند&lt;/p&gt;&lt;p&gt;چو مردی، نه بر گور نفرین کنند&lt;/p&gt;&lt;p&gt;نباید به رسم بد آیین نهاد&lt;/p&gt;&lt;p&gt;که گویند لعنت بر آن، کاین نهاد&lt;/p&gt;&lt;p&gt;وگر بر سرآید خداوند زور&lt;/p&gt;&lt;p&gt;نه زیرش کند عاقبت خاک گور؟&lt;/p&gt;&lt;p&gt;بفرمود دلتنگ روی از جفا&lt;/p&gt;&lt;p&gt;که بیرون کنندش زبان از قفا&lt;/p&gt;&lt;p&gt;چنین گفت مرد حقایق شناس&lt;/p&gt;&lt;p&gt;کز این هم که گفتی ندارم هراس&lt;/p&gt;&lt;p&gt;من از بی زبانی ندارم غمی&lt;/p&gt;&lt;p&gt;که دانم که ناگفته داند همی&lt;/p&gt;&lt;p&gt;اگر بینوایی برم ور ستم&lt;/p&gt;&lt;p&gt;گرم عاقبت خیر باشد چه غم؟&lt;/p&gt;&lt;p&gt;عروسی بود نوبت ماتمت&lt;/p&gt;&lt;p&gt;گرت نیکروزی بود خاتمت&lt;/p&gt;', 'مثنوی شماره ۳۰', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(388, '&lt;p&gt;یکی مشت زن بخت و روزی نداشت&lt;/p&gt;&lt;p&gt;نه اسباب شامش مهیا نه چاشت&lt;/p&gt;&lt;p&gt;ز جور شکم گل کشیدی به پشت&lt;/p&gt;&lt;p&gt;که روزی محال است خوردن به مشت&lt;/p&gt;&lt;p&gt;مدام از پریشانی روزگار&lt;/p&gt;&lt;p&gt;دلش حسرت آورد و تن سوگوار&lt;/p&gt;&lt;p&gt;گهش جنگ با عالم خیره‌کش&lt;/p&gt;&lt;p&gt;گه از بخت شوریده، رویش ترش&lt;/p&gt;&lt;p&gt;گه از دیدن عیش شیرین خلق&lt;/p&gt;&lt;p&gt;فرو می‌شدی آب تلخش به حلق&lt;/p&gt;&lt;p&gt;گه از کار آشفته بگریستی&lt;/p&gt;&lt;p&gt;که کس دید از این تلخ‌تر زیستی؟&lt;/p&gt;&lt;p&gt;کسان شهد نوشند و مرغ و بره&lt;/p&gt;&lt;p&gt;مرا روی نان می‌نبیند تره&lt;/p&gt;&lt;p&gt;گر انصاف پرسی نه نیکوست این&lt;/p&gt;&lt;p&gt;برهنه من و گربه را پوستین&lt;/p&gt;&lt;p&gt;چه بودی که پایم در این کار گل&lt;/p&gt;&lt;p&gt;به گنجی فرو رفتی از کام دل!&lt;/p&gt;&lt;p&gt;مگر روزگاری هوس راندمی&lt;/p&gt;&lt;p&gt;ز خود گرد محنت بیفشاندمی&lt;/p&gt;&lt;p&gt;شنیدم که روزی زمین می‌شکافت&lt;/p&gt;&lt;p&gt;عظام زنخدان پوسیده یافت&lt;/p&gt;&lt;p&gt;به خاک اندرش عقد بگسیخته&lt;/p&gt;&lt;p&gt;گهرهای دندان فرو ریخته&lt;/p&gt;&lt;p&gt;دهان بی زبان پند می‌گفت و راز&lt;/p&gt;&lt;p&gt;که ای خواجه با بینوایی بساز&lt;/p&gt;&lt;p&gt;نه این است حال دهن زیر گل&lt;/p&gt;&lt;p&gt;شکر خورده انگار یا خون دل&lt;/p&gt;&lt;p&gt;غم از گردش روزگاران مدار&lt;/p&gt;&lt;p&gt;که بی ما بگردد بسی روزگار&lt;/p&gt;&lt;p&gt;همان لحظه کاین خاطرش روی داد&lt;/p&gt;&lt;p&gt;غم از خاطرش رخت یک سو نهاد&lt;/p&gt;&lt;p&gt;که ای نفس بی رای و تدبیر و هش&lt;/p&gt;&lt;p&gt;بکش بار تیمار و خود را مکش&lt;/p&gt;&lt;p&gt;اگر بنده‌ای بار بر سر برد&lt;/p&gt;&lt;p&gt;وگر سر به اوج فلک بر برد&lt;/p&gt;&lt;p&gt;در آن دم که حالش دگرگون شود&lt;/p&gt;&lt;p&gt;به مرگ از سرش هر دو بیرون شود&lt;/p&gt;&lt;p&gt;غم و شادمانی نماند ولیک&lt;/p&gt;&lt;p&gt;جزای عمل ماند و نام نیک&lt;/p&gt;&lt;p&gt;کرم پای دارد، نه دیهیم و تخت&lt;/p&gt;&lt;p&gt;بده کز تو این ماند ای نیکبخت&lt;/p&gt;&lt;p&gt;مکن تکیه بر ملک و جاه و حشم&lt;/p&gt;&lt;p&gt;که پیش از تو بوده‌ست و بعد از تو هم&lt;/p&gt;&lt;p&gt;خداوند دولت غم دین خورد&lt;/p&gt;&lt;p&gt;که دنیا به هر حال می‌بگذرد&lt;/p&gt;&lt;p&gt;نخواهی که ملکت برآید بهم&lt;/p&gt;&lt;p&gt;غم ملک و دین هر دو باید بهم&lt;/p&gt;&lt;p&gt;زرافشان، چو دنیا بخواهی گذاشت&lt;/p&gt;&lt;p&gt;که سعدی در افشاند اگر زر نداشت&lt;/p&gt;', 'مثنوی شماره ۳۱', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(389, '&lt;p&gt;حکایت کنند از جفاگستری&lt;/p&gt;&lt;p&gt;که فرماندهی داشت بر کشوری&lt;/p&gt;&lt;p&gt;در ایام او روز مردم چو شام&lt;/p&gt;&lt;p&gt;شب از بیم او خواب مردم حرام&lt;/p&gt;&lt;p&gt;همه روز نیکان از او در بلا&lt;/p&gt;&lt;p&gt;به شب دست پاکان از او بر دعا&lt;/p&gt;&lt;p&gt;گروهی بر شیخ آن روزگار&lt;/p&gt;&lt;p&gt;ز دست ستمگر گرستند زار&lt;/p&gt;&lt;p&gt;که ای پیر دانای فرخنده رای&lt;/p&gt;&lt;p&gt;بگوی این جوان را بترس از خدای&lt;/p&gt;&lt;p&gt;بگفتا دریغ آیدم نام دوست&lt;/p&gt;&lt;p&gt;که هر کس نه در خورد پیغام اوست&lt;/p&gt;&lt;p&gt;کسی را که بینی ز حق بر کران&lt;/p&gt;&lt;p&gt;منه با وی، ای خواجه، حق در میان&lt;/p&gt;&lt;p&gt;دریغ است با سفله گفت از علوم&lt;/p&gt;&lt;p&gt;که ضایع شود تخم در شوره بوم&lt;/p&gt;&lt;p&gt;چو در وی نگیرد عدو داندت&lt;/p&gt;&lt;p&gt;برنجد به جان و برنجاندت&lt;/p&gt;&lt;p&gt;تو را عادت، ای پادشه، حق روی است&lt;/p&gt;&lt;p&gt;دل مرد حق گوی از این جا قوی است&lt;/p&gt;&lt;p&gt;نگین خصلتی دارد ای نیکبخت&lt;/p&gt;&lt;p&gt;که در موم گیرد نه در سنگ سخت&lt;/p&gt;&lt;p&gt;عجب نیست گر ظالم از من به جان&lt;/p&gt;&lt;p&gt;برنجد که دزد است و من پاسبان&lt;/p&gt;&lt;p&gt;تو هم پاسبانی به انصاف و داد&lt;/p&gt;&lt;p&gt;که حفظ خدا پاسبان تو باد&lt;/p&gt;&lt;p&gt;تو را نیست منت ز روی قیاس&lt;/p&gt;&lt;p&gt;خداوند را من و فضل و سپاس&lt;/p&gt;&lt;p&gt;که در کار خیرت به خدمت بداشت&lt;/p&gt;&lt;p&gt;نه چون دیگرانت معطل گذاشت&lt;/p&gt;&lt;p&gt;همه کس به میدان کوشش درند&lt;/p&gt;&lt;p&gt;ولی گوی بخشش نه هر کس برند&lt;/p&gt;&lt;p&gt;تو حاصل نکردی به کوشش بهشت&lt;/p&gt;&lt;p&gt;خدا در تو خوی بهشتی بهشت&lt;/p&gt;&lt;p&gt;دلت روشن و وقت مجموع باد&lt;/p&gt;&lt;p&gt;قدم ثابت و پایه مرفوع باد&lt;/p&gt;&lt;p&gt;حیاتت خوش و رفتنت بر صواب&lt;/p&gt;&lt;p&gt;عبادت قبول و دعا مستجاب&lt;/p&gt;', 'مثنوی شماره ۳۲', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(390, '&lt;p&gt;همی تا برآید به تدبیر کار&lt;/p&gt;&lt;p&gt;مدارای دشمن به از کارزار&lt;/p&gt;&lt;p&gt;چو نتوان عدو را به قوت شکست&lt;/p&gt;&lt;p&gt;به نعمت بباید در فتنه بست&lt;/p&gt;&lt;p&gt;گر اندیشه باشد ز خصمت گزند&lt;/p&gt;&lt;p&gt;به تعویذ احسان زبانش ببند&lt;/p&gt;&lt;p&gt;عدو را به جای خسک زر بریز&lt;/p&gt;&lt;p&gt;که احسان کند کند، دندان تیز&lt;/p&gt;&lt;p&gt;چو دستی نشاید گزیدن، ببوس&lt;/p&gt;&lt;p&gt;که با غالبان چاره زرق است و لوس&lt;/p&gt;&lt;p&gt;به تدبیر رستم در آید به بند&lt;/p&gt;&lt;p&gt;که اسفندیارش نجست از کمند&lt;/p&gt;&lt;p&gt;عدو را به فرصت توان کند پوست&lt;/p&gt;&lt;p&gt;پس او را مدارا چنان کن که دوست&lt;/p&gt;&lt;p&gt;حذر کن ز پیکار کمتر کسی&lt;/p&gt;&lt;p&gt;که از قطره سیلاب دیدم بسی&lt;/p&gt;&lt;p&gt;مزن تا توانی بر ابرو گره&lt;/p&gt;&lt;p&gt;که دشمن اگرچه زبون، دوست به&lt;/p&gt;&lt;p&gt;بود دشمنش تازه و دوست ریش&lt;/p&gt;&lt;p&gt;کسی کش بود دشمن از دوست بیش&lt;/p&gt;&lt;p&gt;مزن با سپاهی ز خود بیشتر&lt;/p&gt;&lt;p&gt;که نتوان زد انگشت بر نیشتر&lt;/p&gt;&lt;p&gt;وگر زو تواناتری در نبرد&lt;/p&gt;&lt;p&gt;نه مردی است بر ناتوان زور کرد&lt;/p&gt;&lt;p&gt;اگر پیل زوری وگر شیر چنگ&lt;/p&gt;&lt;p&gt;به نزدیک من صلح بهتر که جنگ&lt;/p&gt;&lt;p&gt;چو دست از همه حیلتی در گسست&lt;/p&gt;&lt;p&gt;حلال است بردن به شمشیر دست&lt;/p&gt;&lt;p&gt;اگر صلح خواهد عدو سر مپیچ&lt;/p&gt;&lt;p&gt;وگر جنگ جوید عنان بر مپیچ&lt;/p&gt;&lt;p&gt;که گر وی ببندد در کارزار&lt;/p&gt;&lt;p&gt;تو را قدر و هیبت شود یک، هزار&lt;/p&gt;&lt;p&gt;ور او پای جنگ آورد در رکاب&lt;/p&gt;&lt;p&gt;نخواهد به حشر از تو داور حساب&lt;/p&gt;&lt;p&gt;تو هم جنگ را باش چون کینه خواست&lt;/p&gt;&lt;p&gt;که با کینه ور مهربانی خطاست&lt;/p&gt;&lt;p&gt;چو با سفله گویی به لطف و خوشی&lt;/p&gt;&lt;p&gt;فزون گرددش کبر و گردن کشی&lt;/p&gt;&lt;p&gt;به اسبان تازی و مردان مرد&lt;/p&gt;&lt;p&gt;بر آر از نهاد بداندیش گرد&lt;/p&gt;&lt;p&gt;و گر می بر آید به نرمی و هوش&lt;/p&gt;&lt;p&gt;به تندی و خشم و درشتی مکوش&lt;/p&gt;&lt;p&gt;چو دشمن به عجز اندر آمد ز در&lt;/p&gt;&lt;p&gt;نباید که پرخاش جویی دگر&lt;/p&gt;&lt;p&gt;چو زنهار خواهد کرم پیشه کن&lt;/p&gt;&lt;p&gt;ببخشای و از مکرش اندیشه کن&lt;/p&gt;&lt;p&gt;ز تدبیر پیر کهن بر مگرد&lt;/p&gt;&lt;p&gt;که کارآزموده بود سالخورد&lt;/p&gt;&lt;p&gt;در آرند بنیاد رویین ز پای&lt;/p&gt;&lt;p&gt;جوانان به نیروی و پیران به رای&lt;/p&gt;&lt;p&gt;بیندیش در قلب هیجا مفر&lt;/p&gt;&lt;p&gt;چه دانی که زان که باشد ظفر؟&lt;/p&gt;&lt;p&gt;چو بینی که لشکر ز هم دست داد&lt;/p&gt;&lt;p&gt;به تنها مده جان شیرین به باد&lt;/p&gt;&lt;p&gt;اگر بر کناری به رفتن بکوش&lt;/p&gt;&lt;p&gt;وگر در میان لبس دشمن بپوش&lt;/p&gt;&lt;p&gt;وگر خود هزاری و دشمن دویست&lt;/p&gt;&lt;p&gt;چو شب شد در اقلیم دشمن مایست&lt;/p&gt;&lt;p&gt;شب تیره پنجه سوار از کمین&lt;/p&gt;&lt;p&gt;چو پانصد به هیبت بدرد زمین&lt;/p&gt;&lt;p&gt;چو خواهی بریدن به شب راهها&lt;/p&gt;&lt;p&gt;حذر کن نخست از کمینگاهها&lt;/p&gt;&lt;p&gt;میان دو لشکر چو یک روز راه&lt;/p&gt;&lt;p&gt;بماند، بزن خیمه بر جایگاه&lt;/p&gt;&lt;p&gt;گر او پیشدستی کند غم مدار&lt;/p&gt;&lt;p&gt;ور افراسیاب است مغزش بر آر&lt;/p&gt;&lt;p&gt;ندانی که لشکر چو یک روزه راند&lt;/p&gt;&lt;p&gt;سر پنجهٔ زورمندش نماند&lt;/p&gt;&lt;p&gt;تو آسوده بر لشکر مانده زن&lt;/p&gt;&lt;p&gt;که نادان ستم کرد بر خویشتن&lt;/p&gt;&lt;p&gt;چو دشمن شکستی بیفکن علم&lt;/p&gt;&lt;p&gt;که بازش نیاید جراحت به هم&lt;/p&gt;&lt;p&gt;بسی در قفای هزیمت مران&lt;/p&gt;&lt;p&gt;نباید که دور افتی از یاوران&lt;/p&gt;&lt;p&gt;هوا بینی از گرد هیجا چو میغ&lt;/p&gt;&lt;p&gt;بگیرند گردت به زوبین و تیغ&lt;/p&gt;&lt;p&gt;به دنبال غارت نراند سپاه&lt;/p&gt;&lt;p&gt;که خالی بماند پس پشت شاه&lt;/p&gt;&lt;p&gt;سپه را نگهبانی شهریار&lt;/p&gt;&lt;p&gt;به از جنگ در حلقهٔ کارزار&lt;/p&gt;', 'مثنوی شماره ۳۳', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(391, '&lt;p&gt;دلاور که باری تهور نمود&lt;/p&gt;&lt;p&gt;بباید به مقدارش اندر فزود&lt;/p&gt;&lt;p&gt;که بار دگر دل نهد بر هلاک&lt;/p&gt;&lt;p&gt;ندارد ز پیکار یأجوج باک&lt;/p&gt;&lt;p&gt;سپاهی در آسودگی خوش بدار&lt;/p&gt;&lt;p&gt;که در حالت سختی آید به کار&lt;/p&gt;&lt;p&gt;سپاهی که کارش نباشد به برگ&lt;/p&gt;&lt;p&gt;چرا دل نهد روز هیجا به مرگ؟&lt;/p&gt;&lt;p&gt;کنون دست مردان جنگی ببوس&lt;/p&gt;&lt;p&gt;نه آنگه که دشمن فرو کوفت کوس&lt;/p&gt;&lt;p&gt;نواحی ملک از کف بدسگال&lt;/p&gt;&lt;p&gt;به لشکر نگه دار و لشکر به مال&lt;/p&gt;&lt;p&gt;ملک را بود بر عدو دست، چیر&lt;/p&gt;&lt;p&gt;چو لشکر دل آسوده باشند و سیر&lt;/p&gt;&lt;p&gt;بهای سر خویشتن می‌خورد&lt;/p&gt;&lt;p&gt;نه انصاف باشد که سختی برد&lt;/p&gt;&lt;p&gt;چو دارند گنج از سپاهی دریغ&lt;/p&gt;&lt;p&gt;دریغ آیدش دست بردن به تیغ&lt;/p&gt;&lt;p&gt;چه مردی کند در صف کارزار&lt;/p&gt;&lt;p&gt;که دستش تهی باشد و کار، زار&lt;/p&gt;', 'مثنوی شماره ۳۴', 'سعدی', 'بوستان', 'مثنوی', 6, 9);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(392, '&lt;p&gt;به پیکار دشمن دلیران فرست&lt;/p&gt;&lt;p&gt;هژبران به ناورد شیران فرست&lt;/p&gt;&lt;p&gt;به رای جهاندیدگان کار کن&lt;/p&gt;&lt;p&gt;که صید آزموده‌ست گرگ کهن&lt;/p&gt;&lt;p&gt;مترس از جوانان شمشیر زن&lt;/p&gt;&lt;p&gt;حذر کن ز پیران بسیار فن&lt;/p&gt;&lt;p&gt;جوانان پیل افکن شیرگیر&lt;/p&gt;&lt;p&gt;ندانند دستان روباه پیر&lt;/p&gt;&lt;p&gt;خردمند باشد جهاندیده مرد&lt;/p&gt;&lt;p&gt;که بسیار گرم آزموده‌ست و سرد&lt;/p&gt;&lt;p&gt;جوانان شایستهٔ بخت ور&lt;/p&gt;&lt;p&gt;ز گفتار پیران نپیچند سر&lt;/p&gt;&lt;p&gt;گرت مملکت باید آراسته&lt;/p&gt;&lt;p&gt;مده کار معظم به نوخاسته&lt;/p&gt;&lt;p&gt;سپه را مکن پیشرو جز کسی&lt;/p&gt;&lt;p&gt;که در جنگها بوده باشد بسی&lt;/p&gt;&lt;p&gt;به خردان مفرمای کار درشت&lt;/p&gt;&lt;p&gt;که سندان نشاید شکستن به مشت&lt;/p&gt;&lt;p&gt;رعیت نوازی و سر لشکری&lt;/p&gt;&lt;p&gt;نه کاری است بازیچه و سرسری&lt;/p&gt;&lt;p&gt;نخواهی که ضایع شود روزگار&lt;/p&gt;&lt;p&gt;به ناکاردیده مفرمای کار&lt;/p&gt;&lt;p&gt;نتابد سگ صید روی از پلنگ&lt;/p&gt;&lt;p&gt;ز روبه رمد شیر نادیده جنگ&lt;/p&gt;&lt;p&gt;چو پرورده باشد پسر در شکار&lt;/p&gt;&lt;p&gt;نترسد چو پیش آیدش کارزار&lt;/p&gt;&lt;p&gt;به کشتی و نخجیر و آماج و گوی&lt;/p&gt;&lt;p&gt;دلاور شود مرد پرخاشجوی&lt;/p&gt;&lt;p&gt;به گرمابه پرورده و عیش و ناز&lt;/p&gt;&lt;p&gt;برنجد چو بیند در جنگ باز&lt;/p&gt;&lt;p&gt;دو مردش نشانند بر پشت زین&lt;/p&gt;&lt;p&gt;بود کش زند کودکی بر زمین&lt;/p&gt;&lt;p&gt;یکی را که دیدی تو در جنگ پشت&lt;/p&gt;&lt;p&gt;بکش گر عدو در مصافش نکشت&lt;/p&gt;&lt;p&gt;مخنث به از مرد شمشیر زن&lt;/p&gt;&lt;p&gt;که روز وغا سر بتابد چو زن&lt;/p&gt;&lt;p&gt;چه خوش گفت گرگین به فرزند خویش&lt;/p&gt;&lt;p&gt;چو قربان پیکار بربست و کیش&lt;/p&gt;&lt;p&gt;اگر چون زنان جست خواهی گریز&lt;/p&gt;&lt;p&gt;مرو آب مردان جنگی مریز&lt;/p&gt;&lt;p&gt;سواری که در جنگ بنمود پشت&lt;/p&gt;&lt;p&gt;نه خود را که نام آوران را بکشت&lt;/p&gt;&lt;p&gt;شجاعت نیاید مگر زآن دو یار&lt;/p&gt;&lt;p&gt;که افتند در حلقهٔ کارزار&lt;/p&gt;&lt;p&gt;دو همجنس همسفرهٔ همزبان&lt;/p&gt;&lt;p&gt;بکوشند در قلب هیجا به جان&lt;/p&gt;&lt;p&gt;که تنگ آیدش رفتن از پیش تیر&lt;/p&gt;&lt;p&gt;برادر به چنگال دشمن اسیر&lt;/p&gt;&lt;p&gt;چو بینی که یاران نباشند یار&lt;/p&gt;&lt;p&gt;هزیمت ز میدان غنیمت شمار&lt;/p&gt;', 'مثنوی شماره ۳۵', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(393, '&lt;p&gt;دو تن، پرور ای شاه کشور گشای&lt;/p&gt;&lt;p&gt;یکی اهل رزم و دگر اهل رای&lt;/p&gt;&lt;p&gt;ز نام آوران گوی دولت برند&lt;/p&gt;&lt;p&gt;که دانا و شمشیر زن پرورند&lt;/p&gt;&lt;p&gt;هر آن کاو قلم را نورزید و تیغ&lt;/p&gt;&lt;p&gt;بر او گر بمیرد مگو ای دریغ&lt;/p&gt;&lt;p&gt;قلم زن نکودار و شمشیر زن&lt;/p&gt;&lt;p&gt;نه مطرب که مردی نیاید ز زن&lt;/p&gt;&lt;p&gt;نه مردی است دشمن در اسباب جنگ&lt;/p&gt;&lt;p&gt;تو مدهوش ساقی و آواز چنگ&lt;/p&gt;&lt;p&gt;بسا اهل دولت به بازی نشست&lt;/p&gt;&lt;p&gt;که دولت برفتش به بازی ز دست&lt;/p&gt;', 'مثنوی شماره ۳۶', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(394, '&lt;p&gt;نگویم ز جنگ بد اندیش ترس&lt;/p&gt;&lt;p&gt;در آوازهٔ صلح از او بیش ترس&lt;/p&gt;&lt;p&gt;بسا کس به روز آیت صلح خواند&lt;/p&gt;&lt;p&gt;چو شب شد سپه بر سر خفته راند&lt;/p&gt;&lt;p&gt;زره پوش خسبند مرد اوژنان&lt;/p&gt;&lt;p&gt;که بستر بود خوابگاه زنان&lt;/p&gt;&lt;p&gt;به خیمه درون مرد شمشیر زن&lt;/p&gt;&lt;p&gt;برهنه نخسبد چو در خانه زن&lt;/p&gt;&lt;p&gt;بباید نهان جنگ را ساختن&lt;/p&gt;&lt;p&gt;که دشمن نهان آورد تاختن&lt;/p&gt;&lt;p&gt;حذر کار مردان کار آگه است&lt;/p&gt;&lt;p&gt;یزک سد رویین لشکر گه است&lt;/p&gt;', 'مثنوی شماره ۳۷', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(395, '&lt;p&gt;میان دو بد خواه کوتاه دست&lt;/p&gt;&lt;p&gt;نه فرزانگی باشد ایمن نشست&lt;/p&gt;&lt;p&gt;که گر هر دو باهم سگالند راز&lt;/p&gt;&lt;p&gt;شود دست کوتاه ایشان دراز&lt;/p&gt;&lt;p&gt;یکی را به نیرنگ مشغول دار&lt;/p&gt;&lt;p&gt;دگر را برآور ز هستی دمار&lt;/p&gt;&lt;p&gt;اگر دشمنی پیش گیرد ستیز&lt;/p&gt;&lt;p&gt;به شمشیر تدبیر خونش بریز&lt;/p&gt;&lt;p&gt;برو دوستی گیر با دشمنش&lt;/p&gt;&lt;p&gt;که زندان شود پیرهن بر تنش&lt;/p&gt;&lt;p&gt;چو در لشکر دشمن افتد خلاف&lt;/p&gt;&lt;p&gt;تو بگذار شمشیر خود در غلاف&lt;/p&gt;&lt;p&gt;چو گرگان پسندند بر هم گزند&lt;/p&gt;&lt;p&gt;بر آساید اندر میان گوسفند&lt;/p&gt;&lt;p&gt;چو دشمن به دشمن بود مشتغل&lt;/p&gt;&lt;p&gt;تو با دوست بنشین به آرام دل&lt;/p&gt;', 'مثنوی شماره ۳۸ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(396, '&lt;p&gt;چو شمشیر پیکار برداشتی&lt;/p&gt;&lt;p&gt;نگه دار پنهان ره آشتی&lt;/p&gt;&lt;p&gt;که لشکر شکوفان مغفر شکاف&lt;/p&gt;&lt;p&gt;نهان صلح جستند و پیدا مصاف&lt;/p&gt;&lt;p&gt;دل مرد میدان نهانی بجوی&lt;/p&gt;&lt;p&gt;که باشد که در پایت افتد چو گوی&lt;/p&gt;&lt;p&gt;چو سالاری از دشمن افتد به چنگ&lt;/p&gt;&lt;p&gt;به کشتن درش کرد باید درنگ&lt;/p&gt;&lt;p&gt;که افتد کز این نیمه هم سروری&lt;/p&gt;&lt;p&gt;بماند گرفتار در چنبری&lt;/p&gt;&lt;p&gt;اگر کشتی این بندی ریش را&lt;/p&gt;&lt;p&gt;نبینی دگر بندی خویش را&lt;/p&gt;&lt;p&gt;نترسد که دورانش بندی کند&lt;/p&gt;&lt;p&gt;که بر بندیان زورمندی کند؟&lt;/p&gt;&lt;p&gt;کسی بندیان را بود دستگیر&lt;/p&gt;&lt;p&gt;که خود بوده باشد به بندی اسیر&lt;/p&gt;&lt;p&gt;اگر سر نهد بر خطت سروری&lt;/p&gt;&lt;p&gt;چو نیکش بداری، نهد دیگری&lt;/p&gt;&lt;p&gt;اگر خفیه ده دل بدست آوری&lt;/p&gt;&lt;p&gt;از آن به که صد ره شبیخون بری&lt;/p&gt;', 'مثنوی شماره ۳۹ ', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(397, '&lt;p&gt;گرت خویش دشمن شود دوستدار&lt;/p&gt;&lt;p&gt;ز تلبیسش ایمن مشو زینهار&lt;/p&gt;&lt;p&gt;که گردد درونش به کین تو ریش&lt;/p&gt;&lt;p&gt;چو یاد آیدش مهر پیوند خویش&lt;/p&gt;&lt;p&gt;بد اندیش را لفظ شیرین مبین&lt;/p&gt;&lt;p&gt;که ممکن بود زهر در انگبین&lt;/p&gt;&lt;p&gt;کسی جان از آسیب دشمن ببرد&lt;/p&gt;&lt;p&gt;که مر دوستان را به دشمن شمرد&lt;/p&gt;&lt;p&gt;نگه دارد آن شوخ در کیسه در&lt;/p&gt;&lt;p&gt;که بیند همه خلق را کیسه بر&lt;/p&gt;&lt;p&gt;سپاهی که عاصی شود در امیر&lt;/p&gt;&lt;p&gt;ورا تا توانی به خدمت مگیر&lt;/p&gt;&lt;p&gt;ندانست سالار خود را سپاس&lt;/p&gt;&lt;p&gt;تو را هم ندارد، ز غدرش هراس&lt;/p&gt;&lt;p&gt;به سوگند و عهد استوارش مدار&lt;/p&gt;&lt;p&gt;نگهبان پنهان بر او بر گمار&lt;/p&gt;&lt;p&gt;نو آموز را ریسمان کن دراز&lt;/p&gt;&lt;p&gt;نه بگسل که دیگر نبینیش باز&lt;/p&gt;&lt;p&gt;چو اقلیم دشمن به جنگ و حصار&lt;/p&gt;&lt;p&gt;گرفتی، به زندانیانش سپار&lt;/p&gt;&lt;p&gt;که بندی چو دندان به خون در برد&lt;/p&gt;&lt;p&gt;ز حلقوم بیدادگر خون خورد&lt;/p&gt;&lt;p&gt;چو بر کندی از دست دشمن دیار&lt;/p&gt;&lt;p&gt;رعیت به سامان تر از وی بدار&lt;/p&gt;&lt;p&gt;که گر باز کوبد در کارزار&lt;/p&gt;&lt;p&gt;بر آرند عام از دماغش دمار&lt;/p&gt;&lt;p&gt;وگر شهریان را رسانی گزند&lt;/p&gt;&lt;p&gt;در شهر بر روی دشمن مبند&lt;/p&gt;&lt;p&gt;مگو دشمن تیغ زن بر در است&lt;/p&gt;&lt;p&gt;که انباز دشمن به شهر اندر است&lt;/p&gt;', 'مثنوی شماره ۴۰', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(398, '&lt;p&gt;به تدبیر جنگ بد اندیش کوش&lt;/p&gt;&lt;p&gt;مصالح بیندیش و نیت بپوش&lt;/p&gt;&lt;p&gt;منه در میان راز با هر کسی&lt;/p&gt;&lt;p&gt;که جاسوس هم کاسه دیدم بسی&lt;/p&gt;&lt;p&gt;سکندر که با شرقیان حرب داشت&lt;/p&gt;&lt;p&gt;در خیمه گویند در غرب داشت&lt;/p&gt;&lt;p&gt;چو بهمن به زاولستان خواست شد&lt;/p&gt;&lt;p&gt;چپ آوازه افکند و از راست شد&lt;/p&gt;&lt;p&gt;اگر جز تو داند که عزم تو چیست&lt;/p&gt;&lt;p&gt;بر آن رای و دانش بباید گریست&lt;/p&gt;&lt;p&gt;کرم کن، نه پرخاش و کین‌آوری&lt;/p&gt;&lt;p&gt;که عالم به زیر نگین آوری&lt;/p&gt;&lt;p&gt;چو کاری بر آید به لطف و خوشی&lt;/p&gt;&lt;p&gt;چه حاجت به تندی و گردن کشی؟&lt;/p&gt;&lt;p&gt;نخواهی که باشد دلت دردمند&lt;/p&gt;&lt;p&gt;دل دردمندان بر آور ز بند&lt;/p&gt;&lt;p&gt;به بازو توانا نباشد سپاه&lt;/p&gt;&lt;p&gt;برو همت از ناتوانان بخواه&lt;/p&gt;&lt;p&gt;دعای ضعیفان امیدوار&lt;/p&gt;&lt;p&gt;ز بازوی مردی به آید به کار&lt;/p&gt;&lt;p&gt;هر آن کاستعانت به درویش برد&lt;/p&gt;&lt;p&gt;اگر بر فریدون زد از پیش برد&lt;/p&gt;', 'مثنوی شماره ۴۱', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(399, '&lt;p&gt;&lt;br&gt;خدا را ندانست و طاعت نکرد&lt;/p&gt;&lt;p&gt;که بر بخت و روزی قناعت نکرد&lt;/p&gt;&lt;p&gt;قناعت توانگر کند مرد را&lt;/p&gt;&lt;p&gt;خبر کن حریص جهانگرد را&lt;/p&gt;&lt;p&gt;سکونی به دست آور ای بی ثبات&lt;/p&gt;&lt;p&gt;که بر سنگ گردان نروید نبات&lt;/p&gt;&lt;p&gt;مپرور تن ار مرد رای و هشی&lt;/p&gt;&lt;p&gt;که او را چو می‌پروری می‌کشی&lt;/p&gt;&lt;p&gt;خردمند مردم هنر پرورند&lt;/p&gt;&lt;p&gt;که تن پروران از هنر لاغرند&lt;/p&gt;&lt;p&gt;کسی سیرت آدمی گوش کرد&lt;/p&gt;&lt;p&gt;که اول سگ نفس خاموش کرد&lt;/p&gt;&lt;p&gt;خور و خواب تنها طریق دد است&lt;/p&gt;&lt;p&gt;بر این بودن آیین نابخرد است&lt;/p&gt;&lt;p&gt;خنک نیکبختی که در گوشه‌ای&lt;/p&gt;&lt;p&gt;به دست آرد از معرفت توشه‌ای&lt;/p&gt;&lt;p&gt;بر آنان که شد سر حق آشکار&lt;/p&gt;&lt;p&gt;نکردند باطل بر او اختیار&lt;/p&gt;&lt;p&gt;ولیکن چو ظلمت نداند ز نور&lt;/p&gt;&lt;p&gt;چه دیدار دیوش چه رخسار حور&lt;/p&gt;&lt;p&gt;تو خود را از آن در چه انداختی&lt;/p&gt;&lt;p&gt;که چه را ز ره باز نشناختی&lt;/p&gt;&lt;p&gt;بر اوج فلک چون پرد جره باز&lt;/p&gt;&lt;p&gt;که در شهپرش بسته‌ای سنگ آز؟&lt;/p&gt;&lt;p&gt;گرش دامن از چنگ شهوت رها&lt;/p&gt;&lt;p&gt;کنی، رفت تا سدرةالمنتهی&lt;/p&gt;&lt;p&gt;به کم کردن از عادت خویش خورد&lt;/p&gt;&lt;p&gt;توان خویشتن را ملک خوی کرد&lt;/p&gt;&lt;p&gt;کجا سیر وحشی رسد در ملک&lt;/p&gt;&lt;p&gt;نشاید پرید از ثری بر فلک&lt;/p&gt;&lt;p&gt;نخست آدمی سیرتی پیشه کن&lt;/p&gt;&lt;p&gt;پس آن گه ملک خویی اندیشه کن&lt;/p&gt;&lt;p&gt;تو بر کرهٔ توسنی بر کمر&lt;/p&gt;&lt;p&gt;نگر تا نپیچد ز حکم تو سر&lt;/p&gt;&lt;p&gt;که گر پالهنگ از کفت در گسیخت&lt;/p&gt;&lt;p&gt;تن خویشتن کشت و خون تو ریخت&lt;/p&gt;&lt;p&gt;به اندازه خور زاد اگر مردمی&lt;/p&gt;&lt;p&gt;چنین پر شکم، آدمی یا خمی؟&lt;/p&gt;&lt;p&gt;درون جای قوت است و ذکر و نفس&lt;/p&gt;&lt;p&gt;تو پنداری از بهر نان است و بس&lt;/p&gt;&lt;p&gt;کجا ذکر گنجد در انبان آز؟&lt;/p&gt;&lt;p&gt;به سختی نفس می‌کند پا دراز&lt;/p&gt;&lt;p&gt;ندارند تن پروران آگهی&lt;/p&gt;&lt;p&gt;که پر معده باشد ز حکمت تهی&lt;/p&gt;&lt;p&gt;دو چشم و شکم پر نگردد به هیچ&lt;/p&gt;&lt;p&gt;تهی بهتر این رودهٔ پیچ پیچ&lt;/p&gt;&lt;p&gt;چو دوزخ که سیرش کنند از وقید&lt;/p&gt;&lt;p&gt;دگر بانگ دارد که هل من مزید؟&lt;/p&gt;&lt;p&gt;همی میردت عیسی از لاغری&lt;/p&gt;&lt;p&gt;تو در بند آنی که خر پروری&lt;/p&gt;&lt;p&gt;به دین، ای فرومایه، دنیا مخر&lt;/p&gt;&lt;p&gt;تو خر را به انجیل عیسی مخر&lt;/p&gt;&lt;p&gt;مگر می‌نبینی که دد را و دام&lt;/p&gt;&lt;p&gt;نینداخت جز حرص خوردن به دام؟&lt;/p&gt;&lt;p&gt;پلنگی که گردن کشد بر وحوش&lt;/p&gt;&lt;p&gt;به دام افتد از بهر خوردن چو موش&lt;/p&gt;&lt;p&gt;چو موش آن که نان و پنیرش خوری&lt;/p&gt;&lt;p&gt;به دامش در افتی و تیرش خوری&lt;/p&gt;', 'مثنوی شماره ۴۲', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(400, '&lt;p&gt;مرا حاجیی شانهٔ عاج داد&lt;/p&gt;&lt;p&gt;که رحمت بر اخلاق حجاج باد&lt;/p&gt;&lt;p&gt;شنیدم که باری سگم خوانده بود&lt;/p&gt;&lt;p&gt;که از من به نوعی دلش مانده بود&lt;/p&gt;&lt;p&gt;بینداختم شانه کاین استخوان&lt;/p&gt;&lt;p&gt;نمی‌بایدم دیگرم سگ مخوان&lt;/p&gt;&lt;p&gt;مپندار چون سرکهٔ خود خورم&lt;/p&gt;&lt;p&gt;که جور خداوند حلوا برم&lt;/p&gt;&lt;p&gt;قناعت کن ای نفس بر اندکی&lt;/p&gt;&lt;p&gt;که سلطان و درویش بینی یکی&lt;/p&gt;&lt;p&gt;چرا پیش خسرو به خواهش روی&lt;/p&gt;&lt;p&gt;چو یک سو نهادی طمع، خسروی&lt;/p&gt;&lt;p&gt;وگر خود پرستی شکم طبله کن&lt;/p&gt;&lt;p&gt;در خانهٔ این و آن قبله کن&lt;/p&gt;', 'مثنوی شماره ۴۳', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(401, '&lt;p&gt;چه آوردم از بصره دانی عجب&lt;/p&gt;&lt;p&gt;حدیثی که شیرین تر است از رطب&lt;/p&gt;&lt;p&gt;تنی چند در خرقه راستان&lt;/p&gt;&lt;p&gt;گذشتیم بر طرف خرماستان&lt;/p&gt;&lt;p&gt;یکی در میان معده انبار بود&lt;/p&gt;&lt;p&gt;ز پر خواری خویش بس خوار بود&lt;/p&gt;&lt;p&gt;میان بست مسکین و شد بر درخت&lt;/p&gt;&lt;p&gt;وز آنجا به گردن در افتاد سخت&lt;/p&gt;&lt;p&gt;نه هر بار خرما توان خورد و برد&lt;/p&gt;&lt;p&gt;لت انبان بد عاقبت خورد و مرد&lt;/p&gt;&lt;p&gt;رئیس ده آمد که این را که کشت؟&lt;/p&gt;&lt;p&gt;بگفتم مزن بانگ بر ما درشت&lt;/p&gt;&lt;p&gt;شکم دامن اندر کشیدش ز شاخ&lt;/p&gt;&lt;p&gt;بود تنگدل رودگانی فراخ&lt;/p&gt;&lt;p&gt;شکم بند دست است و زنجیر پای&lt;/p&gt;&lt;p&gt;شکم بنده نادر پرستد خدای&lt;/p&gt;&lt;p&gt;سراسر شکم شد ملخ لاجرم&lt;/p&gt;&lt;p&gt;به پایش کشد مور کوچک شکم&lt;/p&gt;&lt;p&gt;برو اندرونی به دست آر، پاک&lt;/p&gt;&lt;p&gt;شکم پر نخواهد شد الّا به خاک&lt;/p&gt;', 'مثنوی شماره ۴۴', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(402, '&lt;p&gt;شکم صوفیی را زبون کرد و فرج&lt;/p&gt;&lt;p&gt;دو دینار بر هر دوان کرد خرج&lt;/p&gt;&lt;p&gt;یکی گفتش از دوستان در نهفت&lt;/p&gt;&lt;p&gt;چه کردی بدین هر دو دینار؟ گفت&lt;/p&gt;&lt;p&gt;به دیناری از پشت راندم نشاط&lt;/p&gt;&lt;p&gt;به دیگر، شکم را کشیدم سماط&lt;/p&gt;&lt;p&gt;فرومایگی کردم و ابلهی&lt;/p&gt;&lt;p&gt;که این همچنان پر نشد وآن تهی&lt;/p&gt;&lt;p&gt;غذا گر لطیف است و گر سرسری&lt;/p&gt;&lt;p&gt;چو دیرت به دست اوفتد خوش خوری&lt;/p&gt;&lt;p&gt;سر آنگه به بالین نهد هوشمند&lt;/p&gt;&lt;p&gt;که خوابش به قهر آورد در کمند&lt;/p&gt;&lt;p&gt;مجال سخن تا نیابی مگوی&lt;/p&gt;&lt;p&gt;چو میدان نبینی نگه دار گوی&lt;/p&gt;&lt;p&gt;وز اندازه بیرون، مرو پیش زن&lt;/p&gt;&lt;p&gt;نه دیوانه‌ای تیغ بر خود مزن&lt;/p&gt;&lt;p&gt;به بی رغبتی شهوت انگیختن&lt;/p&gt;&lt;p&gt;به رغبت بود خون خود ریختن&lt;/p&gt;', 'مثنوی شماره ۴۵', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(403, '&lt;p&gt;یکی نیشکر داشت بر طبقری&lt;/p&gt;&lt;p&gt;چپ و راست گردیده بر مشتری&lt;/p&gt;&lt;p&gt;به صاحبدلی گفت در کنج ده&lt;/p&gt;&lt;p&gt;که بستان و چون دست یابی بده&lt;/p&gt;&lt;p&gt;بگفت آن خردمند زیبا سرشت&lt;/p&gt;&lt;p&gt;جوابی که بر دیده باید نبشت&lt;/p&gt;&lt;p&gt;تو را صبر بر من نباشد مگر&lt;/p&gt;&lt;p&gt;ولیکن مرا باشد از نیشکر&lt;/p&gt;&lt;p&gt;حلاوت ندارد شکر در نیش&lt;/p&gt;&lt;p&gt;چو باشد تقاضای تلخ از پیش&lt;/p&gt;', 'مثنوی شماره ۴۶', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(404, '&lt;p&gt;یکی سلطنت ران صاحب شکوه&lt;/p&gt;&lt;p&gt;فرو خواست رفت آفتابش به کوه&lt;/p&gt;&lt;p&gt;به شیخی در آن بقعه کشور گذاشت&lt;/p&gt;&lt;p&gt;که در دوره قائم مقامی نداشت&lt;/p&gt;&lt;p&gt;چو خلوت نشین کوس دولت شنید&lt;/p&gt;&lt;p&gt;دگر ذوق در کنج خلوت ندید&lt;/p&gt;&lt;p&gt;چپ و راست لشکر کشیدن گرفت&lt;/p&gt;&lt;p&gt;دل پردلان زو رمیدن گرفت&lt;/p&gt;&lt;p&gt;چنان سخت بازو شد و تیز چنگ&lt;/p&gt;&lt;p&gt;که با جنگجویان طلب کرد جنگ&lt;/p&gt;&lt;p&gt;ز قوم پراکنده خلقی بکشت&lt;/p&gt;&lt;p&gt;دگر جمع گشتند و هم رای و پشت&lt;/p&gt;&lt;p&gt;چنان در حصارش کشیدند تنگ&lt;/p&gt;&lt;p&gt;که عاجز شد از تیرباران و سنگ&lt;/p&gt;&lt;p&gt;بر نیکمردی فرستاد کس&lt;/p&gt;&lt;p&gt;که صعبم فرومانده، فریاد رس&lt;/p&gt;&lt;p&gt;به همت مدد کن که شمشیر و تیر&lt;/p&gt;&lt;p&gt;نه در هر وغایی بود دستگیر&lt;/p&gt;&lt;p&gt;چو بشنید عابد بخندید و گفت&lt;/p&gt;&lt;p&gt;چرا نیم نانی نخورد و نخفت؟&lt;/p&gt;&lt;p&gt;ندانست قارون نعمت پرست&lt;/p&gt;&lt;p&gt;که گنج سلامت به کنج اندر است&lt;/p&gt;', 'مثنوی شماره ۴۷', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(405, '&lt;p&gt;کمال است در نفس مرد کریم&lt;/p&gt;&lt;p&gt;گرش زر نباشد چه نقصان و بیم؟&lt;/p&gt;&lt;p&gt;مپندار اگر سفله قارون شود&lt;/p&gt;&lt;p&gt;که طبع لئیمش دگرگون شود&lt;/p&gt;&lt;p&gt;وگر درنیابد کرم پیشه، نان&lt;/p&gt;&lt;p&gt;نهادش توانگر بود همچنان&lt;/p&gt;&lt;p&gt;مروت زمین است و سرمایه زرع&lt;/p&gt;&lt;p&gt;بده کاصل خالی نماند ز فرع&lt;/p&gt;&lt;p&gt;خدایی که از خاک مردم کند&lt;/p&gt;&lt;p&gt;عجب باشد ار مردمی گم کند&lt;/p&gt;&lt;p&gt;ز نعمت نهادن بلندی مجوی&lt;/p&gt;&lt;p&gt;که ناخوش کند آب استاده بوی&lt;/p&gt;&lt;p&gt;به بخشندگی کوش کآب روان&lt;/p&gt;&lt;p&gt;به سیلش مدد می‌رسد ز آسمان&lt;/p&gt;&lt;p&gt;گر از جاه و دولت بیفتد لئیم&lt;/p&gt;&lt;p&gt;دگر باره نادر شود مستقیم&lt;/p&gt;&lt;p&gt;وگر قیمتی گوهری غم مدار&lt;/p&gt;&lt;p&gt;که ضایع نگرداندت روزگار&lt;/p&gt;&lt;p&gt;کلوخ ار چه افتاده بینی به راه&lt;/p&gt;&lt;p&gt;نبینی که در وی کند کس نگاه&lt;/p&gt;&lt;p&gt;و گر خردهٔ زر ز دندان گاز&lt;/p&gt;&lt;p&gt;بیفتد، به شمعش بجویند باز&lt;/p&gt;&lt;p&gt;به در می‌کنند آبگینه ز سنگ&lt;/p&gt;&lt;p&gt;کجا ماند آیینه در زیر زنگ؟&lt;/p&gt;&lt;p&gt;هنر باید و فضل و دین و کمال&lt;/p&gt;&lt;p&gt;که گاه آید و گه رود جاه و مال&lt;/p&gt;', 'مثنوی شماره ۴۸', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(406, '&lt;p&gt;شنیدم ز پیران شیرین سخن&lt;/p&gt;&lt;p&gt;که بود اندر این شهر پیری کهن&lt;/p&gt;&lt;p&gt;بسی دیده شاهان و دوران و امر&lt;/p&gt;&lt;p&gt;سرآورده عمری ز تاریخ عمرو&lt;/p&gt;&lt;p&gt;درخت کهن میوه‌ای تازه داشت&lt;/p&gt;&lt;p&gt;که شهر از نکویی پرآوازه داشت&lt;/p&gt;&lt;p&gt;عجب در زنخدان آن دل فریب&lt;/p&gt;&lt;p&gt;که هرگز نبوده‌ست بر سرو سیب&lt;/p&gt;&lt;p&gt;ز شوخی و مردم خراشیدنش&lt;/p&gt;&lt;p&gt;فرج دید در سر تراشیدنش&lt;/p&gt;&lt;p&gt;به موسی، کهن عمر کوته امید&lt;/p&gt;&lt;p&gt;سرش کرد چون دست موسی سپید&lt;/p&gt;&lt;p&gt;ز سر تیزی آن آهنین دل که بود&lt;/p&gt;&lt;p&gt;به عیب پری‌رخ زبان برگشود&lt;/p&gt;&lt;p&gt;به مویی که کرد از نکوییش کم&lt;/p&gt;&lt;p&gt;نهادند حالی سرش در شکم&lt;/p&gt;&lt;p&gt;چو چنگ از خجالت سر خوبروی&lt;/p&gt;&lt;p&gt;نگونسار و در پیشش افتاده موی&lt;/p&gt;&lt;p&gt;یکی را که خاطر در او رفته بود&lt;/p&gt;&lt;p&gt;چو چشمان دلبندش آشفته بود&lt;/p&gt;&lt;p&gt;کسی گفت جور آزمودی و درد&lt;/p&gt;&lt;p&gt;دگر گرد سودای باطل مگرد&lt;/p&gt;&lt;p&gt;ز مهرش بگردان چو پروانه پشت&lt;/p&gt;&lt;p&gt;که مقراض، شمع جمالش بکشت&lt;/p&gt;&lt;p&gt;برآمد خروش از هوادار چست&lt;/p&gt;&lt;p&gt;که تردامنان را بود عهد سست&lt;/p&gt;&lt;p&gt;پسر خوش منش باید و خوبروی&lt;/p&gt;&lt;p&gt;پدر گو به جهلش بینداز موی&lt;/p&gt;&lt;p&gt;مرا جان به مهرش برآمیخته‌ست&lt;/p&gt;&lt;p&gt;نه خاطر به مویی در آویخته‌ست&lt;/p&gt;&lt;p&gt;چو روی نکو داری انده مخور&lt;/p&gt;&lt;p&gt;که موی ار بیفتد بروید دگر&lt;/p&gt;&lt;p&gt;نه پیوسته رز خوشهٔ تر دهد&lt;/p&gt;&lt;p&gt;گهی برگ ریزد، گهی بر دهد&lt;/p&gt;&lt;p&gt;بزرگان چو خور در حجاب اوفتند&lt;/p&gt;&lt;p&gt;حسودان چو اخگر در آب اوفتند&lt;/p&gt;&lt;p&gt;برون آید از زیر ابر آفتاب&lt;/p&gt;&lt;p&gt;به تدریج و اخگر بمیرد در آب&lt;/p&gt;&lt;p&gt;ز ظلمت مترس ای پسندیده دوست&lt;/p&gt;&lt;p&gt;که ممکن بود کاب حیوان در اوست&lt;/p&gt;&lt;p&gt;نه گیتی پس از جنبش آرام یافت؟&lt;/p&gt;&lt;p&gt;نه سعدی سفر کرد تا کام یافت؟&lt;/p&gt;&lt;p&gt;دل از بی مرادی به فکرت مسوز&lt;/p&gt;&lt;p&gt;شب آبستن است ای برادر به روز&lt;/p&gt;', 'مثنوی شماره ۴۹', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(407, '&lt;p&gt;قضا زنده‌ای را رگ جان برید&lt;/p&gt;&lt;p&gt;دگر کس به مرگش گریبان درید&lt;/p&gt;&lt;p&gt;چنین گفت بیننده‌ای تیز هوش&lt;/p&gt;&lt;p&gt;چو فریاد و زاری رسیدش به گوش&lt;/p&gt;&lt;p&gt;ز دست شما مرده بر خویشتن&lt;/p&gt;&lt;p&gt;گرش دست بودی دریدی کفن&lt;/p&gt;&lt;p&gt;که چندین ز تیمار و دردم مپیچ&lt;/p&gt;&lt;p&gt;که روزی دو پیش از تو کردم بسیچ&lt;/p&gt;&lt;p&gt;فراموش کردی مگر مرگ خویش&lt;/p&gt;&lt;p&gt;که مرگ منت ناتوان کرد و ریش&lt;/p&gt;&lt;p&gt;محقق که بر مرده ریزد گلش&lt;/p&gt;&lt;p&gt;نه بر وی که بر خود بسوزد دلش&lt;/p&gt;&lt;p&gt;ز هجران طفلی که در خاک رفت&lt;/p&gt;&lt;p&gt;چه نالی؟ که پاک آمد و پاک رفت&lt;/p&gt;&lt;p&gt;تو پاک آمدی بر حذر باش و پاک&lt;/p&gt;&lt;p&gt;که ننگ است ناپاک رفتن به خاک&lt;/p&gt;&lt;p&gt;کنون باید این مرغ را پای بست&lt;/p&gt;&lt;p&gt;نه آنگه که سررشته بردت ز دست&lt;/p&gt;&lt;p&gt;نشستی به جای دگر کس بسی&lt;/p&gt;&lt;p&gt;نشیند به جای تو دیگر کسی&lt;/p&gt;&lt;p&gt;اگر پهلوانی و گر تیغزن&lt;/p&gt;&lt;p&gt;نخواهی به در بردن الا کفن&lt;/p&gt;&lt;p&gt;خر وحش اگر بگسلاند کمند&lt;/p&gt;&lt;p&gt;چو در ریگ ماند شود پای بند&lt;/p&gt;&lt;p&gt;تو را نیز چندان بود دست زور&lt;/p&gt;&lt;p&gt;که پایت نرفته‌ست در ریگ گور&lt;/p&gt;&lt;p&gt;منه دل بر این سالخورده مکان&lt;/p&gt;&lt;p&gt;که گنبد نپاید بر او گردکان&lt;/p&gt;&lt;p&gt;چو دی رفت و فردا نیامد به دست&lt;/p&gt;&lt;p&gt;حساب از همین یک نفس کن که هست&lt;/p&gt;', 'مثنوی شماره ۵۰', 'سعدی', 'بوستان', 'مثنوی', 6, 9),
(409, '&lt;p&gt;شب سرشاری بود.&lt;/p&gt;&lt;p&gt;روز از پای صنوبرها، تا فراترها می رفت.&lt;/p&gt;&lt;p&gt;دره مهتاب اندود، و چنان روشن کوه، که خدا پیدا بود .&lt;/p&gt;&lt;p&gt;&lt;br&gt;در بلندی ها، ما .&lt;br&gt;دورها گم، سطح ها شسته، و نگاه از همه شب نازک تر.&lt;br&gt;دست هایت، ساقه سبز پیامی را می داد به من&lt;br&gt;و سفالینه انس، با نفس هایت آهسته ترک می خورد&lt;br&gt;و تپش هامان می ریخت به سنگ.&lt;br&gt;از شرابی دیرین، شن تابستان در رگ ها&lt;br&gt;و لعاب مهتاب، روی رفتارت.&lt;br&gt;تو شگرف ، تو رها، و برازنده خاک.&lt;/p&gt;&lt;p&gt;&lt;br&gt;فرصت سبز حیات، به هوای خنک کوهستان می پیوست.&lt;br&gt;سایه ها برمی گشت.&lt;br&gt;و هنوز، در سر راه نسیم،&lt;br&gt;پونه هایی که تکان می خورد،&lt;br&gt;جذبه هایی که بهم می ریخت.&lt;/p&gt;', 'از روی پلک شب', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(410, '&lt;p&gt;ابری نیست.&lt;br&gt;بادی نیست.&lt;br&gt;می نشینم لب حوض:&lt;br&gt;گردش ماهی ها، روشنی، من، گل ، آب.&lt;br&gt;پاکی خوشه زیست.&lt;/p&gt;&lt;p&gt;&lt;br&gt;مادرم ریحان می چیند.&lt;br&gt;نان و ریحان و پنیر، آسمانی بی ابر، اطلسی هایی تر.&lt;br&gt;رستگاری نزدیک : لای گل های حیاط.&lt;/p&gt;&lt;p&gt;&lt;br&gt;نور در کاسه مس، چه نوازش ها می ریزد!&lt;br&gt;نردبان از سر دیوار بلند ، صبح را روی زمین می آرد.&lt;br&gt;پشت لبخندی پنهان هر چیز.&lt;br&gt;روزنی دارد دیوار زمان ، که از آن، چهره من پیداست .&lt;br&gt;چیزهایی هست، که نمی دانم .&lt;br&gt;می دانم، سبزه ای را بکنم خواهم مرد .&lt;br&gt;می روم بال تا اوج ، من پراز بال و پرم .&lt;br&gt;راه می بینم در ظلمت ، من پراز فانوسم .&lt;br&gt;من پراز نورم و شن&lt;br&gt;و پراز دارو درخت .&lt;br&gt;پرم از راه ، از پل ، از رود ، از موج .&lt;br&gt;پرم از سایه برگی در آب:&lt;br&gt;چه درونم تنهاست.&lt;/p&gt;', 'روشنی، من، گل، آب', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(411, '&lt;p&gt;روزی&lt;br&gt;خواهم آمد ، و پیامی خواهم آورد .&lt;br&gt;در رگ&amp;nbsp;ها ، نور خواهم ریخت .&lt;br&gt;و صدا خواهم در داد : ای سبدهاتان پر خواب ! سیب آوردم ، سیب سرخ خورشید .&lt;/p&gt;&lt;p&gt;&lt;br&gt;خواهم آمد ، گل یاسی به گدا خواهم داد .&lt;br&gt;زن زیبای جذامی را ، گوشواری دیگر خواهم بخشید .&lt;br&gt;کور را خواهم گفت : چه تماشا دارد باغ !&lt;br&gt;دوره گردی خواهم شد ، کوچه ها را خواهم گشت ، جار خواهم زد : آی شبنم ، شبنم ، شبنم .&lt;br&gt;رهگذاری خواهد گفت : راستی را ، شب تاریکی است ، کهکشانی خواهم دادش .&lt;/p&gt;&lt;p&gt;روی پل دخترکی بی پاست ، دب اکبر را بر گردن او خواهم آویخت.&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;هر چه دشنام ، از لب ها خواهم برچید .&lt;br&gt;هر چه دیوار، از جا خواهم برکند .&lt;br&gt;رهزنان را خواهم گفت : کاروانی آمد بارش لبخند !&lt;br&gt;من گره خواهم زد ، چشمان را با خورشید ، دل ها را با عشق، سایه های را با باد .&lt;br&gt;و بهم خواهم پیوست ، خواب کودک را با زمزمه زنجره ها .&lt;/p&gt;&lt;p&gt;&lt;br&gt;بادبادک ها ، به هوا خواهم برد .&lt;br&gt;گلدان ها ، آب خواهم داد .&lt;br&gt;خواهم آمد ، پیش اسبان ، گاوان ، علف سبز نوازش خواهم ریخت .&lt;br&gt;مادیانی تشنه ، سطل شبنم را خواهم آورد .&lt;br&gt;خر فرتوتی در راه ، من مگس هایش را خواهم زد .&lt;/p&gt;&lt;p&gt;&lt;br&gt;خواهم آمد سر هر دیواری ، میخکی خواهم کاشت .&lt;br&gt;پای هر پنجره ای ، شعری خواهم خواند .&lt;br&gt;هر کلاغی را ، کاجی خواهم داد .&lt;br&gt;مار را خواهم گفت : چه شکوهی دارد غوک !&lt;br&gt;آشتی خواهم داد .&lt;br&gt;آشنا خواهم کرد .&lt;br&gt;راه خواهم رفت .&lt;br&gt;نور خواهم خورد .&lt;br&gt;دوست خواهم داشت.&lt;/p&gt;', 'و پیامی در راه', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(412, '&lt;p&gt;آسمان ، آبی تر،&lt;br&gt;آب، آبی تر.&lt;br&gt;من در ایوانم، رعنا سر حوض .&lt;/p&gt;&lt;p&gt;&lt;br&gt;رخت می شوید رعنا .&lt;br&gt;برگ ها می ریزد .&lt;br&gt;مادرم صبحی می گفت : موسم دلگیری است .&lt;br&gt;من به او گفتم : زندگانی سیبی است ، گاز باید زد با پوست .&lt;/p&gt;&lt;p&gt;&lt;br&gt;زن همسایه در پنجره اش ، تور می بافد ، می خواند .&lt;br&gt;من «ودا» میخوانم، گاهی نیز&lt;br&gt;طرح می ریزم سنگی ، مرغی ، ابری .&lt;/p&gt;&lt;p&gt;&lt;br&gt;آفتابی یکدست .&lt;br&gt;سارها آمده اند .&lt;br&gt;تازه لادن ها پیدا شده اند .&lt;br&gt;من اناری را ، می کنم دانه ، به دل می گویم :&lt;br&gt;خوب بود این مردم ، دانه های دلشان پیدا بود .&lt;br&gt;می پرد در چشمم آب انار : اشک می ریزم .&lt;br&gt;مادرم می خندد .&lt;br&gt;رعنا هم.&lt;/p&gt;', 'ساده رنگ', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(413, '&lt;p&gt;آب را گل نکنیم :&lt;br&gt;در فرودست انگار، کفتری می خورد آب .&lt;br&gt;یا که در بیشه دور، سیره ای پر می شوید .&lt;br&gt;یا در آبادی ، کوزه ای پر می گردد .&lt;/p&gt;&lt;p&gt;&lt;br&gt;آب را گل نکنیم :&lt;br&gt;شاید این آب روان ، می رود پای سپیداری ، تا فرو شوید اندوه دلی .&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;دست درویشی شاید ، نان خشکیده فرود برده در آب .&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زن زیبایی آمد لب رود ،&lt;br&gt;آب را گل نکنیم :&lt;br&gt;روی زیبا دو برابر شده است .&lt;/p&gt;&lt;p&gt;&lt;br&gt;چه گوارا این آب!&lt;br&gt;چه زلال این رود!&lt;br&gt;مردم بالادست ، چه صفایی دارند !&lt;br&gt;چشمه هاشان جوشان ، گاوهاشان شیرافشان باد !&lt;br&gt;من ندیدم دهشان ،&lt;br&gt;بی گمان پای چپرهاشان جا پای خداست .&lt;br&gt;ماهتاب آنجا ، می کند روشن پهنای کلام .&lt;br&gt;بی گمان در ده بالادست ، چینه ها کوتاه است .&lt;br&gt;مردمش می دانند ، که شقایق چه گلی است .&lt;br&gt;بی گمان آنجا آبی ، آبی است .&lt;br&gt;غنچه ای می شکفد ، اهل ده با خبرند .&lt;br&gt;چه دهی باید باشد !&lt;br&gt;کوچه باغش پر موسیقی باد !&lt;br&gt;مردمان سر رود ، آب را می فهمند&amp;nbsp;.&lt;br&gt;گل نکردندش ، ما نیز&lt;br&gt;آب را گل نکنیم.&lt;/p&gt;', 'آب', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(414, '&lt;p&gt;دشت هایی چه فراخ !&lt;br&gt;کوه هایی چه بلند !&lt;br&gt;در گلستانه چه بوی علفی می آمد!&lt;br&gt;من در این آبادی ، پی چیزی می گشتم :&lt;br&gt;پی خوابی شاید ،&lt;br&gt;پی نوری ، ریگی ، لبخندی .&lt;/p&gt;&lt;p&gt;&lt;br&gt;پشت تبریزی ها&lt;br&gt;غفلت پاکی بود ، که صدایم می زد .&lt;/p&gt;&lt;p&gt;&lt;br&gt;پای نی زاری ماندم ، باد می آمد ، گوش دادم :&lt;br&gt;چه کسی با من ، حرف می زد ؟&lt;br&gt;سوسماری لغزید .&lt;br&gt;راه افتادم .&lt;br&gt;یونجه زاری سر راه ،&lt;br&gt;بعد جالیز خیار، بوته های گل رنگ&lt;br&gt;و فراموشی خاک.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;لب آبی&lt;br&gt;گیوه ها را کندم ، و نشستم ، پاها در آب :&lt;br&gt;«من چه سبزم امروز&amp;nbsp;&lt;br&gt;و چه اندازه تنم هوشیار است !&lt;br&gt;نکند اندوهی ، سر رسد از پس کوه .&lt;br&gt;چه کسی پشت درختان است ؟&lt;br&gt;هیچ ، می چرد گاوی در کرد .&lt;br&gt;ظهر تابستان است .&lt;br&gt;سایه ها می دانند ، که چه تابستانی است .&lt;br&gt;سایه هایی بی لک ،&lt;br&gt;گوشه ای روشن و پاک ،&lt;br&gt;کودکان احساس ! جای بازی اینجاست .&lt;/p&gt;&lt;p&gt;زندگی خالی نیست :&lt;br&gt;مهربانی هست ، سیب هست ، ایمان هست .&lt;br&gt;آری&lt;br&gt;تا شقایق هست زندگی باید کرد.&lt;/p&gt;&lt;p&gt;&lt;br&gt;در دل من چیزی است ، مثل یک بیشه نور، مثل خواب دم صبح .&lt;br&gt;و چنان بی تابم ، که دلم می خواهد&lt;br&gt;بدوم تا ته دشت ، بروم تا سر کوه.&lt;/p&gt;&lt;p&gt;دورها آوایی است ، که مرا می خواند.»&lt;/p&gt;', 'در گلستانه', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(415, '&lt;p&gt;ماه بالای سر آبادی است ،&lt;br&gt;اهل آبادی در خواب .&lt;br&gt;روی این مهتابی ، خشت غربت را می بویم .&lt;br&gt;باغ همسایه چراغش روشن ،&lt;br&gt;من چراغم خاموش .&lt;br&gt;ماه تابیده به بشقاب خیار، به لب کوزه آب .&lt;/p&gt;&lt;p&gt;&lt;br&gt;غوک ها می خوانند .&lt;br&gt;مرغ حق هم گاهی .&lt;br&gt;کوه نزدیک من است : پشت افراها ، سنجد ها .&lt;br&gt;و بیابان پیداست .&lt;br&gt;سنگ ها پیدا نیست ، گلچه ها پیدا نیست .&lt;br&gt;سایه های از دور ، مثل تنهایی آب ، مثل آواز خدا پیداست.&lt;/p&gt;&lt;p&gt;نیمه شب باید باشد .&lt;br&gt;دب اکبر آن است : دو وجب بالاتر از بام .&lt;br&gt;آسمان آبی نیست ، روز آبی بود .&lt;br&gt;یاد من باشد فردا ، بروم باغ حسن گوجه و قیسی بخرم .&lt;br&gt;یاد من باشد فردا لب سلخ ، طرحی از بزها بردارم ،&lt;br&gt;طرحی از جاروها ، سایه هاشان در آب .&lt;br&gt;یاد من باشد ، هر چه پروانه که می افتد در آب ، زود از آب درآرم.&lt;br&gt;یاد من باشد کاری نکنم ، که به قانون زمین بر بخورد .&lt;br&gt;یاد من باشد فردا لب جوی ، حوله ام را هم با چوبه بشویم .&lt;br&gt;یاد من باشد تنها هستم.&lt;/p&gt;&lt;p&gt;&lt;br&gt;ماه بالای سر تنهایی است.&lt;/p&gt;', 'غربت', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(416, '&lt;p&gt;رفته بودم سر حوض&lt;br&gt;تا ببینم شاید ، عکس تنهایی خود را در آب ،&lt;br&gt;آب در حوض نبود .&lt;br&gt;ماهیان می گفتند:&lt;/p&gt;&lt;p&gt;«هیچ تقصیر درختان نیست.»&amp;nbsp;&lt;/p&gt;&lt;p&gt;ظهر دم کرده تابستان بود ،&lt;br&gt;پسر روشن آب ، لب پاشویه نشست&lt;br&gt;و عقاب خورشید ، آمد او را به هوا برد که برد.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;br&gt;به درک راه نبردیم به اکسیژن آب .&lt;br&gt;برق از پولک ما رفت که رفت .&lt;br&gt;ولی آن نور درشت ،&lt;br&gt;عکس آن میخک قرمز در آب&lt;br&gt;که اگر باد می آمد دل او ، پشت چین های تغافل می زد ،&lt;br&gt;چشم ما بود .&lt;/p&gt;&lt;p&gt;روزنی بود به اقرار بهشت.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تو اگر در تپش باغ خدا را دیدی ، همت کن&lt;br&gt;و بگو ماهی ها ، حوضشان بی آب است.»&lt;/p&gt;&lt;p&gt;&lt;br&gt;باد می رفت به سر وقت چنار .&lt;br&gt;من به سر وقت خدا می رفتم.&lt;/p&gt;', 'پیغام ماهی ها', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(417, '&lt;p&gt;«خانه دوست کجاست؟» در فلق بود که پرسید سوار.&lt;/p&gt;&lt;p&gt;آسمان مکثی کرد .&lt;br&gt;رهگذر شاخه نوری که به لب داشت به تاریکی شن ها بخشید&lt;br&gt;و&amp;nbsp; &amp;nbsp; به انگشت نشان داد سپیداری و گفت :&lt;/p&gt;&lt;p&gt;&lt;br&gt;«نرسیده به درخت،&amp;nbsp;&lt;br&gt;کوچه باغی است که از خواب خدا سبز تر است&lt;br&gt;و در آن عشق به اندازه پرهای صداقت آبی است .&lt;br&gt;می روی تا ته آن کوچه که از پشت بلوغ ، سر بدر می آرد ،&lt;br&gt;پس به سمت گل تنهایی می پیچی ،&lt;br&gt;دو قدم مانده به گل ،&lt;br&gt;پای فواره جاوید اساطیر زمین می مانی&lt;br&gt;و ترا ترسی شفاف فرا می گیرد .&lt;br&gt;در صمیمیت سیال فضا ، خش خشی می شنوی :&lt;br&gt;کودکی می بینی&lt;br&gt;رفته از کاج بلندی بال ، جوجه بر دارد از لنه نور&lt;br&gt;و از او می پرسی&lt;/p&gt;&lt;p&gt;خانه دوست کجاست.»&lt;/p&gt;', 'نشانی', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(418, '&lt;p&gt;به سراغ من اگر می آیید،&lt;br&gt;پشت هیچستانم .&lt;br&gt;پشت هیچستان جایی است .&lt;br&gt;پشت هیچستان رگ های هوا ، پر قاصد هایی است&lt;br&gt;که خبر می آرند ، از گل واشده دورترین بوته خاک .&lt;br&gt;روی شن ها هم ، نقش های سم اسبان سواران ظریفی است که صبح&lt;br&gt;به سر تپه معراج شقایق رفتند .&lt;br&gt;پشت هیچستان ، چتر خواهش باز است :&lt;br&gt;تا نسیم عطشی در بن برگی بدود ،&lt;br&gt;زنگ باران به صدا می آید .&lt;br&gt;آدم اینجا تنهاست&lt;br&gt;و در این تنهایی ، سایه نارونی تا ابدیت جاری است .&lt;/p&gt;&lt;p&gt;&lt;br&gt;به سراغ من اگر می آیید ،&lt;br&gt;نرم و آهسته بیایید ، مبادا که ترک بر دارد&lt;br&gt;چینی نازک تنهایی من .&lt;/p&gt;', 'واحه ای در لحظه', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(419, '&lt;p&gt;قایقی خواهم ساخت ،&lt;br&gt;خواهم انداخت به آب .&lt;br&gt;دور خواهم شد از این خاک غریب&lt;br&gt;که در آن هیچکسی نیست که در بیشه عشق&lt;br&gt;قهرمانان را بیدار کند&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;قایق از تور تهی&lt;br&gt;و دل از آرزوی مروارید ،&lt;br&gt;همچنان خواهم راند .&lt;br&gt;نه به آبی ها دل خواهم بست&lt;br&gt;نه به آن تابش تنهایی ماهی گیران&lt;br&gt;می فشانند فسون از سر گیسوهاشان .&lt;/p&gt;&lt;p&gt;&lt;br&gt;همچنان خواهم راند .&lt;br&gt;همچنان خواهم خواند:&lt;/p&gt;&lt;p&gt;«دور باید شد ، دور.»&lt;/p&gt;&lt;p&gt;مرد آن شهر اساطیر نداشت .&lt;br&gt;زن آن شهر به سرشاری یک خوشه انگور نبود .&lt;br&gt;هیچ آیینه تالاری ، سر خوشی ها را تکرار نکرد .&lt;br&gt;چاله آبی حتی ، مشعلی را ننمود .&lt;br&gt;دور باید شد ، دور .&lt;br&gt;شب سرودش را خواند ،&lt;br&gt;نوبت پنجره هاست.»&lt;/p&gt;&lt;p&gt;&lt;br&gt;همچنان خواهم خواند .&lt;br&gt;همچنان خواهم راند .&lt;/p&gt;&lt;p&gt;&lt;br&gt;پشت دریاها شهری است&lt;br&gt;که در آن پنجره ها رو به تجلی باز است .&lt;br&gt;بام ها جای کبوترهایی است ، که به فواره هوش بشری می نگرند.&lt;br&gt;دست هر کودک ده ساله شهر ، شاخه معرفتی است.&lt;br&gt;مردم شهر به یک چینه چنان می نگرند&lt;br&gt;که به یک شعله ، به یک خواب لطیف .&lt;br&gt;خاک ، موسیقی احساس ترا می شنود&lt;br&gt;و صدای پر مرغان اساطیر می آید در باد.&lt;/p&gt;&lt;p&gt;&lt;br&gt;پشت دریاها شهری است&lt;br&gt;که در آن وسعت خورشید به اندازه چشمان سحر خیزان است.&lt;br&gt;شاعران وارث آب و خرد و روشنی اند .&lt;/p&gt;&lt;p&gt;&lt;br&gt;پشت دریاها شهری است !&lt;br&gt;قایق باید ساخت .&lt;/p&gt;', 'پشت دریاها', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(420, '&lt;p&gt;تا سواد قریه راهی بود .&lt;br&gt;چشم های ما پر از تفسیر زنده بومی ،&lt;br&gt;شب درون آستین هامان .&lt;/p&gt;&lt;p&gt;&lt;br&gt;می گذشتیم از میان آبکندی خشک .&lt;br&gt;از کلم سبزه زاران گوش ها سرشار ،&lt;br&gt;کوله بار از انعکاس شهر های دور .&lt;br&gt;منطق زبر زمین در زیر پا جاری .&lt;/p&gt;&lt;p&gt;&lt;br&gt;زیر دندان های ما طعم فراغت جابجا می شد .&lt;br&gt;پای پوش ما که از جنس نبوت بود ما را با نسیمی از زمین می کند.&lt;br&gt;چوبدست ما به دوش خود بهار جاودان می برد .&lt;br&gt;هر یک از ما آسمانی داشت در هر انحنای فکر .&lt;br&gt;هر تکان دست ما با جنبش یک بال مجذوب سحر می خواند.&lt;br&gt;جیب های ما صدای جیک جیک صبح های کودکی می داد.&lt;/p&gt;&lt;p&gt;ما گروه عاشقان بودیم و راه ما&lt;br&gt;از کنار قریه های آشنا با فقر&lt;br&gt;تا صفای بیکران می رفت .&lt;br&gt;برفراز آبگیری خود بخود سرها همه خم شد :&lt;br&gt;روی صورت های ما تبخیر می شد شب&lt;br&gt;و صدای دوست می آمد به گوش دوست.&lt;/p&gt;', 'تپش سایه دوست', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(421, '&lt;p&gt;با سبد رفتم به میدان ، صبحگاهی بود&amp;nbsp;.&lt;br&gt;میوه ها آواز می خواندند&amp;nbsp;.&lt;br&gt;میوه ها در آفتاب آواز می خواندند&amp;nbsp;.&lt;br&gt;در طبق ها ، زندگی روی کمال پوست ها خواب سطوح جاودان می دید.&lt;br&gt;اضطراب باغ ها در سایه ء هر میوه روشن بود&amp;nbsp;.&lt;br&gt;گاه مجهولی میان تابش به ها شنا می کرد&amp;nbsp;.&lt;br&gt;هر اناری رنگ خود را تا زمین پارسایان گسترش می داد&amp;nbsp;.&lt;br&gt;بینش هم شهریان ، افسوس ،&lt;br&gt;بر محیط رونق نارنج ها خط مماسی بود&amp;nbsp;.&lt;/p&gt;&lt;p&gt;&lt;br&gt;من به خانه باز گشتم ، مادرم پرسید&amp;nbsp;:&lt;br&gt;میوه از میدان خریدی هیچ ؟&lt;br&gt;- میوه های بی نهایت را کجا می شد میان این سبد جا داد؟&lt;br&gt;- گفتم از میدان بخر یک من انار خوب&amp;nbsp;.&lt;br&gt;- امتحان کردم اناری را&lt;br&gt;انبساطش از کنار این سبد سر رفت&amp;nbsp;.&lt;br&gt;- به چه شد ، آخر خوراک ظهر ...&lt;br&gt;... –&lt;/p&gt;&lt;p&gt;&lt;br&gt;ظهر از آیینه ها تصویر به تا دور دست زندگی می رفت.&lt;/p&gt;', 'صدای دیدار', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(422, '&lt;p&gt;گوش کن ، دورترین مرغ جهان می خواند .&lt;br&gt;شب سلیس است ، و یکدست ، و باز .&lt;br&gt;شمعدانی ها&lt;br&gt;و صدا دارترین شاخه فصل ، ماه را می شنوند .&lt;/p&gt;&lt;p&gt;&lt;br&gt;پلکان جلوی ساختمان ،&lt;br&gt;در فانوس به دست&lt;br&gt;و در اسراف نسیم ،&lt;/p&gt;&lt;p&gt;&lt;br&gt;گوش کن ، جاده صدا می زند از دور قدم های ترا .&lt;br&gt;چشم تو زینت تاریکی نیست .&lt;br&gt;پلک ها را بتکان ، کفش به پا کن ، و بیا .&lt;br&gt;و بیا تا جایی ، که پر ماه به انگشت تو هشدار دهد .&lt;br&gt;و زمان روی کلوخی بنشیند با تو&lt;br&gt;و مزامیر شب اندام ترا، مثل یک قطعه آواز به خود جذب کند.&lt;br&gt;پارسایی است در آنجا که ترا خواهد گفت :&lt;br&gt;بهترین چیز رسیدن به نگاهی است که از حادثه عشق تر است.&lt;/p&gt;', 'شب تنهایی خوب', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(423, '&lt;p&gt;به تماشا سوگند&lt;br&gt;و به آغاز کلام&lt;br&gt;و به پرواز کبوتر از ذهن&lt;/p&gt;&lt;p&gt;واژه ای در قفس است.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;حرف هایم ، مثل یک تکه چمن روشن بود .&lt;br&gt;من به آنان گفتم :&lt;br&gt;آفتابی لب درگاه شماست&lt;br&gt;که اگر در بگشایید به رفتار شما می تابد.&lt;/p&gt;&lt;p&gt;&lt;br&gt;و به آنان گفتم :&lt;br&gt;سنگ آرایش کوهستان نیست&lt;br&gt;همچنانی که فلز ، زیوری نیست به اندام کلنگ .&lt;br&gt;در کف دست زمین گوهر نا پیدایی است&lt;br&gt;که رسولان همه از تابش آن خیره شدند .&lt;br&gt;پی گوهر باشید .&lt;br&gt;لحظه ها را به چراگاه رسالت ببرید .&lt;/p&gt;&lt;p&gt;&lt;br&gt;و من آنان را ، به صدای قدم پیک بشارت دادم&lt;br&gt;و به نزدیکی روز ، و به افزایش رنگ .&lt;br&gt;به طنین گل سرخ ، پشت پرچین سخن های درشت .&lt;/p&gt;&lt;p&gt;&lt;br&gt;و به آنان گفتم :&lt;br&gt;هر که در حافظه چوب ببیند باغی&lt;br&gt;صورتش در وزش بیشه شور ابدی خواهد ماند .&lt;br&gt;هر که با مرغ هوا دوست شود&lt;br&gt;خوابش آرام ترین خواب جهان خواهد بود .&lt;br&gt;آنکه نور از سر انگشت زمان بر چیند&lt;br&gt;می گشاید گره پنجره ها را با آه.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;زیر بیدی بودیم .&lt;br&gt;برگی از شاخه بالای سرم چیدم ، گفتم :&lt;br&gt;چشم را باز کنید ، آیتی بهتر از این می خواهید ؟&lt;br&gt;می شنیدم که بهم می گفتند :&lt;br&gt;سحر میداند ، سحر!&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;سر هر کوه رسولی دیدند&lt;br&gt;ابر انکار به دوش آوردند.&lt;br&gt;باد را نازل کردیم&lt;br&gt;تا کله از سرشان بردارد .&lt;br&gt;خانه هاشان پر داوودی بود ،&lt;br&gt;چشمشان را بستیم .&lt;br&gt;دستشان را نرساندیم به سر شاخه هوش .&lt;br&gt;جیبشان را پر عادت کردیم .&lt;br&gt;خوابشان را به صدای سفر آینه ها آشفتیم.&amp;nbsp;&lt;/p&gt;', 'سوره تماشا', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(424, '&lt;p&gt;مانده تا برف زمین آب شود .&lt;br&gt;مانده تا بسته شود این همه نیلوفر وارونه چتر.&lt;br&gt;ناتمام است درخت .&lt;br&gt;زیر برف است تمنای شنا کردن کاغذ در باد&lt;br&gt;و فروغ تر چشم حشرات&lt;br&gt;و طلوع سر غوک از افق درک حیات.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;مانده تا سینی ما پر شود از صحبت سنبوسه و عید .&lt;br&gt;در هوایی که نه افزایش یک ساقه طنینی دارد&lt;br&gt;و نه آواز پری می رسد از روزن منظومه ء برف&lt;/p&gt;&lt;p&gt;تشنه ء زمزمه ام .&lt;br&gt;مانده تا مرغ سرچینه هذیانی اسفند صدا بردارد .&lt;br&gt;پس چه باید بکنم&lt;br&gt;من که در لخت ترین موسم بی چهچه سال&lt;br&gt;تشنه زمزمه ام ؟&lt;/p&gt;&lt;p&gt;&lt;br&gt;بهتر است که برخیزم&lt;br&gt;رنگ را بردارم&lt;br&gt;روی تنهایی خود نقشه مرغی بکشم .&lt;/p&gt;', 'پرهای زمزمه', 'سهراب سپهری', 'حجم سبز', 'کلاسیک و نیمایی', 7, 11),
(445, '&lt;p&gt;در دور دست&lt;/p&gt;&lt;p&gt;قویی پریده بی گاه از خواب&lt;/p&gt;&lt;p&gt;شوید غبار نیل ز بال و پر سپید&lt;/p&gt;&lt;p&gt;لب‌های جویبار&lt;/p&gt;&lt;p&gt;لبریز موج زمزمه در بستر سپید&lt;/p&gt;&lt;p&gt;در هم دویده سایه و روشن.&lt;/p&gt;&lt;p&gt;لغزان میان خرمن دوده&lt;/p&gt;&lt;p&gt;شبتاب می‌فروزد در آذر سپید&lt;/p&gt;&lt;p&gt;همپای رقص نازک نیزار&lt;/p&gt;&lt;p&gt;مرداب می‌گشاید چشم تر سپید.&lt;/p&gt;&lt;p&gt;خطی ز نور روی سیاهی است:&lt;/p&gt;&lt;p&gt;گویی بر آبنوس درخشد رز سپید&lt;/p&gt;&lt;p&gt;دیوار سایه‌ها شده ویران&lt;/p&gt;&lt;p&gt;دست نگاه در افق دور&lt;/p&gt;&lt;p&gt;کاخی بلند ساخته با مرمر سپید.&lt;/p&gt;', 'سپیده', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(448, '&lt;p&gt;روشن است آتش درون شب&lt;/p&gt;&lt;p&gt;وز پس دودش&lt;/p&gt;&lt;p&gt;طرحی از ویرانه‌های دور.&lt;/p&gt;&lt;p&gt;گر به گوش آید صدایی خشک:&lt;/p&gt;&lt;p&gt;استخوان مرده می لغزد درون گور.&lt;/p&gt;&lt;p&gt;&lt;br&gt;دیرگاهی ماند اجاقم سرد&lt;/p&gt;&lt;p&gt;و چراغم بی‌نصیب از نور.&lt;/p&gt;&lt;p&gt;&lt;br&gt;خواب دربان را به راهی برد.&lt;/p&gt;&lt;p&gt;بی صدا آمد کسی از در،&lt;/p&gt;&lt;p&gt;در سیاهی آتشی افروخت .&lt;/p&gt;&lt;p&gt;بی خبر اما&lt;/p&gt;&lt;p&gt;که نگاهی در تماشا سوخت.&lt;/p&gt;&lt;p&gt;&lt;br&gt;گرچه می دانم که چشمی راه دارد بافسون شب،&lt;/p&gt;&lt;p&gt;لیک می بینم ز روزن های خوابی خوش:&lt;/p&gt;&lt;p&gt;آتشی روشن درون شب.&lt;/p&gt;', 'مرغ معما', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(449, '&lt;p&gt;آفتاب است و، بیابان چه فراخ!&lt;/p&gt;&lt;p&gt;نیست در آن نه گیاه و نه درخت.&lt;/p&gt;&lt;p&gt;غیر آوای غرابان، دیگر&lt;/p&gt;&lt;p&gt;بسته هر بانگی از این وادی رخت.&lt;/p&gt;&lt;p&gt;&lt;br&gt;در پس پرده‌ای از گرد و غبار&lt;/p&gt;&lt;p&gt;نقطه‌یی لرزد از دور سیاه:&lt;/p&gt;&lt;p&gt;چشم اگر پیش رود، می‌بیند&lt;/p&gt;&lt;p&gt;آدمی هست که می‌پوید راه.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تنش از خستگی افتاده ز کار.&lt;/p&gt;&lt;p&gt;بر سر و رویش بنشسته غبار.&lt;/p&gt;&lt;p&gt;شده از تشنگی‌اش خشک گلو.&lt;/p&gt;&lt;p&gt;پای عریانش مجروح ز خار.&lt;/p&gt;&lt;p&gt;&lt;br&gt;هر قدم پیش رود، پای افق&lt;/p&gt;&lt;p&gt;چشم او بیند دریایی آب.&lt;/p&gt;&lt;p&gt;اندکی راه چو می‌پیماید&lt;/p&gt;&lt;p&gt;می‌کند فکر که می‌بیند خواب.&lt;/p&gt;', 'روشن شب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(450, '&lt;p&gt;ریخته سرخ غروب&lt;/p&gt;&lt;p&gt;جابجا بر سر سنگ.&lt;/p&gt;&lt;p&gt;کوه خاموش است. می‌خروشد رود.&lt;/p&gt;&lt;p&gt;مانده در دامن دشت&lt;/p&gt;&lt;p&gt;خرمنی رنگ کبود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;سایه آمیخته با سایه.&lt;/p&gt;&lt;p&gt;سنگ با سنگ گرفته پیوند.&lt;/p&gt;&lt;p&gt;روز فرسوده به ره می‌گذرد.&lt;/p&gt;&lt;p&gt;جلوه گر آمده در چشمانش&lt;/p&gt;&lt;p&gt;نقش اندوه پی یک لبخند.&lt;/p&gt;&lt;p&gt;&lt;br&gt;جغد بر کنگره‌ها می‌خواند.&lt;/p&gt;&lt;p&gt;لاشخورها، سنگین،&lt;/p&gt;&lt;p&gt;از هوا، تک تک ، آیند فرود:&lt;/p&gt;&lt;p&gt;لاشه‌ای مانده به دشت&lt;/p&gt;&lt;p&gt;کنده منقار ز جا چشمانش،&lt;/p&gt;&lt;p&gt;زیر پیشانی او&lt;/p&gt;&lt;p&gt;مانده دو گود کبود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تیرگی می‌آید.&lt;/p&gt;&lt;p&gt;دشت می‌گیرد آرام.&lt;/p&gt;&lt;p&gt;قصه رنگی روز می‌رود رو به تمام.&lt;/p&gt;&lt;p&gt;&lt;br&gt;شاخه‌ها پژمرده‌است.&lt;/p&gt;&lt;p&gt;سنگ‌ها افسرده‌است.&lt;/p&gt;&lt;p&gt;رود می‌نالد.&lt;/p&gt;&lt;p&gt;جغد می‌خواند.&lt;/p&gt;&lt;p&gt;غم بیاویخته با رنگ غروب. می‌تراود ز لبم قصه سرد:&lt;/p&gt;&lt;p&gt;دلم افسرده در این تنگ غروب.&lt;/p&gt;', 'سراب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(451, '&lt;p&gt;شب سردی است ، و من افسرده.&lt;/p&gt;&lt;p&gt;راه دوری است ، و پایی خسته.&lt;/p&gt;&lt;p&gt;تیرگی هست و چراغی مرده.&lt;/p&gt;&lt;p&gt;می‌کنم ، تنها، از جاده عبور:&lt;/p&gt;&lt;p&gt;دور ماندند ز من آدم‌ها.&lt;/p&gt;&lt;p&gt;سایه‌ای از سر دیوار گذشت،&lt;/p&gt;&lt;p&gt;غمی افزود مرا بر غم‌ها.&lt;/p&gt;&lt;p&gt;&lt;br&gt;فکر تاریکی و این ویرانی&lt;/p&gt;&lt;p&gt;بی خبر آمد تا با دل من&lt;/p&gt;&lt;p&gt;قصه‌ها ساز کند پنهانی.&lt;/p&gt;&lt;p&gt;&lt;br&gt;نیست رنگی که بگوید با من&lt;/p&gt;&lt;p&gt;اندکی صبر ، سحر نزدیک است:&lt;/p&gt;&lt;p&gt;هردم این بانگ برآرم از دل:&lt;/p&gt;&lt;p&gt;وای، این شب چقدر تاریک است!&lt;/p&gt;&lt;p&gt;خنده‌ای کو که به دل انگیزم؟&lt;/p&gt;&lt;p&gt;قطره‌ای کو که به دریا ریزم؟&lt;/p&gt;&lt;p&gt;صخره‌ای کو که بدان آویزم؟&lt;/p&gt;&lt;p&gt;&lt;br&gt;مثل این است که شب نمناک است.&lt;/p&gt;&lt;p&gt;دیگران را هم غم هست به دل،&lt;/p&gt;&lt;p&gt;غم من ، لیک، غمی غمناک است.&lt;/p&gt;', 'رو به غروب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(452, '&lt;p&gt;فرسود پای خود را چشمم به راه دور&lt;/p&gt;&lt;p&gt;تا حرف من پذیرد آخر که: زندگی&lt;/p&gt;&lt;p&gt;رنگ خیال بر رخ تصویر خواب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;دل را به رنج هجر سپردم، ولی چه سود،&lt;/p&gt;&lt;p&gt;پایان شام شکوه‌ام.&lt;/p&gt;&lt;p&gt;صبح عتاب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;چشمم نخورد آب از این عمر پر شکست:&lt;/p&gt;&lt;p&gt;این خانه را تمامی پی روی آب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;پایم خلیده خار بیابان.&lt;/p&gt;&lt;p&gt;جز با گلوی خشک نکوبیده‌ام به راه.&lt;/p&gt;&lt;p&gt;لیکن کسی، ز راه مددکاری،&lt;/p&gt;&lt;p&gt;دستم اگر گرفت، فریب سراب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;خوب زمانه رنگ دوامی به خود ندید:&lt;/p&gt;&lt;p&gt;کندی نهفته داشت شب رنج من به دل،&lt;/p&gt;&lt;p&gt;اما به کار روز نشاطم شتاب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;آبادی ام ملول شد از صحبت زوال.&lt;/p&gt;&lt;p&gt;بانگ سرور در دلم افسرد، کز نخست&lt;/p&gt;&lt;p&gt;تصویر جغد زیب تن این خراب بود.&lt;/p&gt;', 'غمی غمناک', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(453, '&lt;p&gt;فرسود پای خود را چشمم به راه دور&lt;/p&gt;&lt;p&gt;تا حرف من پذیرد آخر که: زندگی&lt;/p&gt;&lt;p&gt;رنگ خیال بر رخ تصویر خواب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;دل را به رنج هجر سپردم، ولی چه سود،&lt;/p&gt;&lt;p&gt;پایان شام شکوه‌ام.&lt;/p&gt;&lt;p&gt;صبح عتاب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;چشمم نخورد آب از این عمر پر شکست:&lt;/p&gt;&lt;p&gt;این خانه را تمامی پی روی آب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;پایم خلیده خار بیابان.&lt;/p&gt;&lt;p&gt;جز با گلوی خشک نکوبیده‌ام به راه.&lt;/p&gt;&lt;p&gt;لیکن کسی، ز راه مددکاری،&lt;/p&gt;&lt;p&gt;دستم اگر گرفت، فریب سراب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;خوب زمانه رنگ دوامی به خود ندید:&lt;/p&gt;&lt;p&gt;کندی نهفته داشت شب رنج من به دل،&lt;/p&gt;&lt;p&gt;اما به کار روز نشاطم شتاب بود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;آبادی ام ملول شد از صحبت زوال.&lt;/p&gt;&lt;p&gt;بانگ سرور در دلم افسرد، کز نخست&lt;/p&gt;&lt;p&gt;تصویر جغد زیب تن این خراب بود.&lt;/p&gt;', 'خراب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(454, '&lt;p&gt;از هجوم نغمه‌ای بشکافت گور مغز من امشب:&lt;/p&gt;&lt;p&gt;مرده‌ای را جان به رگ‌ها ریخت،&lt;/p&gt;&lt;p&gt;پا شد از جا در میان سایه و روشن،&lt;/p&gt;&lt;p&gt;بانگ زد بر من: مرا پنداشتی مرده&lt;/p&gt;&lt;p&gt;و به خاک روزهای رفته بسپرده؟&lt;/p&gt;&lt;p&gt;لیک پندار تو بیهوده‌است:&lt;/p&gt;&lt;p&gt;پیکر من مرگ را از خویش می‌راند.&lt;/p&gt;&lt;p&gt;سرگذشت من به زهر لحظه‌های تلخ آلوده‌است.&lt;/p&gt;&lt;p&gt;من به هر فرصت که یابم بر تو می‌تازم.&lt;/p&gt;&lt;p&gt;شادیت را با عذاب آلوده می‌سازم.&lt;/p&gt;&lt;p&gt;با خیالت می‌دهم پیوند تصویری&lt;/p&gt;&lt;p&gt;که قرارت را کند در رنگ خود نابود.&lt;/p&gt;&lt;p&gt;درد را با لذت آمی‌زد،&lt;/p&gt;&lt;p&gt;در تپش‌هایت فرو ریزد.&lt;/p&gt;&lt;p&gt;نقش‌های رفته را باز آورد با خود غبار آلود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;مرده لب بربسته بود.&lt;/p&gt;&lt;p&gt;چشم می‌لغزید بر یک طرح شوم.&lt;/p&gt;&lt;p&gt;می‌تراوید از تن من درد.&lt;/p&gt;&lt;p&gt;نغمه می‌آورد بر مغزم هجوم.&lt;/p&gt;', 'جان گرفته', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(455, '&lt;p&gt;قصه‌ام دیگر زنگار گرفت:&lt;/p&gt;&lt;p&gt;با نفس‌های شبم پیوندی است.&lt;/p&gt;&lt;p&gt;پرتویی لغزد اگر بر لب او،&lt;/p&gt;&lt;p&gt;گویدم دل: هوس لبخندی است.&lt;/p&gt;&lt;p&gt;&lt;br&gt;خیره چشمانش با من گوید:&lt;/p&gt;&lt;p&gt;کو چراغی که فروزد دل ما؟&lt;/p&gt;&lt;p&gt;هر که افسرد به جان، با من گفت:&lt;/p&gt;&lt;p&gt;آتشی کو که بسوزد دل ما؟&lt;/p&gt;&lt;p&gt;&lt;br&gt;خشت می‌افتد از این دیوار.&lt;/p&gt;&lt;p&gt;رنج بیهوده نگهبانش برد.&lt;/p&gt;&lt;p&gt;دست باید نرود سوی کلنگ،&lt;/p&gt;&lt;p&gt;سیل اگر آمد آسانش برد.&lt;/p&gt;&lt;p&gt;&lt;br&gt;باد نمناک زمان می‌گذرد،&lt;/p&gt;&lt;p&gt;رنگ می‌ریزد از پیکر ما.&lt;/p&gt;&lt;p&gt;خانه را نقش فساد است به سقف،&lt;/p&gt;&lt;p&gt;سرنگون خواهد شد بر سر ما.&lt;/p&gt;&lt;p&gt;&lt;br&gt;گاه می‌لرزد باروی سکوت:&lt;/p&gt;&lt;p&gt;غول‌ها سر به زمین می‌سایند.&lt;/p&gt;&lt;p&gt;پای در پیش مبادا بنهید،&lt;/p&gt;&lt;p&gt;چشم‌ها در ره شب می‌پایند!&lt;/p&gt;&lt;p&gt;&lt;br&gt;تکیه گاهم اگر امشب لرزید،&lt;/p&gt;&lt;p&gt;بایدم دست به دیوار گرفت.&lt;/p&gt;&lt;p&gt;با نفس‌های شبم پیوندی است:&lt;/p&gt;&lt;p&gt;قصه‌ام دیگر زنگار گرفت.&lt;/p&gt;', 'دلسرد', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(456, '&lt;p&gt;سکوت ، بند گسسته‌است.&lt;/p&gt;&lt;p&gt;کنار دره، درخت شکوه پیکر بیدی.&lt;/p&gt;&lt;p&gt;در آسمان شفق رنگ&lt;/p&gt;&lt;p&gt;عبور ابر سپیدی.&lt;/p&gt;&lt;p&gt;&lt;br&gt;نسیم در رگ هر برگ می‌دود خاموش.&lt;/p&gt;&lt;p&gt;نشسته در پس هر صخره وحشتی به کمین.&lt;/p&gt;&lt;p&gt;کشیده از پس یک سنگ سوسماری سر.&lt;/p&gt;&lt;p&gt;ز خوف دره خاموش&lt;/p&gt;&lt;p&gt;نهفته جنبش پیکر.&lt;/p&gt;&lt;p&gt;به راه می‌نگرد سرد، خشک، تلخ، غمین.&lt;/p&gt;&lt;p&gt;&lt;br&gt;چو مار روی تن کوه می‌خزد راهی ،&lt;/p&gt;&lt;p&gt;به راه، رهگذری.&lt;/p&gt;&lt;p&gt;خیال دره و تنهایی&lt;/p&gt;&lt;p&gt;دوانده در رگ او ترس.&lt;/p&gt;&lt;p&gt;کشیده چشم به هر گوشه نقش چشمه وهم:&lt;/p&gt;&lt;p&gt;ز هر شکاف تن کوه&lt;/p&gt;&lt;p&gt;خزیده بیرون ماری.&lt;/p&gt;&lt;p&gt;به خشم از پس هر سنگ&lt;/p&gt;&lt;p&gt;کشیده خنجر خاری.&lt;/p&gt;&lt;p&gt;&lt;br&gt;غروب پر زده از کوه.&lt;/p&gt;&lt;p&gt;به چشم گم شده تصویر راه و راهگذر.&lt;/p&gt;&lt;p&gt;غمی بزرگ ، پر از وهم&lt;/p&gt;&lt;p&gt;به صخره سار نشسته‌است.&lt;/p&gt;&lt;p&gt;درون دره تاریک&lt;/p&gt;&lt;p&gt;سکوت بند گسسته‌است.&lt;/p&gt;', 'دره خاموش', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(457, '&lt;p&gt;شب ایستاده‌است.&lt;/p&gt;&lt;p&gt;خیره نگاه او&lt;/p&gt;&lt;p&gt;بر چهارچوب پنجره من.&lt;/p&gt;&lt;p&gt;سر تا به پای پرسش، اما&lt;/p&gt;&lt;p&gt;اندیشناک مانده و خاموش:&lt;/p&gt;&lt;p&gt;شاید&lt;/p&gt;&lt;p&gt;از هیچ سو جواب نیاید.&lt;/p&gt;&lt;p&gt;&lt;br&gt;دیری است مانده یک جسد سرد&lt;/p&gt;&lt;p&gt;در خلوت کبود اتاقم.&lt;/p&gt;&lt;p&gt;هر عضو آن ز عضو دگر دور مانده‌است،&lt;/p&gt;&lt;p&gt;گویی که قطعه ، قطعه دیگر را&lt;/p&gt;&lt;p&gt;از خویش رانده‌است.&lt;/p&gt;&lt;p&gt;از یاد رفته در تن او وحدت.&lt;/p&gt;&lt;p&gt;بر چهرهاش که حیرت ماسیده روی آن&lt;/p&gt;&lt;p&gt;سه حفره کبود که خالی است&lt;/p&gt;&lt;p&gt;از تابش زمان.&lt;/p&gt;&lt;p&gt;بویی فساد پرور و زهر آلود&lt;/p&gt;&lt;p&gt;تا مرزهای دور خیالم دویده‌است.&lt;/p&gt;&lt;p&gt;نقش زوال را&lt;/p&gt;&lt;p&gt;بر هرچه هست، روشن و خوانا کشیده‌است.&lt;/p&gt;&lt;p&gt;در اضطراب لحظه زنگار خورده‌ای&lt;/p&gt;&lt;p&gt;که روزهای رفته در آن بود ناپدید،&lt;/p&gt;&lt;p&gt;با ناخن این جسد را&lt;/p&gt;&lt;p&gt;از هم شکافتم،&lt;/p&gt;&lt;p&gt;رفتم درون هر رگ و هر استخوان آن&lt;/p&gt;&lt;p&gt;اما از آنچه در پی آن بودم&lt;/p&gt;&lt;p&gt;رنگی نیافتم.&lt;/p&gt;&lt;p&gt;&lt;br&gt;شب ایستاده‌است.&lt;/p&gt;&lt;p&gt;خیره نگاه او&lt;/p&gt;&lt;p&gt;بر چارچوب پنجره من.&lt;/p&gt;&lt;p&gt;با جنبش است پیکر او گرم یک جدال .&lt;/p&gt;&lt;p&gt;بسته‌است نقش بر تن لب‌هایش&lt;/p&gt;&lt;p&gt;تصویر یک سوال.&lt;/p&gt;', 'نایاب', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(458, '&lt;p&gt;زخم شب می‌شد کبود.&lt;/p&gt;&lt;p&gt;در بیابانی که من بودم&lt;/p&gt;&lt;p&gt;نه پر مرغی هوای صاف را می‌سود&lt;/p&gt;&lt;p&gt;نه صدای پای من همچون دگر شب‌ها&lt;/p&gt;&lt;p&gt;ضربه‌ای بر ضربه می‌افزود.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تا بسازم گرد خود دیواره‌ای سر سخت و پا برجای،&lt;/p&gt;&lt;p&gt;با خود آوردم ز راهی دور&lt;/p&gt;&lt;p&gt;سنگ‌های سخت و سنگین را برهنه‌ای.&lt;/p&gt;&lt;p&gt;ساختم دیوار سنگین بلندی تا بپوشاند&lt;/p&gt;&lt;p&gt;از نگاهم هر چه می‌آید به چشمان پست&lt;/p&gt;&lt;p&gt;و ببندد راه را بر حمله غولان&lt;/p&gt;&lt;p&gt;که خیالم رنگ هستی را به پیکرهایشان می‌بست.&lt;/p&gt;&lt;p&gt;&lt;br&gt;روز و شب‌ها رفت.&lt;/p&gt;&lt;p&gt;من بجا ماندم در این سو، شسته دیگر دست از کارم.&lt;/p&gt;&lt;p&gt;نه مرا حسرت به رگ‌ها می‌دوانید آرزویی خوش&lt;/p&gt;&lt;p&gt;نه خیال رفته‌ها می‌داد آزارم.&lt;/p&gt;&lt;p&gt;لیک پندارم، پس دیوار&lt;/p&gt;&lt;p&gt;نقش‌های تیره می‌انگیخت&lt;/p&gt;&lt;p&gt;و به رنگ دود&lt;/p&gt;&lt;p&gt;طرح‌ها از اهرمن می‌ریخت.&lt;/p&gt;&lt;p&gt;&lt;br&gt;تا شبی مانند شب‌های دگر خاموش&lt;/p&gt;&lt;p&gt;بی صدا از پا در آمد پیکر دیوار:&lt;/p&gt;&lt;p&gt;حسرتی با حیرتی آمیخت.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'دیوار', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(459, '&lt;p&gt;رنگی کنار شب&lt;/p&gt;&lt;p&gt;بی حرف مرده‌است.&lt;/p&gt;&lt;p&gt;مرغی سیاه آمده از راههای دور&lt;/p&gt;&lt;p&gt;می‌خواند از بلندی بام شب شکست.&lt;/p&gt;&lt;p&gt;سرمست فتح آمده از راه&lt;/p&gt;&lt;p&gt;این مرغ غم پرست.&lt;/p&gt;&lt;p&gt;&lt;br&gt;در این شکست رنگ&lt;/p&gt;&lt;p&gt;از هم گسسته رشته هر آهنگ.&lt;/p&gt;&lt;p&gt;تنها صدای مرغک بی باک&lt;/p&gt;&lt;p&gt;گوش سکوت ساده می‌آراید&lt;/p&gt;&lt;p&gt;با گوشوار پژواک.&lt;/p&gt;&lt;p&gt;&lt;br&gt;مرغ سیاه آمده از راههای دور&lt;/p&gt;&lt;p&gt;بنشسته روی بام بلند شب شکست&lt;/p&gt;&lt;p&gt;چون سنگ، بی تکان.&lt;/p&gt;&lt;p&gt;لغزانده چشم را&lt;/p&gt;&lt;p&gt;بر شکل‌های درهم پندارش.&lt;/p&gt;&lt;p&gt;خوابی شگفت می‌دهد آزارش:&lt;/p&gt;&lt;p&gt;گل‌های رنگ سر زده از خاک‌های شب.&lt;/p&gt;&lt;p&gt;در جاده‌های عطر&lt;/p&gt;&lt;p&gt;پای نسیم مانده ز رفتار.&lt;/p&gt;&lt;p&gt;هر دم پی فریبی، این مرغ غم پرست&lt;/p&gt;&lt;p&gt;نقشی کشد به یاری منقار.&lt;/p&gt;&lt;p&gt;&lt;br&gt;بندی گسسته‌است.&lt;/p&gt;&lt;p&gt;خوابی شکسته‌است.&lt;/p&gt;&lt;p&gt;رویای سرزمین&lt;/p&gt;&lt;p&gt;افسانه شکفتن گل‌های رنگ را&lt;/p&gt;&lt;p&gt;از یاد برده‌است.&lt;/p&gt;&lt;p&gt;بی حرف باید از خم این ره عبور کرد:&lt;/p&gt;&lt;p&gt;رنگی کنار این شب بی مرز مرده‌است.&lt;/p&gt;', 'مرگ رنگ', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12);
INSERT INTO `poem` (`id`, `poem`, `name`, `pname`, `book`, `form`, `admin_id`, `book_id`) VALUES
(460, '&lt;p&gt;تنها، و روی ساحل،&lt;/p&gt;&lt;p&gt;مردی به راه می‌گذرد.&lt;/p&gt;&lt;p&gt;نزدیک پای او&lt;/p&gt;&lt;p&gt;دریا، همه صدا.&lt;/p&gt;&lt;p&gt;شب، گیج در تلاطم امواج.&lt;/p&gt;&lt;p&gt;باد هراس پیکر&lt;/p&gt;&lt;p&gt;رو می‌کند به ساحل و در چشم‌های مرد&lt;/p&gt;&lt;p&gt;نقش خاطر را پر رنگ می‌کند.&lt;/p&gt;&lt;p&gt;انگار&lt;/p&gt;&lt;p&gt;هی می‌زند که: مرد! کجا می‌روی، کجا؟&lt;/p&gt;&lt;p&gt;و مرد می‌رود به ره خویش.&lt;/p&gt;&lt;p&gt;و باد سرگران&lt;/p&gt;&lt;p&gt;هی می‌زند دوباره: کجا می‌روی؟&lt;/p&gt;&lt;p&gt;و مرد می‌رود.&lt;/p&gt;&lt;p&gt;و باد همچنان...&lt;/p&gt;&lt;p&gt;&lt;br&gt;امواج، بی امان،&lt;/p&gt;&lt;p&gt;از راه می‌رسند&lt;/p&gt;&lt;p&gt;لبریز از غرور تهاجم.&lt;/p&gt;&lt;p&gt;موجی پر از نهیب&lt;/p&gt;&lt;p&gt;ره می‌کشد به ساحل و می‌بلعد&lt;/p&gt;&lt;p&gt;یک سایه را که برده شب از پیکرش شکیب.&lt;/p&gt;&lt;p&gt;&lt;br&gt;دریا، همه صدا.&lt;/p&gt;&lt;p&gt;شب، گیج در تلاطم امواج.&lt;/p&gt;&lt;p&gt;باد هراس پیکر&lt;/p&gt;&lt;p&gt;رو می‌کند به ساحل و ...&lt;/p&gt;', 'دریا و مرد', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(461, '&lt;p&gt;در شبی تاریک&lt;/p&gt;&lt;p&gt;که صدایی با صدایی در نمی‌آمیخت&lt;/p&gt;&lt;p&gt;و کسی کس را نمی‌دید از ره نزدیک،&lt;/p&gt;&lt;p&gt;یک نفر از صخره‌های کوه بالا رفت&lt;/p&gt;&lt;p&gt;و به ناخن‌های خون آلود&lt;/p&gt;&lt;p&gt;روی سنگی کند نقشی را و از آن پس ندیدش هیچکس دیگر.&lt;/p&gt;&lt;p&gt;شسته باران رنگ خونی را که از زخم تنش جوشید و روی صخره‌ها خشکید.&lt;/p&gt;&lt;p&gt;&lt;br&gt;از میان برده‌است طوفان نقش‌هایی را&lt;/p&gt;&lt;p&gt;که بجا ماند از کف پایش.&lt;/p&gt;&lt;p&gt;گر نشان از هر که پرسی باز&lt;/p&gt;&lt;p&gt;بر نخواهد آمد آوایش.&lt;/p&gt;&lt;p&gt;&lt;br&gt;آن شب&lt;/p&gt;&lt;p&gt;هیچکس از ره نمی‌آمد&lt;/p&gt;&lt;p&gt;تا خبر آرد از آن رنگی که در کار شکفتن بود.&lt;/p&gt;&lt;p&gt;کوه: سنگین، سرگران، خونسرد.&lt;/p&gt;&lt;p&gt;باد می‌آمد، ولی خاموش.&lt;/p&gt;&lt;p&gt;ابر پر می‌زد، ولی آرام.&lt;/p&gt;&lt;p&gt;لیک آن لحظه که ناخن‌های دست آشنای راز&lt;/p&gt;&lt;p&gt;رفت تا بر تخته سنگی کار کندن را کند آغاز،&lt;/p&gt;&lt;p&gt;رعد غرید،&lt;/p&gt;&lt;p&gt;کوه را لرزاند.&lt;/p&gt;&lt;p&gt;برق روشن کرد سنگی را که حک شد روی آن در لحظه‌ای کوتاه&lt;/p&gt;&lt;p&gt;پیکر نقشی که باید جاودان می‌ماند.&lt;/p&gt;&lt;p&gt;&lt;br&gt;امشب&lt;/p&gt;&lt;p&gt;باد و باران هر دو می‌کوبند:&lt;/p&gt;&lt;p&gt;باد خواهد برکند از جای سنگی را&lt;/p&gt;&lt;p&gt;و باران هم&lt;/p&gt;&lt;p&gt;خواهد از آن سنگ نقشی را فرو شوید.&lt;/p&gt;&lt;p&gt;هر دو می‌کوشند. می‌خروشند.&lt;/p&gt;&lt;p&gt;لیک سنگ بی محابا در ستیغ کوه&lt;/p&gt;&lt;p&gt;مانده برجا استوار، انگار با زنجیر پولادین.&lt;/p&gt;&lt;p&gt;سال‌ها آن را نفرسوده‌است.&lt;/p&gt;&lt;p&gt;کوشش هر چیز بیهوده‌است.&lt;/p&gt;&lt;p&gt;کوه اگر بر خویشتن پیچد،&lt;/p&gt;&lt;p&gt;سنگ بر جا همچنان خونسرد می‌ماند&lt;/p&gt;&lt;p&gt;و نمی‌فرساید آن نقشی که رویش کند در یک فرصت باریک&lt;/p&gt;&lt;p&gt;یک نفر کز صخره‌های کوه بالا رفت&lt;/p&gt;&lt;p&gt;در شبی تاریک.&lt;/p&gt;', 'نقش', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(462, '&lt;p&gt;می‌خروشد دریا.&lt;/p&gt;&lt;p&gt;هیچکس نیست به ساحل پیدا.&lt;/p&gt;&lt;p&gt;لکه‌ای نیست به دریا تاریک&lt;/p&gt;&lt;p&gt;که شود قایق&lt;/p&gt;&lt;p&gt;اگر آید نزدیک.&lt;/p&gt;&lt;p&gt;&lt;br&gt;مانده بر ساحل&lt;/p&gt;&lt;p&gt;قایقی ریخته شب بر سر او،&lt;/p&gt;&lt;p&gt;پیکرش را ز رهی نا روشن&lt;/p&gt;&lt;p&gt;برده در تلخی ادراک فرو.&lt;/p&gt;&lt;p&gt;هیچکس نیست که آید از راه&lt;/p&gt;&lt;p&gt;و به آب افکندش.&lt;/p&gt;&lt;p&gt;و دیر وقت که هر کوهه آب&lt;/p&gt;&lt;p&gt;حرف با گوش نهان می‌زندش،&lt;/p&gt;&lt;p&gt;موجی آشفته فرا می‌رسد از راه که گوید با ما&lt;/p&gt;&lt;p&gt;قصه یک شب طوفانی را.&lt;/p&gt;&lt;p&gt;&lt;br&gt;رفته بود آن شب ماهی گیر&lt;/p&gt;&lt;p&gt;تا بگیرد از آب&lt;/p&gt;&lt;p&gt;آنچه پیوندی داشت.&lt;/p&gt;&lt;p&gt;با خیالی در خواب&lt;/p&gt;&lt;p&gt;&lt;br&gt;صبح آن شب، که به دریا موجی&lt;/p&gt;&lt;p&gt;تن نمی‌کوفت به موجی دیگر،&lt;/p&gt;&lt;p&gt;چشم ماهی گیران دید&lt;/p&gt;&lt;p&gt;قایقی را به ره آب که داشت&lt;/p&gt;&lt;p&gt;بر لب از حادثه تلخ شب پیش خبر.&lt;/p&gt;&lt;p&gt;پس کشاندند سوی ساحل خواب آلودش&lt;/p&gt;&lt;p&gt;به همان جای که هست&lt;/p&gt;&lt;p&gt;در همین لحظه غمناک بجا&lt;/p&gt;&lt;p&gt;و به نزدیکی او می‌خروشد دریا&lt;/p&gt;&lt;p&gt;وز ره دور فرا می‌رسد آن موج که می‌گوید باز&lt;/p&gt;&lt;p&gt;از شب طوفانی&lt;/p&gt;&lt;p&gt;داستانی نه دراز.&lt;/p&gt;', 'سرگذشت', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(463, '&lt;p&gt;جهان، آلوده خواب است.&lt;/p&gt;&lt;p&gt;فرو بسته‌است وحشت در به روی هر تپش، هر بانگ&lt;/p&gt;&lt;p&gt;چنان که من به روی خویش&lt;/p&gt;&lt;p&gt;در این خلوت که نقش دلپذیرش نیست&lt;/p&gt;&lt;p&gt;و دیوارش فرو می‌خواندم در گوش:&lt;/p&gt;&lt;p&gt;میان این همه انگار&lt;/p&gt;&lt;p&gt;چه پنهان رنگ‌ها دارد فریب زیست!&lt;/p&gt;&lt;p&gt;&lt;br&gt;شب از وحشت گرانبار است.&lt;/p&gt;&lt;p&gt;جهان آلوده خواب است و من در وهم خود بیدار:&lt;/p&gt;&lt;p&gt;چه دیگر طرح می‌ریزد فریب زیست&lt;/p&gt;&lt;p&gt;در این خلوت که حیرت نقش دیوار است؟&lt;/p&gt;', 'وهم', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(464, '&lt;p&gt;حرف‌ها دارم&lt;/p&gt;&lt;p&gt;با تو ای مرغی که می‌خوانی نهان از چشم&lt;/p&gt;&lt;p&gt;و زمان را با صدایت می‌گشایی!&lt;/p&gt;&lt;p&gt;چه ترا دردی است&lt;/p&gt;&lt;p&gt;کز نهان خلوت خود می‌زنی آوا&lt;/p&gt;&lt;p&gt;و نشاط زندگی را از کف من می‌ربایی؟&lt;/p&gt;&lt;p&gt;&lt;br&gt;در کجا هستی نهان ای مرغ!&lt;/p&gt;&lt;p&gt;زیر تور سبزه‌های تر&lt;/p&gt;&lt;p&gt;یا درون شاخه‌های شوق؟&lt;/p&gt;&lt;p&gt;می‌پری از روی چشم سبز یک مرداب&lt;/p&gt;&lt;p&gt;یا که می‌شویی کنار چشمه ادارک بال و پر؟&lt;/p&gt;&lt;p&gt;هر کجا هستی، بگو با من.&lt;/p&gt;&lt;p&gt;روی جاده نقش پایی نیست از دشمن.&lt;/p&gt;&lt;p&gt;آفتابی شو!&lt;/p&gt;&lt;p&gt;رعد دیگر پا نمی‌کوبد به بام ابر.&lt;/p&gt;&lt;p&gt;مار برق از لانه‌اش بیرون نمی‌آید.&lt;/p&gt;&lt;p&gt;و نمی‌غلتد دگر زنجیر طوفان بر تن صحرا.&lt;/p&gt;&lt;p&gt;روز خاموش است، آرام است.&lt;/p&gt;&lt;p&gt;از چه دیگر می‌کنی پروا؟&lt;/p&gt;', 'با مرغ پنهان', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(465, '&lt;p&gt;می‌مکم پستان شب را&lt;/p&gt;&lt;p&gt;وز پی رنگی به افسون تن نیالوده&lt;/p&gt;&lt;p&gt;چشم پر خاکسترش را با نگاه خویش می‌کاوم.&lt;/p&gt;&lt;p&gt;&lt;br&gt;از پی نابودی‌ام، دیری است&lt;/p&gt;&lt;p&gt;زهر می‌ریزد به رگ‌های خود این جادوی بی آزرم&lt;/p&gt;&lt;p&gt;تا کند آلوده با آن شیر&lt;/p&gt;&lt;p&gt;پس برای آن که رد فکر او را گم کند فکرم،&lt;/p&gt;&lt;p&gt;می‌کند رفتار با من نرم.&lt;/p&gt;&lt;p&gt;لیک چه غافل!&lt;/p&gt;&lt;p&gt;نقشه‌های او چه بی حاصل!&lt;/p&gt;&lt;p&gt;نبض من هر لحظه می‌خندد به پندارش.&lt;/p&gt;&lt;p&gt;او نمی‌داند که روییده‌است&lt;/p&gt;&lt;p&gt;هستی پر بار من در منجلاب زهر&lt;/p&gt;&lt;p&gt;و نمی‌داند که من در زهر می‌شویم&lt;/p&gt;&lt;p&gt;پیکر هر گریه، هر خنده،&lt;/p&gt;&lt;p&gt;در نم زهر است کرم فکرمن زنده،&lt;/p&gt;&lt;p&gt;در زمین زهر می‌روید گیاه تلخ شعر من.&lt;/p&gt;', 'سرود زهر', 'سهراب سپهری', 'مرگ رنگ', 'کلاسیک و نیمایی', 7, 12),
(466, '&lt;p&gt;می‌رفتیم، و درختان چه بلند، و تماشا چه سیاه&lt;/p&gt;&lt;p&gt;راهی بود از ما تا گل هیچ.&lt;/p&gt;&lt;p&gt;مرگی در دامنه‌ها، ابری سر کوه، مرغان لب زیست.&lt;/p&gt;&lt;p&gt;می‌خواندیم: بی تو دری بودم به برون، و نگاهی به کران، و صدایی به کویر.&lt;/p&gt;&lt;p&gt;می‌رفتیم، خاک از ما می‌ترسید، و زمان بر سر ما می‌بارید.&lt;/p&gt;&lt;p&gt;خندیدیم: ورطه پرید از خواب، و نهان‌ها آوایی افشاندند.&lt;/p&gt;&lt;p&gt;ما خاموش، و بیابان نگران، و افق یک رشته نگاه.&lt;/p&gt;&lt;p&gt;بنشستیم، تو چشمت پر دور، من دستم پر تنهایی، و زمین‌ها پر خواب.&lt;/p&gt;&lt;p&gt;خوابیدیم. می گویند: دستی در خوابی گل می‌چید.&lt;/p&gt;', 'تا گل هیچ', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(467, '&lt;p&gt;افتاد. و چه پژواکی که شنید اهریمن. و چه لرزی که دوید از بن غم تا به بهشت.&lt;/p&gt;&lt;p&gt;من در خویش، و کلاغی لب حوض.&lt;/p&gt;&lt;p&gt;خاموشی، و یکی زمزمه ساز.&lt;/p&gt;&lt;p&gt;تنه تاریکی، تبر نقره نور.&lt;/p&gt;&lt;p&gt;و گوارایی بی گاه خطا. بوی تباهی‌ها، گردش زیست.&lt;/p&gt;&lt;p&gt;شب دانایی. و جدا ماندم: کو سختی پیکرها، کو بوی زمین، چینه بی بعد پری‌ها؟&lt;/p&gt;&lt;p&gt;اینک باد، پنجره‌ام رفته به بی پایان. خونی ریخت، بر سینه من ریگ بیابان باد!&lt;/p&gt;&lt;p&gt;چیزی گفت، و زمان‌ها بر کاج حیاط، همواره وزید و وزید. اینهم گل اندیشه، آنهم بت دوست.&lt;/p&gt;&lt;p&gt;نی، که اگر بوی لجن می‌آید، آنهم غوک، که دهانش ابدیت خورده است.&lt;/p&gt;&lt;p&gt;دیدار دگر، آری: روزن زیبای زمان.&lt;/p&gt;&lt;p&gt;ترسید، دستم به زمین آمیخت. هستی لب آیینه نشست، خیره به من: غم نامیرا.&lt;/p&gt;', 'به زمین', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(468, '&lt;p&gt;بالارو، بالارو. بند نگه بشکن، و هم سیه بشکن.&lt;/p&gt;&lt;p&gt;– آمده‌ام، آمده‌ام، بوی دگر می‌شنوم، باد دگر می‌گذرد.&lt;/p&gt;&lt;p&gt;روی سرم بید دگر، خورشید دگر.&lt;/p&gt;&lt;p&gt;– شهر تونی، شهر تونی،&lt;/p&gt;&lt;p&gt;می‌شنوی زنگ زمان: قطره چکید. از پی تو، سایه دوید.&lt;/p&gt;&lt;p&gt;شهر تو در کوی فراترها، دره دیگرها.&lt;/p&gt;&lt;p&gt;– آمده‌ام، آمده‌ام، می‌لغزد صخره سخت، می‌شنوم آواز درخت.&lt;/p&gt;&lt;p&gt;– شهر تونی، شهر تونی،&lt;/p&gt;&lt;p&gt;خسته چرا بال عقاب؟ و زمین تشنه خواب؟&lt;/p&gt;&lt;p&gt;و چرا روییدن، روییدن، رمزی را بوییدن؟&lt;/p&gt;&lt;p&gt;شهر تو رنگش دیگر. خاکش، سنگش دیگر.&lt;/p&gt;&lt;p&gt;– آمده‌ام، آمده‌ام، بسته نه دروازه نه در، جن‌ها هر سو بگذر.&lt;/p&gt;&lt;p&gt;و خدایان هر افسانه که هست. و نه چشمی نگران، و نه نامی ز پرست.&lt;/p&gt;&lt;p&gt;– شهر تونی، شهر تونی،&lt;/p&gt;&lt;p&gt;در کف‌ها کاسه زیبایی، بر لبها تلخی دانایی.&lt;/p&gt;&lt;p&gt;شهر تو در جای دگر، ره می بر با پای دگر.&lt;/p&gt;&lt;p&gt;– آمده‌ام، آمده‌ام، پنجره‌ها می‌شکفند.&lt;/p&gt;&lt;p&gt;کوچه فرو رفته به بی سویی، بی هایی، بی هویی.&lt;/p&gt;&lt;p&gt;– شهر تونی، شهر تونی،&lt;/p&gt;&lt;p&gt;در وزش خاموشی، سیماها در دود فراموشی.&lt;/p&gt;&lt;p&gt;شهر ترا نام دگر، خسته نه ای، گام دگر.&lt;/p&gt;&lt;p&gt;– آمده‌ام، آمده‌ام، درها رهگذر باد عدم.&lt;/p&gt;&lt;p&gt;خانه ز خود وارسته، جام دویی بشکسته. سایه یک روی زمین، روی زمان.&lt;/p&gt;&lt;p&gt;– شهر تونی این و نه آن.&lt;/p&gt;&lt;p&gt;شهر تو گم نشود، پیدا نشود.&lt;/p&gt;', 'تا', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(469, '&lt;p&gt;در آ، که کران را برچیدم، خاک زمان رفتم، آب نگر پاشیدم.&lt;/p&gt;&lt;p&gt;در سفالینه چشم، صدبرگ نگه بنشاندم، بنشستم.&lt;/p&gt;&lt;p&gt;آیینه شکستم، تا سرشار تو من باشم و من. جامه نهادم. رشته گسستم.&lt;/p&gt;&lt;p&gt;زیبایان خندیدند، خواب چرا دادمشان، خوابیدند.&lt;/p&gt;&lt;p&gt;غوکی می‌جست، اندوهش دادم، و نشست.&lt;/p&gt;&lt;p&gt;در کشت گمان، هر سبزه لگد کردم. از هر بیشه، شوری به سبد کردم.&lt;/p&gt;&lt;p&gt;بوی تو می‌آمد، به صدا نیرو، به روان پر دادم، آواز در آ سر دادم.&lt;/p&gt;&lt;p&gt;پژواک تو می‌پیچید، چکه شدم، از بام صدا لغزیدم، و شنیدم.&lt;/p&gt;&lt;p&gt;یک هیچ ترا دیدم، و دویدم.&lt;/p&gt;&lt;p&gt;آب تجلی تو نوشیدم، و دمیدم.&lt;/p&gt;', 'تراو', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(470, '&lt;p&gt;سایه شدم، و صدا کردم:&lt;/p&gt;&lt;p&gt;کو مرز پریدن‌ها، دیدن‌ها؟ کو اوج نه من، دره او؟&lt;/p&gt;&lt;p&gt;و ندا آمد: لب بسته بپو.&lt;/p&gt;&lt;p&gt;مرغی رفت، تنها بود، پر شد جام شگفت.&lt;/p&gt;&lt;p&gt;و ندا آمد: بر تو گوارا باد، تنهایی تنها باد!&lt;/p&gt;&lt;p&gt;دستم در کوه سحر او می‌چید، او می‌چید.&lt;/p&gt;&lt;p&gt;و ندا آمد: و هجومی از خورشید.&lt;/p&gt;&lt;p&gt;از صخره شدم بالا. در هر گام، دنیایی تنهاتر، زیباتر.&lt;/p&gt;&lt;p&gt;و ندا آمد: بالاتر، بالاتر!&lt;/p&gt;&lt;p&gt;آوازی از ره دور: جنگل‌ها می‌خوانند؟&lt;/p&gt;&lt;p&gt;و ندا آمد: خلوت‌ها می‌آیند.&lt;/p&gt;&lt;p&gt;و شیاری ز هراس.&lt;/p&gt;&lt;p&gt;و ندا آمد: یادی بود، پیدا شد، پهنه چه زیبا شد!&lt;/p&gt;&lt;p&gt;او آمد، پرده ز هم وا باید، درها هم.&lt;/p&gt;&lt;p&gt;و ندا آمد: پرها هم.&lt;/p&gt;', 'تنها باد', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(471, '&lt;p&gt;چه گذشت؟&lt;/p&gt;&lt;p&gt;– زنبوری پر زد&lt;/p&gt;&lt;p&gt;– در پهنه…&lt;/p&gt;&lt;p&gt;– وهم. این سو، آن سو، جویای گلی.&lt;/p&gt;&lt;p&gt;– جویای گلی، آری، بی ساقه گلی در پهنه خواب، نوشابه آن..&lt;/p&gt;&lt;p&gt;– اندوه. اندوه نگاه: بیداری چشم، بی برگی دست.&lt;/p&gt;&lt;p&gt;– نی. سبدی می کن، سفری در باغ.&lt;/p&gt;&lt;p&gt;– باز آمده‌ام بسیار، و ره آوردم: تیناب تهی.&lt;/p&gt;&lt;p&gt;– سفری دیگر، ای دوست، و به باغی دیگر.&lt;/p&gt;&lt;p&gt;– بدرود.&lt;/p&gt;&lt;p&gt;– بدرود، و به همراهت نیروی هراس.&lt;/p&gt;', 'روانه', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(472, '&lt;p&gt;بر آبی چین افتاد، سیبی به زمین افتاد.&lt;/p&gt;&lt;p&gt;گامی ماند. زنجره خواند.&lt;/p&gt;&lt;p&gt;همهمه‌ای: خندید. بزمی بود، برچیدند.&lt;/p&gt;&lt;p&gt;خوابی از چشمی بالا رفت. این رهرو تنها رفت، بی ما رفت.&lt;/p&gt;&lt;p&gt;رشته گسست: من پیچم، من تابم. کوزه شکست: من آبم.&lt;/p&gt;&lt;p&gt;این سنگ، پیوندش با من کو؟ آن زنبور، پروازش تا من کو؟&lt;/p&gt;&lt;p&gt;نقشی پیدا آیینه کجا؟ این لبخند، لب‌ها کو؟ موج آمد، دریا کو؟&lt;/p&gt;&lt;p&gt;می‌بویم، بو آمد. از هر سو، های آمد، هو آمد. من رفتم، او آمد، او آمد.&lt;/p&gt;', 'شکپوی', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(473, '&lt;p&gt;من سازم: بندی آواز. برگیرم، بنوازم. برتارم زخمه&lt;/p&gt;&lt;p&gt;لا می زن، راه فنا می زن&lt;/p&gt;&lt;p&gt;من دودم: می‌پیچم، می‌لغزم، نابودم.&lt;/p&gt;&lt;p&gt;می‌سوزم، می‌سوزم: فانوس تمنایم. گل کن تو مرا، و درآ.&lt;/p&gt;&lt;p&gt;آیینه شدم، از روشن و از سایه بری بودم. دیو و پری آمد،&lt;/p&gt;&lt;p&gt;دیو و پری بودم. در بی خبری بودم.&lt;/p&gt;&lt;p&gt;قرآن بالای سرم، بالش من انجیل، بستر من تورات، وزبر پوشم اوستا، می‌بینم خواب:&lt;/p&gt;&lt;p&gt;بودایی در نیلوفر آب.&lt;/p&gt;&lt;p&gt;هر جا گل‌های نیایش رست، من چیدم. دسته گلی دارم، محراب تو دور از دست: او بالا،&lt;/p&gt;&lt;p&gt;من در پست.&lt;/p&gt;&lt;p&gt;خوشبو سخنم، نی؟ باد بیا می‌بردم، بی توشه شدم در کوه کجا، گل چیدم، گل خوردم.&lt;/p&gt;&lt;p&gt;در رگ‌ها همهمه‌ای دارم، از چشمه خود آبم زن، آبم زن.&lt;/p&gt;&lt;p&gt;و به من یک قطه گوارا کن، شورم را زیبا کن.&lt;/p&gt;&lt;p&gt;باد انگیز، درهای سخن بشکن، جا پای صدای می روب. هم دود چرا می بر، هم موج من و ما و شما می بر.&lt;/p&gt;&lt;p&gt;ز شبم تا لاله بیرنگی پل بنشان، زین رؤیا در چشمم گل&lt;/p&gt;&lt;p&gt;بنشان، گل بنشان.&lt;/p&gt;', 'شورم را', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(474, '&lt;p&gt;از خانه بدر، از کوچه برون، تنهایی ما سوی خدا می‌رفت.&lt;/p&gt;&lt;p&gt;در جاده، درختان سبز، گل‌ها وا، شیطان نگران: اندیشه رها&lt;/p&gt;&lt;p&gt;می‌رفت.&lt;/p&gt;&lt;p&gt;خار آمد، و بیابان، و سراب.&lt;/p&gt;&lt;p&gt;کوه آمد و، خواب.&lt;/p&gt;&lt;p&gt;آواز پری: مرغی به هوا می‌رفت؟&lt;/p&gt;&lt;p&gt;– نی، همزاد گیاهی بود، از پیش گیا می‌رفت.&lt;/p&gt;&lt;p&gt;شب می‌شد و روز.&lt;/p&gt;&lt;p&gt;جایی، شیطان نگران: تنهایی ما می‌رفت.&lt;/p&gt;', 'شیطان هم', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(475, '&lt;p&gt;دیشب، لب رود، شیطان زمزمه داشت.&lt;/p&gt;&lt;p&gt;شب بود و چراغک بود.&lt;/p&gt;&lt;p&gt;شیطان، تنها، تک بود.&lt;/p&gt;&lt;p&gt;باد آمده بود، باران زده بود: شب‌تر، گل‌ها پرپر.&lt;/p&gt;&lt;p&gt;بویی نه براه.&lt;/p&gt;&lt;p&gt;ناگاه&lt;/p&gt;&lt;p&gt;آیینه رود، نقش غمی بنمود: شیطان لب آب.&lt;/p&gt;&lt;p&gt;خاک سایه در خواب.&lt;/p&gt;&lt;p&gt;زمزمه‌ای می‌مرد. بادی می‌رفت، رازی می‌برد.&lt;/p&gt;', 'لب آب', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(476, '&lt;p&gt;باد آمد، در بگشا، اندوه خدا آورد.&lt;/p&gt;&lt;p&gt;خانه بروب، افشان گل، پیک آمد، پیک آمد، مژده ز نا آورد.&lt;/p&gt;&lt;p&gt;آب آمد، آب آمد، از دشت خدایان نیز، گل‌های سیا آورد.&lt;/p&gt;&lt;p&gt;ما خفته، او آمد، خنده شیطان را بر لب ما آورد.&lt;/p&gt;&lt;p&gt;مرگ آمد&lt;/p&gt;&lt;p&gt;حیرت ما را برد،&lt;/p&gt;&lt;p&gt;ترس شما آورد.&lt;/p&gt;&lt;p&gt;در خاکی، صبح آمد، سیب طلا، از باغ طلا آورد.&lt;/p&gt;', 'نا', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(477, '&lt;p&gt;دستی افشان، تا ز سر انگشتانت صد قطره چکد، هر&lt;/p&gt;&lt;p&gt;قطره شود خورشیدی&lt;/p&gt;&lt;p&gt;باشد که به صد سوزن نور، شب ما را بکند&lt;/p&gt;&lt;p&gt;روزن روزن.&lt;/p&gt;&lt;p&gt;ما بی تاب، و نیایش بی رنگ.&lt;/p&gt;&lt;p&gt;از مهرت لبخندی کن، بنشان بر لب ما&lt;/p&gt;&lt;p&gt;باشد که سرودی خیزد در خورد نیوشیدن تو.&lt;/p&gt;&lt;p&gt;ما هسته پنهان تماشاییم.&lt;/p&gt;&lt;p&gt;ز تجلی ابری کن، بفرست، که ببارد بر سر ما&lt;/p&gt;&lt;p&gt;باشد که به شوری بشکافیم، باشد که ببالیم و&lt;/p&gt;&lt;p&gt;به خورشید تو پیوندیم.&lt;/p&gt;&lt;p&gt;ما جنگل انبوه دگرگونی.&lt;/p&gt;&lt;p&gt;از آتش همرنگی صد اخگر برگیر، برهم تاب، برهم پیچ:&lt;/p&gt;&lt;p&gt;شلاقی کن، و بزن بر تن ما&lt;/p&gt;&lt;p&gt;باشد که ز خاکستر ما، در ما، جنگل یکرنگی بدر&lt;/p&gt;&lt;p&gt;آرد سر.&lt;/p&gt;&lt;p&gt;چشمان بسپردیم، خوابی لانه گرفت.&lt;/p&gt;&lt;p&gt;نم زن بر چهره ما&lt;/p&gt;&lt;p&gt;باشد که شکوفا گردد زنبق چشم، و شود سیراب&lt;/p&gt;&lt;p&gt;از تابش تو، و فرو افتد.&lt;/p&gt;&lt;p&gt;بینایی ره گم کرد.&lt;/p&gt;&lt;p&gt;یاری کن، و گره زن نگه ما و خودت با هم&lt;/p&gt;&lt;p&gt;باشد که تراود در ما، همه تو.&lt;/p&gt;&lt;p&gt;ما چنگیم: هر تار از ما دردی، سودایی.&lt;/p&gt;&lt;p&gt;زخمه کن از آرامش نامیرا، ما را بنواز&lt;/p&gt;&lt;p&gt;باشد که تهی گردیم، آکنده شویم از والا نت&lt;/p&gt;&lt;p&gt;خاموشی.&lt;/p&gt;&lt;p&gt;آیینه شدیم، ترسیدیم از هر نقش.&lt;/p&gt;&lt;p&gt;خود را در ما بفکن.&lt;/p&gt;&lt;p&gt;باشد که فرا گیرد هستی ما را، و دگر نقشی&lt;/p&gt;&lt;p&gt;ننشیند در ما.&lt;/p&gt;&lt;p&gt;هر سو مرز، هر سو نام.&lt;/p&gt;&lt;p&gt;رشته کن از بی شکلی، گذران از مروارید زمان و مکان&lt;/p&gt;&lt;p&gt;باشد که بهم پیوندد همه چیز، باشد که نماند&lt;/p&gt;&lt;p&gt;مرز، که نماند نام.&lt;/p&gt;&lt;p&gt;ای دور از دست! پر تنهایی خسته است.&lt;/p&gt;&lt;p&gt;گه گاه، شوری بوزان&lt;/p&gt;&lt;p&gt;باشد که شیار پریدین در تو شود خاموش.&lt;/p&gt;', 'نیایش', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(478, '&lt;p&gt;سرچشمه رویش‌هایی، دریایی، پایان تماشایی.&lt;/p&gt;&lt;p&gt;تو تراویدی: باغ جهان‌تر شد، دیگر شد.&lt;/p&gt;&lt;p&gt;صبحی سر زد، مرغی پر زد، یک شاخه شکست: خاموشی هست.&lt;/p&gt;&lt;p&gt;خوابم بر بود، خوابی دیدم: تابش آبی در خواب، لرزش برگی در آب.&lt;/p&gt;&lt;p&gt;این سو تاریکی مرگ، آن سو زیبایی برگ. این‌ها چه، آن‌ها چیست، انبوه زمان‌ها چیست؟&lt;/p&gt;&lt;p&gt;این می‌شکفد، ترس تماشا دارد. آن می‌گذرد، وحشت دریا دارد.&lt;/p&gt;&lt;p&gt;پرتو محرابی، می‌تابی. من هیچم: پیچک خوابی. بر نرده اندوه تو می‌پیچم.&lt;/p&gt;&lt;p&gt;تاریکی پروازی، رویای بی آغازی، بی موجی، بی رنگی، دریای هم آهنگی!&lt;/p&gt;', 'هایی', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(479, '&lt;p&gt;تنها به تماشای چه ای؟&lt;/p&gt;&lt;p&gt;بالا، گل یک روزه نور.&lt;/p&gt;&lt;p&gt;پایین، تاریکی باد.&lt;/p&gt;&lt;p&gt;بیهوده مپای، شب از شاخه نخواهد ریخت، و دریچه خدا روشن نیست.&lt;/p&gt;&lt;p&gt;از برگ سپهر، شبنم ستارگان خواهد پرید.&lt;/p&gt;&lt;p&gt;تو خواهی ماند و هراس بزرگ. ستون نگاه، و پیچک غم.&lt;/p&gt;&lt;p&gt;بیهوده مپای.&lt;/p&gt;&lt;p&gt;برخیز، که وهم گلی، زمین را شب کرد.&lt;/p&gt;&lt;p&gt;راهی شو، که گردش ماهی، شیار اندوهی در پی خود نهاد.&lt;/p&gt;&lt;p&gt;زنجره را بشنو: چه جهان غمناک است، و خدایی نیست، و خدایی هست، و خدایی…&lt;/p&gt;&lt;p&gt;بی گاه است، ببوی و برو، و چهره زیبایی در خواب دگر ببین.&lt;/p&gt;', 'هلا', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(480, '&lt;p&gt;تاریکی، پیچک وار، به چپر ها پیچید، به حناها، افراها.&lt;/p&gt;&lt;p&gt;و هنوز، ما در کشت، در کف داس.&lt;/p&gt;&lt;p&gt;ما ماندیم، تا در رشته شب از گرد چپر ها وا شد، فردا شد.&lt;/p&gt;&lt;p&gt;روز آمد و رفت.&lt;/p&gt;&lt;p&gt;تاریکی، پیچک وار، به چپر ها پیچید، به حناها، افراها.&lt;/p&gt;&lt;p&gt;و هنوز، یک خوشه کشت، در خور چیدن نه، یاد رسیدن نه.&lt;/p&gt;&lt;p&gt;و هزاران روز، و هزاران بار&lt;/p&gt;&lt;p&gt;تاریکی، پیچک وار، به چپر ها پیچید، به حناها، افراها.&lt;/p&gt;&lt;p&gt;پایان شبی، ما در خواب، یک خوشه رسید، مرغی چید.&lt;/p&gt;&lt;p&gt;آواز پرش بیداری ما: ساقه لرزان پیام.&lt;/p&gt;', 'هنگامی', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(481, '&lt;p&gt;درها به طنین‌های تو وا کردم.&lt;/p&gt;&lt;p&gt;هر تکه نگاهم را جایی افکندم، پر کردم هستی ز نگاه.&lt;/p&gt;&lt;p&gt;بر لب مردابی، پاره لبخند تو بر روی لجن دیدم، رفتم به نماز.&lt;/p&gt;&lt;p&gt;در بن خاری، یاد تو پنهان بود، برچیدم، پاشیدم به جهان.&lt;/p&gt;&lt;p&gt;بر سیم درختان زدم آهنگ ز خود روییدن، و به خود گستردن.&lt;/p&gt;&lt;p&gt;و شیاریدم شب یکدست نیایش، افشاندم دانه راز.&lt;/p&gt;&lt;p&gt;و شکستم آویز فریب.&lt;/p&gt;&lt;p&gt;و دویدم تا هیچ. و دویدم تا چهره مرگ، تا هسته هوش.&lt;/p&gt;&lt;p&gt;و فتادم بر صخره درد. از شبنم دیدار توتر شد انگشتم، لرزیدم.&lt;/p&gt;&lt;p&gt;وزشی می‌رفت از دامنه‌ای، گامی همره او رفتم.&lt;/p&gt;&lt;p&gt;ته تاریکی، تکه خورشیدی دیدم، خوردم، و ز خود رفتم، و رها بودم.&lt;/p&gt;', 'و شکستم، و دویدم، و فتادم', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(482, '&lt;p&gt;ای درخور اوج! آواز تو در کوه سحر، و گیاهی به نماز.&lt;/p&gt;&lt;p&gt;غم‌ها را گل کردم، پل زدم از خود تا صخره دوست.&lt;/p&gt;&lt;p&gt;من هستم، و سفالینه تاریکی، و تراویدن راز ازلی.&lt;/p&gt;&lt;p&gt;سر بر سنگ، و هوایی که خنک، و چناری که به فکر، و روانی که پر از ریزش دوست.&lt;/p&gt;&lt;p&gt;خوابم چه سبک، ابر نیایش چه بلند، و چه زیبا بوته زیست، و چه تنها من!&lt;/p&gt;&lt;p&gt;تنها من، و سر انگشتم در چشمه یاد، و و کبوترها لب آب.&lt;/p&gt;&lt;p&gt;هم خنده موج، هم تن زنبوری بر سبزه مرگ، و شکوهی در پنجه باد.&lt;/p&gt;&lt;p&gt;من از تو پرم، ای روزنه باغ هم آهنگی کاج و من و ترس!&lt;/p&gt;&lt;p&gt;هنگام من است، ای در به فراز، آی جاده به نیلوفر خاموش پیام!&lt;/p&gt;', 'و چه تنها', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(483, '&lt;p&gt;می‌رویید. در جنگل، خاموشی رؤیا بود.&lt;/p&gt;&lt;p&gt;شبنم بر جا بود.&lt;/p&gt;&lt;p&gt;درها باز، چشم تماشا باز، چشم تماشاتر، و خدا در هر … آیا بود؟&lt;/p&gt;&lt;p&gt;خورشیدی در هر مشت: بام نگه بالا بود.&lt;/p&gt;&lt;p&gt;می‌بویید. گل وا بود؟ بوییدن بی ما بود: زیبا بود.&lt;/p&gt;&lt;p&gt;تنهایی، تنها بود.&lt;/p&gt;&lt;p&gt;نا پیدا، پیدا بود.&lt;/p&gt;&lt;p&gt;او آنجا، آنجا بود.&lt;/p&gt;', 'پادمه', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(484, '&lt;p&gt;نه تو می‌پایی، و نه کوه. میوه این باغ: اندوه، اندوه.&lt;/p&gt;&lt;p&gt;گل بتراود غم، تشنه سبویی تو. افتد گل، بویی تو.&lt;/p&gt;&lt;p&gt;این پیچک شوق، آبش ده، سیرابش کن. آن کودک ترس، قصه بخوان، خوابش کن.&lt;/p&gt;&lt;p&gt;این لاله هوش، از ساقه بچین. پرپر شد، بشود. چشم خداتر شد، بشود.&lt;/p&gt;&lt;p&gt;و خدا از تو نه بالاتر. نی، تنهاتر، تنهاتر.&lt;/p&gt;&lt;p&gt;بالاها، پستی‌ها یکسان بین. پیدا نه، پنهان بین.&lt;/p&gt;&lt;p&gt;بالی نیست، آیت پروازی هست. کس نیست، رشته آوازی هست.&lt;/p&gt;&lt;p&gt;پژواکی: رؤیایی پر زد رفت. شلپویی: رازی بود، در زد و رفت.&lt;/p&gt;&lt;p&gt;اندیشه: کاهی بود، در آخور ما کردند. تنهایی: آبشخور ما کردند.&lt;/p&gt;&lt;p&gt;این آب روان، ما ساده‌تریم. این سایه، افتاده‌تریم.&lt;/p&gt;&lt;p&gt;نه تو می‌پایی، و نه من، دیده‌تر بگشا. مرگ آمد، در بگشا.&lt;/p&gt;', 'پاراه', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13),
(485, '&lt;p&gt;اینجاست، آیید، پنجره بگشایید، ای من و دگر من‌ها: صد پرتو من در آب!&lt;/p&gt;&lt;p&gt;مهتاب، تابنده نگر، بر لرزش برگ، اندیشه من، جاده مرگ.&lt;/p&gt;&lt;p&gt;آنجا نیلوفرهاست، به بهشت، به خدا درهاست.&lt;/p&gt;&lt;p&gt;اینجا ایوان، خاموشی هوش، پرواز روان.&lt;/p&gt;&lt;p&gt;در باغ زمان تنها نشدیم. ای سنگ و نگاه، ای وهم و درخت، آیا نشدیم؟&lt;/p&gt;&lt;p&gt;من صخره – منام، تو شاخه – تویی.&lt;/p&gt;&lt;p&gt;این بام گلی، آری، این بام گلی، خاک است و من و پندار.&lt;/p&gt;&lt;p&gt;و چه بود این لکه رنگ، این دود سبک؟ پروانه گذشت؟ افسانه دمید؟&lt;/p&gt;&lt;p&gt;نی، این لکه رنگ، این دود سبک، پروانه نبود، من بودم و تو. افسانه نبود،&lt;/p&gt;&lt;p&gt;ما بود و شما.&lt;/p&gt;', 'چند', 'سهراب سپهری', 'شرق اندوه', 'کلاسیک و نیمایی', 7, 13);
INSERT INTO `poet` (`id`, `name`, `biography`) VALUES
(1, 'حافظ', 'حافظ شیرازی ، با نام کامل خواجه شمس‌الدین محمد شیرازی، در شیراز به دنیا آمد. پدرش، بهاءالدین محمد، در دوره‌ی اتابکان از اصفهان به شیراز مهاجرت کرده بود. مادرِ شمس‌الدین اهل کازرون بود و در شیراز زندگی می‌کرد. تاریخ دقیقی برای تولد این شاعر وجود ندارد؛ اما طبق تخمین‌ها حافظ را از شاعرهای قرن هشتم هجری قمری می‌دانند. حافظ شیرازی در همان دوران کودکی پدرش را از دست داد. زمانی که نوجوان بود، در نانوایی شاگردی می‌کرد و به مکتب‌خانه‌ی کنار نانوایی می‌رفت. او به یادگیری علوم مذهبی و ادبی علاقه‌ی زیادی داشت و در مجالس علمی و ادبی شرکت می‌کرد. شمس‌الدین زبان عربی را یاد گرفت و در سن نوجوانی توانست قرآن را حفظ کند. به همین خاطر او را با لقب «حافظ» می‌شناختند.\r\n\r\nحافظ شیرازی در دهه‌ی بیست‌سالگی بیشتر علوم دینی را می‌دانست و در مجالس صاحب‌نظر بود. قوام‌الدین عبدالله، مولانا بهاءالدین عبدالصمد بحرآبادی، مولانا شمس‌الدین عبدالله، قاضی عضدالدین عیجی، سید شریف علامه گرگانی از استادهای او بودند. او علاوه‌بر قرآن، تفسیر، فلسفه و نحو عربی را هم آموخت. طبق گفته‌ی خودش هر صبح مجلس درس قرآن داشت. یادگیری‌های حافظ شیرازی به همین‌ها ختم نشد و مباحثی درباره‌ی موسیقی خواند. او دستگاه‌ها و پرده‌های موسیقی را به‌خوبی می‌شناخت.'),
(2, 'خیام', 'نام کامل او عمر خیام نیشابوری غیاث‌الدین ابوالفتح عمر بن ابراهیم نیشابوری است. نام خیام (به معنی خیمه‌ساز) ممکن است از حرفه‌ی پدری او برگرفته شده باشد. از زندگی خیام اطلاعات چندان دقیقی در دست نیست و اختلاف‌نظرهای بسیاری در خصوص تمام ابعاد زندگی او، حتی زمان تولد و وفاتش وجود دارد.\r\n\r\nتولد عمر خیام نیشابوری را در حدود 439 هجری قمری برآورد کرده‌اند. خیام پیش از سفر به سمرقند در زادگاهش نیشابور به تحصیل در فلسفه و علوم مختلف پرداخت. وقایع سیاسی قرن یازدهم نقش مهمی در سیر زندگی خیام داشته است. ترکان سلجوقی قبایلی بودند که در قرن یازدهم به جنوب غربی آسیا حمله کردند و در نهایت حکومتی را پایه‌گذاری کردند که اغلب نقاط ایران و بین‌النهرین را زیر سلطه‌ی خود داشت. حاکم سلجوقی در نیشابور خود را سلطان خواند و سپس عازم بغداد شد. در طول استیلای این حکومتِ نظامی و سست‌بنیاد بود که عمر خیام نیشابوری بالیدن گرفت. اندیشمندان و حتی بزرگانی نظیر خیام نیز از اقدامات سبعانه‌ی این حکومت مصون نبودند مگر اینکه می‌توانستند به نحوی، نظر حکام محلی را به خود جلب کنند و از حمایت آنان‌ بهره‌مند شوند. حتی در آن صورت نیز این حمایت، ثبات چندانی نداشت چرا که سیاست‌های منطقه‌ای و توزیع قدرت و ثروت، مدام معاملات قدرت را در مناطق مختلف ایران تغییر می‌داد و بر دشواری شرایط می‌افزود.'),
(3, 'هوشنگ ابتهاج', 'امیر هوشنگ ابتهاج در شهر رشت در خانواده‌ای برجسته دیده به جهان گشود. پدر او از پزشکان مشهور این شهر و مدتی نیز رئیس بیمارستان پورسینای رشت بود. ابتهاج دوران تحصیلی دبستان را در شهر زادگاهش، و دوران دبیرستان را در تهران سپری کرد. او نوزده سال بیشتر نداشت که در سال 1325، اولین مجموعه شعر خود را با عنوان «نخستین نغمه‌ها» منتشر کرد. پس از چاپ کتاب نخستین نغمه‌ها، هوشنگ ابتهاج به طور جدی به سرودن شعر ادامه داد تا آنکه پس از گذشت پنج سال، در سال 1330، دومین اثر خود را با عنوان «سراب» به انتشار رساند.\r\n\r\nهوشنگ ابتهاج در سال 1337 و در 31سالگی، با آلما مایکیال ازدواج کرد. ثمره‌ی زندگی مشترک 63ساله‌ی آن‌ها چهار فرزند است. \r\n\r\nهوشنگ ابتهاج پس از کناره‌گیری داوود پیرنیا از سرپرستی برنامه‌ی رادیویی گلچین هفته یا همان «گلها»، از سال 1350 تا 1356 مسئولیت این برنامه‌ی محبوب را برعهده گرفت. وی همچنین در برهه‌ای به عنوان مدیر کل شرکت سیمان تهران فعالیت داشت.'),
(4, 'مولانا', 'مولانا جلال‌ الدین محمد بلخی ، مشهور به مولانا، شاعر، عارف و الهی‌دان برجسته‌ی ایرانی بود که در قرن هفتم هجری قمری می‌زیست. پدر او، بهاء‌الدین ولد، معروف به «سلطان‌العلما»، یکی از عالمان بزرگِ دوران بود و در بلخ شاگردان بسیار داشت. اگرچه مولانا جلال‌ الدین محمد بلخی بعدها نزد استادانی جز پدر تلمذ نمود، تربیت اولیه‌ و آشنایی ابتدایی‌ او با مسائل فقهی و اعتقادی تحت هدایت همین پدرِ فاضل صورت گرفت.\r\n\r\nمولانا جلال‌ الدین محمد بلخی در نوجوانی از بلخ بیرون آمد و پس از اقامت‌های کوتاه در چندین شهر در لارنده (قرامان کنونی) مسکن گزید. در همین دوران و سنین هفده یا هجده‌سالگی بود که با گوهر خاتون ازدواج کرد. دو پسر مولانا با نام‌های بهاء‌الدین و علاء‌الدین نیز در همین شهر چشم به جهان گشودند.\r\n\r\nپس از فوت سلطان‌العلما، شاگردان این صوفی بزرگ از جلال‌‌ الدین محمدِ بیست‌وچهار‌ساله انتظار داشتند که جای پدر را پر کند و بر مسند وعظ و فتوی نشیند؛ چنین نیز شد. چندی بعد، برهان‌‌الدین محقق ترمذی که از مریدان سلطان‌العلما در بلخ بود، نزد جلال‌ الدین جوان رفت و او را تحت ارشاد و تربیت خویش گرفت. به توصیه‌ی ترمذی، مولانا جلال‌ الدین محمد بلخی به سیر و سلوک روی آورد و به تصفیه و پالایش روح خویش پرداخت. اما این پالایش اتفاق نیفتاد، تا زمانی که شمس تبریزی از راه رسید و انقلابی در روح مولانای جوان پدید آورد.'),
(5, 'فرخی یزدی', 'میرزا محمد فرخی یزدی ملقب به تاج الشعرا (۱۲۶۸ شمسی – ۲۵ مهر ۱۳۱۸ شمسی) شاعر و روزنامه‌نگار آزادی‌خواه و دموکرات صدر مشروطیت است. وی سردبیر روزنامهٔ طوفان بود. او همچنین نمایندهٔ مردم یزد در دوره هفتم مجلس شورای ملی بود و در زندان قصر کشته شد. مدفن او ناشناخته است.'),
(6, 'سعدی', 'مشرف الدین مصلح بن عبدالله شیرازی شاعر و نویسندهٔ بزرگ قرن هفتم هجری قمری است. تخلص او «سعدی» است که از نام اتابک مظفرالدین سعد پسر ابوبکر پسر سعد پسر زنگی گرفته شده است. وی احتمالاً بین سالهای ۶۰۰ تا ۶۱۵ هجری قمری زاده شده است. در جوانی به مدرسهٔ نظامیهٔ بغداد رفت و به تحصیل ادب و تفسیر و فقه و کلام و حکمت پرداخت. سپس به شام و مراکش و حبشه و حجاز سفر کرد و پس از بازگشت به شیراز، به تألیف شاهکارهای خود دست یازید. وی در سال ۶۵۵ ه.ق سعدی‌نامه یا بوستان را به نظم درآورد و در سال بعد (۶۵۶ ه.ق) گلستان را تألیف کرد. علاوه بر اینها قصاید، غزلیات، قطعات، ترجیع بند، رباعیات و مقالات و قصاید عربی نیز دارد که همه را در کلیات وی جمع کرده‌اند. وی بین سالهای ۶۹۰ تا ۶۹۴ هجری قمری در شیراز درگذشت و در همانجا به خاک سپرده شد.'),
(7, 'سهراب سپهری', 'سهراب سپهری اهل کاشان بود. او در 15 مهرماه سال 1307 در این شهر چشم به جهان گشود. پدربزرگش میرزا نصرالله خان سپهری را نخستین رئیس تلگراف‌خانه‌ی کاشان می‌دانند. پدرش، اسدالله، و مادرش، ماه‌جبین، هر دو اهل هنر و شعر بودند و شاید همین امر در رشد و بالندگی ادبی و هنری سهراب سپهری تأثیر فراوان گذاشت.\r\n\r\nسهراب سپهری در کاشان در دبستان خیام و دبیرستان پهلوی تحصیل کرد و پس از آنکه دانش‌سرای مقدماتی پسران را به اتمام رسانید، در سال 1325 در اداره‌ی فرهنگ کاشان مشغول به کار شد. پس از دریافت دیپلم به تهران آمد و در دانشکده‌ی هنرهای زیبای دانشگاه تهران به تحصیل پرداخت. هم‌زمان با تحصیل به استخدام شرکت نفت در تهران درآمد، اما هنوز یک‌ سال نگذشته بود که استعفا داد. در آذر 1333 در موزه‌ی اداره‌ی کل هنرهای زیبا شروع به کار کرد و همزمان به تدریس در هنرستان هنرهای زیبا نیز می‌پرداخت.');
